INSERT INTO `industrial_innovation` VALUES (551, '辽宁省仿生医用材料研发工程实验室', '中国科学院金属研究所', '2016', '省级', '院所', '新材料领域创新资源机构', '工程实验室', '辽宁省仿生医用材料工程实验室是在辽宁省、沈阳市发改委共同支持下，依托中国科学院金属研究所工程合金研究部于2015年开始筹建的省级工程实验室。', '实验室主要研发（1）纳米磷酸钙材料；（2）人工骨材料；（3）人工心脏瓣膜材料；（4）3D打印功能材料等。\n', '实验室承担了科技部重点研发计划、中科院前沿科学重点研究计划、中科院知识创新工程、国家自然科学基金等多项研究任务，申报（或取得授权）发明专利10余项，并拥有完备的材料制备与表征设备，能满足多种生物医用材料的生产、定制加工和性能检测需求。', NULL, NULL, '沈阳市文化路72号中国科学院金属研究所 ', '110016\n', '李毅  ', NULL, '024-23902021 ', NULL, 'BBMLab@163.com ', NULL);
INSERT INTO `industrial_innovation` VALUES (552, '辽宁省生物炭技术工程实验室', '沈阳农业大学', '2017', '省级', '高校', '新材料领域创新资源机构', '工程实验室', '辽宁省生物炭技术工程实验室依托沈阳农业大学，被辽宁省发展和改革委员会批准为省级工程实验室，负责人为农学院孟军教授。', NULL, NULL, NULL, '目前，生物炭研究团队与辽宁金和福农业科技股份有限公司、沈阳隆泰生物工程有限责任公司及辽宁神农佰奥生物质科技开发有限公司建立了产、学、研合作模式，不仅为实验室设备研发提供支撑，也将进一步推动科技成果转化。', '沈阳市沈河区东陵路120号 ', '110866\n', '冯玉龙 ', NULL, '024-88487163 ', '024-88490070  ', NULL, NULL);
