INSERT INTO `industrial_innovation` VALUES (922, '辽宁省消化内镜诊疗工程技术研究中心', '中国医科大学', '2018', '省级', '高校', '其他领域创新资源机构', '工程技术研究中心', '依托于中国医科大学，辽宁省消化内镜诊疗工程技术研究中心获批于2018年。', NULL, NULL, NULL, '专注于废水处理净化这一关键环节，通过分析水质及废水中污染物的特征，“从源头到末端”开展创新性的组合，集成相关处理技术，并对具有市场价值的科研成果进行工程化开发和系统集成，最终实现示范应用，从而彻底改变当前化工企业“遇污治污”的被动局面，为未来化工园区实现智慧化管理积累大数据。', '沈北新区蒲河路77号 ', '110178\n', '孙思予 ', '张凯 ', '18940251329、18040079518 ', NULL, NULL, NULL);
