INSERT INTO `industrial_innovation` VALUES (926, '辽宁省北方杂交粳稻工程技术研究中心', '辽宁省稻作研究所', '2005', '省级', '院所', '其他领域创新资源机构', '工程技术研究中心', '依托辽宁省稻作研究所，辽宁省北方杂交粳稻工程技术中心于2005年被辽宁省科技厅批准组建。', NULL, NULL, NULL, '中心的总体目标是：面向全国，走向世界，使工程中心成为具有国际先进水平的新型肥料工艺与产品的研发基地与技术产地，推进我国肥料行业向前发展。', '沈阳市苏家屯区枫杨路129号 ', '110101', '李跃东 ', NULL, '13940368487', NULL, NULL, NULL);
