INSERT INTO `industrial_innovation` VALUES (994, '辽宁省采矿工程重点实验室', '东北大学', '2006', '省级', '高校', '其他领域创新资源机构', '工程实验室', '辽宁省采矿工程重点实验室于2006年12月由辽宁省科技厅批准建立，依托东北大学，采矿工程国家重点学科。以国家“211工程”和“985工程”重点学科建设项目为契机，紧紧围绕矿产资源开发与利用理论与技术发展的要求，以“安全、高效开发资源和矿业可持续发展”为研究主题，坚持重点学科的自身特色，促进不同学科的联合和优势互补，凝炼学科研究方向，深入开展采矿新技术研究，促进矿业可持续发展和东北老工业基地振兴。', '采矿工程实验室经过长期的研究与实践，并通过重点实验室建设，逐步形成了特色鲜明、优势突出的四个矿床安全高效低贫损综合开采、矿山动力灾害发生机理与预测、矿产低废开采与废弃物资源化利用、数字矿山技术与优化决策。\n', '实验室已拥有岩石失稳与控制系统、虚拟现实系统、采矿方法模型实验系统、开采诱发微震定位监测与分析系统、爆破声发射监测与分析系统、度弃物综合利用制备与检测系统、大规模高性能岩石破裂数值试验系统、两相渗流试验系统等一批先进、大型实验设备，形成了设备配套较为齐全的实验研究体系，具有承担重大科研项目的能力。', NULL, '采矿工程重点实验室建设期间，“211工程”、“985工程”和地方政府对实验室投资4150万元，加上学校配套资金，共投资8460万元，新增设备总价值2295万元，新添10万元以上设备40件/套、价值1347万元，新增实验室使用面积2233m2。', '辽宁省沈阳市和平区文化路三巷11号 ', '110004', '任凤玉 ', '畅大鸿 ', '024-83671626 ', '024-83671626 ', 'yangtianhong@mail.neu.edu.cn ', NULL);
