INSERT INTO `industrial_innovation` VALUES (1003, '辽宁省村镇污水处理与资源化工程研究中心', '辽宁省环境科学研究院', '2009', '省级', '院所', '其他领域创新资源机构', '工程研究中心', '辽宁省环境科学研究院和辽宁大学环境学院联合组建，辽宁省村镇污水处理与资源化工程研究中心于2009年9月经辽宁省发展和改革委员会批准（辽发改高技术[2009]952号）正式组建。该工程研究中心的组建标志着环境学院已成为全省农村环境保护技术研究开发、成果转化和公共技术服务创新平台的重要技术支撑单位。', NULL, NULL, NULL, '（1）作为村镇污水处理技术研发平台，带动北方地区污水处理技术的发展；（2）作为村镇污水处理技术与设备转化平台，加快技术产业化速度；（3）作为村镇污水处理技术交流平台，加强社会服务能力。', '沈阳市皇姑区泰山路88巷3号 ', '110031\n', '赵军 ', NULL, '024-86806780 ', NULL, NULL, NULL);
