INSERT INTO `site_policy_rule` VALUES ('00d61f650fea4e8c81ed47c5cd3953a0', 'ZCFG_00d61f650fea4e8c81ed47c5cd3953a0', '沈阳市科技计划项目验收实施细则（试行）', '2', '沈阳市科学技术局', '<p style=\"background: white; text-align: center;\"><br/></p><p style=\"text-align:center;background:white\"><span style=\"font-size: 24px;font-family: 宋体\">第一章&nbsp;总&nbsp;则</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第一条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">为进一步加强和规范沈阳市科技计划项目验收管理工作，确保实现科技计划执行效果和绩效目标，根据《沈阳市科技计划管理改革方案》（沈科发〔2016〕37号）、《沈阳市科学技术计划项目管理办法》（沈科发〔2016〕53号）等有关规定，制定本细则。</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第二条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">科技计划项目验收是指由沈阳市科技局（以下简称市科技局）及其认定的专业服务机构，按照规定的方式和程序，对市科技计划立项项目完成情况进行检查验收，并做出评价结论。</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第三条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">项目验收工作坚持职责明晰、突出重点、分类实施、科学高效、公开透明的原则。</span></p><p style=\"text-align:center;background:white\"><span style=\"font-size: 24px;font-family: 宋体\">第二章&nbsp;工作职责</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第四条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">市科技局分管处室主要负责验收项目的审核工作，包括：</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（一）审核项目验收申请书及相关配套材料；</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（二）对审核合格，可以进行验收的项目盖章；</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（三）将盖章后的验收材料移交受委托的专业机构进行验收；</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（四）对验收项目材料进行归档管理。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">市科技局计划处主要负责验收项目的综合管理，对项目验收结果进行复核，形成中期项目验收批复意见或结题验收证书。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">市科技局监察处负责项目验收过程监审工作。</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第五条&nbsp;</span></strong><span style=\"font-size: 24px;font-family: 宋体\">科技服务机构受市科技局委托，负责组织专家对项目进行评审验收，并形成项目验收意见。承担市科技局委托的其他相关工作事项。</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第六条&nbsp;</span></strong><span style=\"font-size: 24px;font-family: 宋体\">沈阳市科学技术总院为市科技局委托的专业科技服务机构。驻沈高等学校、科研院所、医疗机构也可以受市科技局委托，对本单位承担立项项目进行验收。</span></p><p style=\"text-align:center;background:white\"><span style=\"font-size: 24px;font-family: 宋体\">第三章&nbsp;验收管理</span></p><p style=\"text-indent: 47px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第七条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">验收范围。对重点科技研发计划、科技创新平台建设计划、产业发展应用基础研究计划项目实行验收管理。</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第八条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">验收分类。项目验收分为中期验收和结题验收两种。</span></p><p style=\"background: white\"><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 24px;font-family: 宋体\">（一）中期验收项目。包括：</span></p><p style=\"text-indent: 42px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">1.</span><span style=\"font-size: 24px;font-family: 宋体\">重点科技研发计划的重大共性关键技术创新专项中的产业类项目；</span></p><p style=\"text-indent: 42px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">2.</span><span style=\"font-size: 24px;font-family: 宋体\">重点科技研发计划的重大共性关键技术创新专项中的公共领域类项目；</span></p><p style=\"text-indent: 42px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">3.</span><span style=\"font-size: 24px;font-family: 宋体\">重点科技研发计划的对外交流合作项目。</span></p><p style=\"text-indent: 42px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（二）结题验收项目。包括：</span></p><p style=\"text-indent: 42px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">1.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">重点科技研发计划项目；</span></p><p style=\"text-indent: 42px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">2.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">科技创新平台建设计划项目；</span></p><p style=\"text-indent: 42px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">3.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">产业发展应用基础研究计划项目。</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第九条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">验收内容</span></p><p style=\"text-indent: 42px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（一）中期验收。包括：</span></p><p style=\"line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">1.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目合同书约定的中期任务、目标和指标完成情况；</span></p><p style=\"line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">2.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目中期产生的经济社会效益；</span></p><p style=\"line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">3.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目中期取得的成果及其应用情况；</span></p><p style=\"line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">4.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目中期经费使用情况；</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">5.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目后续技术经费安排计划等。</span></p><p style=\"line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">该验收结论作为拨付阶段后补助和前资助资金的重要依据。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（二）结题验收。包括：</span></p><p style=\"line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">1.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目合同书约定的任务、目标和指标完成情况；</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">2.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目产生的经济社会效益；</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">3.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目取得的成果及其应用情况；</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">4.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目经费使用情况等。</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">该验收结论作为拨付阶段后补助资金的重要依据。</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第十条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">验收方式</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（一）会议验收。<span style=\"letter-spacing:-0\">对支持经费在10万元以上，</span>需要通过现场考察、测试，或经过质询讨论才能做出结论的，采用会议形式验收。会议验收可采取单项会议验收或多项集中会议验收方式。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（二）函审验收。<span style=\"letter-spacing:-0\">对支持经费在10万元及以下的项目可采取函审的方式验收。</span></span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（三）委托验收。<span style=\"letter-spacing:-0\">对支持经费在10万元及以下由</span>高等学校、科研院所、医疗机构等单位承担<span style=\"letter-spacing:-0\">的项目可采取</span>委托验收<span style=\"letter-spacing:-0\">的方式验收。</span></span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">会议验收、函审验收由沈阳科技总院承担完成；委托验收由市科技局委托高等学校、科研院所、医疗机构等单位完成。</span></p><p style=\"text-align:center;background:white\"><span style=\"font-size: 24px;font-family: 宋体\">第四章&nbsp;验收申请</span></p><p style=\"text-indent: 43px;line-height: 39px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第十一条&nbsp;</span></strong><a><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目承担单位应按照项目约定验收时间提出验收申请，在沈阳市科技创新管理平台上填写验收材料，报市科技局审核。</span></a></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第十二条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">申请验收时间。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（一）中期验收。重点科技研发计划重大共性关键技术创新专项中的产业类项目可在项目实施满10个月后，1个月期限内，由项目承担单位申请中期验收；公共领域类项目<span style=\"letter-spacing:-0\">、对外交流合作类及科技创新平台建设计划项目</span>可在项目实施满1年后，1个月期限内，由项目承担单位申请中期验收。</span></p><p style=\"text-indent: 43px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（二）结题验收。<span style=\"letter-spacing:-0\">重点科技研发计划、科技创新平台建设计划、产业发展应用基础研究计划实行结题验收管理。结题验收应在合同执行期满后6个月内完成。项目承担单位在合同实施期内已全面完成项目合同所约定各项任务和指标的，可申请提前结题验收。</span></span></p><p style=\"background: white\"><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><strong><span style=\"font-size: 24px;font-family: 宋体\">第十三条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">验收材料。包括：</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">（一）验收申请书。</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">（二）项目合同书。</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">（三）项目执行情况总结报告。</span></p><p style=\"text-indent: 43px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">（四）项目合同书签订的实施内容和考核指标完成情况证明材料，包括工艺、技术、产品检测报告，实验数据报告，用户应用报告，论文、人才及团队建设情况等资料或照片，以及有关经济、社会效益证明材料等。</span></p><p style=\"text-indent: 43px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">（五）有关知识产权、技术标准、查新检索材料以及法律、法规规定需提供的相关证明材料等。</span></p><p style=\"text-indent: 43px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">（六）项目资金使用情况的审计报告。由具有相关资质的审计机构依据市科技局与项目承担单位签订的立项合同及项目预算表，对项目进行专项审计后出具。主要审计内容包括项目执行期内自筹资金投入情况、财政补助资金预算执行情况、财政补助资金会计核算情况等，并附立项合同及项目预算表（原件）、项目财务核算账及主要支出票据（复印件）等资料。</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">（七）其他相关材料。</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目承担单位应据实提供相关证明材料，并对所提供的验收材料的真实性负责。</span></p><p style=\"text-align:center;background:white\"><span style=\"font-size: 24px;font-family: 宋体\">第五章&nbsp;验收程序</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第十四条&nbsp;</span></strong><span style=\"font-size: 24px;font-family: 宋体\">市科技局分管处室负责督促项目承担单位按照项目约定结题时间提出验收申请，在沈阳市科技创新管理平台上填写验收材料，报市科技局审核。验收申请材料经市科技局分管处室审核通过后，移交科技服务机构等受委托单位受理验收材料和组织验收评审。</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第十五条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">科技服务机构等受委托单位组织专家对项目进行验收评审，形成专家验收意见，并将项目验收结果报送市科技局计划处。</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第十六条&nbsp;</span></strong><span style=\"font-size: 24px;font-family: 宋体\">市科技局计划处对验收结果复审后，对通过中期验收的项目，依据专家提出的中期验收考核意见，出具中期验收批复意见，其考核结果作为拨付后续项目资金的依据。对通过结题验收的项目，由专家提出结题验收考核意见，验收通过项目由市科技局计划处出具《沈阳市科学技术计划项目验收证书》，并履行拨付项目剩余资金相关手续。</span></p><p style=\"text-align:center;background:white\"><span style=\"font-size: 24px;font-family: 宋体\">第六章&nbsp;专家评审</span></p><p style=\"text-indent: 43px;line-height: 39px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第十七条&nbsp;</span></strong><span style=\"font-size: 24px;font-family: 宋体\">专家组成。<span style=\"letter-spacing:-0\">专家组一般由5名专家组成，其中应有财务专家1名，由技术专家担任专家组组长。评审专家应从沈阳市科技评审专家库中选取。专家应客观、公正、独立地对验收项目的技术、经济、资金等方面情况进行审查。项目承担单位、参与单位及合作单位的人员以及其他可能影响项目验收公正性的利益相关人员，不能作为专家参加验收工作。</span></span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第十八条&nbsp;</span></strong><span style=\"font-size: 24px;font-family: 宋体\">评审程序。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（一）会议评审。包括：</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">1.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">查阅项目验收资料；</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">2.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">查阅或听取项目承担单位的项目完成情况报告；</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">3.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">查阅或听取项目承担单位的项目财务决算报告；</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">4.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">专家对有关问题质询；</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">5.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">专家对重大项目进行现场核验；</span></p><p style=\"text-indent: 43px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">6.</span><span style=\"font-size: 24px;font-family: 宋体\">专家组讨论形成验收意见。</span></p><p style=\"text-indent: 43px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">会议评审由服务机构组织实施。</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">（二）委托评审。由市科技局委托高等学校、科研院所、医疗机构等项目组织单位自行组织评审，有关程序参照会议评审。评审结束后10个工作日内将评审结果报市科技局备案。</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">（三）函审。由服务机构组织专家对书面验收材料进行审核后，独立出具专家意见，专家组长综合其他专家意见后，形成专家组验收意见。</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第十九条&nbsp;</span></strong><span style=\"font-size: 24px;font-family: 宋体\">验收意见。专家验收意见包括：</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">1.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">何时、何地、何单位组织验收；专家听取项目承担单位的项目执行情况报告和对有关问题的质询；验收材料是否齐全、规范，是否符合验收要求；</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">2.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目完成情况：主要技术指标、经济指标、知识产权、人才培养、项目前景等；</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">3.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目资金落实与使用情况；经费使用是否规范合理；</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">4.</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">验收结论。验收结论分为通过验收、不通过验收。项目已按照合同书要求完成规定目标和任务且经费使用符合规定的，为通过验收。</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">凡具有下列情况之一，为不通过验收：</span></p><p style=\"text-indent: 42px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">（1）合同约定的主要实施内容和考核指标未完成的；</span></p><p style=\"text-indent: 43px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">（2）提供的验收资料、数据不完整、不真实的；</span></p><p style=\"text-indent: 43px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">（3）挪用资金或违反资金使用规定的；</span></p><p style=\"text-indent: 43px;line-height: 39px;background: white\"><span style=\"font-size: 24px;font-family: 宋体\">（4）存在其他严重违约、违纪行为的。</span></p><p style=\"text-align:center;line-height:39px;background:white\"><span style=\"font-size: 24px;font-family: 宋体\">第七章&nbsp;验收复核</span></p><p style=\"text-indent: 43px;line-height: 39px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第二十条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">对不通过验收的项目，由项目承担单位进行整改，在3个月内提出二次验收申请；对不按期提出验收申请或仍未通过二次验收的项目，视为最终不通过验收项目。</span></p><p style=\"text-indent: 43px;line-height: 39px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第二十一条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体;letter-spacing: -0\">项目承担单位对验收意见有异议的，可在验收后5个工作日内向市科技局提出复核申请。由市科技局组织复核，并做出最终结论意见。</span></p><p style=\"text-align:center;background:white\"><span style=\"font-size: 24px;font-family: 宋体\">第八章&nbsp;验收责任</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第二十二条</span></strong><strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span></strong><strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span></strong><span style=\"font-size: 24px;font-family: 宋体\">接受委托验收任务的服务机构应严格按照验收规定程序和原则组织验收，确保项目验收公平、公正、及时、准确。如有违纪、违规现象发生，取消承担委托任务的资格，并追究相关组织者的责任。</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第二十三条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">参加项目验收组织工作的人员，应当严格遵守职业道德规范，如发现违纪现象，将取消其参与验收工作的资格，并追究相应责任。&nbsp;</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第二十四条</span></strong><span style=\"font-size: 24px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 24px;font-family: 宋体\">项目承担单位在验收过程中如有徇私舞弊行为，一经查实，应当中止验收；已经完成验收的，应当予以撤销，并按有关规定进行处理。</span></p><p style=\"text-align:center;background:white\"><span style=\"font-size: 24px;font-family: 宋体\">第九章&nbsp;附&nbsp;则</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 24px;font-family: 宋体\">第二十五条&nbsp;</span></strong><span style=\"font-size: 24px;font-family: 宋体\">本实施细则自发布之日起施行。</span></p><p><br/></p>', '0', '0', '1', '2017-04-06 00:00:00', '1', '2017-11-13 14:08:47', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 14:51:28', '0', '');
INSERT INTO `site_policy_rule` VALUES ('01945057ad9b415ea3d78d7dfa4a8e8f', 'ZCFG_01945057ad9b415ea3d78d7dfa4a8e8f', '沈阳市科技企业孵化器认定和管理办法', '2', '沈阳市科学技术局', '<p style=\"text-align: center;\"><br/></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一条 为了提高自主创新能力，加快科技成果转化，培育中小科技企业，发展高新技术产业，规范科技企业孵化器的认定和管理工作，推动科技企业孵化器的健康发展，依据《科技企业孵化器认定和管理办法》(国科发高〔2010〕680号) 和《沈阳市科技进步条例》的规定，结合本市实际，制定本办法。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二条 科技企业孵化器(以下简称孵化器)，是指以促进科技成果转化、培养高新技术企业和企业家为宗旨的科技创业服务载体。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">孵化器是区域创新体系的重要组成部分，是创新创业人才培养基地，具体包括高新技术创业服务中心、大学科技园、各类科技创业园等形式。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三条 市科技局是全市孵化器的行政主管部门，负责市级孵化器的认定工作，并对全市孵化器进行宏观管理和业务指导。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四条 孵化器的主要功能是以科技型创业企业(以下简称在孵企业)为服务对象，通过开展创业培训、辅导、咨询，提供研发、试制、经营的场地和共享设施，以及政策、法律、财务、投融资、企业管理、人力资源、市场推广和加速成长等方面的服务，以降低创业风险和创业成本，提高企业的成活率和成长性，培养成功的科技企业和企业家。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五条 孵化器的发展目标：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">一)落实自主创新战略，营造适合科技创业的局部优化环境，培育高端的、前瞻的和具有带动作用的战略性新兴产业的早期企业，贡献于区域产业升级和经济结构调整，促进经济增长方式转变；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">二)落实人才强市战略，以孵化器为载体，以培养科技创业人才为目标，构建并完善创业服务网络，持续培养、造就具有创新精神和创业能力的创业领军人才，吸引海内外科技创业者服务于创新型城市建设。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六条 鼓励大型企业、科研机构等建立专业孵化器，形成专业技术、项目、人才和服务资源的集聚，促进传统产业的技术升级和经济结构调整，提升行业竞争力。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">专业孵化器是指围绕特定技术领域或者特殊人群，在孵化对象、服务内容、运行模式和技术平台上实现专业化服务的孵化器。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七条 申请认定市级孵化器应当具备下列条件：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">一)发展方向明确，符合本办法第二条和第四条规定的条件。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">二)领导班子得力，机构设置合理，管理人员中具有大专以上学历的占职工总数的70%以上。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">三)可自主支配孵化场地面积在5000平方米以上(专业孵化器3000平方米以上)。其中，在孵企业使用的场地(含公共服务场地)占2/3以上；孵化场地面积的扩大，依据可自主支配性和在孵企业使用性的原则确定。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">公共服务场地是指孵化器提供给在孵企业共享的活动场所，包括公共餐厅和接待室、会议室、展示室、活动室、技术检测室等非盈利性配套服务场地。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">四)服务设施齐备，服务功能完善，可为企业提供商务、资金、信息、咨询、市场、培训、技术开发与交流、国际合作等方面服务。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">五)管理规范，具有严格的财务管理制度，自身及在孵企业的统计数据齐全；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">六)可自主支配场地内的在孵企业达到20家以上(专业孵化器的在孵企业10家以上)。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">七)累计毕业企业达到10家以上。毕业企业和在孵企业提供400个以上的就业岗位(专业孵化器的毕业企业5家以上，毕业企业和在孵化企业提供200个以上的就业岗位)。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">八)孵化器自有种子资金或者孵化资金100万元人民币以上，并与创业投资、担保等机构建立正常业务联系。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">九)实际运营时间在1年以上，且经营状况良好。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">十)专业孵化器应当具备专业技术领域的公共平台或者中试平台，并具有专业化的技术服务能力和管理团队。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第八条 在孵企业应当具备下列条件：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">一)企业注册地和主要研发、办公场所必须在本孵化场地内；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">二)企业在孵时间一般不超过3年；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">三)企业注册资金一般不得超过200万元；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">四)企业年研发投入占年销售收入的5%以上；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">五)单一在孵企业入驻时使用的孵化场地面积，一般不大于1000平方米(从事航空航天等特殊领域的在孵企业，一般不大于3000平方米)；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">六)在孵企业从事研发、生产的主营项目(产品)，应当符合国家战略性新兴产业的发展导向，并符合国家节能减排标准；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">七)在孵企业开发的项目(产品)，知识产权界定清晰，无纠纷；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">八)在孵企业团队具有开拓创新精神，对技术、市场、经营和管理有一定驾驭能力。留学生和大学生企业的团队主要管理者或者技术带头人，由其本人担任。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第九条 毕业企业应当具备下列条件中至少二条：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">一)有自主知识产权；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">二)有2年以上的运营期，经营状况良好，主导产品有一定的生产规模，年技工贸总销售收入500万元以上，且有100万元以上的固定资产和自有资金；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">三)被认定为高新技术企业；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">四)被兼并、收购或者在国内外资本市场上市。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十条 申报市级孵化器的基本程序：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">一)申报单位向所在区、县(市)、开发区科技行政主管部门提出申请，由所在区、县(市)、开发区科技行政主管部门上报市科技局；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">二)市科技局对基本具备条件的申报单位组织专家评审，依据评审意见，将符合条件的单位确认为市级孵化器，并颁发标牌。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">被认定为市级孵化器的单位，其原产权和隶属关系不变。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十一条 申报单位应当提供下列材料：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">一)市级孵化器认定申请书;</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">二)法人代码证书(或者营业执照)复印件及上年度财务报表;</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">三)孵化场地证明材料(采取租赁、合作等方式形成的企业孵化场地,需提供产权方产权证明和相关协议文件);</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">四)孵化器管理机构设置与职能相关文件的复印件;</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">五)孵化器管理团队名册及学位证书的复印件;</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">六)孵化器运行管理相关制度的复印件;</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">七)孵化器在孵企业名单汇总表(包括：企业名称、入驻时间、注册资金、上年度的营业收入、注册时间、技术领域、所占孵化场地面积、上年度年末职工数、企业负责人学历等)，并提供在孵企业营业执照复印件；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">八)在孵企业与孵化器签署的孵化服务协议或者入驻协议复印件；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">九)毕业企业名单(包括：企业名称、入驻时间、毕业时间、技术领域、毕业时是否被认定为高新技术企业等)；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">十)孵化器自有种子资金或者孵化资金证明材料和上年度使用说明;</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">十一)近2年成功孵化案例介绍(2-3个);</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">十二)如申报专业技术型孵化器，需附上专业技术平台或者专业化中试基地设备清单(包括：设备名称、用途、单价等);</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">十三)其他证明材料。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十二条 经认定的市级孵化器，市，区、县(市)人民政府、开发区以及各级科技行政主管部门应当在科技计划立项、信息交流、人才培训等方面给予支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十三条 市科技局应当将孵化器工作纳入高新技术产业化工作体系，依据国家统计局审定的《科技企业孵化器综合情况》报表，对孵化器实行年度统计分析。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十四条 市级孵化器实行审核和动态管理，并择优推荐申报省级、国家级孵化器，考核未达标的责令整改。连续2年未达标则取消市级孵化器资格，且此后2年内不予受理市级孵化器申请。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">对已获得国家级和省级认定资格的孵化器，自动纳入全市孵化器的统计范围。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十五条 市级孵化器每年应当向市科技局报送孵化器年度运行报告及统计情况。对重大事项，及时向市科技局报告。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十六条 孵化器应当对在孵企业进行动态管理，实行淘汰制度。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十七条 市科技局指导孵化器行业组织在各孵化器间或者孵化器与中介机构、创业投资机构等的合作与交流，采用信息咨询、培训、研讨、交流与服务等方式，提高服务能力。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十八条 本办法自2013年1月1日起施行，有效期至2017年12月31日。2007年7月2日发布的《沈阳市科技企业孵化器认定和管理办法》(沈科发〔2007〕24号)同时废止。</span></p><p><br/></p>', '0', '0', '1', '2015-07-07 00:00:00', '1', '2017-11-13 14:10:17', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 14:52:57', '0', '');
INSERT INTO `site_policy_rule` VALUES ('09252be945344732ae96349af101ec51', 'ZCFG_09252be945344732ae96349af101ec51', '沈阳市科学技术普及条例', '2', '沈阳市人民政府', '<p style=\"background: white; text-align: center;\"><br/></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一章总则</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一条为了加强科学技术普及工作，提高公众科学文化素质，促进社会主义物质文明和精神文明建设，根据《中华人民共和国科学技术进步法》和有关法律、法规，结合本市实际，制定本条例。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二条本市行政区域内的机关、社会团体、企事业单位及其他组织和公民，均应遵守本条例。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三条本条例所称科学技术普及（以下简称科普），是指以提高公众科学文化素质为目的，用公众易于理解和接受的方式，将科学知识、科学精神、科学思想和科学方法向公众传播的行为。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四条各级人民政府应当加强对科普工作的领导和管理，将其纳入国民经济、社会发展计划；研究解决重大问题，组织动员全体市民共同参与，为科普工作创造良好的条件和社会环境。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五条市人民政府科学技术行政部门主管全市科普工作及本条例的实施。其职责是制定科普工作总体规划和年度计划，督促检查，推动科普工作发展。市人民政府其他有关部门应当按照各自职责协助做好科普工作。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六条科学技术协会是科学技术工作者的群众组织，是科普工作的主要社会力量，依法履行科普工作职责。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七条科普是一项社会公益性事业，应坚持长期、稳定、有效发展的原则。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第八条科普工作应面向全体市民，重点是青少年、农村干部群众、企业职工和各级领导干部。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第九条市、区、县（市）人民政府应当建立科普工作联席会议制度，由各级科学技术行政主管部门组织统筹协调本地区科普工作。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二章科普工作的内容与形式</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十条科普工作的内容：</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）弘扬科学精神，倡导实事求是、探索求知、崇尚真理、勇于创新的精神；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）普及科学思想，介绍科学对人类社会发展的引导和促进作用，树立科学的世界观、人生观、价值观和科学技术是第一生产力的思想；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）介绍当代科学技术的新思想、新理论、新方法和新成果以及当前国内外科学技术发展动向、前景、问题和对策等方面的知识；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）推广先进适用的新技术、新工艺和新材料；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（五）普及日常生活中的科学知识，倡导科学、文明、健康的生活方式；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（六）宣传科学方法，介绍运用唯物辩证法和现代科技手段解决经济、社会和技术问题的办法和途径；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（七）其他有关科普工作的内容。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十一条科普工作的形式：</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）举行科普讲座、专题报告会、研讨会和科普作品展示会；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）开展科学技术咨询、服务、新技术推广、科学技术信息发布、科学技术示范活动；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）在各类学校开展科学发明、科技制作、专题研究、撰写科技论文和组织科学考察等活动；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）组织科技人员下乡，开展科学技术兴农活动；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（五）组织各类科学技术培训和岗位技术培训，举办技术、技能竞赛；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（六）编写、制作、出版科普读物和科普影视作品；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（七）设立科普画廊橱窗，展示科普图片、模型和实物；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（八）开放科学技术场馆，开设科普图书、报刊阅读场所，放映科普电影、科普录像；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（九）开通科普网站，开设广播、电视科普专题节目，开辟报刊科普专栏；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（十）开展科技周等活动；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（十一）其他科普工作的形式。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三章社会责任</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十二条普及科学技术是全社会的共同责任，社会各界均应积极参加和支持开展科普工作。市民均有参加科普活动的权利、接受科普教育的义务。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十三条各单位应建立职工学习现代科学知识和接受科普教育的制度。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十四条各级科学技术协会应当开展经常性、群众性和社会性的科普活动；加强对所属团体和专业研究会科普工作的组织管理与业务指导。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十五条各级工会、共青团、妇联应当组织职工、青年、妇女开展技术培训、技术推广等科普活动。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十六条城乡基层社会自治组织应当结合各自的特点，开展科普宣传，组织多种形式的科普活动。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十七条各类学校应当结合学生特点，开展多种形式的科普活动，提高学生的科学兴趣，培养学生的创新精神和实践能力。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十八条科研机构、技术推广机构、农村专业技术协会（研究会）等应当面向农村、贫困地区，开展科普工作，普及先进适用技术，推广先进农业科技成果，培训农村科技人才。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十九条厂矿企业应当向职工普及与生产有关的科学技术、职业卫生、安全防护等方面的知识，提高职工的科学文化素质和生产技能。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十条商业、服务业企业应当结合商品销售和服务项目开展科普工作。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十一条环境保护、医药卫生和计划生育机构应当结合环境保护、资源合理利用、医疗保健、计划生育等方面内容开展科普工作。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十二条宣传部门应当加强科普宣传，报纸、刊物、广播、电视、网络等传播媒体应当广泛开展多种形式的科普宣传。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十三条鼓励高等院校、科研机构、高新技术企业以及其他组织面向青少年开放实验室、研究场所、观测站、技术推广示范基地等，开展科普活动。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十四条鼓励科技、教育工作者从事科普作品创作，宣传科学知识，传授科学技术与方法，担任中小学专题研究活动的指导教师。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四章科普场所</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十五条科普场所是指科技馆（宫）、博物馆、天文馆、图书馆、青少年科技活动中心、青少年宫、科普基地等开展科普活动的场所。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十六条科普场所开展科普活动应向社会开放，改进科普工作形式和充实科普内容，提高科普工作质量，并建立健全科普管理的规章制度。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十七条医院、公园、旅游景点、影剧院、体育场馆、商场、车站、机场等公共场所的经营管理单位，应当根据各自特点，开展面向公众的科普宣传。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五章科普工作者</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十八条专门从事科普工作的人员及中小学科技辅导教师为专职科普工作者。其他从事科普工作的人员为兼职科普工作者。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十九条科普工作者享有下列权利：</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）依法创办或者参加科普组织，自主开展科普活动；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）向有关部门申请科普项目经费；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）专职科普工作者按照有关规定参加相应系列的专业技术职称评审时，其科普著作、论文和其他科普优秀成果，应当作为晋升专业技术职称的依据之一；兼职科普工作者的科普著作、论文和其他科普优秀成果，应当作为晋升专业技术职称的参考条件；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）参加专业技术培训，提高工作水平；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（五）向有关部门提出科普工作意见和建议。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十条科普工作者应当履行下列义务：</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）开展或者参加科普活动，传播普及科学技术知识，推广应用科学技术成果；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）反对封建迷信活动，抵制反科学、伪科学行为；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）学习新知识、新技术，提高自身素质；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）宣传和执行科普方面法律法规。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六章保障措施</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十一条各级人民政府应当保证科普经费的投入，科普经费应列入同级财政支出预算，及时拨付，专款专用。科普经费主要用于开展经常性、群众性和社会性的科普工作。市科学技术普及活动经费的投入应为市本级年度财政支出预算的0.5‰以上，且不低于市总人口每人平均0.50元的水平。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十二条各级人民政府鼓励国内外组织和个人兴建、联建科普设施，捐助资金，发展科普事业。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十三条各级人民政府应当将科普设施建设纳入城市建设规划，加速科普设施建设。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十四条科普场所开展科普活动按文化事业管理，可获得专项经费资助和活动经费；接受合法捐赠；申请科普专题项目。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十五条在公共场所设立科普画廊、橱窗及开展科普宣传，经市人民政府科学技术行政主管部门认定并依照有关规定履行审批手续后，可减免收费。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十六条对在科普工作中做出突出贡献或者对科普事业捐资数额较大的组织和个人，由各级人民政府予以表彰和奖励。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七章法律责任</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十七条擅自将科普场所改作他用的，由科普工作主管部门责令限期改正；情节严重的，对直接责任人给予行政处分。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十八条损害、破坏科普场所、设施的，责令其赔偿损失或恢复原状；构成犯罪的，依法追究刑事责任。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十九条挪用、克扣、截留科普经费的，由经费划拨部门责令限期改正，其主管部门对直接责任人给予行政处分；构成犯罪的，依法追究刑事责任。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十条以科普名义从事封建迷信活动，扰乱社会秩序，危害公共利益或者骗取财物的，由公安机关依法予以处罚；构成犯罪的，依法追究刑事责任。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十一条国家机关工作人员因玩忽职守、滥用职权，给科普事业造成损失，侵犯科普工作者合法权益的，由其所在单位或者上级机关给予行政处分；构成犯罪的，依法追究刑事责任。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第八章附则</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十二条本条例应用中的具体问题，由市人民政府负责解释。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十三条本条例自2000年6月18日起施行</span></p><p><br/></p>', '0', '0', '1', '2000-06-18 00:00:00', '1', '2017-11-13 14:14:12', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:34:11', '0', '');
INSERT INTO `site_policy_rule` VALUES ('098bbf9cca2a4fb7aa67237be7372cd7', 'ZCFG_098bbf9cca2a4fb7aa67237be7372cd7', '沈阳市人民政府关于加快推进IC装备产业发展的实施意见', '2', '沈阳市人民政府', '<p style=\"text-align: left;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 18px;\">各区、县（市）人民政府，市政府各部门，各直属单位：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">IC</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">装备产业是基础性、战略性产业，是衡量一个国家和地区装备制造业发展水平和综合竞争力的重要标志。发展IC装备产业，对建设具有国际竞争力的先进装备制造基地具有重要的推动作用。为加快我市IC装备产业发展，根据国家、省相关文件精神，结合我市实际，制定实施意见如下：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">一、明确推进IC装备产业发展的指导思想和发展目标</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）指导思想。以邓小平理论、“三个代表”重要思想和科学发展观为指导，坚持政府扶植与市场推动并举、规模扩张与产业升级并重，以IC产业链建设为主线，以关键精密零部件加工与制造为支撑，努力建设国家IC装备产业高地，为沈阳装备制造业转型升级奠定坚实基础。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）发展目标。依托沈阳IC装备产业园，做强做大一批整机设备及关键单元部件制造企业，建设8英寸及以上IC制造和芯片封装测试企业，打造集设计、制造、材料为一体的全产业链条，努力将IC装备产业培育成支柱产业。到2017年，IC装备产业实现年销售收入70亿元、利税24亿元；到2020年，实现年销售收入200亿元、利税70亿元。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">二、制定推动IC装备产业健康快速发展的政策措施</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）加大资金支持力度。从2014年开始，根据实际需求，连续3年每年从我市支持产业发展类资金中统筹安排，以无偿资助、政策性补贴等方式支持我市IC装备产业发展，东陵区（浑南新区）要给予同等规模资金配套；市财政、东陵区（浑南新区）共同组建的规模5亿元的创业投资基金，重点保证沈阳IC装备产业园基础设施和重大产业化项目建设，推动IC装备产业规模化、集群化发展。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）支持IC装备产业园开发建设。将沈阳IC装备产业园基础设施、重点项目建设，列入市重点工业地产项目计划，优先安排一定的项目建设用地计划指标；对开展工业地产开发建设的投资主体，待项目竣工核定后，给予完成固定资产投资额的5%补助。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（五）鼓励企业争取国家项目。积极帮助具备条件的企业争取国家科技重大专项和经费支持；对承担和参与国家科技重大专项的，按照国家和省明确的地方配套要求，给予配套支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（六）支持企业开展技术创新和引进先进技术。对于IC装备企业独立或联合开展的各类研发及产业化项目，以及企业向境外机构购买技术使用权或所有权，并进行引进消化再创新的研发项目，给予重点支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（七）支持企业加大知识产权工作力度。鼓励IC装备企业开展发明创造、引进并实施专利技术；促进专利技术转化和以专利技术创办IC装备科技型企业，推进专利技术在我市运用和产业化；我市专利择优费向IC装备产业重点倾斜和支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（八）支持企业重大项目建设。对符合IC装备产业创新要求及产业发展方向的重点项目，可提供代建生产及科研用房，并免收前3年房租。所需资金由东陵区（浑南新区）政府负责。对新引进具有重大影响力的产业化项目，在项目扶持、产业用地等方面，给予积极的支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（九）鼓励园区企业开展合作。园区内企业之间生产的整机装备、关键单元部件、精密零部件、设备控制系统等实现内配的，对符合条件的研发、产业化项目给予重点支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（十）支持孵化器建设。鼓励国内外有关单位创建企业孵化器，对一次性拓展孵化面积超过1万平方米、在孵企业超过30家或新建并被认定为国家级孵化器的单位，对其公共技术服务平台建设给予支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（十一）鼓励公共技术服务平台建设。支持国内外企业及相关机构创建各类公共技术服务平台，对单独或联合建立专业性公共技术服务平台的单位，根据公共技术服务平台实现的功能，给予投资总额一定比例的支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（十二）引进、培养支撑IC装备产业发展的创新人才。对企业引进紧缺、具有特殊才能的人才，按照省、市特殊人才引进政策给予支持。市人才资源开发专项资金对企业引进的高端人才和高层次人才（团队）予以重点资助；对引进并列入国家“千人计划”、“万人计划”(服务年限不低于5年)IC产业人才的企业，给予资金补助；在随迁家属工作安置及子女就学方面给予照顾。鼓励有条件的高校采取与IC装备企业联合办学等方式培养IC装备产业专业人才。支持建立校企结合的人才综合培训和实践基地。允许进入园区内的企业实行股权、期权激励政策。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">三、建立促进IC装备产业发展的工作机制</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（十三）加强组织领导。成立市推进IC装备产业发展工作领导小组。组长由市长担任，副组长由相关分管副市长担任，领导小组办公室设在市科技局，办公室主任由市科技局局长担任。领导小组成员由市直相关部门、东陵区（浑南新区）政府共同组成，负责协调推进沈阳IC装备产业基地建设、总体规划审议、重大政策制定、重大项目引进及解决其他重大问题。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（十四）完善市区联动机制。按照市区联动、属地负责的原则，市直相关部门要大力支持IC装备产业发展，保证财政资金到位，加强对资金使用情况的监管。要加强服务、分类指导、一企一策，帮助我市IC装备企业解决发展中的实际问题，推进企业做强做大；东陵区（浑南新区）要建立运转高效、服务到位的园区管理机制，加强建设管理，保障建设成效。要创新资金使用方式，有效利用融资租赁等各种金融工具，形成加快推进IC装备产业发展的政策合力。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（十五）建设科技创新服务平台。整合优势资源，鼓励企业、高校、科研机构、行业组织等单位共同建设面向园区企业、资源共享的科技创新服务平台，为知识创新、技术创新、模式创新提供服务。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（十六）制定产业发展规划。尽快组织编制《沈阳IC装备产业发展规划》，根据产业发展趋势，梳理我市IC装备产业发展现状，突出优势与特色，明晰产业布局，确定发展目标和发展重点。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（十七）加快产业园区建设。在东陵区（浑南新区）集中规划建设占地3平方公里的沈阳IC装备产业园，东陵区（浑南新区）要做好园区建设规划，推进产业园区“七通一平”，加快标准化厂房、孵化器、科研人员公寓等基础和配套服务设施建设。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-align:right\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-align:right\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">沈阳市人民政府</span></p><p style=\"text-align:right\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">2013</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">年12月17日</span></p><p><br/></p>', '0', '0', '1', '2013-12-17 00:00:00', '1', '2017-11-13 14:20:04', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:33:17', '0', '');
INSERT INTO `site_policy_rule` VALUES ('09960612e4a54e3e80406a0861ae39a2', 'ZCFG_09960612e4a54e3e80406a0861ae39a2', '辽宁省人民政府关于进一步做好促进科技成果转化和技术转移工作的通知', '1', '辽宁省人民政府', '<p style=\"text-align: center;\"><br/></p><p><span style=\"font-size: 16px;font-family: 宋体\">各市人民政府，省政府各厅委、各直属机构： </span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">&nbsp;</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　为贯彻落实《国务院关于印发实施〈中华人民共和国促进科技成果转化法〉若干规定的通知》（国发〔2016〕16号）和《辽宁省人民政府关于进一步促进科技成果转化和技术转移的意见》（辽政发〔2015〕55号）精神，进一步激发广大科技人员投身创新创业的活力，推进科技成果转化转移，加快辽宁老工业基地新一轮全面振兴，现就有关事项通知如下：</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　一、支持高等院校、科研院所转化转移科技成果</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（一）鼓励高等院校、科研院所通过转让、许可或者作价投资等方式，向企业或者其他组织转移科技成果。高等院校、科研院所对其持有的科技成果可以自主决定转让、许可或者作价投资，除涉及国家秘密、国家安全外，不需审批或者备案。（牵头部门:省教育厅、省科技厅，配合部门：省财政厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（二）高等院校、科研院所转化科技成果所获得的收入全部留归单位，纳入单位预算，不上缴国库；扣除对完成和转化职务科技成果做出重要贡献人员的奖励和报酬后，应当主要用于科学技术研发与成果转化等相关工作，并对技术转移机构的运行和发展给予保障。（牵头部门：省教育厅、省科技厅，配合部门：省财政厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　经所在单位同意，高等院校、科研院所研发团队可享有研发成果在本省转化的使用权、处置权和收益权。（牵头部门：省科技厅、省教育厅，配合部门：省财政厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（三）高等院校、科研院所对其持有的科技成果，应当通过协议定价、在技术交易市场挂牌交易、拍卖等市场化方式确定价格。协议定价的，科技成果持有单位应当在本单位公示科技成果名称和拟交易价格，公示时间不少于15日。公示单位应当明确并公开异议处理程序和办法。（牵头部门：省教育厅、省科技厅，配合部门：省财政厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（四）高等院校、科研院所制定转化科技成果收益分配制度时，要按照规定充分听取本单位科技人员的意见，并在本单位公开相关制度。依法对职务科技成果完成人和为成果在本省转化作出重要贡献的其他人员给予奖励时，按照以下规定执行：</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　1．以技术转让或者许可方式在本省转化职务科技成果的，应当从技术转让或者许可所取得的净收入中提取不低于70%的比例用于奖励。</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　2．以科技成果作价投资在本省实施转化的，应当从作价投资取得的股份或者出资比例中提取不低于70%的比例用于奖励。</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　3．在研究开发和科技成果在本省转化中作出主要贡献的人员，获得奖励的份额不低于奖励总额的70%。</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　4．对科技人员在本省科技成果转化工作中开展技术开发、技术咨询、技术服务等活动给予的奖励，可按照促进科技成果转化法和本规定执行。（牵头部门：省教育厅、省科技厅，配合部门：省财政厅、省人力资源社会保障厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（五）对于担任领导职务的科技人员获得科技成果转化奖励，按照分类管理的原则执行：</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　1．高等院校、科研院所等事业单位（不含内设机构）正职领导，以及上述事业单位所属具有独立法人资格单位的正职领导，是科技成果的主要完成人或者对科技成果在本省转化作出重要贡献的，可以按照促进科技成果转化法和本通知规定获得现金奖励，原则上不得获取股权激励。其他担任领导职务的科技人员，是科技成果的主要完成人或者对科技成果在本省转化作出重要贡献的，可以按照促进科技成果转化法和本通知规定获得现金、股份或者出资比例等奖励和报酬；按照干部管理权限审批后，可以在本省科技成果转化企业兼职，但不能领取其他报酬。</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　2．对担任领导职务的科技人员的科技成果转化收益分配实行公开公示制度，不得利用职权侵占他人科技成果转化收益。（牵头部门：省委组织部，配合部门：省教育厅、省科技厅、省财政厅、省人力资源社会保障厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（六）高等院校、科研院所科技人员在履行岗位职责、完成本职工作、不损害本单位利益的前提下，经征得单位同意，可以到本省企业兼职从事科技成果转化活动，或者离岗在本省创业从事科技成果转化活动，在原则上不超过3年时间内保留人事关系。获得报酬按照规定计缴个人所得税后归个人所有。（牵头部门：省人力资源社会保障厅，配合部门：省委组织部、省教育厅、省科技厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　高等院校、科研院所应当建立制度规定或者与科技人员约定兼职、离岗从事科技成果转化活动期间、期满后的权利和义务。离岗创业期间，科技人员所承担的各级科技计划和基金项目原则上不得中止，确需中止的应当按照有关管理办法办理手续。（牵头部门：省教育厅、省科技厅，配合部门：省人力资源社会保障厅、省财政厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（七）科技成果转化过程中，通过技术交易市场挂牌交易、拍卖等方式确定价格的，或者通过协议定价并在本单位及技术交易市场公示拟交易价格的，单位领导在履行勤勉尽责义务、没有牟取非法利益的前提下，免除其在科技成果定价中因科技成果在本省转化后续价值变化产生的决策责任。（牵头部门：省教育厅、省科技厅，配合部门：省委组织部、省人力资源社会保障厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（八）完善科技创新领域专业技术职称评聘工作。高等院校、科研院所要对从事科技成果转化人员实施分类考核。支持科技成果转化政策试点高等院校、科研院所探索有利于提高科技成果转化，激发广大科研人员创新创业活力的人才评价制度和办法，在取得成功经验的基础上，由相关部门研究制定专门管理办法。（牵头部门：省人力资源社会保障厅，配合部门：省委组织部、省教育厅、省科技厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　二、激励高等院校、科研院所科技人员服务企业研发活动</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（九）改革企业委托的横向课题经费管理方式。</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　1．支持科技成果转化政策试点高等院校、科研院所开展省内企业委托的横向课题经费管理方式改革创新探索，在取得成功经验的基础上，由相关部门研究制定专门管理办法。（牵头部门：省科技厅、省教育厅，配合部门：省财政厅、省人力资源社会保障厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　2．研发团队为保障完成省内企业委托的横向课题合同任务，在高等院校、科研院所现有科研设备、科研耗材无法满足科研需求的前提下，确需购置的科研设备等固定资产及科研耗材可以由研发团队自行组织采购。（牵头部门：省财政厅，配合部门：省教育厅、省科技厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（十）提高科技人员为企业提供科研服务的劳务收入比重。承担省内企业委托的横向课题的高等院校、科研院所研发团队和科技人员，可在课题经费中获得科研劳务收入，其中软件开发类、设计类、规划类和咨询类项目的比例最高可达团队使用经费部分的70%，其他项目比例最高可达50%。高等院校、科研院所科技人员的科研劳务收入按照单项劳务报酬计缴个人所得税，不纳入调控的绩效工资总额。（牵头部门：省教育厅、省科技厅，配合部门：省财政厅、省地税局）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（十一）推进落实科技成果转化的减免税政策。</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　1．加大落实技术性服务增值税减免政策。税务机关积极支持、引导高等院校、科研院所为省内企业提供技术转让、技术开发和与之相关的技术咨询、技术服务，对高等院校、科研院所提供的符合法定条件的技术转让、技术开发和与之相关的技术咨询、技术服务，依法免征增值税。（牵头部门：省地税局、省国税局，配合部门：省科技厅、省教育厅、省财政厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　2．从简落实企业研发费加计扣除政策。省内企业委托高等院校、科研院所开展研究开发项目的费用支出，按照费用实际发生额的80%计入委托方研发费用，并按规定计算加计扣除。（牵头部门：省地税局、省国税局，配合部门：省科技厅、省教育厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　三、完善促进科技成果转移转化的保障机制</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（十二）高等院校、科研院所应当建立健全技术转移工作体系和机制，完善科技成果转化的管理制度，明确科技成果转化各项工作的责任主体，建立健全科技成果转化重大事项领导班子集体决策制度，加强专业化科技成果转化队伍建设，优化科技成果转化流程，通过本单位负责技术转移工作的机构或者委托独立的科技成果转化服务机构开展技术转移。鼓励高等院校、科研院所在不增加编制的前提下建设专业化技术转移机构。（牵头部门：省教育厅、省科技厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（十三）高等院校、科研院所应当按照规定格式，于每年3月30日前，向其主管部门报送本单位上一年度科技成果转化情况的年度报告。主管部门审核后，于每年4月30日前将各单位科技成果转化年度报告报送至本级科技、财政行政主管部门。年度报告内容主要包括：</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　1．科技成果转化取得的总体成效和面临的问题。</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　2．依法取得科技成果的数量及有关情况。</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　3．科技成果转让、许可和作价投资情况。</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　4．推进产学研合作情况。包括自建、共建研究开发机构、技术转移机构、科技成果转化服务平台情况，签订技术开发合同、技术咨询合同、技术服务合同情况，人才培养和人员流动情况等。</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　5．科技成果转化绩效和奖惩情况。包括科技成果转化取得收入及分配情况，对科技成果转化人员的奖励和报酬等。（牵头部门：省科技厅、省财政厅，配合部门：省教育厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（十四）高等院校、科研院所的主管部门以及财政、科技等相关部门，根据单位科技成果转化年度报告等情况，对单位科技成果转化绩效予以评价，并将评价结果作为予以支持的参考依据之一。（牵头部门：省教育厅、科研院所主管部门，配合部门：省财政厅、省科技厅）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（十五）各市、各有关部门要切实加强对科技成果转化和技术转移工作的组织领导，及时研究新情况、新问题，加强协同配合，优化政策环境，开展监测评估，及时总结和推广科技成果转化工作的先进典型和经验做法，对作出突出贡献的单位、个人按照国家有关规定给予表彰奖励，并加大宣传力度，提升科技成果转化的质量和效率，推动我省经济转型升级、提质增效。（各市政府、有关部门分别负责）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（十六）中央驻辽企事业单位比照执行辽宁省相关政策。</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　本通知自印发之日起施行，此前省政府公布的相关规定与本通知不一致的，按本通知执行。</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　辽宁省人民政府</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　2016年4月29日</span></p><p><br/></p>', '0', '0', '1', '2016-05-06 00:00:00', '1', '2017-11-13 13:44:30', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:38:07', '0', '');
INSERT INTO `site_policy_rule` VALUES ('10a110f790e744639a0ea306ce0091d3', 'ZCFG_10a110f790e744639a0ea306ce0091d3', '沈阳市科学事业费管理办法', '2', '沈阳市科学技术局', '<p style=\"text-align: center;\"><br/></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一章&nbsp;总&nbsp;则</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一条 为进一步加强市科学事业费管理，充分发挥财政资金使用效率，根据《国家重点基础研究发展计划专项经费管理办法》、《公益性行业科研专项经费管理试行办法》等相关规定，结合实际情况，制定本办法。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二条 市科学事业费是我市财政本级预算根据国家和省市政府有关规定设立，用于财务归口管理的市属科研院所、市科技局及所属的财政拨款科学事业单位的相应人员经费、公用经费和项目经费。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三条 市科学事业费由市科技局和市财政局负责管理，各司其责。市财政局负责编制科学事业费年度预算，审核项目资金预算，会审下达资金安排计划，按规定拨付资金，对资金使用情况进行追踪问效，监督检查。市科技局负责组织科学事业费预算决算申报及日常管理，组织项目申报、评审，编制年度项目计划，提出项目资金安排建议，与市财政局会签科学事业费项目计划安排意见</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">，向项目单位拨付经费，组织项目验收和日常监察工作，会同市财政局组织抽取项目进行项目实施情况年度检查和财务审计。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二章&nbsp;科学事业费开支范围</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四条 科学事业费包括：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）人员及公用经费：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">1</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">．市属科研院所人员经费是指根据《关于转发市科技局〈沈阳市深化市属科研院所体制改革实施方案〉的通知》（沈政办发〔2002〕17号）、《印发〈关于深化财务关系归口管理的市属开发类科研院所改革若干规定〉的通知》（沈科发〔2003〕36号）等有关规定，纳入事业费支出的离退休人员及在职人员经费。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">2</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">．行政事业单位人员及公用经费是指市科技局及所属的财政拨款事业单位的人员及公用经费。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）项目经费:</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">1</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">．公益性研究及科技服务项目经费是指以提高研究开发及服务能力，增强科技储备为目的，根据我市科技、经济和社会发展需要用于实施社会公益科技服务、技术研发项目的专项经费。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">2</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">．公共科技项目经费是指以开展科学技术普及活动，奖励在科技进步活动中做出突出贡献的公民、组织，支持专利申请、运用和保护等为目的，用于我市科普、科技奖励、专利保护以及其他科技公益事业补贴费用等的专项经费。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">3</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">．科学事业费管理费是指科学事业费管理部门在管理全市科学事业费过程中，用于组织项目的申报、咨询、评审评估、监督检查、财务审计、绩效考评、招标和监理、验收、日常管理，以及组织科学事业费预决算编制、财务培训等工作中发生的经费。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五条 科学事业费的支出范围：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）人员及公用经费依据市财政局有关规定确定的开支范围；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）项目经费的开支范围一般包括设备购置费、能源材料费、外协费、资料印刷费、租赁费、培训费、差旅费、会议费、其他费用。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">设备购置费：指实施项目所必需的专用仪器、设备购置和维修费用，项目研究有关的样品、样机购置费用，以及为此发生的运输、包装、装卸、安装和零星土建的费用。其中从国外引进的仪器、设备、样品、样机的购置费包括海关关税和运输保险费用。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">能源材料费：指实施项目所需的水、电、燃料、原材料、辅助材料、低值易耗品、零配件的购置费用，以及为此发生的运杂包装费用。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">外协费：指实施项目中委托外单位或合作单位进行的试验、加工、测试、文献检索、专利申请等发生的相关费用。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">资料印刷费：指为实施项目所发生的书刊、资料、计算机软件、复印、印刷的费用。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">租赁费：指实施项目中为租赁专用仪器、设备、场地、房屋、实验基地等所发生的相关费用。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">差旅费：指实施项目中进行的调研、考察、现场试验等工作所发生的相关费用。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">会议费：指实施项目中组织召开或参加的与项目实施有关的专题技术、培训服务、学术会议等费用。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">其他费用：指与实施项目直接相关的其他支出，需经市科技局和市财政局批准后列支。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三章&nbsp;科学事业费预决算管理</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六条 科学事业费预决算编制。科学事业费资金实行预决算管理，编制经费预决算应当结合工作（项目）的实际需要，坚持目标相关性、政策相符性和经济合理性的原则。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">人员及公用经费预决算依据市财政局确定的预决算编制方案进行编制。编制程序为各预算单位申报，市科技局初核、汇总，报市财政局核定。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">公共科技项目经费、科学事业费管理费预算由各预算单位申报，市科技局初核，报市财政局核定。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">公益性研究及科技服务项目经费预算由申报单位填写项目责任书相关预算内容，经市科技局初审，报市财政局审批。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">资金使用单位要按有关要求，认真编制本单位决算，决算表要做到数字真实、计算准确、内容完整、说明清楚。对有意篡改决算表中有关数字的责任人要严肃处理。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七条 科学事业费经费的使用应严格执行国家有关财务制度规定，保证经费专款专用，严禁截留、挪用；使用科学事业费资金形成的固定资产、无形资产等均属国有资产，应按照国家、省、市有关规定执行管理。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">科学事业费经费支出中属于政府集中采购范围的，必须按照《政府采购法》及相关规定确定的采购方式和采购程序进行采购。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第八条 资金使用单位应严格按照市财政局审核下达的部门预算或沈阳市科学事业费科技项目责任书确定的用途、范围使用资金，不得擅自变更预算内容。确因特殊情况需要进行预算调整的，资金使用单位应按原程序报市财政局审批。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第九条 人员经费、公用经费当年结余资金，按市财政局经费结余规定重新列入下年度预算。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十条 公益性研究及科技服务项目已实施，但当年尚未完成所形成的项目支出专项结余资金，可在项目实施期限内继续使用，但不得自行改变经费用途；其他公益性研究及科技服务项目结余资金按市财政局有关规定执行。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十一条 公共科技项目费、科学事业费管理费当年结余资金，可结转下一年度继续使用，但不得自行改变经费用途。第二年末仍有结余经费，第三年核减相应经费或不再安排相应经费。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四章 科学事业费经费拨付</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十二条 人员及公用经费根据批准的部门预算按月足额拨付。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十三条 项目经费根据批准下达的部门预算或市财政局、市科技局共同会签的科学事业费计划安排意见全额拨付。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五章 公益性研究及科技服务项目的管理</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十四条&nbsp;公益性研究及科技服务项目的申报。各单位根据年度《科学事业费公益性研究及科技服务项目申报指南》于规定时间内向市科技局申报项目。申报项目需提交项目申报书、项目可行性研究报告以及《科学事业费公益性研究及科技服务项目申报指南》规定的其他申报材料。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十五条&nbsp;公益性研究及科技服务项目的评审：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）市科技局对申报的项目实行项目库管理，并负责委托有关部门组织专家进行评审；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）受委托部门组织评审专家按着公平、公开、公正的原则对申报的项目及预算进行评审，提出评审意见，并作出优先立项、可立项和不可立项的结论。评审后的项目将输入项目库，以供立项时参考。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十六条&nbsp;公益性研究及科技服务项目的立项。市科技局根据专家评审结果、各单位实际情况及以前年度项目实施与验收情况，会同市财政局审定并下达科学事业费计划安排意见。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十七条 公益性研究及科技服务项目实行责任制管理。项目承担单位与市科技局和市财政局签订《科学事业费公益性研究及科技服务项目责任书》。在《科学事业费公益性研究及科技服务项目责任书》中项目起始时间、项目内容、考核指标、项目进度安排等内容应与提交评审的项目申报书和可行性报告的相关内容要一致，经费支出应当不超过项目申报时所列的支出范围和额度，并且与事业费计划确定的经费额度相一致。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十八条 公益性研究及科技服务项目实施中，《科学事业费公益性研究及科技服务项目责任书》中项目内容原则上不可变更，若确需调整变更，项目承担单位提出书面申请，经市科技局和市财政局批准后可重新签订。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十九条 公益性研究及科技服务项目承担单位的职责为：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）负责组织项目的具体实施，保证完成责任书的各项指标；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）在每年一月底之前报告上一年度项目实施情况和经费使用情况；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）及时报告影响项目正常实施的重大问题；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）保证项目经费专款专用，按预算执行，严禁截留、挪用。各单位法定代表人或负责人对科学事业费的使用负责；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（五）积极配合项目管理部门对项目实施及经费使用情况的抽查；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（六）在项目完成后两个月内申请验收，报送相关验收报告及经费决算。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十条 公益性研究及科技服务项目的验收与备案科学事业费公益性研究及科技服务项目验收按《沈阳市科技计划项目验收暂行办法》确定的程序和方式进行。项目验收后，项目单位应在市科技局、市财政局履行备案手续。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十一条 市科技局和市财政局每年度委托相应中介机构对科学事业费使用情况进行财务审计，发现有弄虚作假、截留、挪用和挤占事业费等违反财务制度和财经纪律行为的，按相应规定对有关单位、部门和责任人给予通报批评及相应处理。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十二条 对在项目申报、评审、检查、执行和验收过程中，弄虚作假、徇私舞弊以及违规操作或因主观原因未能完成责任书确定任务并造成严重损失的，按相应规定对有关单位和责任人给予通报批评及相应处理。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十三条&nbsp;项目单位的经费使用情况、项目验收情况将作为以后年度安排科学事业费经费的重要依据。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七章 附&nbsp;则</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十四条 本办法自2011年12月20日起实施，《沈阳市科学事业费财务管理办法（试行）》（沈科发〔2005〕40号文件）同时废止。</span></p><p><br/></p>', '0', '0', '1', '2015-07-07 00:00:00', '1', '2017-11-13 14:15:54', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:39:44', '0', '');
INSERT INTO `site_policy_rule` VALUES ('128784cb7973449d90d0b29ba4f2e9cf', 'ZCFG_128784cb7973449d90d0b29ba4f2e9cf', '沈阳市专利技术补助资金管理暂行办法', '2', '沈阳市知识产权局', '<p style=\"text-align: center;\"><br/></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一章&nbsp;总则</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一条&nbsp;为了鼓励创新，促进专利运用，规范沈阳市专利技术补助资金使用，根据《沈阳市专利促进条例》等相关规定，并结合本市实际，制定本办法。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二条&nbsp;沈阳市专利技术补助资金（以下简称：补助资金）是指由市本级财政预算安排，对本市企业通过自主研发、引进、许可、入股等方式获得并具有良好产业化前景或显著社会效益的专利技术进行补助的经费。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三条&nbsp;补助资金的使用和管理遵循“公开公平、择优支持、科学管理、加强监督”的原则。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四条&nbsp;补助资金由市知识产权局、市财政局各司其职，共同管理。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">市知识产权局负责确定补助资金年度支持重点，编制项目年度计划；负责申报项目的汇总和形式审查，并会同市财政局组织项目评审，下达项目计划；负责对项目实施进行管理；会同市财政局委托中介机构进行绩效评估等。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">市财政局负责编制专利补助资金年度预算，审核项目单位相关财务状况，并负责拨付资金；参与确定年度支持重点，负责监督检查资金使用情况。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二章&nbsp;补助范围及标准</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五条&nbsp;补助资金是对企业通过自主研发和技术引进等方式取得的发明专利，特别是围绕主要发明专利的由多个发明专利、实用新型专利、外观设计专利构建的专利簇（或专利池）给予补助。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六条&nbsp;补助标准应当根据专利价值的一定比例给予补助，单件专利补助额度一般不超过50万元；专利技术创新价值较高且具有较好市场前景或市政府确定的重点项目可适当提高补助比例，但单件专利补助额度不得高于200万元;由多个专利组成的专利簇（池）总体补助额度不超过500万。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">对于企业从本地科研院所和大专院校购买的的专利，补助额度适当上浮，上浮部分补助科研院所和大专院校，用于其科技成果再研发。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七条&nbsp;专利价值的认定原则：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）对于企业通过自主研发创新取得的专利技术，由市知识产权局、市财政局共同委托的资产评估机构对专利研发投入成本进行评估。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）对于有偿转让方式取得其他专利权人的专利技术，依据国家知识产权局专利权人变更公告确认，按转让金额合同等证明材料进行价值确定。对于同一关联主体间的专利技术转让，由市知识产权局、市财政局共同委托的资产评估机构对专利研发投入成本进行评估。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）对于通过订立许可合同的方式取得的专利技术，参考国家知识产权局专利许可合同备案等证明材料进行价值确定。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）对于通过入股（或期权）形式实施的专利技术，参考单位注册成立时确定的专利价值等证明材料进行价值确定，如无法确定其专利价值，由市知识产权局、市财政局共同委托的资产评估机构对专利研发投入成本进行评估。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（五）对于通过无偿转让取得的专利，由市知识产权局、市财政局共同委托的资产评估机构对原专利权人的专利研发投入成本进行评估。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（六）对于在技术转让中包含的专利技术，由市知识产权局、市财政局共同委托的资产评估机构对转让合同中的专利价值进行评估。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三章 申报条件</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第八条&nbsp;申请补助的企业应当具备以下条件：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）依法在本市工商行政管理部门登记注册，具有独立法人资格，具有健全的财务管理制度，无不良信用记录；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）企业应当保证按需提取年度技术研究与开发经费，按规定缴纳各项税费；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）具有基本生产条件，具有较强的市场拓展、技术开发、资金筹措、项目实施能力以及较高的资信等级；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）无侵犯和假冒他人专利的行为，具有良好的商业信誉；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（五）其他应具备的条件。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第九条&nbsp;申请补助的专利技术项目应当具备以下条件：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）拥有获得授权的有效发明专利，专利权属明晰；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）符合国家、省、市产业政策，有利于推动技术改造升级、产品和产业结构调整；&nbsp;</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）技术先进，具有良好的预期经济效益和社会效益及产业化前景；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）专利权利不完全归属于一个权利主体的，必须以一个权利主体名义申报，同时取得其他权利主体同意，并提供书面证明材料；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（五）符合资助的其他相关条件。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四章 申请及审批</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十条&nbsp;补助资金申请以每年发布的补助资金年度指南内容为准，补助资金年度指南于每年上半年发布。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十一条&nbsp;市知识产权局负责受理符合补助条件的项目申请及审批事宜。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十二条&nbsp;项目单位申请补助资金应当提供如下材料：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）项目申报书；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）项目可行性研究报告；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）项目所包含的专利证书复印件；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）项目所包含的专利有效证明材料；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（五）项目申报单位的营业执照复印件；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（六）专利权转让、许可、入股（或期权）、技术转让的证明材料；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（七）专利技术价值证明材料；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（八）专利经济社会效益证明材料；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（九）企业专利管理水平证明材料；</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（十）其他需要提供的有关材料。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十三条&nbsp;申报项目后，先由市知识产权局进行形式审查，然后由市知识产权局组织相关机构、专家进行技术评审，由市财政局组织相关机构、专家进行财务评审，再由市知识产权局、市财政局共同委托的价值评估机构进行专利价值确定，最后由市知识产权局、市财政局择优确定补助项目及额度。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十四条&nbsp;两年内项目单位不得以同一专利多头申报市本级同类财政专项资金，一经发现，取消单位当年项目申报资格，上级机关另有规定的除外。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五章 管理监督</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十五条&nbsp;补助资金专款专用，具体使用范围如下：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）技术再购买：用于企业新购专利、新取得专利使用权等费用。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）创新投入经费：用于企业新增自主研发投入，包括设备购置、试验加工、能源耗材等费用。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）专利所需经费：包括专利申请费用、代理费用、维持费用、专利情报分析相关费用、专利维权保护的调查取证和诉讼费用等费用。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）专利引进及转化中的其他直接相关的支出。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（五）奖励经费：按照单位内部奖励制度执行，或参考《中华人民共和国专利法》及相关法律法规、《辽宁省专利条例》和《沈阳市专利促进条例》等有关内容对发明人进行奖励。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（六）补助资金禁止用于征地拆迁、房屋建筑物及车辆购置、人员工资、省外差旅调研学习、设施维护及水电费等支出。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（七）补助资金应当在两年内支出，逾期仍未支出的，视同补助资金净结余，原渠道收回。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">安排专利技术补助管理费，用于在管理专利技术补助资金过程中组织项目的申报、咨询、评审评估、监督检查、财务审计、绩效考评、日常管理等工作中发生的经费。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十六条　市财政局在资产评估机构作出价值确定后，委托审计机构对资产评估机构作出的价值确定予以抽样审计，以确保价值确定的合理性。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十七条&nbsp;企业收到的补助资金，按照《企业财务通则》（财政部令〔2006〕41号）第二十条的相关规定进行财务处理。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十八条&nbsp;补助资金实行绩效管理制度，建立公开、透明、规范、完整的执行体制，对资金进行全过程监控。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十九条　项目承担单位应当对补助资金专账核算，并于每年12月31日前向市知识产权局报告项目实施进展情况，向市财政局报告资金使用执行情况。市知识产权局、市财政局将选择项目进行检查。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十条&nbsp;项目承担单位要规范补助资金管理，负责提供真实、完整的基础材料及凭证，对专利价值确定环节需扣除项目应主动提供相关材料。对任何形式的弄虚作假、截留、挪用等违反财经纪律的行为，一经发现，原补助资金全额退回，取消资金申报资格，三年内不得申报专利技术补助；拒不退回的，按照《财政违法行为处罚处分条例》（国务院第427号令）规定予以查处。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六章&nbsp;附则</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十一条&nbsp;鼓励各区、县（市）结合本地实际，建立相应的专利补助资金。</span></p><p><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十二条&nbsp;本办法自2015年1月1日起执行，有效期至2017年12月31日。</span></p><p><br/></p>', '0', '0', '1', '2015-01-01 00:00:00', '1', '2017-11-13 14:29:02', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:41:37', '0', '');
INSERT INTO `site_policy_rule` VALUES ('1699d951fa69412784b5a90da780855e', 'ZCFG_1699d951fa69412784b5a90da780855e', '国家自然科学基金条例', '0', '国务院', '<p style=\"text-align:center\"><strong><span style=\"font-size:20px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></strong></p><p><span style=\"font-size: 11px; font-family: Simsun, serif; color: rgb(51, 51, 51);\">&nbsp;2007</span><span style=\"font-size: 11px; font-family: 宋体; color: rgb(51, 51, 51);\">年</span><span style=\"font-size: 11px; font-family: Simsun, serif; color: rgb(51, 51, 51);\">2</span><span style=\"font-size: 11px; font-family: 宋体; color: rgb(51, 51, 51);\">月</span><span style=\"font-size: 11px; font-family: Simsun, serif; color: rgb(51, 51, 51);\">14</span><span style=\"font-size: 11px; font-family: 宋体; color: rgb(51, 51, 51);\">日国务院第</span><span style=\"font-size: 11px; font-family: Simsun, serif; color: rgb(51, 51, 51);\">169</span><span style=\"font-size: 11px; font-family: 宋体; color: rgb(51, 51, 51);\">次常务会议通过，现予公布，自</span><span style=\"font-size: 11px; font-family: Simsun, serif; color: rgb(51, 51, 51);\">2007</span><span style=\"font-size: 11px; font-family: 宋体; color: rgb(51, 51, 51);\">年</span><span style=\"font-size: 11px; font-family: Simsun, serif; color: rgb(51, 51, 51);\">4</span><span style=\"font-size: 11px; font-family: 宋体; color: rgb(51, 51, 51);\">月</span><span style=\"font-size: 11px; font-family: Simsun, serif; color: rgb(51, 51, 51);\">1</span><span style=\"font-size: 11px; font-family: 宋体; color: rgb(51, 51, 51);\">日起施行。　　国务院　　二</span><span style=\"font-size: 11px; font-family: Simsun, serif; color: rgb(51, 51, 51);\">○○</span><span style=\"font-size: 11px; font-family: 宋体; color: rgb(51, 51, 51);\">七年二月二十四日</span><span style=\"font-size: 11px; font-family: Simsun, serif; color: rgb(51, 51, 51);\">&nbsp;</span></p><p><span style=\"font-size:11px;font-family:宋体;color:#333333\"><br/></span></p><p><span style=\"font-size:11px;font-family:宋体;color:#333333\">第一章　总则</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第一条　为了规范国家自然科学基金的使用与管理，提高国家自然科学基金使用效益，促进基础研究，培养科学技术人才，增强自主创新能力，根据《中华人民共和国科学技术进步法》，制定本条例。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第二条　国家设立国家自然科学基金，用于资助《中华人民共和国科学技术进步法》规定的基础研究。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第三条　国家自然科学基金主要来源于中央财政拨款。国家鼓励自然人、法人或者其他组织向国家自然科学基金捐资。　　中央财政将国家自然科学基金的经费列入预算。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第四条　国家自然科学基金资助工作遵循公开、公平、公正的原则，实行尊重科学、发扬民主、提倡竞争、促进合作、激励创新、引领未来的方针。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第五条　确定国家自然科学基金资助项目（以下简称基金资助项目），应当充分发挥专家的作用，采取宏观引导、自主申请、平等竞争、同行评审、择优支持的机制。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第六条　国务院自然科学基金管理机构（以下简称基金管理机构）负责管理国家自然科学基金，监督基金资助项目的实施。　　国务院科学技术主管部门对国家自然科学基金工作依法进行宏观管理、统筹协调。国务院财政部门依法对国家自然科学基金的预算、财务进行管理和监督。审计机关依法对国家自然科学基金的使用与管理进行监督。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第二章　组织与规划</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第七条　基金管理机构应当根据国民经济和社会发展规划、科学技术发展规划以及科学技术发展状况，制定基金发展规划和年度基金项目指南。基金发展规划应当明确优先发展的领域，年度基金项目指南应当规定优先支持的项目范围。国家自然科学基金应当设立专项资金，用于培养青年科学技术人才。　　基金管理机构制定基金发展规划和年度基金项目指南，应当广泛听取高等学校、科学研究机构、学术团体和有关国家机关、企业的意见，组织有关专家进行科学论证。年度基金项目指南应当在受理基金资助项目申请起始之日</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">30</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">日前公布。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第八条　中华人民共和国境内的高等学校、科学研究机构和其他具有独立法人资格、开展基础研究的公益性机构，可以在基金管理机构注册为依托单位。　　本条例施行前的依托单位要求注册为依托单位的，基金管理机构应当予以注册。　　基金管理机构应当公布注册的依托单位名称。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第九条　依托单位在基金资助管理工作中履行下列职责：　　（一）组织申请人申请国家自然科学基金资助；　　（二）审核申请人或者项目负责人所提交材料的真实性；　　（三）提供基金资助项目实施的条件，保障项目负责人和参与者实施基金资助项目的时间；　　（四）跟踪基金资助项目的实施，监督基金资助经费的使用；　　（五）配合基金管理机构对基金资助项目的实施进行监督、检查。　　基金管理机构对依托单位的基金资助管理工作进行指导、监督。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第三章　申请与评审</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第十条　依托单位的科学技术人员具备下列条件的，可以申请国家自然科学基金资助：　　（一）具有承担基础研究课题或者其他从事基础研究的经历；　　（二）具有高级专业技术职务（职称）或者具有博士学位，或者有</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">名与其研究领域相同、具有高级专业技术职务（职称）的科学技术人员推荐。　　从事基础研究的科学技术人员具备前款规定的条件、无工作单位或者所在单位不是依托单位的，经与在基金管理机构注册的依托单位协商，并取得该依托单位的同意，可以依照本条例规定申请国家自然科学基金资助。依托单位应当将其视为本单位科学技术人员，依照本条例规定实施有效管理。　　申请人应当是申请基金资助项目的负责人。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第十一条　申请人申请国家自然科学基金资助，应当以年度基金项目指南为基础确定研究项目，在规定期限内通过依托单位向基金管理机构提出书面申请。　　申请人申请国家自然科学基金资助，应当提交证明申请人符合本条例第十条规定条件的材料；年度基金项目指南对申请人有特殊要求的，申请人还应当提交符合该要求的证明材料。　　申请人申请基金资助的项目研究内容已获得其他资助的，应当在申请材料中说明资助情况。申请人应当对所提交申请材料的真实性负责。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第十二条　基金管理机构应当自基金资助项目申请截止之日起</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">45</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">日内，完成对申请材料的初步审查。符合本条例规定的，予以受理，并公布申请人基本情况和依托单位名称、申请基金资助项目名称。有下列情形之一的，不予受理，通过依托单位书面通知申请人，并说明理由：　　（一）申请人不符合本条例规定条件的；　　（二）申请材料不符合年度基金项目指南要求的；　　（三）申请人申请基金资助项目超过基金管理机构规定的数量的。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第十三条　基金管理机构应当聘请具有较高的学术水平、良好的职业道德的同行专家，对基金资助项目申请进行评审。聘请评审专家的具体办法由基金管理机构制定。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第十四条　基金管理机构对已受理的基金资助项目申请，应当先从同行专家库中随机选择</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">名以上专家进行通讯评审，再组织专家进行会议评审；对因国家经济、社会发展特殊需要或者其他特殊情况临时提出的基金资助项目申请，可以只进行通讯评审或者会议评审。　　评审专家对基金管理机构安排其评审的基金资助项目申请认为难以作出学术判断或者没有精力评审的，应当及时告知基金管理机构；基金管理机构应当依照本条例规定，选择其他评审专家进行评审。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第十五条　评审专家对基金资助项目申请应当从科学价值、创新性、社会影响以及研究方案的可行性等方面进行独立判断和评价，提出评审意见。　　评审专家对基金资助项目申请提出评审意见，还应当考虑申请人和参与者的研究经历、基金资助经费使用计划的合理性、研究内容获得其他资助的情况、申请人实施基金资助项目的情况以及继续予以资助的必要性。　　会议评审提出的评审意见应当通过投票表决。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第十六条　对通讯评审中多数评审专家认为不应当予以资助，但创新性强的基金资助项目申请，经</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">名参加会议评审的评审专家署名推荐，可以进行会议评审。但是，本条例第十四条规定的因特殊需要或者特殊情况临时提出的基金资助项目申请除外。　　基金管理机构应当公布评审专家的推荐意见。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第十七条　基金管理机构根据本条例的规定和专家提出的评审意见，决定予以资助的研究项目。基金管理机构不得以与评审专家有不同的学术观点为由否定专家的评审意见。　　基金管理机构决定予以资助的，应当及时书面通知申请人和依托单位，并公布申请人基本情况以及依托单位名称、申请基金资助项目名称、拟资助的经费数额等；决定不予资助的，应当及时书面通知申请人和依托单位，并说明理由。　　基金管理机构应当整理专家评审意见，并向申请人提供。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第十八条　申请人对基金管理机构作出的不予受理或者不予资助的决定不服的，可以自收到通知之日起</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">15</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">日内，向基金管理机构提出书面复审请求。对评审专家的学术判断有不同意见，不得作为提出复审请求的理由。　　基金管理机构对申请人提出的复审请求，应当自收到之日起</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">60</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">日内完成审查。认为原决定符合本条例规定的，予以维持，并书面通知申请人；认为原决定不符合本条例规定的，撤销原决定，重新对申请人的基金资助项目申请组织评审专家进行评审、作出决定，并书面通知申请人和依托单位。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第十九条　在基金资助项目评审工作中，基金管理机构工作人员、评审专家有下列情形之一的，应当申请回避：　　（一）基金管理机构工作人员、评审专家是申请人、参与者近亲属，或者与其有其他关系、可能影响公正评审的；　　（二）评审专家自己申请的基金资助项目与申请人申请的基金资助项目相同或者相近的；　　（三）评审专家与申请人、参与者属于同一法人单位的。　　基金管理机构根据申请，经审查作出是否回避的决定；也可以不经申请直接作出回避决定。　　基金资助项目申请人可以向基金管理机构提供</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">名以内不适宜评审其申请的评审专家名单，基金管理机构在选择评审专家时应当根据实际情况予以考虑。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第二十条　基金管理机构工作人员不得申请或者参与申请国家自然科学基金资助，不得干预评审专家的评审工作。　　基金管理机构工作人员和评审专家不得披露未公开的评审专家的基本情况、评审意见、评审结果等与评审有关的信息。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第四章　资助与实施</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第二十一条　依托单位和项目负责人自收到基金管理机构基金资助通知之日起</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">20</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">日内，按照评审专家的评审意见、基金管理机构确定的基金资助额度填写项目计划书，报基金管理机构核准。　　依托单位和项目负责人填写项目计划书，除根据评审专家的评审意见和基金管理机构确定的基金资助额度对已提交的申请书内容进行调整外，不得对其他内容进行变更。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第二十二条　基金管理机构对本年度予以资助的研究项目，应当按照《中华人民共和国预算法》和国家有关规定，及时向国务院财政部门申请基金资助项目的预算拨款。但是，本条例第十四条规定的因特殊需要或者特殊情况临时提出的基金资助项目除外。　　依托单位自收到基金资助经费之日起</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">7</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">日内，通知基金管理机构和项目负责人。　　项目负责人应当按照项目计划书的要求使用基金资助经费，依托单位应当对项目负责人使用基金资助经费的情况进行监督。项目负责人、依托单位不得以任何方式侵占、挪用基金资助经费。基金资助经费使用与管理的具体办法由国务院财政部门会同基金管理机构制定。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第二十三条　项目负责人应当按照项目计划书组织开展研究工作，作好基金资助项目实施情况的原始记录，通过依托单位向基金管理机构提交项目年度进展报告。　　依托单位应当审核项目年度进展报告，查看基金资助项目实施情况的原始记录，并向基金管理机构提交年度基金资助项目管理报告。　　基金管理机构应当对项目年度进展报告和年度基金资助项目管理报告进行审查。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第二十四条　基金资助项目实施中，依托单位不得擅自变更项目负责人。　　项目负责人有下列情形之一的，依托单位应当及时提出变更项目负责人或者终止基金资助项目实施的申请，报基金管理机构批准；基金管理机构也可以直接作出终止基金资助项目实施的决定：　　（一）不再是依托单位科学技术人员的；　　（二）不能继续开展研究工作的；　　（三）有剽窃他人科学研究成果或者在科学研究中有弄虚作假等行为的。　　项目负责人调入另一依托单位工作的，经所在依托单位与原依托单位协商一致，由原依托单位提出变更依托单位的申请，报基金管理机构批准。协商不一致的，基金管理机构作出终止该项目负责人所负责的基金资助项目实施的决定。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第二十五条　基金资助项目实施中，研究内容或者研究计划需要作出重大调整的，项目负责人应当及时提出申请，经依托单位审核报基金管理机构批准。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第二十六条　自基金资助项目资助期满之日起</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">60</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">日内，项目负责人应当通过依托单位向基金管理机构提交结题报告；基金资助项目取得研究成果的，应当同时提交研究成果报告。　　依托单位应当对结题报告进行审核，建立基金资助项目档案。依托单位审核结题报告，应当查看基金资助项目实施情况的原始记录。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第二十七条　基金管理机构应当及时审查结题报告。对不符合结题要求的，应当提出处理意见，并书面通知依托单位和项目负责人。　　基金管理机构应当将结题报告、研究成果报告和基金资助项目申请摘要予以公布，并收集公众评论意见。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第二十八条　发表基金资助项目取得的研究成果，应当注明得到国家自然科学基金资助。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第五章　监督与管理</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第二十九条　基金管理机构应当对基金资助项目实施情况、依托单位履行职责情况进行抽查，抽查时应当查看基金资助项目实施情况的原始记录。抽查结果应当予以记录并公布，公众可以查阅。　　基金管理机构应当建立项目负责人和依托单位的信誉档案。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第三十条　基金管理机构应当定期对评审专家履行评审职责情况进行评估；根据评估结果，建立评审专家信誉档案；对有剽窃他人科学研究成果或者在科学研究中有弄虚作假等行为的评审专家，不再聘请。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第三十一条　基金管理机构应当在每个会计年度结束时，公布本年度基金资助的项目、基金资助经费的拨付情况以及对违反本条例规定行为的处罚情况等。　　基金管理机构应当定期对基金资助工作进行评估，公布评估报告，并将评估报告作为制定基金发展规划和年度基金项目指南的依据。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第三十二条　评审专家对申请人的基金资助项目申请提出评审意见后，申请人可以就评审专家的评审工作向基金管理机构提出意见；基金管理机构在对评审专家履行评审职责进行评估时应当参考申请人的意见。　　任何单位或者个人发现基金管理机构及其工作人员、评审专家、依托单位及其负责基金资助项目管理工作的人员、申请人或者项目负责人、参与者有违反本条例规定行为的，可以检举或者控告。　　基金管理机构应当公布联系电话、通讯地址和电子邮件地址。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第三十三条　基金管理机构依照本条例规定对外公开有关信息，应当遵守国家有关保密规定。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第六章　法律责任</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第三十四条　申请人、参与者伪造或者变造申请材料的，由基金管理机构给予警告；其申请项目已决定资助的，撤销原资助决定，追回已拨付的基金资助经费；情节严重的，</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">至</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">年不得申请或者参与申请国家自然科学基金资助，不得晋升专业技术职务（职称）。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第三十五条　项目负责人、参与者违反本条例规定，有下列行为之一的，由基金管理机构给予警告，暂缓拨付基金资助经费，并责令限期改正；逾期不改正的，撤销原资助决定，追回已拨付的基金资助经费；情节严重的，</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">至</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">7</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">年不得申请或者参与申请国家自然科学基金资助：　　（一）不按照项目计划书开展研究的；　　（二）擅自变更研究内容或者研究计划的；　　（三）不依照本条例规定提交项目年度进展报告、结题报告或者研究成果报告的；　　（四）提交弄虚作假的报告、原始记录或者相关材料的；　　（五）侵占、挪用基金资助经费的。　　项目负责人、参与者有前款第（四）项、第（五）项所列行为，情节严重的，</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">至</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">7</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">年不得晋升专业技术职务（职称）。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第三十六条　依托单位有下列情形之一的，由基金管理机构给予警告，责令限期改正；情节严重的，通报批评，</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">至</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">年不得作为依托单位：　　（一）不履行保障基金资助项目研究条件的职责的；　　（二）不对申请人或者项目负责人提交的材料或者报告的真实性进行审查的；　　（三）不依照本条例规定提交项目年度进展报告、年度基金资助项目管理报告、结题报告和研究成果报告的；　　（四）纵容、包庇申请人、项目负责人弄虚作假的；　　（五）擅自变更项目负责人的；　　（六）不配合基金管理机构监督、检查基金资助项目实施的；　　（七）截留、挪用基金资助经费的。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第三十七条　评审专家有下列行为之一的，由基金管理机构给予警告，责令限期改正；情节严重的，通报批评，基金管理机构不得再聘请其为评审专家：　　（一）不履行基金管理机构规定的评审职责的；　　（二）未依照本条例规定申请回避的；　　（三）披露未公开的与评审有关的信息的；　　（四）对基金资助项目申请不公正评审的；　　（五）利用工作便利谋取不正当利益的。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第三十八条　基金管理机构工作人员有下列行为之一的，依法给予处分：　　（一）未依照本条例规定申请回避的；　　（二）披露未公开的与评审有关的信息的；　　（三）干预评审专家评审工作的；　　（四）利用工作便利谋取不正当利益的。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第三十九条　违反本条例规定，有下列行为之一，构成犯罪的，依法追究刑事责任：　　（一）侵吞、挪用基金资助经费的；　　（二）基金管理机构工作人员、评审专家履行本条例规定的职责，索取或者非法收受他人财物或者谋取其他不正当利益的；　　（三）申请人或者项目负责人、参与者伪造、变造国家机关公文、证件或者伪造、变造印章的；　　（四）申请人或者项目负责人、参与者、依托单位及其负责基金资助项目管理工作的人员为谋取不正当利益，给基金管理机构工作人员、评审专家以财物的；　　（五）泄露国家秘密的。　　申请人或者项目负责人、参与者因前款规定的行为受到刑事处罚的，终身不得申请或者参与申请国家自然科学基金资助。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第四十条　违反有关财政法律、行政法规规定的，依照有关法律、行政法规的规定予以处罚、处分。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第七章　附则</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第四十一条　本条例施行前已决定资助的研究项目，按照作出决定时国家有关规定执行。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第四十二条　基金管理机构在基金资助工作中，涉及项目组织实施费和与基础研究有关的学术交流活动、基础研究环境建设活动的基金资助经费的使用与管理的，按照国务院财政部门的有关规定执行。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　第四十三条　本条例自</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2007</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">年</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">4</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">月</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">日起施行。</span></p><p><br/></p>', '0', '0', '1', '2007-02-24 00:00:00', '1', '2017-11-13 13:20:41', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:43:00', '0', '');
INSERT INTO `site_policy_rule` VALUES ('1bc359100be746018de4339341396e06', 'ZCFG_1bc359100be746018de4339341396e06', '沈阳市工程技术研究中心管理办法', '2', '沈阳市科学技术局', '<p style=\"background: white; text-align: center;\"><br/></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center;background:white\"><span style=\"font-size: 21px;font-family: 黑体\">第一章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 黑体\">总则</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第一条</span></strong><strong><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span></strong><strong><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span></strong><strong><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">为规范沈阳市工程技术研究中心（以下简称工程中心）的建设和管理，充分发挥工程中心的作用，推动以企业为主体、市场为导向、产学研相结合的技术创新体系建设，加快国家创新型城市建设步伐，根据国家、省、市有关文件规定，结合我市实际，制定本办法。</span></strong></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第二条</span></strong><strong><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span></strong><strong><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span></strong><strong><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">本办法所指工程中心主要是以具有较强科技实力的科技型企业、科研机构或高等院校为依托，拥有较强的工程技术研发、设计和试验的专业人才队伍，具有较完备的工程技术综合配套试验条件，能够为相关单位提供多种综合性服务，并具有良性循环发展机制的科研开发实体。</span></strong></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第三条</span></strong><strong><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span></strong><strong><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span></strong><strong><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">工程中心的主要任务</span></strong></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（一）针对相关行业或领域发展中的关键性和共性技术问题，持续不断地进行系统化、配套化和工程化研究开发，为行业提供成熟配套的技术工艺和技术装备，并不断推出具有高附加值的系列新产品。</span></strong></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（二）实行开放服务，接受地方政府、行业或部门以及企业、科研机构和高等院校等单位委托的工程技术研究、设计和试验任务，并为其提供技术咨询服务。</span></strong></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（三）运用其工程化研究开发和设计优势，积极开展引进技术的消化、吸收与创新，成为企业吸收国外先进技术、提高产品质量的技术依托。</span></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（四）培训相关行业或领域需要的高素质工程技术人员和工程管理人员。</span></strong></p><p style=\"text-indent: 43px;background: white\"><strong><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（五）在工程技术研发方面全方位地开展国内外合作与交流。参与相关行业、国家标准制定工作，促进行业、领域的科技进步。</span></strong></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center;background:white\"><span style=\"font-size: 21px;font-family: 黑体\">第二章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 黑体\">工程中心的申报和审核</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第四条</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">申报建设工程中心的依托单位，应具备以下基本条件：</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（一）在沈阳市内注册登记的具有独立法人资格的科技型企业、科研机构和高等院校。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（二）在某一技术领域具有较强的科研实力，具备承担国家、省、市重点科技任务的能力。拥有较强的工程技术研究和设计基础以及成果转化经验。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（三）具有技术水平高、工程化实践经验的工程技术带头人，拥有一定数量和较高水平的工程技术人员和承担工程试验任务的熟练技术工人，其中专职研发人员不少于20人，拥有硕士学位或中级职称的人员不低于30%。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（四）具备较好的工程技术试验条件和基础设施，有必要的检测、分析、测试手段和工艺设备（不包括生产用设备），设备原值应当不低于200万元。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（五）拥有与领域相关的专利、软件著作权、集成电路布图设计专有权、植物新品种等自主知识产权。其中：依托高等院校、科研机构建设的工程中心，要保证落实工程中心的建设资金和日常开发经费；依托企业建设的工程中心，上年研究开发经费占年销售收入的比例不低于2%。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（六）拥有敢于创新、高效精干的领导班子，有强有力的组织管理机构和管理队伍。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（七）与本行业或本领域的高等院校、科研机构以及企业有良好的合作关系。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第五条 工程中心的认定须经过依托单位组织申报、专家论证、市科技局依法审核三个步骤。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第六条 申报单位需提供如下材料：</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（一）沈阳市工程技术研究中心申报报告（详见附件）。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（二）其他附件及证明材料。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第七条 市科技局组织有关专家进行论证，在综合专家评审意见的基础上，依法提出审核意见。</span></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center;background:white\"><span style=\"font-size: 21px;font-family: 黑体\">第三章 运行管理</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第八条 工程中心要采取市场化运行机制，面向全市相关行业、企业承接工程化研究开发任务，实行有偿服务，逐步实现科研—开发—产品—市场的良性循环。工程中心取得的经济收益，主要用于自身的事业发展。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第九条 工程中心实行主任负责制。实行独立核算，可与依托单位共有一个法人代表。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第十条 工程中心要采取有效措施，逐步形成一支年龄梯次、专业结构合理的优秀工程技术队伍。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第十一条</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">经批准的工程中心，可通过沈阳市科技计划管理网申报年度科技计划项目，履行科技计划项目立项审核程序，择优列入年度科技计划予以科技经费支持。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第十二条</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">鼓励企业与科研机构、高等院校共同出资，按照现代企业制度建设工程中心。本市辖区内原则上不重复审批业务范围相同的工程中心。</span></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center;background:white\"><span style=\"font-size: 21px;font-family: 黑体\">第四章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 黑体\">监督与考评</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第十三条 依托单位于每年六月和十二月底前将工程中心工作进展情况向市科技局作出半年和年度总结报告。市科技局将随时对工程中心工作进展情况进行检查。</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第十四条 市科技局每两年对工程中心运行情况及绩效进行考评。对运行正常并取得突出成绩的将给予表彰和奖励，对管理不善的，责成限期改进。</span></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center;background:white\"><span style=\"font-size: 21px;font-family: 黑体\">第五章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 黑体\">附 则</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第十五条</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">本管理办法自2015年8月1日日起施行。</span></p><p><br/></p>', '0', '0', '1', '2015-08-01 00:00:00', '1', '2017-11-13 13:53:38', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:44:37', '0', '');
INSERT INTO `site_policy_rule` VALUES ('1cb67dfe086342d68fbef0f8bcb65f04', 'ZCFG_1cb67dfe086342d68fbef0f8bcb65f04', '国务院关于国家重大科研基础设施和大型科研仪器向社会开放的意见', '3', '国务院', '<p style=\"text-align: left;\"><br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 国发〔2014〕70号<br/><br/>&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp; 各省、自治区、直辖市人民政府，国务院各部委、各直属机构：<br/><br/>　　国家重大科研基础设施和大型科研仪器（以下称科研设施与仪器）是用于探索未知世界、发现自然规律、实现技术变革的复杂科学研究系统，是突破科学前沿、解决经济社会发展和国家安全重大科技问题的技术基础和重要手段。近年来，科研设施与仪器规模持续增长，覆盖领域不断拓展，技术水平明显提升，综合效益日益显现。同时，科研设施与仪器利用率和共享水平不高的问题也逐渐凸显出来，部分科研设施与仪器重复建设和购置，存在部门化、单位化、个人化的倾向，闲置浪费现象比较严重，专业化服务能力有待提高，科研设施与仪器对科技创新的服务和支撑作用没有得到充分发挥。为加快推进科研设施与仪器向社会开放，进一步提高科技资源利用效率，现提出以下意见。<br/><br/>　　一、总体要求<br/><br/>　　（一）指导思想。以邓小平理论、“三个代表”重要思想、科学发展观为指导，深入贯彻党的十八大和十八届二中、三中、四中全会精神，认真落实党中央和国务院的决策部署，围绕健全国家创新体系和提高全社会创新能力，通过深化改革和制度创新，加快推进科研设施与仪器向高校、科研院所、企业、社会研发组织等社会用户开放，实现资源共享，避免部门分割、单位独占，充分释放服务潜能，为科技创新和社会需求服务，为实施创新驱动发展战略提供有效支撑。<br/><br/>　　（二）主要目标。力争用三年时间，基本建成覆盖各类科研设施与仪器、统一规范、功能强大的专业化、网络化管理服务体系，科研设施与仪器开放共享制度、标准和机制更加健全，建设布局更加合理，开放水平显著提升，分散、重复、封闭、低效的问题基本解决，资源利用率进一步提高。<br/><br/>　　（三）基本原则。<br/><br/>　　制度推动。制定促进科研设施与仪器开放的管理制度和办法，明确管理部门和单位的责任，理顺开放运行的管理机制，逐步纳入法制化轨道，推动非涉密和无特殊规定限制的科研设施与仪器一律向社会开放。<br/><br/>　　信息共享。搭建统一的网络管理平台，实现科研设施与仪器配置、管理、服务、监督、评价的全链条有机衔接。<br/><br/>　　资源统筹。既要盘活存量，统筹管理，挖掘现有科研设施与仪器的潜力，促进利用效率最大化；又要调控增量，合理布局新增科研设施与仪器，以开放共享推动解决重复购置和闲置浪费的问题。<br/><br/>　　奖惩结合。建立以用为主、用户参与的评估监督体系，形成科研设施与仪器向社会服务的数量质量与利益补偿、后续支持紧密挂钩的奖惩机制。<br/><br/>　　分类管理。对于不同类型的科研设施与仪器，采取不同的开放方式，制定相应的管理制度、支撑措施及评价办法。<br/><br/>　　（四）适用范围。科研设施与仪器包括大型科学装置、科学仪器中心、科学仪器服务单元和单台套价值在50万元及以上的科学仪器设备等，主要分布在高校、科研院所和部分企业的各类重点实验室、工程（技术）研究中心、分析测试中心、野外科学观测研究站及大型科学设施中心等研究实验基地。其中，科学仪器设备可以分为分析仪器、物理性能测试仪器、计量仪器、电子测量仪器、海洋仪器、地球探测仪器、大气探测仪器、特种检测仪器、激光器、工艺试验仪器、计算机及其配套设备、天文仪器、医学科研仪器、核仪器、其他仪器等15类。<br/><br/>　　二、重点措施<br/><br/>　　（一）所有符合条件的科研设施与仪器都纳入统一网络平台管理。<br/><br/>　　科技部会同有关部门和地方建立统一开放的国家网络管理平台，并将所有符合条件的科研设施与仪器纳入平台管理。科研设施与仪器管理单位（以下简称管理单位）按照统一的标准和规范，建立在线服务平台，公开科研设施与仪器使用办法和使用情况，实时提供在线服务。管理单位的服务平台统一纳入国家网络管理平台，逐步形成跨部门、跨领域、多层次的网络服务体系。<br/><br/>　　管理单位建立完善科研设施与仪器运行和开放情况的记录，并通过国家网络管理平台，向社会发布科研设施与仪器开放制度及实施情况，公布科研设施与仪器分布、利用和开放共享情况等信息。<br/><br/>　　（二）按照科研设施与仪器功能实行分类开放共享。<br/><br/>　　对于大型科学装置、科学仪器中心，有关部门和管理单位要将向社会开放纳入日常运行管理工作。对于科学仪器服务单元和单台套价值在50万元及以上的科学仪器设备，科技行政主管部门要加强统筹协调，按不同专业领域或仪器功能，打破管理单位的界限，推动形成专业化、网络化的科学仪器服务机构群。对于单台套价值在50万元以下的科学仪器设备，可采取管理单位自愿申报、行政主管部门择优加入的方式，纳入国家网络管理平台管理。对于通用科学仪器设备，通过建设仪器中心、分析测试中心等方式，集中集约管理，促进开放共享和高效利用。对于拟新建设施和新购置仪器，应强化查重评议工作，并将开放方案纳入建设或购置计划。管理单位应当自科研设施与仪器完成安装使用验收之日起30个工作日内，将科研设施与仪器名称、规格、功能等情况和开放制度提交国家网络管理平台。<br/><br/>　　鼓励国防科研单位在不涉密条件下探索开展科研设施与仪器向社会开放服务。<br/><br/>　　对于利用科研设施与仪器形成的科学数据、科技文献（论文）、科技报告等科技资源，要根据各自特点采取相应的方式对外开放共享。开放共享情况要作为科技资源建设和科技计划项目管理考核的重要内容。<br/><br/>　　（三）建立促进开放的激励引导机制。<br/><br/>　　管理单位对外提供开放共享服务，可以按照成本补偿和非盈利性原则收取材料消耗费和水、电等运行费，还可以根据人力成本收取服务费，服务收入纳入单位预算，由单位统一管理。管理单位对各类科研设施与仪器向社会开放服务建立公开透明的成本核算和服务收费标准，行政主管部门要加强管理和监督。对于纳入国家网络管理平台统一管理、享受科教用品和科技开发用品进口免税政策的科学仪器设备，在符合监管条件的前提下，准予用于其他单位的科技开发、科学研究和教学活动。探索建立用户引导机制，鼓励共享共用。<br/><br/>　　统筹考虑和严格控制在新上科研项目中购置科学仪器设备。将优先利用现有科研设施与仪器开展科研活动作为各科研单位获得国家科技计划（专项、基金等）支持的重要条件。<br/><br/>　　鼓励企业和社会力量以多种方式参与共建国家重大科研基础设施，组建专业的科学仪器设备服务机构，促进科学仪器设备使用的社会化服务。<br/><br/>　　（四）建立科研设施与仪器开放评价体系和奖惩办法。<br/><br/>　　科技部会同有关部门建立评价制度，制定评价标准和办法，引入第三方专业评估机制，定期对科研设施与仪器的运行情况、管理单位开放制度的合理性、开放程度、服务质量、服务收费和开放效果进行评价考核。评价考核结果向社会公布，并作为科研设施与仪器更新的重要依据。对于通用科研设施与仪器，重点评价用户使用率、用户的反馈意见、有效服务机时、服务质量以及相关研究成果的产出、水平与贡献；对于专用科研设施与仪器，重点评价是否有效组织了高水平的设施应用专业团队以及相关研究成果的产出、水平与贡献。<br/><br/>　　管理单位应在满足单位科研教学需求的基础上，最大限度推进科研设施与仪器对外开放，不断提高资源利用率。对于科研设施与仪器开放效果好、用户评价高的管理单位，同级财政部门会同有关部门根据评价考核结果和财政预算管理的要求，建立开放共享后补助机制，调动管理单位开放共享积极性。对于不按规定如实上报科研设施与仪器数据、不按规定公开开放与利用信息、开放效果差、使用效率低的管理单位，科技行政主管部门会同有关部门在网上予以通报，限期整改，并采取停止管理单位新购仪器设备、在申报科技计划（专项、基金等）项目时不准购置仪器设备等方式予以约束。对于通用性强但开放共享差的科研设施与仪器，结合科技行政主管部门的评价考核结果，相关行政主管部门和财政部门可以按规定在部门内或跨部门无偿划拨，管理单位也可以在单位内部调配。科技行政主管部门、相关行政主管部门要建立投诉渠道，接受社会对科研设施与仪器调配的监督。<br/><br/>　　（五）加强开放使用中形成的知识产权管理。<br/><br/>　　用户独立开展科学实验形成的知识产权由用户自主拥有，所完成的著作、论文等发表时，应明确标注利用科研设施与仪器情况。加强网络防护和网络环境下数据安全管理，管理单位应当保护用户身份信息以及在使用过程中形成的知识产权、科学数据和技术秘密。<br/><br/>　　（六）强化管理单位的主体责任。<br/><br/>　　管理单位是科研设施与仪器向社会开放的责任主体，要强化法人责任，切实履行开放职责，自觉接受相关部门的考核评估和社会监督。要根据科研设施与仪器的类型和用户需求，建立相应的开放、运行、维护、使用管理制度，保障科研设施与仪器的良好运行与开放共享。要落实实验技术人员岗位、培训、薪酬、评价等政策。科学仪器设备集中使用的单位，要建立专业化的技术服务团队，不断提高实验技术水平和开放水平。<br/><br/>　　各行政主管部门要切实履行对管理单位开放情况的管理和监督职责，实施年度考核，把开放水平和结果作为年度考核的重要内容。<br/><br/>　　三、组织实施和进度安排<br/><br/>　　改革分阶段实施，在2014年科技部会同有关部门和地方启动现有科研设施与仪器的资源调查，摸清家底，建立科研设施与仪器资源数据库的基础上，逐步实现科研设施与仪器向社会开放的全覆盖。<br/><br/>　　2015年，科技部会同有关部门充分利用现有全国大型科学仪器设备协作共用平台，启动统一开放的科研设施与仪器国家网络管理平台建设，年底前基本建立。遴选状态良好、管理制度健全、开放绩效突出并具有代表性的科研设施与仪器，先行开展向社会开放试点。制定管理单位服务平台的标准规范，制定并发布统一的评价办法，开展评价考核工作，财政部门会同有关部门建立开放共享后补助机制。完善科技部、财政部、教育部、中科院等相关部门对新购科学仪器设备的查重和联合评议机制。所有管理单位制定完善的开放制度，并在国家网络管理平台上发布。<br/><br/>　　2016年，科技部会同有关部门和地方建成覆盖各类科研设施与仪器、统一规范、功能强大的专业化、网络化国家网络管理平台，将所有符合条件的科研设施与仪器纳入平台管理。所有管理单位按照统一的标准规范建成各自的服务平台，明确服务方式、服务内容、服务流程，纳入国家网络管理平台，形成跨部门、跨领域、多层次的网络服务体系。所有管理单位在国家网络管理平台上发布符合开放条件的科研设施与仪器开放清单和开放信息。<br/><br/>　　2017年，科技行政主管部门对管理单位的科研设施与仪器向社会开放情况进行评价考核，并向社会公布评价考核结果。<br/><br/>&nbsp;<br/><br/>　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　国务院<br/><br/>　　　　　　　　　　　　　　　　　　　　　　　　　　　　2014年12月31日</p>', '0', '0', '1', '2014-12-31 00:00:00', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-23 09:28:40', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-23 09:28:40', '0', '');
INSERT INTO `site_policy_rule` VALUES ('1dfa1698c949430c911ec34049cb4563', 'ZCFG_1dfa1698c949430c911ec34049cb4563', '关于印发《高新技术企业认定管理工作指引》的通知附件', '0', '科学技术部  财政部   国家税务总局', '<p style=\"text-align: center;\"><span style=\"font-family: 宋体; font-size: 24px;\">科学技术部 &nbsp;</span><span style=\"font-family: 宋体; font-size: 24px;\">财政部&nbsp;</span><span style=\"font-family: 宋体; font-size: 24px; letter-spacing: 1px;\">国家税务总局</span><span style=\"font-family: 宋体; font-size: 24px;\">&nbsp;</span><span style=\"font-family: 宋体; font-size: 24px;\">文件</span><br/></p><p style=\"text-align:center;line-height:27px\"><strong><span style=\"font-size:16px;font-family:宋体;color:#333333\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">国科发火〔</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">2008</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">〕</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">362</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">号</span></p><p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:宋体;color:#333333\">&nbsp;</span></strong></p><p><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span></p><p style=\"text-align:center;line-height:48px\"><strong><span style=\"font-size:29px;font-family:宋体;color:#333333\">关于印发《高新技术企业认定</span></strong></p><p style=\"text-align:center;line-height:48px\"><strong><span style=\"font-size:29px;font-family:宋体;color:#333333\">管理工作指引》的通知</span></strong></p><p style=\"text-align:center;line-height:37px\"><span style=\"font-size:24px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"line-height: 37px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">各省、自治区、直辖市及计划单列市科技厅（局）、财政厅（局）、国家税务局、地方税务局:</span></p><p style=\"text-indent: 42px;line-height: 43px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">《高新技术企业认定管理办法》（国科发火</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">[2008]172</span><span style=\"font-size: 24px;font-family:仿宋_GB2312;color:#333333\">号，以<span style=\"letter-spacing:-1px\">下称《认定办法》）及《国家重点支持的高新技术领域》已经印</span>发给你们。为确保认定管理工作高效、规范，根据《认定办法》第<span style=\"letter-spacing:-1px\">十九条的规定，现将《高新技术企业认定管理工作指引》（以下</span>称《工作指引》）印发给你们，并就有关事项通知如下：</span></p><p style=\"text-indent: 42px;line-height: 43px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">一、各省、自治区、直辖市及计划单列市的科技、财政、税务部门应充分认识高新技术企业认定管理工作的重要性，密切配合，及时成立认定管理机构，共同做好本地区高新技术企业认定和税收优惠政策的落实工作。</span></p><p style=\"text-indent: 42px;line-height: 43px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">二、</span><span style=\"font-size: 24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">2007</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">年底前国家高新技术产业开发区（包括北京市新技术产业开发试验区）内、外已按原认定办法认定的仍在有效期内的高新技术企业资格依然有效，但在按《认定办法》和《工作指引》重新认定合格后方可依照《企业所得税法》及其实施条例等有关规定享受企业所得税优惠政策。企业可提前按《认定办法》和《工作指引》申请重新认定，亦可在资格到期后申请重新认定。</span></p><p style=\"text-indent: 42px;line-height: 43px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">三、对原依法享受企业所得税定期减免税优惠未期满的高新技术企业，可依照《国务院关于实施企业所得税过渡优惠政策的通知》（国发</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">[2007]39</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">号</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">)</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">的有关规定执行。</span></p><p style=\"text-indent: 42px;line-height: 43px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">四、对经济特区和上海浦东新区内新设立并按《认定办法》和《工作指引》认定的高新技术企业，按《国务院关于经济特区和上海浦东新区新设立高新技术企业实行过渡性税收优惠的通知》（国发</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">[2007]40</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">号）的有关规定执行。</span></p><p style=\"text-indent: 42px;line-height: 43px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">五、高新技术企业认定管理工作政策性强、专业要求高，各地应配备骨干人员，保障认定工作所需经费，及时对本地区在认定工作中出现的新情况、新问题提出切实可行的政策建议。</span></p><p style=\"text-indent: 42px;line-height: 42px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">附件：高新技术企业认定管理工作指引</span></p><p style=\"line-height: 37px\"><span style=\"font-size: 20px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;</span></p><p style=\"line-height: 37px\"><span style=\"font-size: 20px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;</span></p><p style=\"line-height: 37px\"><span style=\"font-size: 20px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;</span></p><p style=\"text-indent: 31px;line-height: 37px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">科</span><span style=\"font-size: 24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">技</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">部</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">财</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">政</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">部</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">国家税务总局</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">二ＯＯ八年七月八日</span></p><p><br/></p>', '0', '0', '1', '2008-07-08 00:00:00', '1', '2017-11-13 11:54:32', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:46:52', '0', '');
INSERT INTO `site_policy_rule` VALUES ('2225d952ee60446fa0fa514cc1365fa3', 'ZCFG_2225d952ee60446fa0fa514cc1365fa3', '沈阳市专利促进条例', '2', '沈阳市人民政府', '<p style=\"background: white; text-align: center;\"><br/></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">2009</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">年11月11日沈阳市第十四届人民代表大会常务委员会第十五次会议通过；2010年1月8日辽宁省第十一届人民代表大会常务委员会第十四次会议批准</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一章 总 则</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一条 根据《中华人民共和国专利法》等法律、法规，结合本市实际，制定本条例。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二条 本市行政区域内专利的创造、运用、保护、管理以及相关活动，适用本条例。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三条 市人民政府专利行政主管部门负责本市行政区域内的专利工作。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">区、县（市）人民政府专利行政主管部门负责本行政区域内的专利工作。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">市和区、县（市）人民政府有关部门应当按照各自职责，做好相关专利工作。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四条专利促进工作应当遵循激励创造、有效运用、依法保护、科学管理的原则，完善专利管理体系，健全专利执法体制，引导和支持企业、事业单位成为发明创造和专利应用的主体。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五条市和区、县（市）人民政府应当将促进专利事业发展纳入国民经济和社会发展规划，为专利工作提供必要的条件和保障，并采取有效措施促进专利权的产生、运用、保护和管理。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六条市和区、县（市）人民政府应当在财政科技资金中设立专利专项资金，支持专利申请、专利技术转化、专利引进、专利奖励、专利维权援助、专利宣传培训、专利信息开发、专利中介服务以及其他专利促进工作。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">专利专项资金应当逐年增加。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七条市和区、县（市）人民政府应当将专利指标纳入国民经济与社会发展评价体系、科技计划实施评价体系和国有及国有控股企业绩效考核评价体系。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二章 专利创造</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第八条 鼓励单位和个人将发明创造申请专利。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第九条市和区、县（市）人民政府有关部门应当将获得专利的质量和数量作为财政资金支持的研究开发、技术改造、高新技术产业化等项目立项、核准、验收的重要指标。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十条市和区、县（市）人民政府有关部门应当将拥有专利的质量和数量作为认定和核准科技园区、科技企业孵化器、高新技术企业、工程研究中心、工程技术中心、企业技术中心、重点实验室、工程实验室的重要指标。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十一条 鼓励企业、事业单位围绕本市主导产业和重点技术领域进行发明创造，并利用自主专利参与有关技术标准的制定。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十二条被授予专利权的单位未与发明人或者设计人约定奖金支付方式和数额的，应当自专利权公告之日起3个月内发给发明人或者设计人奖金。一项发明专利的奖金不少于4000元，一项实用新型专利或者外观设计专利的奖金不少于1000元。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十三条评定市人民政府科技进步奖和自然科学领域科技人员专业技术职称，应当将取得发明专利权或者完成职务发明作为优先条件。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三章 专利运用</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十四条市和区、县（市）人民政府有关部门应当推动专利技术在本地的运用和产业化，对拥有自主专利权的科技项目优先在科技计划中立项。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十五条 具有自主专利技术的产品首次投放市场的，政府采购应当率先购买。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十六条 鼓励企业、事业单位引进并实施专利技术。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">鼓励和支持企业与高等学校、科研院所等加强产学研合作，促进专利技术转化和以专利技术创办科技型企业。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十七条被授予专利权的单位未与发明人或者设计人约定报酬支付方式和数额，在专利权有效期限内实施专利的，应当依法提取报酬支付给发明人或者设计人。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">被授予专利权的单位可以结合本单位实际,采取多种方式向发明人或者设计人支付报酬。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十八条　鼓励专利权人以专利权入股、质押、转让、许可等方式在本市运用专利权。以专利权作价出资入股，应当进行资产评估，涉及国有资产的按照有关法律规定执行。鼓励专利权人以专利权到埠外、国（境）外投资或者入股。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十九条申请政府资助的重大科技项目，项目承担单位应当提供其相关技术的专利文献检索报告。未提交的，有关行政主管部门不得立项。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">对政府资助的重大科技项目，应当规定其产出专利的指标。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十条鼓励企业、事业单位在技术研究、产品开发前以及过程中进行相关技术的专利文献检索，建立相关专利技术跟踪制度和管理档案。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">企业、事业单位在重大科技项目的立项、交流、合作和实施前，应当制定专利预警方案，避免发生专利纠纷。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四章 专利保护编辑第二十一条 市和区、县（市）人民政府专利行政主管部门应当依法查处假冒专利行为，处理专利侵权纠纷，适时向社会公告。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">市人民政府专利行政主管部门负责处理跨区、县（市）的专利纠纷。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">区、县（市）人民政府专利行政主管部门负责处理本行政区域内的专利纠纷。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十二条 市和区、县（市）人民政府专利行政主管部门应当建立专利侵权举报投诉制度，鼓励和支持举报专利侵权行为。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十三条市和区、县（市）人民政府专利行政主管部门应当建立专利维权援助机制，为企业、事业单位和个人依法开展专利维权提供服务。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十四条展览会、交易会、展示会、推广会等展会的主办方，对标注专利标识的参展产品或者技术，应当查验参展方提供的专利有效证明文件。未提供的，主办方应当禁止其以专利产品、专利技术名义参展。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十五条广播、电视、报刊、网络等单位在发布专利广告时，应当要求广告主或者广告经营者提供有效的专利证明文件。未提供的，有关单位不得为其以专利产品或者专利技术的名义发布广告。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五章 专利管理</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十六条市和区、县（市）人民政府专利行政主管部门应当加强对企业、事业单位专利工作的指导，鼓励其设立专利管理工作机构，配备专利管理人员，制定和实施专利战略，完善专利工作制度。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十七条　市人民政府专利行政主管部门应当建立专利信息检索系统、网上专利展示交易系统等服务平台。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十八条鼓励和支持专利代理、专利技术交易、专利资产评估、专利信息咨询等中介服务机构发展。市人民政府专利行政主管部门应当对其进行业务指导和监督。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十九条 市和区、县（市）人民政府专利行政主管部门应当采取多种措施，加强专利知识的宣传和培训。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">广播、电视、报刊、网络等单位，应当把专利有关知识作为重要宣传内容，提高全社会的专利意识。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十条市人民政府应当设立专利奖，对重大发明创造和在本市实施并取得显著经济、社会效益的专利项目，以及为促进专利发展做出突出贡献的个人，给予奖励。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">市人民政府应当对本市获得中国专利奖的项目给予资金奖励。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">任何单位和个人不得以虚假材料骗取专利奖。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六章法律责任</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十一条　违反本条例第十九条规定，对未提交相关技术专利文献检索报告的项目予以立项，造成经济损失或者不良影响的，依法对有关行政主管部门的主管人员和直接责任人给予行政处分；以虚假专利文献检索报告骗取科学技术经费的，由有关行政主管部门追回科学技术经费，对直接责任人依法给予处分；构成犯罪的，依法追究刑事责任。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十二条违反本条例第二十四条规定，主办方未查验参展方专利有效证明文件而同意其以专利产品或者专利技术名义参展的，由市和区、县（市）人民政府有关行政主管部门责令改正，并依法给予处罚。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十三条违反本条例第二十五条规定，广告主或者广告经营者未提供有效专利证明文件，广播、电视、报刊、网络等单位为其发布专利广告的，由市和区、县（市）人民政府有关行政主管部门对有关广告发布单位责令改正，并依法给予处罚。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十四条违反本条例第三十条规定，以虚假材料骗取专利奖的，由专利行政主管部门撤销奖励，追回奖金,并依法给予处罚；构成犯罪的，依法追究刑事责任。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十五条市和区、县（市）人民政府专利行政主管部门的工作人员以及其他有关国家机关工作人员玩忽职守、滥用职权、徇私舞弊的，依法给予行政处分；构成犯罪的，依法追究刑事责任。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七章 附 则</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十六条 本条例自2010年3月1日起施行。</span></p><p><br/></p>', '0', '0', '1', '2010-03-01 00:00:00', '1', '2017-11-13 14:28:08', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:49:43', '0', '');
INSERT INTO `site_policy_rule` VALUES ('2238900f05294049b0877b43d5c2ef6e', 'ZCFG_2238900f05294049b0877b43d5c2ef6e', '关于印发《关于实施科技创新券制度的 若干意见（试行）》的通知', '1', '辽宁省科学技术厅 辽宁省财政厅', '<p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:宋体\">关于印发《辽宁省科学技术厅 辽宁省</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:宋体\">财政厅关于实施科技创新券制度的</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:宋体\">若干意见（试行）》的通知</span></strong></p><p style=\"text-align:center;line-height:28px\"><span style=\"font-size:21px;font-family:宋体\">&nbsp;</span></p><p><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;&nbsp;&nbsp;&nbsp;各市、绥中县、昌图县科技局、财政局，各省级以上高新区管委会，各有关单位：</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">现将《辽宁省科学技术厅 辽宁省财政厅关于实施科技创新券制度的若干意见（试行）》印发给你们，请结合实际贯彻落实。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;</span></p><p style=\"text-indent:192px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;辽宁省科学技术厅&nbsp;&nbsp;&nbsp;&nbsp; 辽宁省财政厅</span></p><p style=\"text-indent:395px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;</span></p><p style=\"text-indent:288px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2015</span><span style=\"font-size:21px;font-family:仿宋_GB2312\">年6月8日</span></p><p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:宋体\">辽宁省科学技术厅 辽宁省财政厅关于实施科技创新券制度的若干意见（试行）</span></strong></p><p style=\"text-align:center\"><span style=\";font-family:黑体\">&nbsp;</span></p><p style=\"text-align:center\"><span style=\";font-family:黑体\">&nbsp;</span></p><p style=\"text-align:center;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第一章&nbsp; 总&nbsp; 则</span></p><p style=\"text-indent:42px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第一条</span><span style=\"font-size:21px;font-family: 仿宋_GB2312\"> 为深入实施创新驱动发展战略，创新财政科技投入方式，鼓励我省中小微企业高效利用省内高校、科研院所等社会科技资源，推动大众创业、万众创新，省科技厅、省财政厅决定实施科技创新券（以下简称“创新券”）制度。</span></p><p style=\"text-indent:42px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第二条</span><span style=\"font-size:21px;font-family: 仿宋_GB2312\"> 创新券是政府免费向中小微企业发放，专门用于向高校、科研机构购买科技创新服务的权益凭证。实施创新券制度是政府推动企业加大科技投入，加强产学研合作，提升科技创新能力的一种激励措施。</span></p><p style=\"text-indent:42px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第三条</span><span style=\"font-size:21px;font-family: 仿宋_GB2312\"> 省科技厅、省财政厅共同负责创新券的顶层设计、决策指导、统筹协调、监督检查。各创新券实施区（包括各市、省级以上高新区等）负责创新券的具体工作。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第四条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 我省开展创新券工作采取先行先试的方式。即支持条件成熟的地区先行开展创新券试点工作，取得经验后在全省适时逐步推广。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第五条 </span><span style=\"font-size:21px;font-family:仿宋_GB2312\">创新券资金来源于各级财政科技资金，其中以创新券实施区所出资金为主，省本级资金为引导资金。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:黑体\">第六条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 创新券的使用和管理须遵守国家有关法律、行政法规和财务规章制度，遵循公开透明、专款专用的原则。</span></p><p style=\"text-align:center;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第二章&nbsp; 工作组织与要求</span></p><p style=\"text-indent:42px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第七条 </span><span style=\"font-size:21px;font-family: 仿宋_GB2312\">各创新券实施区应依据本意见，结合当地实际情况制定相应管理办法、工作方案或实施细则，成立专门管理机构（以下简称“专门机构”），并具体负责创新券制作、发放、兑现等日常管理工作。专门机构应由创新券实施区的科技管理部门会同同级财政管理部门组成。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第八条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 创新券拟实施区自愿提出申请，申请材料包括：本地开展创新券工作的正式申请文件，内容要包括本地基本情况、科技型小微企业发展情况、开展创新券的工作基础、保障条件及地方财政的创新券资金额度；高新区须所在市政府出具以支持该高新区开展创新券工作为主要内容的正式文件；本地创新券管理办法、工作方案或实施细则。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第九条 </span><span style=\"font-size:21px;font-family:仿宋_GB2312\">省科技厅会同省财政厅根据各创新券拟实施区的申请材料、省本级财政经费预算等情况，本着鼓励先进的原则，确定创新券实施区及经费分配额度。</span></p><p style=\"text-indent: 43px;line-height: 28px\"><span style=\"font-size:21px;font-family:黑体\">第十条 </span><span style=\"font-size:21px;font-family:仿宋_GB2312\">省直有关部门根据部门工作任务和工作重点可直接向省科技厅、省财政厅提出开展创新券工作申请，并根据本意见制定相应管理办法、工作方案或实施细则。</span></p><p style=\"text-indent: 43px;line-height: 28px\"><span style=\"font-size:21px;font-family:黑体\">第十一条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 省内各高校、科研院所等单位应积极支持创新券工作开展，主动向社会开放共享科技资源，提供科技创新服务。</span></p><p style=\"text-indent: 43px;line-height: 28px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;</span></p><p style=\"text-align:center;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第三章&nbsp; 支持范围与方式</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第十二条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 创新券支持对象为我省具备企业法人资格、注册期限不满5年、上年度销售收入不超过2000万元、且原则上未获得过各级财政科技资金支持的科技型小微企业（以下简称“企业”）。优先支持在各类创新创业大赛上取得名次的企业和创业者。</span></p><p style=\"line-height:28px\">　　<span style=\"font-size: 21px;font-family:黑体\">第十三条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 创新券用于企业向我省高校、科研院所等单位或机构购买科技服务、科技成果以及实施科技成果转化项目的相关科技创新活动支出。</span></p><p style=\"line-height:28px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">　　购买科技服务、科技成果以及实施科技成果转化项目是指：企业在研究开发等科技创新活动中开展的科技研发及合作、试验研究、产品研制、工艺验证、检验检测、技术转让、专利购买等；创新券不支持非科技创新活动，如法定检测、强制检测等。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:黑体\">第十四条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 创新券采取无偿资助方式，实行实名制及登记备案制，不得转让、买卖，有效期原则上为1年，最多不超过2年。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:黑体\">&nbsp;</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:黑体\">第四章&nbsp; 发放与兑现</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第十五条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 创新券的申领由企业携带相关材料向专门机构提出，经审核后发放。企业申领的创新券总金额最多为创新券有效期内企业科技创新活动预期支出额，原则上不高于20万元。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">申领所需相关材料包括：企业营业执照副本；税务登记证副本；组织机构代码证副本；法定代表人身份证；近两年财务报表；计划使用创新券的科技创新活动简介；其它有利于本企业取得创新券的证明材料。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第十六条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 企业持创新券及相关材料，在创新券有效期内，向专门机构一次性提出兑现申请，经审核后一次性兑现。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">兑现所需相关材料包括：发票或收据；技术合同；创新成果证明材料；资金支出证明材料以及项目经费支出情况；其它证明材料等。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第十七条 </span><span style=\"font-size:21px;font-family:仿宋_GB2312\">兑现金额原则上要大于企业之前领取创新券总金额的二分之一，小于或等于企业科技创新活动实际支出额的三分之一。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第十八条 </span><span style=\"font-size:21px;font-family:仿宋_GB2312\">兑现后的企业可重复申领创新券，但同一个企业累计兑现金额不能超过20万元。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:黑体\">&nbsp;</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:黑体\">第五章&nbsp; 监督与管理</span></p><p style=\"line-height:28px\">　　<span style=\"font-size: 21px;font-family:黑体\">第十九条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 省科技厅、省财政厅会同专门机构授权或委托有关机构对申请材料的真实性进行审查和监督。对创新券申请兑现材料弄虚作假，恶意骗取财政资金的，按《财政违法行为处罚处分条例》的相关规定进行处理，除追回创新券及已兑现资金外，相关企业将被列入信用“黑名单”，相关高校、科研院所等单位视情况也将被追究相应责任。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:黑体\">第二十条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 省科技厅、省财政厅对各创新券实施区的实际工作情况进行跟踪、监督和评价，根据评价结果，对创新券实施管理适时提出调整意见。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:黑体\">&nbsp;</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:黑体\">第六章&nbsp; 附&nbsp; 则</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第二十一条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 本意见由省科技厅、省财政厅负责解释。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:黑体\">第二十二条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 本意见自2015年7月1日起施行。</span></p><p><br/></p>', '0', '0', '1', '2015-06-08 00:00:00', '1', '2017-11-13 13:36:08', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:52:55', '0', '');
INSERT INTO `site_policy_rule` VALUES ('224fc0c010b44f8390db64587884981b', 'ZCFG_224fc0c010b44f8390db64587884981b', '沈阳市科技条件平台管理办法（暂行）', '2', '沈阳市科学技术局', '<p style=\"text-align:center\"><strong><span style=\"font-size: 29px;font-family: 宋体\">沈阳市科技条件平台管理办法</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size: 29px;font-family: 宋体\">（暂行）</span></strong></p><p style=\"text-align:center\"><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family: 仿宋\">为进一步推动沈阳市科技条件平台（简称条件平台）规范建设和高效运行，优化创新生态环境，发挥科技资源服务沈阳辐射沈阳经济区作用，激发创新创造活力，促进实体经济又好又快发展，建设东北亚科技创新中心，按照国务院《关于国家重大科研基础设施和大型科研仪器向社会开放的意见》（国发〔2014〕70号）要求，结合我市实际，制定本办法。</span></p><p style=\"text-align:center\"><span style=\"font-size: 21px;font-family: 黑体\">第一章&nbsp; 总则</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family: 仿宋\">第一条&nbsp; 条件平台是优化科技基础条件资源，夯实科技创新物质基础，加快推进科技资源优化配置和开放共享的重要载体；是激发高校和科研院所创新活力，促进科研能力转化为现实生产力的有效途径；是实现企业（创新团队）信息互通、资源共享、协同创新的综合性科技服务平台。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family: 仿宋\">第二条&nbsp; 条件平台的主要任务是：通过建立集科研仪器设施共享、创新人才服务和科技成果转化等多种功能于一体的平台，聚集科技要素，整合科技资源，为我市高新技术研究、新兴产业发展和产业技术创新提供资源共享、供需对接、研发检测和成果转化等科技服务，全面提高我市科技创新能力。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第三条&nbsp; 沈阳市科学技术局（简称市科技局）是条件平台的主管部门，负责条件平台的总体规划、指导、监督和绩效考评等管理工作，协调解决条件平台建设中的重大问题。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第四条&nbsp; 沈阳科技资源共享服务中心（简称共享中心）受市科技局委托承担条件平台的建设、运营和管理，主要负责： </span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（一）条件平台信息系统（简称信息系统）的建设、运行与管理。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（二）编制条件平台建设和运行的年度计划与经费预算。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（三）受理条件平台服务机构（简称服务机构）加入申请、用户注册登记和科技创新券的申领、兑付等工作。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（四）收集条件平台科技服务和用户需求信息，促进供需对接，做好相关统计备案工作。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（五）开展对服务机构和条件平台区、县（市）工作站（简称工作站）的绩效考核工作。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family: 仿宋\">（六）做好条件平台的宣传推广、探索跨区域科技合作、工作模式创新和市科技局交办的其他工作。&nbsp;&nbsp;&nbsp; </span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family: 仿宋\">（七）定期向市科技局报告条件平台建设和运行等情况，接受市科技局的指导、检查、监督和考核评估。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family: 仿宋\">第五条 </span><span style=\"font-size: 21px;font-family: 黑体\">&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋\">具备设立条件平台的区、县（市）政府，由区、县（市）科技局协助市科技局建立工作站。工作站是条件平台建设与运行的基层服务窗口。</span></p><p style=\"text-align:center\"><span style=\"font-size: 21px;font-family: 黑体\">第二章&nbsp; 服务机构</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第六条&nbsp; 申请加入条件平台的服务机构是依托高校、科研院所、企业等法人单位，拥有科研仪器设备、科技服务人才和科技成果等科技资源，并对社会提供开放共享服务的机构。专业服务机构是服务机构所属的科技资源或技术相对集中的专业服务部门。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第七条&nbsp; 申请加入条件平台的服务机构需向共享中心提交《沈阳市科技条件平台服务机构申报报告》，共享中心组织专家评审通过公示7日后，无异议的即可认定为条件平台的服务机构。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family: 仿宋\">第八条 申请加入条件平台的服务机构，须拥有产权或使用权的服务场地和设备，规章制度和服务体系健全，无不良信用记录。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">服务机构还须依法符合以下至少一种条件：</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（一）提供大型科学仪器设备共享服务的单位或机构。拥有能够开放共享的科研仪器设备，或拥有特殊用途的精密、稀缺的科学仪器设备，性能指标达到我市现有装备的先进水平，运行正常、数据准确、加工精度高，配有熟练操作人员，能向社会提供服务。具备专业分析检测相关的技术咨询、培训的能力。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（二）提供专业技术服务的单位或机构。具备稳定的服务机构管理人员，有明确的专业服务方向，具有一定的专业技术管理优势和较强的组织协调能力，配备专业技术带头人和技术服务人员。有较好的前期工作基础，有对外提供专业技术服务的案例，有相对稳定的用户群体。具备承担国家、省、市级科研项目的能力。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（三）提供科技文献、科学数据等科技信息服务的单位或机构。拥有专职服务人员，拥有一定规模的数字化文献、情报、信息资源，可为用户提供文献检索、全文下载、电子图书在线阅读、科技查新与情报信息等服务。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（四）提供知识产权服务的单位或机构。拥有知识产权执业资格人员，具备信息数据分析、挖掘、预警、知识产权战略研究能力，能够对社会提供相关服务。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family: 仿宋\">（五）其他可对外提供各类公共科技服务的单位或机构。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family: 仿宋\">第九条&nbsp; 服务机构应承担面向社会提供优质科技服务的义务，及时向共享中心提供完善的单位信息和科技资源信息，并纳入条件平台信息系统，向社会开放服务。服务机构接受条件平台的统一管理，及时更新维护单位相关信息。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第十条&nbsp; 服务机构应妥善保管对外服务获得的实验、技术数据等信息，遵守有关知识产权保护的规定，为用户保守技术秘密和商业秘密，保持对外服务的仪器设备完好正常，无正当理由不得拒绝用户使用共享仪器设备要求。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第十一条&nbsp; 纳入条件平台共享的仪器设备和科技服务对外收费原则：已实行政府定价和政府指导价的项目，按照政府定价和政府指导价执行；其他项目实行市场调节价，由用户和服务机构协商确定。&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"text-align:center\"><span style=\"font-size: 21px;font-family: 黑体\">第三章&nbsp; 服务流程</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第十二条&nbsp; 为保障网络安全和供需双方权益，用户需在信息系统注册登记，经共享中心审核通过后方可使用条件平台的相关科技资源和服务。非注册用户和服务机构产生的交易不能计入绩效考核，也不能作为补助凭证。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第十三条&nbsp; 条件平台服务流程：</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（一）需求申请。用户通过信息系统在线提交需求申请。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（二）供需对接。条件平台负责将用户需求推荐给条件匹配的服务机构，再将反馈信息发送给申请需求用户；服务机构也可自行登录信息系统在线浏览需求申请，将接单意向信息反馈给条件平台，条件平台提供对接服务。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（三）签订实施。服务机构与用户签署合同后，供需双方根据相关要求，认真履行职责，条件平台提供全过程的支持服务。&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（四）填报信息。合同履行结束后，用户需在信息系统反馈服务质量意见，服务机构需将签订的合同线上报共享中心。相关填报内容作为享受补助的主要依据。</span></p><p style=\"text-align:center\"><span style=\"font-size: 21px;font-family: 黑体\">第四章&nbsp; 信息管理</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第十四条　条件平台信息系统重点发布条件平台建设动态、科技资源共享、科技服务目录、相关政策法规和其他与条件平台建设服务工作相关的信息。&nbsp; </span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第十五条　条件平台应建立健全信息系统安全管理制度，加强设备安全检查，落实技术防范措施，确保条件平台数据安全和运行顺畅。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第十六条　共享中心负责条件平台的信息更新维护。条件平台发布的信息应严格履行审批程序，未经审核不得发布。信息审核要求：</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（一）信息内容无涉密问题；</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（二）信息数据真实准确、及时有效；</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（三）其他符合国家关于信息发布的相关要求。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第十七条　工作站和服务机构应按照条件平台要求，通过信息系统或以电子邮件方式将信息提供给共享中心，经审核员签字批准后，统一由信息发布员发布。</span></p><p style=\"text-align:center\"><span style=\"font-size: 21px;font-family: 黑体\">第五章&nbsp; 考核评价和监督</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第十八条&nbsp; 市科技局在沈阳市科技创新专项资金中设立条件平台专项计划（简称条件平台专项），支持条件平台建设、维护、运行和补助。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第十九条&nbsp; 服务机构和工作站的补助资金可依法用于设备运行维护、服务人员奖励、管理人员培训补助和服务机构（工作站）建设的其他经费补助。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">第二十条&nbsp; 对弄虚作假，骗取补助的，一经查实，依据《沈阳市科学技术计划项目管理办法》（沈科发〔2016〕53号）和《沈阳市科技创新专项资金管理办法》（沈财企〔2017〕439号）相关规定执行，涉嫌违法的，交由司法机关依法处理。</span></p><p style=\"text-align:center\"><span style=\"font-size: 21px;font-family: 黑体\">第六章&nbsp; 附则</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family: 仿宋\">第二十一条&nbsp; 本办法由沈阳市科技局负责解释。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family: 仿宋\">第二十二条&nbsp; 本办法自发布之日起施行。</span></p><p><br/></p>', '0', '0', '1', '2017-11-07 00:00:00', '1', '2017-11-15 15:00:00', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:56:24', '0', '');
INSERT INTO `site_policy_rule` VALUES ('444c0c98dc0a48728ce7f8a313fb538e', 'ZCFG_444c0c98dc0a48728ce7f8a313fb538e', '沈阳市科技条件平台绩效考评实施细则（试行）', '2', '市科技局', '<p style=\"text-align:center\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">沈科发〔2018〕47号</span></p><p style=\"text-align:center\"><span style=\"font-size:29px;font-family:仿宋\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;text-align:center;line-height:37px\"><span style=\"font-size:29px;font-family:方正小标宋简体;color:black\">关于印发《</span><span style=\"font-size:29px;font-family:方正小标宋简体\">沈阳市科技条件平台绩效考评</span></p><p style=\"text-align:center;line-height:44px\"><span style=\"font-size:29px;font-family:方正小标宋简体\">实施细则（试行）</span><span style=\"font-size:29px;font-family:方正小标宋简体;color:black\">》的通知</span></p><p style=\"line-height:36px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;</span></p><p style=\";line-height:36px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">各有关单位</span><span style=\"font-size:21px;font-family:仿宋_GB2312\">：</span></p><p style=\"text-indent:43px;line-height:36px;text-autospace:ideograph-numeric\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">为科学评价沈阳市科技条件平台服务机构和工作站的运行情况，促进条件平台充分发挥资源整合和对外服务作用，特制定《沈阳市科技条件平台绩效考评实施细则（试行）》，现印发给你们，请遵照执行。</span></p><p style=\"text-indent:43px;line-height:36px;text-autospace:ideograph-numeric\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;</span></p><p style=\"text-indent:43px;line-height:36px;text-autospace:ideograph-numeric\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;</span></p><p style=\"text-indent:43px;line-height:36px;text-autospace:ideograph-numeric\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;</span></p><p style=\"line-height:36px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋_GB2312\">沈阳市科学技术局</span></p><p style=\"line-height:36px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2018</span><span style=\"font-size:21px;font-family:仿宋_GB2312\">年5月24日</span></p><p style=\"line-height:36px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\"><br/></span></p><p style=\"margin: 10px 0px; line-height: 37px; text-align: center;\"><span style=\"font-size:29px;font-family:方正小标宋简体\">沈阳市科技条件平台绩效考评实施细则（试行）</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;text-align:center;line-height:37px\"><span style=\"font-size:20px;font-family:黑体\">&nbsp;</span></p><p style=\"text-align:center;line-height:43px\"><span style=\"font-size:21px;font-family:黑体\">第一章　总 则</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第一条&nbsp; 为规范沈阳市科技条件平台（简称条件平台）建设，加强对条件平台服务机构（简称服务机构）和工作站的管理和服务，提高条件平台绩效考评后补助资金（简称后补助资金）的使用效率，提升服务机构和工作站整合、开放、共享、服务能力，使条件平台成为整合资源、聚集需求、畅通渠道、高效运行的载体，依据《沈阳市科技条件平台管理办法（暂行）》（沈科发〔2017〕68号）和国家有关要求，特制定本实施细则。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第二条&nbsp; 绩效考评依据：服务机构和工作站在条件平台信息系统实际发生并记录的开放共享科技资源、科技服务活动、经济社会效益等数据信息，以及按照市科技局要求报送的考评材料。 </span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第三条&nbsp; 绩效考评坚持公开、公平、公正的原则，采用科学、规范的考评程序和操作方案，客观衡量条件平台运行服务效果。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第四条&nbsp; 根据绩效考评结果，对表现优秀的单位给予后补助资金支持。</span></p><p style=\"text-align:center;line-height:43px\"><span style=\"font-size:21px;font-family:黑体\">第二章　组织实施</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第五条&nbsp; 市科技局负责条件平台绩效考评工作的总体规划，指导制定考评工作方案和指标体系，对绩效考评工作进行监督、检查和决策，督促整改考评中发现的问题。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第六条&nbsp; 沈阳科技资源共享服务中心（简称共享中心）受市科技局委托，负责绩效考评工作的具体实施，协助市科技局管理拨付后补助资金。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第七条&nbsp; 后补助资金从科技创新专项资金中统一安排。</span></p><p style=\"text-align:center;line-height:43px\"><span style=\"font-size:21px;font-family:黑体\">第三章　考评对象和内容</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第八条&nbsp; 考评对象：服务机构和工作站。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第九条&nbsp; 考评内容：按照条件平台建设要求，围绕服务机构和工作站的机构建设、资源整合、运营服务等工作，确定绩效考评内容。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">服务机构考评要点：服务机构制度建设和云平台使用维护情况、开放共享科技资源情况、通过科技条件平台服务企业实现合同额和服务企业数量、创新券实施效果等方面。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">工作站考评要点：工作站管理服务制度建设情况、聚集辖区科技需求、开展供需对接、培训等活动情况、促成科技成果在本辖区转化情况、推动实施创新券工作情况等方面。&nbsp; </span></p><p style=\"text-align:center;line-height:43px\"><span style=\"font-size:21px;font-family:黑体\">第四章　考评方式</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第十条&nbsp; 条件平台绩效考评采取日常管理与专家评审、定量与定性考评相结合的方式，分类组织、定量比较、综合评价、择优鼓励，每年度考评一次。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第十一条&nbsp; 考评方法：</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">1.</span><span style=\"font-size:21px;font-family:仿宋\">对服务机构和工作站分别组织考评，分类设定后补助资金额度。服务机构按照依托单位性质，分为高等院校、科研院所和企业三类，其中，企业类按照提供服务类别，分为研发检测类、咨询服务类和其他服务类。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">2.</span><span style=\"font-size:21px;font-family:仿宋\">依据绩效考评得分情况进行排名，以不超过30%的比例择优给予后补助资金支持。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">3.</span><span style=\"font-size:21px;font-family:仿宋\">绩效考评指标包括专家评分指标和客观评分指标，其中客观评分指标的得分计算方法：</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">某项指标考评系数=某服务机构（工作站）该项指标实际完成数÷该类参加绩效考评的服务机构（工作站）该项指标实际完成数总和；</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">某项指标考评得分=该项指标权重分数×该项指标考评系数。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">某服务机构（工作站）得分=客观评分指标考评得分（换算百分制后）总和+专家评分指标考评得分总和。</span></p><p style=\"text-align:center;line-height:43px\"><span style=\"font-size:21px;font-family:黑体\">&nbsp;</span></p><p style=\"text-align:center;line-height:43px\"><span style=\"font-size:21px;font-family:黑体\">第五章　考评流程</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第十二条&nbsp; 市科技局每年初发布绩效考评工作方案，明确考评内容和指标体系。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第十三条&nbsp; 各有关单位须在日常开展科技资源开放共享服务工作过程中，及时在条件平台信息系统填报相关数据信息。共享中心每季度将有关数据信息统计后，报市科技局审核认定。经审核认定的数据信息，作为各单位定量考评依据。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第十四条&nbsp; 市科技局发布开展绩效考评的通知，共享中心组织各有关单位在条件平台信息系统填写绩效完成情况表、撰写并上报年度工作报告。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第十五条&nbsp; 提供相关附件和证明材料，主要包括：</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">1.</span><span style=\"font-size:21px;font-family:仿宋\">服务机构营业执照副本复印件（加盖单位公章）；</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">2.</span><span style=\"font-size:21px;font-family:仿宋\">管理服务制度；</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">3.</span><span style=\"font-size:21px;font-family:仿宋\">服务合同复印件（服务机构提供）；</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">4.</span><span style=\"font-size:21px;font-family:仿宋\">区级支持条件平台和创新券的相关政策（工作站提供）；</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">5.</span><span style=\"font-size:21px;font-family:仿宋\">其他相关材料。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第十六条以上材料须服务机构（工作站）依托单位负责人（区县科技局领导）审核签字，加盖服务机构（区县科技局）公章后，报送共享中心审查备案，同时报送电子版。申报材料要真实、可靠、齐全、规范。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第十七条&nbsp; 共享中心负责组织专家评审，汇总评审意见，形成考评结果，经市科技局审核公示后，履行政府审批程序，拨付后补助资金。</span></p><p style=\"text-align:center;line-height:43px\"><span style=\"font-size:21px;font-family:黑体\">第六章　资金管理与监督</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第十八条　服务机构和工作站的依托单位负责后补助资金的管理和监督，应严格按照《沈阳市科技创新专项资金管理办法》（沈财企〔2017</span><span style=\"font-size:21px;font-family:宋体\">〕</span><span style=\"font-size:21px;font-family:仿宋\">439</span><span style=\"font-size:21px;font-family:仿宋\">号）执行，做到手续完备、账目明晰、内容真实、核算准确、使用合规。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">有弄虚作假、截留、挪用、挤占后补助资金等行为的，一经查实，依据《沈阳市科学技术计划项目管理办法》（沈科发〔2016</span><span style=\"font-size:21px;font-family:宋体\">〕53</span><span style=\"font-size:21px;font-family:仿宋\">号）、《沈阳市科技创新专项资金管理办法》（沈财企〔2017</span><span style=\"font-size:21px;font-family:宋体\">〕</span><span style=\"font-size:21px;font-family:仿宋\">439</span><span style=\"font-size:21px;font-family:仿宋\">号）相关规定执行，涉嫌违法的，交由司法机关依法处理。</span></p><p style=\"text-align:center;line-height:43px\"><span style=\"font-size:21px;font-family:黑体\">第七章</span>　<span style=\"font-size:21px;font-family:黑体\">附则</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第十九条　本实施细则由市科技局负责解释。</span></p><p style=\"text-indent:43px;line-height:43px\"><span style=\"font-size:21px;font-family:仿宋\">第二十条　本实施细则自公布之日起实施。</span></p><p style=\"line-height:43px\"><span style=\"font-family:仿宋_GB2312\">&nbsp;</span></p><p style=\"line-height:43px\"><span style=\"font-family:仿宋_GB2312\">&nbsp;</span></p><table><tbody><tr style=\";height:45px\" class=\"firstRow\"><td style=\"border-width: 1px medium; border-style: solid none; border-color: windowtext currentcolor; padding: 0px 7px;\" valign=\"top\" width=\"595\"><p style=\"text-indent:14px;line-height:36px\"><br/></p>&nbsp; &nbsp; &nbsp;<span style=\";position:absolute;z-index:-2;left:0px;margin-left:360px;margin-top:42px;width:35px;height:34px\"> &nbsp; &nbsp;&nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"font-size:21px;font-family:仿宋_GB2312\">沈阳市科学技术局办公室&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2018年5月24日印发</span> &nbsp;</td></tr></tbody></table><p style=\"line-height:5px\"><span style=\"font-size:21px;font-family: 仿宋_GB2312\">&nbsp;</span></p><p><br/></p>', '0', '0', '', '2018-07-17 00:00:00', 'cec3c5b1065c4e0e84a900512d97522e', '2018-07-17 11:03:33', 'cec3c5b1065c4e0e84a900512d97522e', '2018-07-17 11:08:33', '0', '');
INSERT INTO `site_policy_rule` VALUES ('4871d977629a45f2867c9d454a5f3148', 'ZCFG_4871d977629a45f2867c9d454a5f3148', '沈阳市科技振兴奖实施办法', '2', '沈阳市人民政府', '<p style=\"text-align: center;\"><br/></p><p><span style=\"font-size: 18px\">《沈阳市科技振兴奖实施办法》，业经市政府第十九次常务会议通过，现予发布，自发布之日起施行。</span></p><p style=\";margin-bottom:0\"><strong><span style=\"font-size: 18px;font-family: 宋体\">　　第一条</span></strong><span style=\"font-size: 18px\">　为充分发挥科学技术在经济建设中的重大作用，促进科技成果转化，推动我市社会经济的发展，制定本办法。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　<strong><span style=\"font-family:宋体\">第二条</span></strong>　科技振兴奖是市政府对依靠科学技术，振兴沈阳经济，推动科技进步和社会发展中取得重大经济效益的科技项目以及在这方面做出重大贡献的个人授予的最高科技荣誉奖励。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　<strong><span style=\"font-family:宋体\">　第三条</span></strong>　科技振兴奖每年评选一次，评选</span><span style=\"font-size: 18px;font-family: Simsun, serif\">10</span><span style=\"font-size: 18px\">项（含个人）。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　<strong><span style=\"font-family:宋体\">第四条</span></strong>　市科技振兴奖评审领导小组，下设专家评审委员会，办公室设在市科委，具体负责承办科技振兴奖的申报评审及管理工作。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　<strong><span style=\"font-family:宋体\">　第五条</span></strong>　凡以科技支持经济发展为目的，在科技成果转化过程中，对我市社会经济发展做出重大贡献并取得显著经济效益和社会效益的科技项目及个人，在上一个年度中，具备下列条件之一的，均可以申报科技振兴奖。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　（一）在科研开发、科技攻关和科技成果推广等科技创新及科技成果转化活动中，获国家发明二等奖、国家科技进步一等奖以上的项目，通过应用达到一定的生产规模，取得显著经济效益和社会效益的项目；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　（二）在科技经济对接活动中，科研院所、大专院校、高科技企业的科技成果转给企业应用后，形成了一定生产规模，一个财政年度利税总额达</span><span style=\"font-size: 18px;font-family: Simsun, serif\">500</span><span style=\"font-size: 18px\">万元以上的项目；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　（三）企业自行开发或引进的高新技术及产品，形成产业化规模，一个财政年度利税总额达</span><span style=\"font-size: 18px;font-family: Simsun, serif\">500</span><span style=\"font-size: 18px\">万元以上的项目；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　（四）高新技术成果、新技术、新工艺、新品种在全市推广，实施单位所形成的规模效益，一个财政年度利税总额达</span><span style=\"font-size: 18px;font-family: Simsun, serif\">1000</span><span style=\"font-size: 18px\">万元以上；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　（五）引进和消化吸收国外先进技术，形成一定生产规模，一个财政年度利税总额达</span><span style=\"font-size: 18px;font-family: Simsun, serif\">500</span><span style=\"font-size: 18px\">万元以上的项目；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　（六）在发展</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">两高一优</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">农业生产中，推广应用的农业畜禽新品种及先进生产技术，其推广应用覆盖面达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">70%</span><span style=\"font-size: 18px\">以上，作物增产达</span><span style=\"font-size: 18px;font-family: Simsun, serif\">10%</span><span style=\"font-size: 18px\">以上，畜禽及产品增产达</span><span style=\"font-size: 18px;font-family: Simsun, serif\">5%</span><span style=\"font-size: 18px\">以上的项目；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　（七）通过技术创新，使企业主导产品达到国内领先水平，一个财政年度利税总额达</span><span style=\"font-size: 18px;font-family: Simsun, serif\">500</span><span style=\"font-size: 18px\">万元；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　（八）在国土资源开发、资源综合利用与治理、、节省资源、环境保护、保障人民身体健康等社会发展事业方面，具有国际先进水平，并取得明显综合效益的项目以及为我市科技发展、经济建设与繁荣做出重大贡献，为我市争得荣誉，在国内外产生重大影响或在某一学科领域中有重大突破，成绩卓著，获得较高荣誉的个人。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><strong><span style=\"font-size: 18px;font-family: 宋体\">　　第六条</span></strong><span style=\"font-size: 18px\">　科技振兴奖采取逐级推荐评选的方法。由市科委对上报奖项进行调查，在广泛征求社会各界意见的基础上由评审委员会进行考核、初审。初审后的项目由评审领导小组审定后报市政府常务会讨论决定，由市长签发。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　<strong><span style=\"font-family:宋体\">　第七条</span></strong>　科技振兴奖的申报：</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　（一）市属单位按行政隶属关系上报主管部门或区、县</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">市</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">人民政府同意后，报市科委。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　（二）驻沈中央、省属单位及外埠驻沈单位直接向市科委申报。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　（三）三资、私营企业及个人、外藉人员由当地区、县</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">市</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">政府初审推荐。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　（四）两个以上单位共同完成的项目，由项目主持单位组织联合申报科技振兴奖，不得重复申报。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　每年三月末前，将申报科技振兴奖的有关材料</span><span style=\"font-size: 18px;font-family: Simsun, serif\">( </span><span style=\"font-size: 18px\">包括申报书、科技成果鉴定、有关证明</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">一式二十份报市科委。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　<strong><span style=\"font-family:宋体\">第八条</span></strong>　对获奖项目实现利税总额在</span><span style=\"font-size: 18px;font-family: Simsun, serif\">500-800</span><span style=\"font-size: 18px\">万元的，奖励人民币</span><span style=\"font-size: 18px;font-family: Simsun, serif\">10</span><span style=\"font-size: 18px\">万元；实现利税总额在</span><span style=\"font-size: 18px;font-family: Simsun, serif\">800-1000</span><span style=\"font-size: 18px\">万元的，奖励人民币</span><span style=\"font-size: 18px;font-family: Simsun, serif\">20</span><span style=\"font-size: 18px\">万元；实现利税总额在</span><span style=\"font-size: 18px;font-family: Simsun, serif\">1000</span><span style=\"font-size: 18px\">万元以上的，奖励人民币</span><span style=\"font-size: 18px;font-family: Simsun, serif\">30</span><span style=\"font-size: 18px\">万元。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　获奖项目的奖金应按贡献大小合理分配给项目完成者。项目主要完成者（指前三名）所获奖金不得低于奖金总额的</span><span style=\"font-size: 18px;font-family: Simsun, serif\">70%</span><span style=\"font-size: 18px\">。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　对获奖个人奖励人民币</span><span style=\"font-size: 18px;font-family: Simsun, serif\">5-10</span><span style=\"font-size: 18px\">万元。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　<strong><span style=\"font-family:宋体\">第九条</span></strong>　对科技振兴奖的获奖项目及其完成项目的人员</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">限</span><span style=\"font-size: 18px;font-family: Simsun, serif\">7</span><span style=\"font-size: 18px\">人以内</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">和获奖个人，分别由市政府颁发奖牌、奖励证书及奖金。并向全市公告。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　科技振兴奖奖金免征个人所得税。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　<strong><span style=\"font-family:宋体\">第十条</span></strong>　对荣获科技振兴奖的外藉人员授予沈阳市荣誉市民称号。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　<strong><span style=\"font-family:宋体\">　第十一条</span></strong>　科技振兴奖所需奖金及其他费用，由市财政部门按实际需要核拨。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　<strong><span style=\"font-family:宋体\">第十二条</span></strong>　对获得市科技振兴奖的项目和个人，如发现有弄虚作假，剽窃他人成果等行为，经查明属实，撤销其奖励，追回奖金，并按情节轻重给予纪律处分或依法追究法律责任，向社会公布。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　<strong><span style=\"font-family:宋体\">第十三条</span></strong>　本办法执行中的具体问题由市科学技术委员会负责解释。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px\">　　<strong><span style=\"font-family:宋体\">第十四条</span></strong>　本办法自发布之日起施行。《沈阳市科技振兴奖实施办法》</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">一九九二年三月十六日沈阳市人民政府令第</span><span style=\"font-size: 18px;font-family: Simsun, serif\">1</span><span style=\"font-size: 18px\">号</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">同时废止。</span></p><p><br/></p>', '0', '0', '1', '2016-02-17 00:00:00', '1', '2017-11-13 14:11:42', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:58:01', '0', '');
INSERT INTO `site_policy_rule` VALUES ('52a3f5926de24cc687f26d803fb64420', 'ZCFG_52a3f5926de24cc687f26d803fb64420', '中华人民共和国科学技术进步法', '0', '全国人民代表大会常务委员会', '<p style=\"text-align:center\"><strong><span style=\"font-size:20px;font-family:宋体;color:#333333\">中华人民共和国科学技术进步法</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:20px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></strong></p><p style=\"line-height:30px\"><span style=\"font-size:12px;color:#333333\">中华人民共和国主席令</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span>　　 <span style=\"font-size:12px;color:#333333\">第　八十二　号</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　《中华人民共和国科学技术进步法》已由中华人民共和国第十届全国人民代表大会常务委员会第三十一次会议于</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2007</span><span style=\"font-size:12px;color:#333333\">年</span><span style=\"font-size:12px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">12</span><span style=\"font-size:12px;color:#333333\">月</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">29</span><span style=\"font-size:12px;color:#333333\">日修订通过，现将修订后的《中华人民共和国科学技术进步法》公布，自</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2008</span><span style=\"font-size:12px;color:#333333\">年</span><span style=\"font-size:12px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">7</span><span style=\"font-size:12px;color:#333333\">月</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size:12px;color:#333333\">日起施行。</span><span style=\"font-size:12px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　中华人民共和国主席　胡锦涛</span><span style=\"font-size: 12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span>　　<span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2007</span><span style=\"font-size: 12px;color:#333333\">年</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">12</span><span style=\"font-size:12px;color:#333333\">月</span><span style=\"font-size:12px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">29</span><span style=\"font-size:12px;color:#333333\">日</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　中华人民共和国科学技术进步法</span><span style=\"font-size: 12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span>　　<span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1993</span><span style=\"font-size: 12px;color:#333333\">年</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">7</span><span style=\"font-size:12px;color:#333333\">月</span><span style=\"font-size:12px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size:12px;color:#333333\">日第八届全国人民代表大会常务委员会第二次会议通过，</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2007</span><span style=\"font-size:12px;color:#333333\">年</span><span style=\"font-size:12px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">12</span><span style=\"font-size:12px;color:#333333\">月</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">29</span><span style=\"font-size:12px;color:#333333\">日第十届全国人民代表大会常务委员会第三十一次会议修订）</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　目　录</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第一章　总　则</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第二章　科学研究、技术开发与科学技术应用</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第三章　企业技术进步</span><span style=\"font-size: 12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第四章　科学技术研究开发机构</span><span style=\"font-size: 12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第五章　科学技术人员</span><span style=\"font-size: 12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第六章　保障措施</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第七章　法律责任</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第八章　附　则</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> &nbsp;<br/> </span><span style=\"font-size:12px;color:#333333\">第一章　总　则</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第一条　为了促进科学技术进步，发挥科学技术第一生产力的作用，促进科学技术成果向现实生产力转化，推动科学技术为经济建设和社会发展服务，根据宪法，制定本法。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第二条</span> <span style=\"font-size:12px;color:#333333\">国家坚持科学发展观，实施科教兴国战略，实行自主创新、重点跨越、支撑发展、引领未来的科学技术工作指导方针，构建国家创新体系，建设创新型国家。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第三条</span> <span style=\"font-size:12px;color:#333333\">国家保障科学技术研究开发的自由，鼓励科学探索和技术创新，保护科学技术人员的合法权益。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　全社会都应当尊重劳动、尊重知识、尊重人才、尊重创造。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　学校及其他教育机构应当坚持理论联系实际，注重培养受教育者的独立思考能力、实践能力、创新能力，以及追求真理、崇尚创新、实事求是的科学精神。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第四条</span> <span style=\"font-size:12px;color:#333333\">经济建设和社会发展应当依靠科学技术，科学技术进步工作应当为经济建设和社会发展服务。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　国家鼓励科学技术研究开发，推动应用科学技术改造传统产业、发展高新技术产业和社会事业。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第五条</span> <span style=\"font-size:12px;color:#333333\">国家发展科学技术普及事业，普及科学技术知识，提高全体公民的科学文化素质。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　国家鼓励机关、企业事业组织、社会团体和公民参与和支持科学技术进步活动。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第六条</span> <span style=\"font-size:12px;color:#333333\">国家鼓励科学技术研究开发与高等教育、产业发展相结合，鼓励自然科学与人文社会科学交叉融合和相互促进。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　国家加强跨地区、跨行业和跨领域的科学技术合作，扶持民族地区、边远地区、贫困地区的科学技术进步。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　国家加强军用与民用科学技术计划的衔接与协调，促进军用与民用科学技术资源、技术开发需求的互通交流和技术双向转移，发展军民两用技术。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第七条</span> <span style=\"font-size:12px;color:#333333\">国家制定和实施知识产权战略，建立和完善知识产权制度，营造尊重知识产权的社会环境，依法保护知识产权，激励自主创新。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　企业事业组织和科学技术人员应当增强知识产权意识，增强自主创新能力，提高运用、保护和管理知识产权的能力。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第八条</span> <span style=\"font-size:12px;color:#333333\">国家建立和完善有利于自主创新的科学技术评价制度。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　科学技术评价制度应当根据不同科学技术活动的特点，按照公平、公正、公开的原则，实行分类评价。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第九条</span> <span style=\"font-size:12px;color:#333333\">国家加大财政性资金投入，并制定产业、税收、金融、政府采购等政策，鼓励、引导社会资金投入，推动全社会科学技术研究开发经费持续稳定增长。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第十条</span> <span style=\"font-size:12px;color:#333333\">国务院领导全国科学技术进步工作，制定科学技术发展规划，确定国家科学技术重大项目、与科学技术密切相关的重大项目，保障科学技术进步与经济建设和社会发展相协调。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　地方各级人民政府应当采取有效措施，推进科学技术进步。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第十一条</span> <span style=\"font-size:12px;color:#333333\">国务院科学技术行政部门负责全国科学技术进步工作的宏观管理和统筹协调；国务院其他有关部门在各自的职责范围内，负责有关的科学技术进步工作。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　县级以上地方人民政府科学技术行政部门负责本行政区域的科学技术进步工作；县级以上地方人民政府其他有关部门在各自的职责范围内，负责有关的科学技术进步工作。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第十二条</span> <span style=\"font-size:12px;color:#333333\">国家建立科学技术进步工作协调机制，研究科学技术进步工作中的重大问题，协调国家科学技术基金和国家科学技术计划项目的设立及相互衔接，协调军用与民用科学技术资源配置、科学技术研究开发机构的整合以及科学技术研究开发与高等教育、产业发展相结合等重大事项。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第十三条</span> <span style=\"font-size:12px;color:#333333\">国家完善科学技术决策的规则和程序，建立规范的咨询和决策机制，推进决策的科学化、民主化。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　制定科学技术发展规划和重大政策，确定科学技术的重大项目、与科学技术密切相关的重大项目，应当充分听取科学技术人员的意见，实行科学决策。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第十四条</span> <span style=\"font-size:12px;color:#333333\">中华人民共和国政府发展同外国政府、国际组织之间的科学技术合作与交流，鼓励科学技术研究开发机构、高等学校、科学技术人员、科学技术社会团体和企业事业组织依法开展国际科学技术合作与交流。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第十五条</span> <span style=\"font-size:12px;color:#333333\">国家建立科学技术奖励制度，对在科学技术进步活动中做出重要贡献的组织和个人给予奖励。具体办法由国务院规定。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　国家鼓励国内外的组织或者个人设立科学技术奖项，对科学技术进步给予奖励。</span><span style=\"font-size:12px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> &nbsp;<br/> </span><span style=\"font-size:12px;color:#333333\">第二章　科学研究、技术开发与科学技术应用</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第十六条</span> <span style=\"font-size:12px;color:#333333\">国家设立自然科学基金，资助基础研究和科学前沿探索，培养科学技术人才。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　国家设立科技型中小企业创新基金，资助中小企业开展技术创新。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　国家在必要时可以设立其他基金，资助科学技术进步活动。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第十七条</span> <span style=\"font-size:12px;color:#333333\">从事下列活动的，按照国家有关规定享受税收优惠：</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（一）从事技术开发、技术转让、技术咨询、技术服务；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（二）进口国内不能生产或者性能不能满足需要的科学研究或者技术开发用品；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（三）为实施国家重大科学技术专项、国家科学技术计划重大项目，进口国内不能生产的关键设备、原材料或者零部件；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（四）法律、国家有关规定规定的其他科学研究、技术开发与科学技术应用活动。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第十八条</span> <span style=\"font-size:12px;color:#333333\">国家鼓励金融机构开展知识产权质押业务，鼓励和引导金融机构在信贷等方面支持科学技术应用和高新技术产业发展，鼓励保险机构根据高新技术产业发展的需要开发保险品种。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　政策性金融机构应当在其业务范围内，为科学技术应用和高新技术产业发展优先提供金融服务。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第十九条</span> <span style=\"font-size:12px;color:#333333\">国家遵循科学技术活动服务国家目标与鼓励自由探索相结合的原则，超前部署和发展基础研究、前沿技术研究和社会公益性技术研究，支持基础研究、前沿技术研究和社会公益性技术研究持续、稳定发展。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　科学技术研究开发机构、高等学校、企业事业组织和公民有权依法自主选择课题，从事基础研究、前沿技术研究和社会公益性技术研究。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第二十条</span> <span style=\"font-size:12px;color:#333333\">利用财政性资金设立的科学技术基金项目或者科学技术计划项目所形成的发明专利权、计算机软件著作权、集成电路布图设计专有权和植物新品种权，除涉及国家安全、国家利益和重大社会公共利益的外，授权项目承担者依法取得。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　项目承担者应当依法实施前款规定的知识产权，同时采取保护措施，并就实施和保护情况向项目管理机构提交年度报告；在合理期限内没有实施的，国家可以无偿实施，也可以许可他人有偿实施或者无偿实施。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　项目承担者依法取得的本条第一款规定的知识产权，国家为了国家安全、国家利益和重大社会公共利益的需要，可以无偿实施，也可以许可他人有偿实施或者无偿实施。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　项目承担者因实施本条第一款规定的知识产权所产生的利益分配，依照有关法律、行政法规的规定执行；法律、行政法规没有规定的，按照约定执行。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第二十一条</span> <span style=\"font-size:12px;color:#333333\">国家鼓励利用财政性资金设立的科学技术基金项目或者科学技术计划项目所形成的知识产权首先在境内使用。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　前款规定的知识产权向境外的组织或者个人转让或者许可境外的组织或者个人独占实施的，应当经项目管理机构批准；法律、行政法规对批准机构另有规定的，依照其规定。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第二十二条</span> <span style=\"font-size:12px;color:#333333\">国家鼓励根据国家的产业政策和技术政策引进国外先进技术、装备。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　利用财政性资金和国有资本引进重大技术、装备的，应当进行技术消化、吸收和再创新。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第二十三条</span> <span style=\"font-size:12px;color:#333333\">国家鼓励和支持农业科学技术的基础研究和应用研究，传播和普及农业科学技术知识，加快农业科学技术成果转化和产业化，促进农业科学技术进步。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　县级以上人民政府应当采取措施，支持公益性农业科学技术研究开发机构和农业技术推广机构进行农业新品种、新技术的研究开发和应用。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　地方各级人民政府应当鼓励和引导农村群众性科学技术组织为种植业、林业、畜牧业、渔业等的发展提供科学技术服务，对农民进行科学技术培训。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第二十四条</span> <span style=\"font-size:12px;color:#333333\">国务院可以根据需要批准建立国家高新技术产业开发区，并对国家高新技术产业开发区的建设、发展给予引导和扶持，使其形成特色和优势，发挥集聚效应。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第二十五条</span> <span style=\"font-size:12px;color:#333333\">对境内公民、法人或者其他组织自主创新的产品、服务或者国家需要重点扶持的产品、服务，在性能、技术等指标能够满足政府采购需求的条件下，政府采购应当购买；首次投放市场的，政府采购应当率先购买。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　政府采购的产品尚待研究开发的，采购人应当运用招标方式确定科学技术研究开发机构、高等学校或者企业进行研究开发，并予以订购。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第二十六条</span> <span style=\"font-size:12px;color:#333333\">国家推动科学技术研究开发与产品、服务标准制定相结合，科学技术研究开发与产品设计、制造相结合；引导科学技术研究开发机构、高等学校、企业共同推进国家重大技术创新产品、服务标准的研究、制定和依法采用。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第二十七条</span> <span style=\"font-size:12px;color:#333333\">国家培育和发展技术市场，鼓励创办从事技术评估、技术经纪等活动的中介服务机构，引导建立社会化、专业化和网络化的技术交易服务体系，推动科学技术成果的推广和应用。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　技术交易活动应当遵循自愿、平等、互利有偿和诚实信用的原则。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第二十八条</span> <span style=\"font-size:12px;color:#333333\">国家实行科学技术保密制度，保护涉及国家安全和利益的科学技术秘密。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　国家实行珍贵、稀有、濒危的生物种质资源、遗传资源等科学技术资源出境管理制度。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第二十九条</span> <span style=\"font-size:12px;color:#333333\">国家禁止危害国家安全、损害社会公共利益、危害人体健康、违反伦理道德的科学技术研究开发活动。</span><span style=\"font-size:12px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> &nbsp;<br/> </span><span style=\"font-size:12px;color:#333333\">第三章　企业技术进步</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第三十条</span> <span style=\"font-size:12px;color:#333333\">国家建立以企业为主体，以市场为导向，企业同科学技术研究开发机构、高等学校相结合的技术创新体系，引导和扶持企业技术创新活动，发挥企业在技术创新中的主体作用。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第三十一条</span> <span style=\"font-size:12px;color:#333333\">县级以上人民政府及其有关部门制定的与产业发展相关的科学技术计划，应当体现产业发展的需求。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　县级以上人民政府及其有关部门确定科学技术计划项目，应当鼓励企业参与实施和平等竞争；对具有明确市场应用前景的项目，应当鼓励企业联合科学技术研究开发机构、高等学校共同实施。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第三十二条</span> <span style=\"font-size:12px;color:#333333\">国家鼓励企业开展下列活动：</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（一）设立内部科学技术研究开发机构；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（二）同其他企业或者科学技术研究开发机构、高等学校联合建立科学技术研究开发机构，或者以委托等方式开展科学技术研究开发；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（三）培养、吸引和使用科学技术人员；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（四）同科学技术研究开发机构、高等学校、职业院校或者培训机构联合培养专业技术人才和高技能人才，吸引高等学校毕业生到企业工作；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（五）依法设立博士后工作站；</span><span style=\"font-size: 12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（六）结合技术创新和职工技能培训，开展科学技术普及活动，设立向公众开放的普及科学技术的场馆或者设施。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第三十三条</span> <span style=\"font-size:12px;color:#333333\">国家鼓励企业增加研究开发和技术创新的投入，自主确立研究开发课题，开展技术创新活动。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　国家鼓励企业对引进技术进行消化、吸收和再创新。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　企业开发新技术、新产品、新工艺发生的研究开发费用可以按照国家有关规定，税前列支并加计扣除，企业科学技术研究开发仪器、设备可以加速折旧。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第三十四条</span> <span style=\"font-size:12px;color:#333333\">国家利用财政性资金设立基金，为企业自主创新与成果产业化贷款提供贴息、担保。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　政策性金融机构应当在其业务范围内对国家鼓励的企业自主创新项目给予重点支持。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第三十五条</span> <span style=\"font-size:12px;color:#333333\">国家完善资本市场，建立健全促进自主创新的机制，支持符合条件的高新技术企业利用资本市场推动自身发展。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　国家鼓励设立创业投资引导基金，引导社会资金流向创业投资企业，对企业的创业发展给予支持。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第三十六条</span> <span style=\"font-size:12px;color:#333333\">下列企业按照国家有关规定享受税收优惠：</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（一）从事高新技术产品研究开发、生产的企业；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（二）投资于中小型高新技术企业的创业投资企业；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（三）法律、行政法规规定的与科学技术进步有关的其他企业。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第三十七条</span> <span style=\"font-size:12px;color:#333333\">国家对公共研究开发平台和科学技术中介服务机构的建设给予支持。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　公共研究开发平台和科学技术中介服务机构应当为中小企业的技术创新提供服务。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第三十八条</span> <span style=\"font-size:12px;color:#333333\">国家依法保护企业研究开发所取得的知识产权。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　企业应当不断提高运用、保护和管理知识产权的能力，增强自主创新能力和市场竞争能力。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第三十九条</span> <span style=\"font-size:12px;color:#333333\">国有企业应当建立健全有利于技术创新的分配制度，完善激励约束机制。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　国有企业负责人对企业的技术进步负责。对国有企业负责人的业绩考核，应当将企业的创新投入、创新能力建设、创新成效等情况纳入考核的范围。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第四十条</span> <span style=\"font-size:12px;color:#333333\">县级以上地方人民政府及其有关部门应当创造公平竞争的市场环境，推动企业技术进步。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　国务院有关部门和省、自治区、直辖市人民政府应当通过制定产业、财政、能源、环境保护等政策，引导、促使企业研究开发新技术、新产品、新工艺，进行技术改造和设备更新，淘汰技术落后的设备、工艺，停止生产技术落后的产品。</span><span style=\"font-size:12px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> &nbsp;<br/> </span><span style=\"font-size:12px;color:#333333\">第四章　科学技术研究开发机构</span><span style=\"font-size: 12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第四十一条</span> <span style=\"font-size:12px;color:#333333\">国家统筹规划科学技术研究开发机构的布局，建立和完善科学技术研究开发体系。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第四十二条</span> <span style=\"font-size:12px;color:#333333\">公民、法人或者其他组织有权依法设立科学技术研究开发机构。国外的组织或者个人可以在中国境内依法独立设立科学技术研究开发机构，也可以与中国境内的组织或者个人依法联合设立科学技术研究开发机构。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　从事基础研究、前沿技术研究、社会公益性技术研究的科学技术研究开发机构，可以利用财政性资金设立。利用财政性资金设立科学技术研究开发机构，应当优化配置，防止重复设置；对重复设置的科学技术研究开发机构，应当予以整合。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　科学技术研究开发机构、高等学校可以依法设立博士后工作站。科学技术研究开发机构可以依法在国外设立分支机构。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第四十三条</span> <span style=\"font-size:12px;color:#333333\">科学技术研究开发机构享有下列权利：</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（一）依法组织或者参加学术活动；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（二）按照国家有关规定，自主确定科学技术研究开发方向和项目，自主决定经费使用、机构设置和人员聘用及合理流动等内部管理事务；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（三）与其他科学技术研究开发机构、高等学校和企业联合开展科学技术研究开发；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（四）获得社会捐赠和资助；</span><span style=\"font-size: 12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（五）法律、行政法规规定的其他权利。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第四十四条</span> <span style=\"font-size:12px;color:#333333\">科学技术研究开发机构应当按照章程的规定开展科学技术研究开发活动；不得在科学技术活动中弄虚作假，不得参加、支持迷信活动。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　利用财政性资金设立的科学技术研究开发机构开展科学技术研究开发活动，应当为国家目标和社会公共利益服务；有条件的，应当向公众开放普及科学技术的场馆或者设施，开展科学技术普及活动。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第四十五条</span> <span style=\"font-size:12px;color:#333333\">利用财政性资金设立的科学技术研究开发机构应当建立职责明确、评价科学、开放有序、管理规范的现代院所制度，实行院长或者所长负责制，建立科学技术委员会咨询制和职工代表大会监督制等制度，并吸收外部专家参与管理、接受社会监督；院长或者所长的聘用引入竞争机制。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第四十六条</span> <span style=\"font-size:12px;color:#333333\">利用财政性资金设立的科学技术研究开发机构，应当建立有利于科学技术资源共享的机制，促进科学技术资源的有效利用。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第四十七条</span> <span style=\"font-size:12px;color:#333333\">国家鼓励社会力量自行创办科学技术研究开发机构，保障其合法权益不受侵犯。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　社会力量设立的科学技术研究开发机构有权按照国家有关规定，参与实施和平等竞争利用财政性资金设立的科学技术基金项目、科学技术计划项目。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　社会力量设立的非营利性科学技术研究开发机构按照国家有关规定享受税收优惠。</span><span style=\"font-size:12px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> &nbsp;<br/> </span><span style=\"font-size:12px;color:#333333\">第五章　科学技术人员</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第四十八条</span> <span style=\"font-size:12px;color:#333333\">科学技术人员是社会主义现代化建设事业的重要力量。国家采取各种措施，提高科学技术人员的社会地位，通过各种途径，培养和造就各种专门的科学技术人才，创造有利的环境和条件，充分发挥科学技术人员的作用。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第四十九条</span> <span style=\"font-size:12px;color:#333333\">各级人民政府和企业事业组织应当采取措施，提高科学技术人员的工资和福利待遇；对有突出贡献的科学技术人员给予优厚待遇。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第五十条</span> <span style=\"font-size:12px;color:#333333\">各级人民政府和企业事业组织应当保障科学技术人员接受继续教育的权利，并为科学技术人员的合理流动创造环境和条件，发挥其专长。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第五十一条</span> <span style=\"font-size:12px;color:#333333\">科学技术人员可以根据其学术水平和业务能力依法选择工作单位、竞聘相应的岗位，取得相应的职务或者职称。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第五十二条</span> <span style=\"font-size:12px;color:#333333\">科学技术人员在艰苦、边远地区或者恶劣、危险环境中工作，所在单位应当按照国家规定给予补贴，提供其岗位或者工作场所应有的职业健康卫生保护。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第五十三条</span> <span style=\"font-size:12px;color:#333333\">青年科学技术人员、少数民族科学技术人员、女性科学技术人员等在竞聘专业技术职务、参与科学技术评价、承担科学技术研究开发项目、接受继续教育等方面享有平等权利。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　发现、培养和使用青年科学技术人员的情况，应当作为评价科学技术进步工作的重要内容。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第五十四条</span> <span style=\"font-size:12px;color:#333333\">国家鼓励在国外工作的科学技术人员回国从事科学技术研究开发工作。利用财政性资金设立的科学技术研究开发机构、高等学校聘用在国外工作的杰出科学技术人员回国从事科学技术研究开发工作的，应当为其工作和生活提供方便。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　外国的杰出科学技术人员到中国从事科学技术研究开发工作的，按照国家有关规定，可以依法优先获得在华永久居留权。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第五十五条</span> <span style=\"font-size:12px;color:#333333\">科学技术人员应当弘扬科学精神，遵守学术规范，恪守职业道德，诚实守信；不得在科学技术活动中弄虚作假，不得参加、支持迷信活动。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第五十六条</span> <span style=\"font-size:12px;color:#333333\">国家鼓励科学技术人员自由探索、勇于承担风险。原始记录能够证明承担探索性强、风险高的科学技术研究开发项目的科学技术人员已经履行了勤勉尽责义务仍不能完成该项目的，给予宽容。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第五十七条</span> <span style=\"font-size:12px;color:#333333\">利用财政性资金设立的科学技术基金项目、科学技术计划项目的管理机构，应当为参与项目的科学技术人员建立学术诚信档案，作为对科学技术人员聘任专业技术职务或者职称、审批科学技术人员申请科学技术研究开发项目等的依据。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第五十八条</span> <span style=\"font-size:12px;color:#333333\">科学技术人员有依法创办或者参加科学技术社会团体的权利。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　科学技术协会和其他科学技术社会团体按照章程在促进学术交流、推进学科建设、发展科学技术普及事业、培养专门人才、开展咨询服务、加强科学技术人员自律和维护科学技术人员合法权益等方面发挥作用。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　科学技术协会和其他科学技术社会团体的合法权益受法律保护。</span><span style=\"font-size:12px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> &nbsp;<br/> </span><span style=\"font-size:12px;color:#333333\">第六章　保障措施</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第五十九条</span> <span style=\"font-size:12px;color:#333333\">国家逐步提高科学技术经费投入的总体水平；国家财政用于科学技术经费的增长幅度，应当高于国家财政经常性收入的增长幅度。全社会科学技术研究开发经费应当占国内生产总值适当的比例，并逐步提高。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第六十条</span> <span style=\"font-size:12px;color:#333333\">财政性科学技术资金应当主要用于下列事项的投入：</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（一）科学技术基础条件与设施建设；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（二）基础研究；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（三）对经济建设和社会发展具有战略性、基础性、前瞻性作用的前沿技术研究、社会公益性技术研究和重大共性关键技术研究；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（四）重大共性关键技术应用和高新技术产业化示范；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（五）农业新品种、新技术的研究开发和农业科学技术成果的应用、推广；</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　（六）科学技术普及。</span><span style=\"font-size: 12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　对利用财政性资金设立的科学技术研究开发机构，国家在经费、实验手段等方面给予支持。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第六十一条</span> <span style=\"font-size:12px;color:#333333\">审计机关、财政部门应当依法对财政性科学技术资金的管理和使用情况进行监督检查。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　任何组织或者个人不得虚报、冒领、贪污、挪用、截留财政性科学技术资金。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第六十二条</span> <span style=\"font-size:12px;color:#333333\">确定利用财政性资金设立的科学技术基金项目，应当坚持宏观引导、自主申请、平等竞争、同行评审、择优支持的原则；确定利用财政性资金设立的科学技术计划项目的项目承担者，应当按照国家有关规定择优确定。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　利用财政性资金设立的科学技术基金项目、科学技术计划项目的管理机构，应当建立评审专家库，建立健全科学技术基金项目、科学技术计划项目的专家评审制度和评审专家的遴选、回避、问责制度。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第六十三条</span> <span style=\"font-size:12px;color:#333333\">国家遵循统筹规划、优化配置的原则，整合和设置国家科学技术研究实验基地。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　国家鼓励设置综合性科学技术实验服务单位，为科学技术研究开发机构、高等学校、企业和科学技术人员提供或者委托他人提供科学技术实验服务。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第六十四条</span> <span style=\"font-size:12px;color:#333333\">国家根据科学技术进步的需要，按照统筹规划、突出共享、优化配置、综合集成、政府主导、多方共建的原则，制定购置大型科学仪器、设备的规划，并开展对以财政性资金为主购置的大型科学仪器、设备的联合评议工作。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第六十五条</span> <span style=\"font-size:12px;color:#333333\">国务院科学技术行政部门应当会同国务院有关主管部门，建立科学技术研究基地、科学仪器设备和科学技术文献、科学技术数据、科学技术自然资源、科学技术普及资源等科学技术资源的信息系统，及时向社会公布科学技术资源的分布、使用情况。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　科学技术资源的管理单位应当向社会公布所管理的科学技术资源的共享使用制度和使用情况，并根据使用制度安排使用；但是，法律、行政法规规定应当保密的，依照其规定。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　科学技术资源的管理单位不得侵犯科学技术资源使用者的知识产权，并应当按照国家有关规定确定收费标准。管理单位和使用者之间的其他权利义务关系由双方约定。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第六十六条</span> <span style=\"font-size:12px;color:#333333\">国家鼓励国内外的组织或者个人捐赠财产、设立科学技术基金，资助科学技术研究开发和科学技术普及。</span></p><p style=\"line-height:30px\"><span style=\"font-size:12px;color:#333333\">第七章　法律责任</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第六十七条</span> <span style=\"font-size:12px;color:#333333\">违反本法规定，虚报、冒领、贪污、挪用、截留用于科学技术进步的财政性资金，依照有关财政违法行为处罚处分的规定责令改正，追回有关财政性资金和违法所得，依法给予行政处罚；对直接负责的主管人员和其他直接责任人员依法给予处分。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第六十八条</span> <span style=\"font-size:12px;color:#333333\">违反本法规定，利用财政性资金和国有资本购置大型科学仪器、设备后，不履行大型科学仪器、设备等科学技术资源共享使用义务的，由有关主管部门责令改正，对直接负责的主管人员和其他直接责任人员依法给予处分。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第六十九条</span> <span style=\"font-size:12px;color:#333333\">违反本法规定，滥用职权，限制、压制科学技术研究开发活动的，对直接负责的主管人员和其他直接责任人员依法给予处分。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第七十条</span> <span style=\"font-size:12px;color:#333333\">违反本法规定，抄袭、剽窃他人科学技术成果，或者在科学技术活动中弄虚作假的，由科学技术人员所在单位或者单位主管机关责令改正，对直接负责的主管人员和其他直接责任人员依法给予处分；获得用于科学技术进步的财政性资金或者有违法所得的，由有关主管部门追回财政性资金和违法所得；情节严重的，由所在单位或者单位主管机关向社会公布其违法行为，禁止其在一定期限内申请国家科学技术基金项目和国家科学技术计划项目。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第七十一条</span> <span style=\"font-size:12px;color:#333333\">违反本法规定，骗取国家科学技术奖励的，由主管部门依法撤销奖励，追回奖金，并依法给予处分。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　违反本法规定，推荐的单位或者个人提供虚假数据、材料，协助他人骗取国家科学技术奖励的，由主管部门给予通报批评；情节严重的，暂停或者取消其推荐资格，并依法给予处分。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第七十二条</span> <span style=\"font-size:12px;color:#333333\">违反本法规定，科学技术行政等有关部门及其工作人员滥用职权、玩忽职守、徇私舞弊的，对直接负责的主管人员和其他直接责任人员依法给予处分。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第七十三条</span> <span style=\"font-size:12px;color:#333333\">违反本法规定，其他法律、法规规定行政处罚的，依照其规定；造成财产损失或者其他损害的，依法承担民事责任；构成犯罪的，依法追究刑事责任。</span></p><p style=\"line-height:30px\"><span style=\"font-size:12px;color:#333333\">第八章　附　则</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第七十四条</span> <span style=\"font-size:12px;color:#333333\">涉及国防科学技术的其他有关事项，由国务院、中央军事委员会规定。</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:12px;color:#333333\">　　第七十五条</span> <span style=\"font-size:12px;color:#333333\">本法自</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2008</span><span style=\"font-size:12px;color:#333333\">年</span><span style=\"font-size:12px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">7</span><span style=\"font-size:12px;color:#333333\">月</span><span style=\"font-size:12px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size:12px;color:#333333\">日起施行。</span></p><p><br/></p>', '0', '0', '1', '2007-12-19 00:00:00', '1', '2017-11-13 13:25:22', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:01:35', '0', '');
INSERT INTO `site_policy_rule` VALUES ('6307e126516a433baf83a8c35f63575d', 'ZCFG_6307e126516a433baf83a8c35f63575d', '关于印发沈阳市沈大国家自主创新示范区建设三年行动计划(2017-2019年)实施方案的通知', '2', '沈阳市人民政府', '<p style=\"text-align: center;\"><span style=\"font-size: 24px;\"><strong><span style=\"font-family: 宋体; color: rgb(41, 147, 199);\">沈阳市人民政府关于印发沈阳市沈大国家自主创新示范区建设</span></strong></span></p><p><span style=\"font-size: 24px;\"><strong><span style=\"font-family: 宋体; color: rgb(41, 147, 199);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三年行动计划</span></strong><strong><span style=\"font-family: Simsun, serif; color: rgb(41, 147, 199);\">(2017-2019</span></strong><strong><span style=\"font-family: 宋体; color: rgb(41, 147, 199);\">年</span></strong><strong><span style=\"font-family: Simsun, serif; color: rgb(41, 147, 199);\">)</span></strong><strong><span style=\"font-family: 宋体; color: rgb(41, 147, 199);\">实施方案的通知</span></strong></span></p><p><span style=\"font-size: 18px\">各区、县</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">市</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">人民政府，市政府有关部门、有关直属单位：</span></p><p><span style=\"font-size: 18px\">　　现将《沈阳市沈大国家自主创新示范区建设三年行动计划</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(2017-2019</span><span style=\"font-size: 18px\">年</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">实施方案》印发给你们，请结合实际，认真贯彻执行。</span></p><p style=\"text-align:right\"><span style=\"font-size: 18px\">　　沈阳市人民政府</span></p><p style=\"text-align:right\">　　<span style=\"font-size: 18px;font-family: Simsun, serif\">2017</span><span style=\"font-size: 18px\">年</span><span style=\"font-size: 18px;font-family: Simsun, serif\">5</span><span style=\"font-size: 18px\">月</span><span style=\"font-size: 18px;font-family: Simsun, serif\">23</span><span style=\"font-size: 18px\">日</span></p><p><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">此件公开发布</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p style=\"text-align:center\"><span style=\"font-size: 18px\">　<strong><span style=\"font-family:宋体\">　沈阳市沈大国家自主创新示范区建设三年行动计划</span></strong></span><strong><span style=\"font-size: 18px;font-family: Simsun, serif\">(2017-2019</span></strong><strong><span style=\"font-size: 18px;font-family: 宋体\">年</span></strong><strong><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></strong><strong><span style=\"font-size: 18px;font-family: 宋体\">实施方案</span></strong></p><p><span style=\"font-size: 18px\">　　为全面实现我市沈大国家自主创新示范区</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">以下简称自创区</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)“</span><span style=\"font-size: 18px\">高端装备研发制造集聚区、转型升级引领区、创新创业生态区、开放创新先导区</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">的发展目标，根据《辽宁省人民政府关于印发辽宁省沈大国家自主创新示范区三年行动计划</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(2017-2019</span><span style=\"font-size: 18px\">年</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">实施方案的通知》</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">辽政发〔</span><span style=\"font-size: 18px;font-family: Simsun, serif\">2017</span><span style=\"font-size: 18px\">〕</span><span style=\"font-size: 18px;font-family: Simsun, serif\">10</span><span style=\"font-size: 18px\">号</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">精神，结合我市实际，特制定本方案。</span></p><p><strong><span style=\"font-size: 18px;font-family: 宋体\">　　一、总体思路</span></strong></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">一</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">指导思想。全面贯彻党的十八大和十八届三中、四中、五中、六中全会精神，深入实施创新驱动发展战略，加快自创区创新资源集聚，强化科技创新与产业发展融合，增加科技创新政策和体制机制创新效能，着力构建系统化的科技公共服务平台和服务体系，提高自创区自主创新能力、产业创新能力和科技服务能力，打造全市最优创新环境，建设引领沈阳振兴的高端装备研发制造集聚区、转型升级引领区、创新创业生态区、开放创新先导区，为将我市建设成为东北亚地区科技创新创业中心提供重要支撑。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">二</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">功能定位。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">1.</span><span style=\"font-size: 18px\">创新体系建设与高端产业集聚发展的核心区，为区域产业升级、发展方式转换提供创新引领。吸纳、集聚、整合科技与产业资源，加强政产学研金紧密结合，相互贯通，开放协作，强力促进技术创新、成果转化和高端产业的集群化发展，使自创区成为科技资源与高端产业密集度最高、创新活跃度最强、发展质量最优、速度最快的高科技与高端产业功能区，成为带动区域发展的主引擎。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">2.</span><span style=\"font-size: 18px\">体制机制创新的先行区，为化解制约老工业基地振兴的体制机制障碍发挥破冰引路作用。把体制机制创新作为增强创新活力、吸引力和核心竞争力的基础性工程，放到自创区建设工作的首位。从思想观念、工作方法、政策体系、管理体系、知识产权保护到科技金融创新等，进行全方位、多领域的体制机制创新探索，走出</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">东北现象</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">的体制机制困局，树立典范。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">3.“</span><span style=\"font-size: 18px\">大众创业、万众创新</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">的引领区，为推动新常态下区域经济发展增添新动力。努力为</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">大众创业、万众创新</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">提供多方位、多层次、多渠道的创新创业服务，吸引各类人才向自创区集聚流动，大力发展众创空间、孵化器等创新创业载体，加快完善技术创新服务体系，积极发展科技服务业，打造创新要素集聚、创业载体丰富、创业服务专业、创新资源开放、创新文化浓郁的创新创业引领区。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">4.</span><span style=\"font-size: 18px\">实施创新驱动战略的先导区，为东北振兴战略实施提供示范引领与战略支撑。在发挥创新体系核心区功能，推动开放合作的区域协同创新网络建设、特色产业集群打造、经济发展速度、规模和质量、体制机制活力释放等诸多方面不断创新突破，形成在自身快速做大、做强、做优的同时，对区域发展的辐射力、影响力、推动力同步提升的良好局面，成为启动老工业基地振兴的重要枢纽。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">5.</span><span style=\"font-size: 18px\">支持发展功能的特色区，推动自创区形成协同发展、特色发展的新格局。浑南区要集聚创新资源，培育战略性新兴产业集群，提升高新技术企业数量，成为东北地区的创新驱动引领区；和平区要以创业创新引领产业转型升级为主线，激发双创活力，培育科技小微企业，发展现代服务业，成为双创驱动转型先行区；铁西区中德</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">沈阳</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">高端装备制造产业园</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">以下简称中德园</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">要加强</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">中国制造</span><span style=\"font-size: 18px;font-family: Simsun, serif\">2025”</span><span style=\"font-size: 18px\">与</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">德国工业</span><span style=\"font-size: 18px;font-family: Simsun, serif\">4.0”</span><span style=\"font-size: 18px\">的高效对接，发展智能制造、高端装备、汽车制造等主导产业，逐步成为国际先进装备制造业发展示范区。</span></p><p><span style=\"font-size: 18px\">　<strong><span style=\"font-family:宋体\">　二、总体目标</span></strong></span></p><p><span style=\"font-size: 18px\">　　到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">2019</span><span style=\"font-size: 18px\">年，自创区高端装备制造业销售收入占装备制造业比例达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">38%</span><span style=\"font-size: 18px\">；高新技术产品产值占规模以上工业总产值比例达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">70%</span><span style=\"font-size: 18px\">；万人有效发明专利达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">25</span><span style=\"font-size: 18px\">件；新增高新技术企业</span><span style=\"font-size: 18px;font-family: Simsun, serif\">200</span><span style=\"font-size: 18px\">家以上；新增众创空间、孵化器面积达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">30</span><span style=\"font-size: 18px\">万</span><span style=\"font-size: 18px;font-family: Simsun, serif\">m</span><sup><span style=\"font-size: 13px;font-family: Simsun, serif\">2</span></sup><span style=\"font-size: 18px\">，总面积达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">150</span><span style=\"font-size: 18px\">万</span><span style=\"font-size: 18px;font-family: Simsun, serif\">m</span><sup><span style=\"font-size: 13px;font-family: Simsun, serif\">2</span></sup><span style=\"font-size: 18px\">；</span><span style=\"font-size: 18px;font-family: Simsun, serif\">R</span><span style=\"font-size: 18px\">＆</span><span style=\"font-size: 18px;font-family: Simsun, serif\">D</span><span style=\"font-size: 18px\">投入占地区生产总值比例达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">3%</span><span style=\"font-size: 18px\">；建成高水平产业技术研究院、东北科技大市场等一批科技成果转化及科技创新服务平台，建成浑南国家双创基地、三好街创业创新大街等一批创新创业基地。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">1.</span><span style=\"font-size: 18px\">产业核心竞争力显著加强。产业结构进一步调整优化，智能制造、高端装备等主导产业具有国际竞争力，战略性新兴产业快速发展，高端装备制造业销售收入占装备制造业比例达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">38%</span><span style=\"font-size: 18px\">，高新技术产品产值占规模以上工业总产值的比例达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">70%</span><span style=\"font-size: 18px\">，形成</span><span style=\"font-size: 18px;font-family: Simsun, serif\">10-15</span><span style=\"font-size: 18px\">个具有较强国内竞争力的创新型产业集群，高新技术企业总数量超过</span><span style=\"font-size: 18px;font-family: Simsun, serif\">400</span><span style=\"font-size: 18px\">家。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">2.</span><span style=\"font-size: 18px\">自主创新能力大幅提升。创新资源和要素进一步集聚，各类主体协同的区域创新体系进一步完善，建成一批先进水平的创新技术平台，突破和掌握一批核心关键技术，聚集一批高层次人才团队，</span><span style=\"font-size: 18px;font-family: Simsun, serif\">R</span><span style=\"font-size: 18px\">＆</span><span style=\"font-size: 18px;font-family: Simsun, serif\">D</span><span style=\"font-size: 18px\">投入占地区生产总值比例达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">3%</span><span style=\"font-size: 18px\">，每万人发明专利拥有量达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">25</span><span style=\"font-size: 18px\">件，科技进步贡献率达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">65%</span><span style=\"font-size: 18px\">。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">3.</span><span style=\"font-size: 18px\">创新创业生态显著优化。建成一批专业化众创空间和科技企业孵化器，打造众创、众包、众扶、众筹支撑平台，打通科技成果转化通道，建立较为完善的科技金融服务体系和创业人才支撑体系，实现创新要素区域间的有机整合和流动，众创空间、孵化器数量达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">50</span><span style=\"font-size: 18px\">家，孵化场地面积达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">150</span><span style=\"font-size: 18px\">万</span><span style=\"font-size: 18px;font-family: Simsun, serif\">m</span><sup><span style=\"font-size: 13px;font-family: Simsun, serif\">2</span></sup><span style=\"font-size: 18px\">，在孵企业数量达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">4000</span><span style=\"font-size: 18px\">家以上。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">4</span><span style=\"font-size: 18px;font-family: &#39;Times New Roman&#39;, serif\"></span><span style=\"font-size: 18px\">区域创新体系基本形成。浑南区、和平区、铁西区中德园发展定位明确，区域创新引领作用更加突出，区域科技创新合作机制更加完善，形成分工协作、优势互补的自创区创新发展一体化格局。建设一流的大学科技园和科研机构，新型研发机构达到</span><span style=\"font-size: 18px;font-family: Simsun, serif\">20</span><span style=\"font-size: 18px\">家以上；建成一批科技公共服务平台，国家重点实验室数量大幅增长；建立有效的科技对口合作机制，带动东北地区振兴发展。</span></p><p><strong><span style=\"font-size: 18px;font-family: 宋体\">　　三、重点任务和实现目标</span></strong></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">一</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">优化高新技术产业布局，培育战略性新兴产业。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">1.</span><span style=\"font-size: 18px\">加快打造四大千亿产业集群。推进现有园区资源整合和集聚，加强重大项目的引进和培育，实施机器人及自动化成套装备、单片处理设备研发及产业化、庞巴迪</span><span style=\"font-size: 18px;font-family: Simsun, serif\">Q400</span><span style=\"font-size: 18px\">、东软数字医疗等一批重点项目。以产业龙头项目延伸拓展产业链，做大做强电子商务、民用航空、健康医疗和生物医药、机器人和信息技术四大产业，为加快打造千亿规模产业集群奠定坚实基础。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市经济和信息化委、发展改革委，浑南区政府等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">2.</span><span style=\"font-size: 18px\">构建机器人产业生态圈。扶持机器人产业有序发展，建设以新松机器人、沈阳通用机器人等行业领军企业为主体，联合机器人学国家重点实验室、机器人国家工程研究中心等研究机构在浑南区建设机器人产业园。依托中瑞福宁机器人</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">沈阳</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">有限公司、瑞典机器人谷，在中德园内打造</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">东方机器人谷</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">。以</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">一园一谷</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">两大机器人产业区为核心，构建集科研创新、生产制造、检验检测、应用创新于一体的机器人生态圈。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市经济和信息化委、发展改革委、科技局，浑南区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">3.</span><span style=\"font-size: 18px\">建设人工智能</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">创新特区</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">。充分利用以色列、美国等国外智能产业资源及深圳等国内城市的创新资源，结合我市先进制造业等基础优势，依托深圳光启高等理工研究院、深圳源创力离岸创新中心等机构，设立沈阳人工智能特区研究院、特区金融控股公司和特区开发公司</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">三位一体</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">的运营机构，重点规划建设</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">人工智能未来城</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">和</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">国际创新聚集区</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">，构建集生产、生活、教育、文化、体验生态于一体的创新产城融合体。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市发展改革委、经济和信息化委、国资委、科技局，浑南区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">4.</span><span style=\"font-size: 18px\">发展高新技术产业园区。不断完善产业政策体系，聚焦发展新一代信息技术、</span><span style=\"font-size: 18px;font-family: Simsun, serif\">IC</span><span style=\"font-size: 18px\">装备、电子商务、生命健康和新能源汽车等新兴产业，建设具有国际竞争力的产业体系；建设</span><span style=\"font-size: 18px;font-family: Simsun, serif\">IC</span><span style=\"font-size: 18px\">装备、大数据与云计算、</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">互联网</span><span style=\"font-size: 18px;font-family: Simsun, serif\">+”</span><span style=\"font-size: 18px\">、电子商务、生物医药、清华启迪园等专业化产业园。推进信息化与工业化的深度融合，加大用信息技术改造提升传统产业的力度，增强区域的融合带动作用。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：浑南区政府、和平区政府，市发展改革委、经济和信息化委、市科技局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">5.</span><span style=\"font-size: 18px\">建设中德高端制造业集聚区。学习德国高科技产业园区发展成功模式，实施定制政策，精准招商服务，促进中德创新市场主体深度合作，培育根植型企业，建设中德园。重点发展智能制造、高端装备、汽车制造、工业服务等四大主导产业；鼓励发展物联网、云计算、新型功能材料、可再生能源等战略性新兴产业。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：中德园管委会，市发展改革委、经济和信息化委等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">6.</span><span style=\"font-size: 18px\">打造现代服务业集聚区。以大三好街</span><span style=\"font-size: 18px;font-family: Simsun, serif\">45km</span><sup><span style=\"font-size: 13px;font-family: Simsun, serif\">2</span></sup><span style=\"font-size: 18px\">为现代服务业核心发展区域，实施品牌战略，统筹制定发展规划，重点发展新一代信息服务业、科技金融服务业、现代商务服务业、创新创业服务业、现代商贸会展业等产业，突出发展科技服务业，以研发设计、技术转移、创业孵化、科技金融、知识产权、科技咨询等为重点，全面推动科技服务业发展壮大。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：和平区政府，市发展改革委、科技局、规划国土局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">7.</span><span style=\"font-size: 18px\">培育新兴产业创新主体。实施新兴产业龙头企业和跨国企业培养引进计划，新增一批有影响力、竞争力的大型企业集团。实施高新技术企业和科技</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">小巨人</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">企业培育工程，以智能制造、电子信息、新材料、航空航天、生物医药、大数据等领域为重点，在自创区内筛选高新技术企业和科技</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">小巨人</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">企业各</span><span style=\"font-size: 18px;font-family: Simsun, serif\">100</span><span style=\"font-size: 18px\">家建立培育库，实施分阶段培育。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市经济和信息化委、国资委、科技局、发展改革委，浑南区政府、和平区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">二</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">引进培育科技创新资源，建立产业创新体系。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">8.</span><span style=\"font-size: 18px\">创建国家创新中心。依托新松机器人自动化股份有限公司、哈工大机器人集团、埃夫特智能装备、国际智能科技等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">14</span><span style=\"font-size: 18px\">家企业，共同投资创建国家机器人及智能制造技术创新中心。依托中科院金属研究所、东北大学，支持沈阳材料科学国家</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">联合</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">实验室建设国家科学中心。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市经济和信息化委、科技局，浑南区政府等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">9.</span><span style=\"font-size: 18px\">推进新型研发机构建设。依托骨干企业、高校、科研院所，围绕传统产业提质增效和战略性新兴产业发展，重点建设中科院机器人与智能制造创新研究院、东北大学新兴产业技术研究院、沈阳工业大学智能制造产业研究院、眼产业研究院、中德创新研究院等一批产业技术研究院。依托沈抚新城和</span><span style=\"font-size: 18px;font-family: Simsun, serif\">IC</span><span style=\"font-size: 18px\">装备、生物医药等产业园区创建沈阳米拉国际汽车研发与检测中心、</span><span style=\"font-size: 18px;font-family: Simsun, serif\">IC</span><span style=\"font-size: 18px\">装备创新中心、生物医药和医疗设备创新中心。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局、财政局、经济和信息化委、发展改革委，浑南区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">10.</span><span style=\"font-size: 18px\">建设产业技术创新联盟。以产业发展需求为导向，围绕机器人、</span><span style=\"font-size: 18px;font-family: Simsun, serif\">IC</span><span style=\"font-size: 18px\">装备、数字医疗、大数据、生物医药等重点产业领域，加快构建以企业为主导，高校、科研院所、科技服务机构等参加的产业技术创新联盟。实施产业技术创新战略联盟升级工程，探索联盟知识产权利用和保护、科技成果分享机制的有效模式。制定产业技术创新联盟管理办法，加强联盟单位紧密性合作。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局，浑南区政府、和平区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">11.</span><span style=\"font-size: 18px\">实施</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">开放实验室</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">计划。推进高校、科研院所、企业等创新主体开展</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">开放实验室</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">计划，破解科技创新</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">孤岛现象</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">。深入开展重点科技资源摸底调研和登记，加快整合自创区内科技设施、科研数据、科技文献等资源，建立科技资源数据库。引进首都科技条件平台和提升大型仪器共享平台使用效能，将自创区内国家重点实验室、国家工程技术研究中心等面向社会开放。依托行业领军企业搭建科技资源开放平台，面向产业链上下游中小企业开放实验室、检验检测机构等。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局、发展改革委、经济和信息化委，浑南区政府、和平区政府等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">12.</span><span style=\"font-size: 18px\">创建东北国家科技成果转化示范区。推进协同创新，加强校企、校院、校地合作，建立以企业为主导的产学研科技成果协同转化应用体系；以东北科技大市场为依托，建立科技成果信息共享平台；建立国家技术转移</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">东北</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">中心、中关村天合科技成果转化辽宁促进中心、中科合创科技成果评价中心沈阳分中心、中国工程院双创平台、中国技术交易所沈阳中心等成果转化机构，建立完善的科技成果转化体系，创建国家科技成果转移转化示范区。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局、发展改革委、经济和信息化委，市委组织部，市金融办，浑南区政府、和平区政府等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">三</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">推进科技服务体系建设，打造创新生态环境。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">13.</span><span style=\"font-size: 18px\">建设沈阳科技服务交易平台。以建设东北科技大市场为核心，通过政府引导、市场配置、模式创新、政策支撑、服务集成</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">五措并举</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">，搭建</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">一厅、一网、两中心</span><span style=\"font-size: 18px;font-family: Simsun, serif\">.</span><span style=\"font-size: 18px\">十平台</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">的科技服务交易平台。启动项目库、成果库、专家库等信息资源平台建设，提供技术交易、政策咨询、知识产权、技术经纪人等一站式专业服务，逐步打造国内一流的科技创新服务平台。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：浑南区政府，市科技局、发展改革委、经济和信息化委、大数据局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">14.</span><span style=\"font-size: 18px\">培育市场化科技服务机构。推进国家科技服务业区域试点建设，打造科技服务集聚区，培育一批拥有知名品牌的科技服务机构和龙头企业。依托东北科技大市场，整合科技服务机构资源，提升技术转移、科技咨询、创业孵化、知识产权、科技金融等各类科技服务机构的专业化、市场化服务能力；依托中德园德中企业服务中心，建设集会展中心、产品展示中心、调查研究招商中心等功能于一体的综合服务中心，为企业提供从市场咨询、律师服务、秘书翻译、谈判展览、金融服务等专业化服务。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局，浑南区政府、和平区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">15.</span><span style=\"font-size: 18px\">建设科技公共服务平台。围绕重点发展的产业方向，引导企业建设一批公共技术研发服务、科技金融服务、科技创新人才服务、知识产权与成果转化服务、创业孵化服务科技公共服务平台，打造全产业链的技术创新和服务平台。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局，浑南区政府、和平区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">16.</span><span style=\"font-size: 18px\">健全自创区知识产权管理体系。加强知识产权保护和运用，打通知识产权创造、运用、保护、管理和服务全链条。推进集专利、商标、版权等知识产权于一体的综合管理改革，设立中德</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">沈阳</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">装备园知识产权维权援助工作站。建设中国</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">沈阳装备制造业</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">知识产权保护中心。打通知识产权维权渠道，成立知识产权快速维权中心、知识产权法律服务与司法保障中心，形成</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">全天候</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">知识产权举报快速反应机制。加大企业知识产权维权援助力度。定期开展知识产权保护宣传活动。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局、工商局，浑南区政府、和平区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">四</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">加快科技金融有效结合，促进金融服务发展。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">17.</span><span style=\"font-size: 18px\">建设科技金融示范区。引进银行、保险、基金等各类金融机构，通过建立科技投融资综合服务平台、风险资金池，建立知识产权、股权质押融资风险补偿机制等试点，为企业提供科技金融服务。建设东北亚互联网金融总部基地，引进及培育优质互联网金融企业，推动互联网金融新业务发展，探索完善金融机构间的合作模式，构建全方位的创业金融服务体系。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市金融办，浑南区政府、和平区政府等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">18.</span><span style=\"font-size: 18px\">加强多层次资本市场建设。支持符合条件的科技企业利用多层次资本市场，通过境内外证券市场上市、</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">新三板</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">和辽宁股权交易中心挂牌等方式，融资发展，做大做强。建立完善由政府、证券公司和中介服务机构共同参与的科技企业上市联合培育机制。对完成上市、挂牌、债券及资产证券化融资的科技企业，按规定给予补贴。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市金融办、财政局、发展改革委等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">19.</span><span style=\"font-size: 18px\">建立科技投融资综合服务平台。建立创投、信贷、债券、担保、保险等有机结合的科技投融资综合服务平台。省、市共同引导金融资本采用</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">信用</span><span style=\"font-size: 18px;font-family: Simsun, serif\">+</span><span style=\"font-size: 18px\">担保</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">的组合方式，助力自创区科技中小型企业发展。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市金融办、科技局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">五</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">引进集聚科技创新人才，打造</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">创新人才特区</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">20.</span><span style=\"font-size: 18px\">创新人才引进机制。依托国家</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">千人计划</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”“</span><span style=\"font-size: 18px\">万人计划</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">中科院</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">百人计划</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">等人才计划和</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">盛京人才</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">计划、欧美同学会、</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">千人计划</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">创新创业基地等平台，大力引进智能制造、</span><span style=\"font-size: 18px;font-family: Simsun, serif\">IC</span><span style=\"font-size: 18px\">装备、生物医药等主导产业急需的创新人才，推动创新人才向我市聚集。加大对辽宁籍各行各业人才的发现、培养和资助力度，</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">多方借力</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">宣传人才优惠政策，</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">多线搭桥</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">畅通人才回流渠道。依托国家重大科研项目、国际科技合作项目、重点学科和科研基地建设，培养高端领军人才和团队，打造东北创新人才高地。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市人力资源社会保障局，市委组织部，市科技局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">21.</span><span style=\"font-size: 18px\">加快培育创业者群体。落实高校、科研院所等专业技术人员离岗创业政策，建立健全科研人员双向流动机制。鼓励高校和科研院所依托科技企业孵化器、大学科技园等搭建创业教育实践平台。大力引导和支持大学毕业生在自创区创业就业。鼓励企业内部人员二次创业，支持企业建立创业股权奖励机制，激励优秀员工、创业项目及孵化企业。鼓励草根创业，打造面向人人的创客空间，开展创业大讲堂、创业训练营等活动。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市人力资源社会保障局，市委组织部，市科技局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">22.</span><span style=\"font-size: 18px\">加大创新人才激励政策。落实科技成果</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">三权</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">改革政策，增强科技成果转化者获得感。完善科研人员收入分配政策，依法赋予创新领军人才更大的人财物支配权、技术路线决定权，实行以增加知识价值为导向的激励机制。落实</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">盛京人才</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">计划，对培育及引进的高层次人才予以政策性奖励。完善高层次、高技能人才特殊津贴制度，出台高层次人才在住房、医疗、户籍、配偶安置、出入境等方面的配套政策。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局，市委组织部，市人力资源社会保障局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">23.</span><span style=\"font-size: 18px\">优化人才培养机制。鼓励在沈高校和企业引进国外优质教育资源和教学模式，建立教育机构或合作办学，加快培养国际化的创新人才。鼓励在沈高校与企业开展智能机器人、工业设计、中德知识产权学院等合作办学。拓展人才双向流动机制，允许科技创新人才在高校、科研机构和企业间双向兼职，鼓励科技人才利用科技成果创办科技型企业。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市人力资源社会保障局，市委组织部，市教育局、科技局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">24.</span><span style=\"font-size: 18px\">建设职业教育</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">双元制</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">培训体系。为满足企业发展和产业转型升级对高端技术技能人才的需求，通过产教融合，建立企业与学校双元培养、双主体育人、双证书评价的全新技术技能人才培育模式，构建</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“1+1+X”</span><span style=\"font-size: 18px\">职业教育体系，在中德园内建立</span><span style=\"font-size: 18px;font-family: Simsun, serif\">1</span><span style=\"font-size: 18px\">所全新体制的中德应用技术学院、</span><span style=\"font-size: 18px;font-family: Simsun, serif\">1</span><span style=\"font-size: 18px\">所公共跨企业实训中心和若干个企业实训基地。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：中德园管委会，市人力资源社会保障局、教育局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">六</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">加强创新创业载体建设，营造双创良好氛围。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">25.</span><span style=\"font-size: 18px\">建设浑南国家双创示范基地。加强浑南国家双创示范区内涵发展，加快推动各类双创载体建设，完善支持政策体系、公共服务体系、科技金融体系。探索龙头企业自主创新、人才团队自由创业、广大创客积极创富等双创模式，形成双创氛围浓厚、产业创新活跃、辐射沈阳经济区的双创示范基地。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：浑南区政府，市发展改革委、人力资源社会保障局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">26.</span><span style=\"font-size: 18px\">建设三好街特色创业街区。建设三好众创空间、东北大学双创示范园，推动电子卖场等老旧楼宇整体改造升级，进一步提升三好街在搭建研发孵化平台、提供科技金融服务、创业人才培养、知识产权保护、技术交易等方面的支撑能力。支持清华启迪</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">沈阳</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">科技园、浪潮大数据创新应用中心发展；引进中国技术交易所、中国工程院双创平台；建设中国</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">沈阳装备制造业</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">知识产权保护中心，发展知识产权服务业。培育科技金融、科技服务、创意文化、总部经济等新兴业态，打造自创区创新创业策源地。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：和平区政府，市发展改革委、人力资源社会保障局、科技局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">27.</span><span style=\"font-size: 18px\">实施众创空间、孵化器提质升级计划。支持自创区骨干企业、科研院所、高校建设具有投资、人才、技术平台等增值服务，基于</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">互联网</span><span style=\"font-size: 18px;font-family: Simsun, serif\">+”</span><span style=\"font-size: 18px\">、实现全要素资源共享、持续提供后孵化支撑的专业化众创空间和孵化器。推动浑南区科创中心、三好众创空间、和平区创业服务中心等现有众创空间、孵化器升级。推广</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">孵化</span><span style=\"font-size: 18px;font-family: Simsun, serif\">+</span><span style=\"font-size: 18px\">创投</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">模式，多方式多渠道对接国内外创业投资机构，为创业企业提供资本支撑。以锦联新经济产业园、国际软件园为试点，建立孵化加速联动机制，加强创业孵化服务衔接，完善创新创业孵化生态系统。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局，浑南区政府、和平区政府等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">28.</span><span style=\"font-size: 18px\">建设东北大学</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">盛京双创走廊</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">。依托东北大学南湖校区建设三好街</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">双创园区</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">，成为东北大学释放输出校内创新资源的前沿基地和吸纳国际国内多主体参与的、开放的、市场化的创新高地；依托东北大学浑南校区建设</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">产业创新园区</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">，以承接三好街</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">双创园区</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">创新成果产业化，育成并集中建设东北大学云计算、金属材料、东网大数据、东软数字医疗、东软熙康等产业创新园区。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：浑南区政府、和平区政府，市发展改革委、科技局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">29.</span><span style=\"font-size: 18px\">建设浪潮大数据双创平台。依托浪潮大数据创新应用中心，建设浪潮大数据创新创业平台。开展互联网大数据采集、处理和分析，围绕</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">数据生产</span><span style=\"font-size: 18px;font-family: Simsun, serif\">-</span><span style=\"font-size: 18px\">数据交易</span><span style=\"font-size: 18px;font-family: Simsun, serif\">-</span><span style=\"font-size: 18px\">数据应用</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">的链条，将大数据资源开放给创客、创业公司，孵化大数据产业；提供实时数据</span><span style=\"font-size: 18px;font-family: Simsun, serif\">API</span><span style=\"font-size: 18px\">、大数据应用运行云服务，以及创业项目包装推广等服务。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市经济和信息化委、大数据局、科技局，和平区政府等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">30.</span><span style=\"font-size: 18px\">开展创新创业主题活动。组织参与省创新创业大赛、省</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">互联网</span><span style=\"font-size: 18px;font-family: Simsun, serif\">+”</span><span style=\"font-size: 18px\">大学生创新创业大赛、</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">中国好项目</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">东北三省创投大赛、盛京青年创新创业大赛等创新创业活动，建设我市双创品牌。策划举办</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">沈阳创新创业周</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">，开展创业沙龙、创业大讲堂、创业训练营、科技型企业创新创业座谈会，为创业者提供创业指导、风险投资和人脉网络。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局、人力资源社会保障局，浑南区政府、和平区政府等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">七</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">构建大开放、大合作的协同创新格局。</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">31.</span><span style=\"font-size: 18px\">构筑开放创新合作平台。推进自创区和自贸区沈阳片区协同发展，创新投资和贸易便利化制度，推动自创区溢出创新动能和创新红利，构建国际化高端科技服务体系。依托浑南国家电子商务示范基地，引进德国</span><span style=\"font-size: 18px;font-family: Simsun, serif\">SAP</span><span style=\"font-size: 18px\">公司，建设全球供应链采购交易平台、东北</span><span style=\"font-size: 18px;font-family: Simsun, serif\">-</span><span style=\"font-size: 18px\">全球供应链交易综合服务区</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">基地</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">和</span><span style=\"font-size: 18px;font-family: Simsun, serif\">SAP</span><span style=\"font-size: 18px\">产业互联网创新基地。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局、外经贸局、外办，浑南区政府等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">32.</span><span style=\"font-size: 18px\">探索对外科技创新合作机制。引进国际高端创新机构、研发中心和科技组织在自创区落户。重点支持高校、科研院所、企业与国外研发机构合作共建研发机构，联合开展技术攻关，支持企业在境外设立研发基地。加强与周边国家及欧美等发达国家务实合作，推动合作机制及平台建设。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局、外经贸局、外办，浑南区政府、和平区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">33.</span><span style=\"font-size: 18px\">开展军民融合创新示范区建设。以空港经济区和沈抚工业园区为依托，高标准规划，进一步整合区域军民融合资源，打造军民融合创新示范区。完善军民融合机制，支持军工科研院所组建军民产业技术创新联盟，加快建设航天十二院军民融合技术转移中心，推动军民科技要素融合，促进军民科技成果双向转移及产业化。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市经济和信息化委、科技局，浑南区政府、和平区政府等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">34.</span><span style=\"font-size: 18px\">推进自创区与中关村合作对接。借助首都高端创新资源聚集优势，支持我市提升创新能力、完善创新体系、加速技术转移、促进成果转化、推进产业转型。推动中关村天合科技成果转化辽宁促进中心、工业大数据研究院东北分院、中科合创科技成果评价中心沈阳分中心、中国工程院双创平台、中国技术交易所沈阳分中心等项目建设。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局，浑南区政府、和平区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p><span style=\"font-size: 18px\">　<strong><span style=\"font-family:宋体\">　四、主要政策措施</span></strong></span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">1.</span><span style=\"font-size: 18px\">赋予自创区</span><span style=\"font-size: 18px;font-family: Simsun, serif\">27</span><span style=\"font-size: 18px\">项市级行政审批职能，主要包括商业特许经营备案权限、内资融资租赁试点企业申报推荐和监督管理权限、《食品经营许可证》审批权限等。根据辽政发〔</span><span style=\"font-size: 18px;font-family: Simsun, serif\">2017</span><span style=\"font-size: 18px\">〕</span><span style=\"font-size: 18px;font-family: Simsun, serif\">10</span><span style=\"font-size: 18px\">号文件规定，参照中德园做法，赋予自创区相应的省级行政、经济等审批管理权限。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市编委办，浑南区政府、和平区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">2.</span><span style=\"font-size: 18px\">自创区实行</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">无费区</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">管理，除资源类、补偿类收费外，免征涉企其他行政事业性收费。凡企业涉及免征行政事业性收费项目时，由执收单位按照收费标准核算收费数额，并将收费数额报送所在区财政部门，再由所在区财政部门根据报送数额分别计算上缴国家、省、市的资金数额，并据实支付。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：浑南区政府、和平区政府、中德园管委会，市财政局、发展改革委等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">3.</span><span style=\"font-size: 18px\">自创区内高新技术企业上缴税金中的市财政增量部分</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">以</span><span style=\"font-size: 18px;font-family: Simsun, serif\">2016</span><span style=\"font-size: 18px\">年为基数</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">全部返还所在区财政，集中支持科技创新和产业发展。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市财政局、科技局，浑南区政府、和平区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">4.</span><span style=\"font-size: 18px\">探索高校、科研院所职务科技成果混合所有制改革试验，将职务科技成果知识产权在单位与职务发明人</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">发明人团队</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">之间进行分割确权，对职务发明人</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">发明人团队</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">进行知识产权奖励，推动</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">先确权、后转化</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局、国资委等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">5.</span><span style=\"font-size: 18px\">探索放宽装备制造业领域高新技术企业认定标准，将企业近</span><span style=\"font-size: 18px;font-family: Simsun, serif\">3</span><span style=\"font-size: 18px\">个会计年度的研究开发费用总额占同期销售收入总额的比例分别降低</span><span style=\"font-size: 18px;font-family: Simsun, serif\">1%</span><span style=\"font-size: 18px\">，将近</span><span style=\"font-size: 18px;font-family: Simsun, serif\">1</span><span style=\"font-size: 18px\">年高新技术产品</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">服务</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">收入占企业同期总收入的比例由不低于</span><span style=\"font-size: 18px;font-family: Simsun, serif\">60%</span><span style=\"font-size: 18px\">降低为不低于</span><span style=\"font-size: 18px;font-family: Simsun, serif\">50%</span><span style=\"font-size: 18px\">。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局、财政局、国税局、地税局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">6.</span><span style=\"font-size: 18px\">探索自创区内高新技术企业再认定期间所得税</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">先减后补</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">。区内高企在高企再认定期间，企业所得税仍按</span><span style=\"font-size: 18px;font-family: Simsun, serif\">15%</span><span style=\"font-size: 18px\">的优惠税率缴纳；待再认定后，若被取消高企资格，企业补交前期减缴的税率，若仍为高企则企业所得税不变。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局、财政局、国税局、地税局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">7.</span><span style=\"font-size: 18px\">科技型中小企业开展研发活动中实际发生的研发费用，未形成无形资产计入当期损益的，在按规定据实扣除的基础上，在</span><span style=\"font-size: 18px;font-family: Simsun, serif\">2017</span><span style=\"font-size: 18px\">年</span><span style=\"font-size: 18px;font-family: Simsun, serif\">1</span><span style=\"font-size: 18px\">月</span><span style=\"font-size: 18px;font-family: Simsun, serif\">1</span><span style=\"font-size: 18px\">日至</span><span style=\"font-size: 18px;font-family: Simsun, serif\">2019</span><span style=\"font-size: 18px\">年</span><span style=\"font-size: 18px;font-family: Simsun, serif\">12</span><span style=\"font-size: 18px\">月</span><span style=\"font-size: 18px;font-family: Simsun, serif\">31</span><span style=\"font-size: 18px\">日期间，再按照实际发生额的</span><span style=\"font-size: 18px;font-family: Simsun, serif\">75%</span><span style=\"font-size: 18px\">在税前加计扣除；形成无形资产的，在上述期间内按照无形资产成本的</span><span style=\"font-size: 18px;font-family: Simsun, serif\">175%</span><span style=\"font-size: 18px\">在税前摊销。研发费用包括人员费用</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">含直接从事研发活动人员的工资薪金、</span><span style=\"font-size: 18px;font-family: Simsun, serif\">“</span><span style=\"font-size: 18px\">五险一金</span><span style=\"font-size: 18px;font-family: Simsun, serif\">”</span><span style=\"font-size: 18px\">、外聘人员劳务费用</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">、直接投入费用、折旧费用、无形资产摊销、新产品设计费、新工艺规程制定费、新药研制的临床试验费、勘探开发技术的现场试验费和其他相关费用。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市国税局、地税局、科技局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">8.</span><span style=\"font-size: 18px\">自创区内有限合伙制创业投资企业采取股权投资方式投资于未上市的中小高新技术企业满</span><span style=\"font-size: 18px;font-family: Simsun, serif\">2</span><span style=\"font-size: 18px\">年的，该有限合伙制创业投资企业的法人合伙人可按照其对未上市中小高新技术企业投资额的</span><span style=\"font-size: 18px;font-family: Simsun, serif\">70%</span><span style=\"font-size: 18px\">，抵扣该法人合伙人从该有限合伙制创业投资企业分得的应纳税所得额，当年不足抵扣的，可在以后纳税年度结转抵扣。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市国税局、地税局、科技局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">9.</span><span style=\"font-size: 18px\">自创区内中小高新技术企业以未分配利润、盈余和资本公积向个人股东转增股本，高新技术企业转化科技成果给予本企业相关技术人员的股权奖励，个人股东和相关技术人员一次性缴纳税款有困难的，可根据实际情况自行制定分期缴税计划，在</span><span style=\"font-size: 18px;font-family: Simsun, serif\">5</span><span style=\"font-size: 18px\">年内分期缴纳个人所得税，并将有关资料报主管税务机关备案。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市地税局、科技局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">10.</span><span style=\"font-size: 18px\">支持高校院所和国有企事业单位职务成果在自创区转化，转化净收益或取得的股权，按照不低于</span><span style=\"font-size: 18px;font-family: Simsun, serif\">70%</span><span style=\"font-size: 18px\">的比例奖励成果完成人和为转化成果做出重要贡献的人员。研发团队自行在自创区实施成果转化转移的收益，其所得不低于</span><span style=\"font-size: 18px;font-family: Simsun, serif\">70%</span><span style=\"font-size: 18px\">。高校院所职务成果在自创区转化，以股份或出资比例等股权形式给予科技人员个人奖励，暂不征收个人所得税；获奖人按股份、出资比例获得分红或转让股份、出资比例时，依法缴纳个人所得税。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市地税局、科技局、教育局、财政局、人力资源社会保障局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">11.</span><span style=\"font-size: 18px\">对世界</span><span style=\"font-size: 18px;font-family: Simsun, serif\">500</span><span style=\"font-size: 18px\">强企业、国际知名研发机构在沈设立研发机构的，根据其掌握核心技术</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">专利</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">及创新绩效，给予研发补助，最高不超过</span><span style=\"font-size: 18px;font-family: Simsun, serif\">1000</span><span style=\"font-size: 18px\">万元；对我市高校、科研院所、企业与国际研发机构联合建立新型研发机构的，给予</span><span style=\"font-size: 18px;font-family: Simsun, serif\">50—200</span><span style=\"font-size: 18px\">万元的研发经费补助。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局、财政局、外经贸局等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p><strong><span style=\"font-size: 18px;font-family: 宋体\">　　五、保障措施</span></strong></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">一</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">加强组织保障。一是全面落实国家、省自创区建设总体要求，加强我市自创区建设工作的组织领导。建立市统筹、区主体、部门协作的工作机制，定期检查督促重点工作的落实进度和质量。二是浑南区政府、和平区政府、中德园管委会要树立主体意识、履行主体责任、发挥主体作用。设立自创区建设工作组织机构，合理配备专职人员，打造一支专业素质高、富于创新激情和奉献精神的干部队伍，切实担负起组织实施自创区建设各项重点任务的责任。三是积极主动与国家、省相应部门建立紧密顺畅的沟通机制，与上下对口部门建立密切联系，形成强大的整体推动力。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局，浑南区政府、和平区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">二</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">加强制度保障。推进落实自创区建设发展规划纲要，明确市自创区建设领导小组成员的部门责任，落实国家、省、市相关支持政策。强化考核自创区创新体系建设、科技进步、辐射带动能力等创新能力相关指标，科学合理制定考核目标，及时对自创区创建与创新改革试验情况进行总结、评估，定期对工作进展情况进行跟踪督办，为自创区建设快速推进提供重要保障。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局，浑南区政府、和平区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">三</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">加强资金扶持保障。把自创区建设发展摆在我市创新发展全局的核心位置，市直有关部门要强化要素聚集和政策支持，齐心合力推进自创区建设。财政部门要根据实际情况，统筹做好资金保障工作，重点支持自创区发展特色产业、培育创新主体、建设创新平台载体、集聚高端创新人才、开展重大创新活动等。充分发挥政府资金的引导和杠杆作用，带动更多的企业和社会资本注入，共同推进科技、交通、能源、水利、生态、信息等重大项目建设，为自创区发展提供重要支撑。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市财政局、科技局，浑南区政府、和平区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p>　　<span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">四</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span><span style=\"font-size: 18px\">完善考核评价。探索建立以科技创新为核心的考核评价体系，打破对自创区核心区以一般县区考核的模式，突出考核新产业新业态、创新创业生态、高端链接和辐射带动、体制机制和政策突破等内容。健全自创区发展数据统计、监测评价制度，探索建立政府、社会组织、公众等多方参与的评价机制。加强规划实施动态管理，引入专业第三方机构，定期开展规划实施情况评估和经验总结，引导建设工作科学有序推进。</span><span style=\"font-size: 18px;font-family: Simsun, serif\">(</span><span style=\"font-size: 18px\">责任单位：市科技局、市政府督考办、市统计局，浑南区政府、和平区政府、中德园管委会等</span><span style=\"font-size: 18px;font-family: Simsun, serif\">)</span></p><p><br/></p>', '0', '0', '1', '2017-05-23 00:00:00', '1', '2017-11-13 14:21:36', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:16:41', '0', '');
INSERT INTO `site_policy_rule` VALUES ('634254cebc8e46ac9855668409a836fb', 'ZCFG_634254cebc8e46ac9855668409a836fb', '沈阳市科学技术进步条例', '2', '沈阳市人民代表大会常务委员会', '<p style=\"background: white; text-align: center;\"><br/></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">目&nbsp;录</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一章　总　则</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二章　企业技术进步</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三章　科学技术研究开发与推广应用</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四章　科学技术机构</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五章　科学技术人员</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六章　科学技术投入</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七章　法律责任</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第八章　附　则</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">沈阳市人民代表大会常务委员会公告（第6号）</span></p><p style=\"line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">《沈阳市科学技术进步条例》由沈阳市第十四届人民代表大会常务委员会第七次会议于2008年12月4日修订，已经辽宁省第十一届人民代表大会常务委员会第六次会议于2009年1月9日批准，现予公布，自2009年3月1日起施行。</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">附：《沈阳市科学技术进步条例》</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">沈阳市人民代表大会常务委员会2009年2月3日</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">沈阳市科学技术进步条例（1991年11月9日沈阳市第十届人民代表大会常务委员会第二十六次会议通过　1991年11月30日辽宁省第七届人民代表大会常务委员会第二十五次会议批准　根据1997年3月28日沈阳市第十一届人民代表大会常务委员会第二十八次会议通过　1997年5月30日辽宁省第八届人民代表大会常务委员会第二十八次会议批准《关于修改〈沈阳市科学技术进步条例〉的决定》修正　2008年12月4日沈阳市第十四届人民代表大会常务委员会第七次会议修订　2009年1月9日辽宁省第十一届人民代表大会常务委员会第六次会议批准）</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一章　总　则</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一条　根据《中华人民共和国科学技术进步法》等有关法律、法规，结合本市实际，制定本条例。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二条　凡在本市行政区域内进行科学研究、技术和产品开发、科学技术成果推广应用以及相关的服务和行政管理活动，适用本条例。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三条　市人民政府科学技术行政主管部门负责本行政区域内科学技术进步工作的宏观管理和统筹协调。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">区、县（市）人民政府科学技术行政主管部门负责本行政区域内的科学技术进步工作。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">市和区、县（市）人民政府其他有关部门按照各自职责，负责相关的科学技术进步工作。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四条　市人民政府应当制定科学技术发展规划，纳入本市国民经济和社会发展总体规划，并建立科学技术进步工作协调机制。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五条　市人民政府考核区、县（市）人民政府工作，应当将推进科学技术进步工作列为重要内容。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六条　本市发展与国内外政府机构、组织之间的科学技术合作与交流，对科学技术研究开发合作、技术贸易、建立科研生产联合体等活动给予支持。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七条　市人民政府建立科学技术奖励制度，对在科学技术进步活动中作出突出贡献的组织、个人给予奖励，具体办法由市人民政府规定。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">鼓励国内外组织和个人设立科学技术奖项。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二章　企业技术进步</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第八条　支持企业研究开发自主知识产权产品，培育科技名牌产品，发挥企业在技术创新中的主体作用。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第九条　鼓励企业引进有利于提升产业核心竞争力的专利和技术，进行消化、吸收和再创新。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">企业利用财政性资金或者国有资本引进重大技术、装备，应当组织专家论证、评估和配套技术攻关。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十条　鼓励企业设立科学技术研究开发机构。企业科学技术研究开发机构被认定为国家级或者省级企业技术中心、工程技术（研究）中心或者检测中心、重点实验室的，按照规定给予经费等扶持。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">支持企业依法设立博士后工作站，鼓励企业培养、吸引和使用科学技术人员，提高研究开发能力。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十一条　鼓励企业同国内外其他企业或者科学技术研究开发机构、高等学校联合建立科学技术研究开发机构。支持企业与高等学校、研究院所建立产学研联合体。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十二条　鼓励企业参与行业标准、国家标准和国际标准的制定，对在行业标准、国家标准和国际标准制定中起主导作用的企业给予支持。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十三条　市有关部门根据国家产业技术政策，结合本市实际，定期公布鼓励发展的技术目录和淘汰的技术目录。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十四条　鼓励组织和个人按照自筹资金、自愿结合、自主经营、自负盈亏的原则，设立各种形式的科技型企业。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十五条　支持企业通过风险投资、贷款担保、参加科技保险等方式，提高融资和抗御风险的能力。鼓励金融机构为高新技术企业发展提供服务。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三章　科学技术研究开发与推广应用</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十六条　市人民政府应当组织社会力量对经济建设和社会发展中的重大共性技术开展科技攻关，实施重大科技专项工程，解决产业升级和区域经济发展中的重大技术问题。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十七条　鼓励中央和省属科研机构、高等学校、军工企业为本市的发展开展科学技术开发活动，对在本市开展科技成果转让、与企业联合进行技术开发、攻关等活动并取得经济社会效益的，按照市人民政府的有关规定给予支持。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十八条　市人民政府应当支持沈阳国家高新技术产业开发区的建设，发挥其在本市高新技术产业带中的引领、示范、聚集和辐射作用。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">市和区、县（市）人民政府应当对本市高新技术产业带内经认定的高新技术企业给予支持。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十九条　鼓励和支持农业科学技术的应用研究、开发及其成果的推广应用。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">鼓励建立多种形式的农业技术推广服务机构，建立健全多元化的农业技术社会化服务体系。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">推行农业科技特派员制度，推动农业科学技术的推广和应用。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">鼓励高等学校、科研机构、企业事业组织、各类中介机构为农业、农村和农民提供科学技术服务，进行科学技术培训，培养农村实用人才。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十条　市和区、县（市）人民政府应当采取措施，促进社会事业的科学技术进步，加强对资源与环境、新能源与节能、人口与健康、防灾减灾、公共安全、城镇化与城市发展等方面的研究和技术开发。鼓励应用先进科学技术及其成果促进社会事业发展。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十一条　鼓励面向服务业的技术创新活动。对应用科学技术成果提升信息服务、商贸、金融、物流等现代服务业水平的给予支持。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十二条　鼓励科研机构、高等学校、企业、其他组织和个人采取多种形式从事技术开发、技术转让、技术咨询、技术服务，开展技术产权交易。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十三条　鼓励科研机构、高等学校、军工企业及其他组织，以大型仪器设备等各种科学技术资源向社会提供公益性服务，促进科学技术资源的整合与共享。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">以财政性资金为主购置大型科学仪器、设备必须进行可行性论证；已有大型科学仪器设备及实验设施的单位应当承担共享义务。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十四条　市和区、县（市）人民政府及其有关部门，应当重视软科学的研究和应用。在制定重大决策和发展规划时，应当开展软科学研究，采用软科学研究成果。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四章　科学技术机构</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十五条　市人民政府应当根据城市创新和发展需要，统筹规划科学技术研究开发和中介服务机构的布局，建立和完善科学技术研究开发和中介服务体系。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十六条　市和区、县（市）人民政府应当建立健全科学技术顾问咨询机构。在制定科学技术发展规划和重大政策，确定科学技术的重大项目、与科学技术密切相关的重大项目时，应当向科学技术顾问咨询机构咨询，或者委托科学技术顾问咨询机构进行论证。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十七条　利用财政性资金设立的科研机构依法享有研究开发、生产经营、机构设置、经费使用、人员聘用等方面的自主权。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十八条　鼓励国内外组织和个人在本市创办科学技术研究开发和中介服务机构，并为其获得财政性资金设立的科学技术计划项目或者基金项目提供条件。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十九条　鼓励科技创业孵化、生产力促进、科技风险投资、科技担保、国际科技交流等各类科技中介机构发展，建设公共研发、检测或者服务平台，引导其为中小企业技术创新服务。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五章　科学技术人员</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十条　市和区、县（市）人民政府、企业事业组织应当采取措施，提高科学技术人员的收入水平和社会地位，创造有利于科学技术人员创业与发挥作用的环境和条件。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十一条　市和区、县（市）人民政府、企业事业组织应当建立科学技术人员培训等制度，保障科学技术人员接受继续教育的权利。加强对中青年科学技术骨干和学科带头人的培养和使用。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十二条　鼓励用人单位采取多种形式引进国内外高层次专业技术实用人才。有关部门和用人单位应当为引进的人才提供良好的工作、生活条件，对解决其从事科学技术研究开发所必需的仪器设备、专有技术等给予支持。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十三条　鼓励国内外科学技术人员在本市领办、创办科技型企业。科学技术人员依法以其拥有的知识产权等作为出资投资于企业，并按照法律规定或者合同约定取得收益。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六章　科学技术投入</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十四条　市和区、县（市）财政用于科学技术支出的年增长幅度，应当高于本级财政经常性收入的增长幅度。其中，市级财政每年安排的科学技术项目支出（原科学技术三项经费）应当占财政一般预算支出的2%以上，区、县（市） 级财政每年安排的科学技术项目支出应当不低于财政一般预算支出的1%。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十五条　市人民政府应当设立风险投资、科技担保等科学技术专项基金，用于支持科学技术进步活动。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">区、县（市）人民政府应当在同级财政科技经费中建立相应的科学技术专项基金。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十六条　市和区、县（市）人民政府有关部门每年应当从所管理的行业、产业发展基金或者资金中安排必要的项目用于支持行业、产业的科学技术进步。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十七条　市人民政府有关部门应当完善科学技术经费使用和管理制度，加强对科学技术项目和经费的管理，提高经费的使用效益；财政、审计、监察等部门应当根据职责，对财政科学技术经费使用情况进行监督检查。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七章　法律责任</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十八条　在科学技术进步工作中，骗取科学技术经费和优惠待遇的，由主管部门追回科学技术经费，取消优惠待遇，对直接责任人员依法给予处分。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十九条　在科学技术进步工作中，骗取科学技术奖励的，由主管部门依法撤销奖励，追回奖金，对相关责任人依法给予处分。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十条　在科学技术进步工作中，以财政性资金购置大型科学仪器设备及实验设施的单位不履行资源共享义务的，由主管部门责令改正，对直接负责的主管人员和其他直接责任人员依法给予处分。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十一条　在科学技术进步工作中，虚报、冒领、贪污、挪用、截留用于科学技术进步的财政性资金的，依照有关财政违法行为处罚处分的规定责令改正，追回有关财政性资金和违法所得，对直接负责的主管人员和其他责任人员依法给予处分。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十二条　科学技术行政等有关部门及其工作人员滥用职权、玩忽职守、徇私舞弊的，对直接负责的主管人员和其他责任人员依法给予处分。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十三条　在科学技术进步工作中，违反法律、法规规定造成财产损失或者其他损害的，依法承担民事责任；构成犯罪的，依法追究刑事责任。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第八章　附　则</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十四条　本条例自2009年3月1日起施行。</span></p><p><br/></p>', '0', '0', '1', '2009-03-01 00:00:00', '1', '2017-11-13 14:12:57', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:18:10', '0', '');
INSERT INTO `site_policy_rule` VALUES ('66e9f8fddefa4950b0248fd3dca2a3c8', 'ZCFG_66e9f8fddefa4950b0248fd3dca2a3c8', '沈阳市科技创新&ldquo;双百工程&rdquo;贷款贴息资金实施细则（试行）', '2', '沈阳市科学技术局', '<p style=\"margin-top:auto;margin-bottom: auto;text-align:center;line-height:44px\"><span style=\"font-size: 21px; font-family: 黑体;\">第一章</span><span style=\"font-size: 21px; font-family: Simsun, serif;\">&nbsp;&nbsp;</span><span style=\"font-size: 21px; font-family: 黑体;\">总则</span><br/></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第一条</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">为积极推进沈阳市科技与金融结合试点工作，逐步加大财政科技专项资金的间接投入力度，根据《沈阳市促进科技和金融结合试点工作实施方案》（沈政发〔2017〕23号）、《关于印发沈阳市科技创新“双百工程”支持措施的通知》（沈科发〔2017〕29号）等有关规定，特制定《沈阳市科技创新“双百工程”贷款贴息资金实施细则》（以下简称“细则”）。</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第二条</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">本细则的贴息资金采取后补助方式，在市科技创新专项资金中列支。</span></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center;line-height:47px\"><span style=\"font-size: 21px;font-family: 黑体\">第二章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 黑体\">适用范围</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第三条</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">申请贴息资金的单位，应具备独立的法人资格、健全的财务制度、良好的经营业绩、无不良信用记录和违法记录，还应符合以下条件：</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（一）项目列入沈阳市科技创新“双百工程”，即“沈阳市百项重大科技研发项目”和“沈阳市百项重大科技成果转化项目”。</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（二）在沈阳市依法开展信贷业务的银行业金融机构办理的贷款业务。</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第四条</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">申请贴息资金的单位应遵守以下规定：</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（一）申请贴息资金的贷款应为沈阳市科技创新“双百工程”配套的流动资金贷款；</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（二）申请贴息资金的贷款符合沈阳市科技创新“双百工程”项目科技研发和成果转化相关资金投入与使用规定，贷款金额不得超过项目研发投入自筹金额；</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（三）申请贴息资金的贷款期限在沈阳市科技创新“双百工程”项目清单所列的研发周期及转化周期内。</span></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center;line-height:47px\"><span style=\"font-size: 21px;font-family: 黑体\">第三章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 黑体\">贴息标准</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第五条</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">对符合本细则第二章规定的单位，入选沈阳市科技创新“双百工程”项目贷款实际发生利息，按照央行同期贷款基准利率，一次性给予不超过一年期贷款贴息。</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第六条每个项目只能申请在同一银行业金融机构办理的单笔贷款的贴息。</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第七条</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">单笔贷款贴息金额最高不超过100万元。</span></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center;line-height:47px\"><span style=\"font-size: 21px;font-family: 黑体\">第四章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 黑体\">贴息资金申请、审批及拨付</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第八条市科技局委托沈阳市民营科技机构协调服务中心负责申请贷款贴息的审核工作。</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第九条沈阳市科技创新“双百工程”贷款贴息从2018年1月1日起开始接受申请。</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第十条符合本细则规定贴息范围的单位，需提供相关材料，具体如下：</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（一）登陆市科技局网站（www.syskjj.gov.cn）下载《沈阳市科技创新“双百工程”贷款贴息申请表》，填写后连同相关材料一并送到沈阳市民营科技机构协调服务中心（和平区十一纬路82号科技大厦1305室）进行审核。</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（二）应提交的其他相关材料：</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">1.</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">贷款合同、贷款到账凭证、付息凭证原件及复印件；</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">2.</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">申请单位所承担的沈阳市科技创新“双百工程”项目任务合同书原件；</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">3.</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">单位营业执照或法人执照、组织机构代码证复印件；</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">4.</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">申请单位对报送材料的真实性声明；</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">5.</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">其他需要提供相关说明材料。</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">（三）《沈阳市科技创新“双百工程”贷款贴息申请表》一式三份，其他相关材料的复印件一式两份并加盖单位公章，原件审核后返还。</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第十一条沈阳市民营科技机构协调服务中心审核后，在《沈阳市科技创新“双百工程”贷款贴息申请表》填写审核意见连同相关材料报市科技局。</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第十二条</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">市科技局负责贴息资金的拨付。</span></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center;line-height:47px\"><span style=\"font-size: 21px;font-family: 黑体\">第五章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 黑体\">监督管理</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第十三条申请贴息单位如通过弄虚作假等不正当手段骗取贴息资金的，一经核实，纳入失信项目管理，并从纳入之日起3年内不再受理项目承担单位（高等学校、科研院所为项目所在二级单位）、项目负责人及项目组主要成员申报市级科技计划项目和不再推荐其申报上级科技计划项目，并收回贴息资金。</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第十四条</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">本实施细则由市科技局负责解释。</span></p><p style=\"text-indent: 43px;line-height: 47px\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">第十五条</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">本实施细则自发布之日起实施。</span></p><p><br/></p>', '0', '0', '1', '2017-09-05 00:00:00', '1', '2017-11-13 13:48:59', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:21:10', '0', '');
INSERT INTO `site_policy_rule` VALUES ('69ce3b518e34402b9fe35eade5b68f5c', 'ZCFG_69ce3b518e34402b9fe35eade5b68f5c', '关于技术创新体系建设工作方案和科技支撑行动方案的通知', '1', '辽宁省人民政府办公厅', '<p style=\"text-align: center;\"><span style=\"font-size: 11px; color: rgb(51, 51, 51);\">（辽政办发〔</span><span style=\"font-size: 11px; font-family: Simsun, serif; color: rgb(51, 51, 51);\">2007</span><span style=\"font-size: 11px; color: rgb(51, 51, 51);\">〕</span><span style=\"font-size: 11px; font-family: Simsun, serif; color: rgb(51, 51, 51);\">10</span><span style=\"font-size: 11px; color: rgb(51, 51, 51);\">号）</span><br/></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">各市人民政府，省政府各厅委、各直属机构：</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　经省政府同意，现将省科技厅《关于以企业为主体的技术创新体系建设工作实施方案》、《关于社会主义新农村建设科技支撑行动方案》转发给你们，请认真贯彻执行。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">辽宁省人民政府办公厅</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">二</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">○○</span><span style=\"font-size: 11px;color:#333333\">七年二月十五日</span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　关于以企业为主体的技术创新体系建设工作实施方案</span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　为贯彻全国科技大会和省第十次党代会精神，落实《辽宁省中长期科学技术发展规划纲要</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">(2006-2020</span><span style=\"font-size:11px;color:#333333\">年</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">)</span><span style=\"font-size:11px;color:#333333\">》提出的加快建设以企业为主体、市场为导向、产学研相结合的技术创新体系的要求，全面提升企业的技术创新能力，加速推进经济结构调整和经济增长方式转变，支撑辽宁老工业基地的全面振兴，特制定以企业为主体的技术创新体系建设工作实施方案。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">　　一、建立以企业为主体的技术创新体系的重要性和必要性</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　我省大中型企业数量多、资产存量大，进一步搞活企业的机制和体制，依靠科技创新，提高企业的核心竞争力，已经成为实现老工业基地振兴的战略任务。发挥政府的引导作用，建立和完善以企业为主体的技术创新体系，是实现辽宁老工业基地全面振兴的历史任务。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　我省是国家重要的装备制造业和原材料生产基地，集中了一批关系国家综合竞争实力、涉及国计民生的主导产业、产品和企业。在未来激烈的竞争中，企业能否不断提高科技创新能力和水平，决定着我省未来的兴衰成败。只有在深化企业改革，建立现代企业制度的过程中，引导社会科技资源向企业凝聚，促进企业成为科技创新的主体，才能使科技创新在我省经济结构的战略性调整和经济增长方式转变的过程中，真正发挥作用，保持我省的产业优势、产品优势。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　经过</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">九五</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size:11px;color:#333333\">、</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">十五</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size:11px;color:#333333\">的企业技术改造，我省老工业基地已经实现了良好开局，步入了全面振兴的新阶段。把以企业为主体的创新体系建设作为建设创新型辽宁的切入点和突破口，时机已经成熟。通过企业创新体系的建设，必将充分发挥市场对资源的配置作用，促进产学研更紧密全面的合作，有力地推进全省经济的跨越式发展。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">　　二、指导思想和基本原则</span><span style=\"font-size: 11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">(</span><span style=\"font-size:11px;color:#333333\">一</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">)</span><span style=\"font-size:11px;color:#333333\">指导思想。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　深入贯彻落实全国科技大会和省第十次党代会精神，认真落实</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">坚持用科技创新引领老工业基地改造</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size: 11px;color:#333333\">的战略决策，以提高企业技术创新能力和产业竞争力为目标，以深化企业改革、加强体制机制创新为动力，以加强企业研发中心建设、改善企业创新环境为重点，积极推进产学研合作和科技成果转化，引导全社会科技资源向企业凝聚，加快建立以企业为主体、市场为导向、产学研相结合的技术创新体系，促进国家新型产业基地的建设，为辽宁老工业基地振兴提供强大的科技支撑。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">(</span><span style=\"font-size:11px;color:#333333\">二</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">)</span><span style=\"font-size:11px;color:#333333\">基本原则。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　坚持以企业为主体、市场为导向、产学研相结合的原则。充分发挥市场在科技资源配置中的基础性作用，突出企业在技术创新中的主体作用，大力促进产学研结合，形成技术创新的整体合力。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　坚持体制创新和机制创新的原则。解放思想，更新观念，用新体制、新机制调动广大科技人员的积极性和创造性，全面激活各种创新要素和资源，增强技术创新的内在动力。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　坚持有所为，有所不为，重点突破的原则。集中力量着力解决制约辽宁技术创新能力提升和科技向现实生产力转化的瓶颈问题和薄弱环节。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　坚持突出示范作用的原则。通过开展以企业为主体的技术创新体系建设工作，积极探索解决区域创新体系建设中的共性问题，为国家区域创新体系的建设提供经验。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">　　三、主要目标</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　到</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2010</span><span style=\"font-size:11px;color:#333333\">年，基本建成适应全省经济发展要求，体制全新、机制灵活、功能完善的技术创新体系，使大部分企业真正成为技术创新的投入主体、研发主体和成果应用主体，全面提高企业的技术创新能力和市场竞争力，为建设国家新型产业基地提供强大的科技支撑。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　全省企业技术创新能力和技术装备水平大幅度提高。大中型骨干企业全部建立研发机构，拥有自主知识产权、核心技术和自主品牌，拥有创新团队和高素质创新人才，企业的主导产品达到或者接近国际先进水平，形成一批技术创新能力强、能参与国际市场竞争的现代企业；中小企业得到快速发展，形成一批特色产业创新集群和自主创新龙头企业。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　产学研合作水平显著提高。高校、科研单位的研究方向和重点更加符合经济发展、企业竞争的需求，人才培养进一步满足经济社会的发展需要，产学研合作模式和方法不断创新，合作机制更加灵活，形成一批基于产学研紧密合作的</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">技术联盟</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size:11px;color:#333333\">。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　技术创新服务体系更加完善。形成覆盖全省的科技信息服务网络，生产力促进中心、企业投融资服务、风险投资、科技成果转移和转化、企业科技创新孵化基地等各级、各类科技中介机构建设取得重要进展，服务范围得到较大拓展，服务功能得到显著增强，人员素质和服务质量、水平得到明显提高。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">　　四、主要任务</span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（一）引导企业成为技术创新的主体。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size:11px;color:#333333\">．开展创新型企业创建活动。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　在我省主导行业和优势产业中选择</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">100</span><span style=\"font-size:11px;color:#333333\">家大中型企业和</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">200</span><span style=\"font-size: 11px;color:#333333\">家成长性好的科技型中小企业开展创建创新型企业活动（以下简称创建活动）。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　创建活动以鼓励和引导企业开展体制机制创新、管理创新和技术创新为宗旨，按照企业自愿申报，政府通过各类计划和政策引导、扶持的方式进行。开展创新型企业的评估和命名工作。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2. </span><span style=\"font-size:11px;color:#333333\">加强企业技术创新能力建设。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　扶持企业加强创新能力建设。在重点行业支持大型企业和骨干企业，实行新体制新机制，建设</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">200</span><span style=\"font-size:11px;color:#333333\">个各类省级企业研发中心</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">(</span><span style=\"font-size: 11px;color:#333333\">企业技术中心、工程</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">(</span><span style=\"font-size:11px;color:#333333\">技术</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">)</span><span style=\"font-size:11px;color:#333333\">研究中心</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">)</span><span style=\"font-size:11px;color:#333333\">，创建一批国家级企业研发中心</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">(</span><span style=\"font-size: 11px;color:#333333\">企业技术中心、工程</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">(</span><span style=\"font-size:11px;color:#333333\">技术</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">)</span><span style=\"font-size:11px;color:#333333\">研究中心</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">)</span><span style=\"font-size:11px;color:#333333\">，提高企业的研发能力和工程化、产业化水平。</span><span style=\"font-size: 11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　加强全社会科技资源整合，提升企业的集成创新、引进消化吸收再创新和高技术产业化的能力。一是实施</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">100</span><span style=\"font-size:11px;color:#333333\">项重大科技攻关项目。在关系国民经济和社会发展的重要领域，解决一批制约我省国民经济发展的关键</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">瓶颈</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size:11px;color:#333333\">技术问题。二是实施</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">100</span><span style=\"font-size:11px;color:#333333\">个引进消化吸收再创新项目。支持企业开展国际科技合作，引进国外先进技术和产品，通过消化吸收再创新实现国产化。三是实施</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">100</span><span style=\"font-size:11px;color:#333333\">项重大高技术产业化项目。支持企业在重点行业和领域实施高技术产业化项目，加速科技成果产业化。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3. </span><span style=\"font-size:11px;color:#333333\">增强中小企业创新能力。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　通过实施</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">科技型中小企业孵育工程</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size: 11px;color:#333333\">，引导企业加强产学研合作，加大研发投入，开发新产品。重点支持电子信息、生物制药、光电子、新材料和为我省主导产业配套的高新技术产品开发。孵育</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1000</span><span style=\"font-size:11px;color:#333333\">家科技型中小企业，聚集发展科技型中小企业群。开展中小企业专精特产品认定和名优产品评选工作。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">4</span><span style=\"font-size:11px;color:#333333\">．培育企业自主创新团队和创新人才。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　实施</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">企业自主创新团队培育工程</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size: 11px;color:#333333\">，每年培养</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">10</span><span style=\"font-size:11px;color:#333333\">个有重要影响的企业创新团队和</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">100</span><span style=\"font-size: 11px;color:#333333\">名高层次创新人才。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　实施</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">企业百千万人才创新工程</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size: 11px;color:#333333\">等人才培养计划。增加企业专业技术人员入选省百千万人才工程的比例，敞开招收企业项目博士后；支持企业开展创新型人才培育工作，通过承担国家、省重大科技项目，组织培训、锻炼和培养一批能满足企业创新需要的高级人才。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　实施创新型技能人才培育工程，通过组织开展职工技术创新活动，建立健全激励职工积极参与岗位创新、实现岗位成才的长效机制，有效激发职工自我提升素质的内在动力和创新热情。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">(</span><span style=\"font-size:11px;color:#333333\">二</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">)</span><span style=\"font-size:11px;color:#333333\">促进产学研合作。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size:11px;color:#333333\">．引导企业建立技术联盟。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　实施</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">企业技术联盟引导工程</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size: 11px;color:#333333\">。充分发挥高校和科研院所在企业创新中的生力军作用，引导重点行业中的骨干企业，以解决企业技术需求为目的，与具有相关优势的高校、科研院所建立长期、稳定的合作关系，形成各种形式的产学研合作技术联盟，重点扶持</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">150</span><span style=\"font-size:11px;color:#333333\">个企业技术联盟建设，为全面提升企业创新能力提供持续的科技支撑。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size:11px;color:#333333\">．组织实施</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">省院校合作工程</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size:11px;color:#333333\">。</span><span style=\"font-size:11px;font-family: &#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　广泛吸引省内外重点院校、科研院所与我省企业开展产学研合作，重点支持</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">200</span><span style=\"font-size:11px;color:#333333\">项对产业发展具有重大促进作用的重大产学研合作项目，帮助企业解决发展中的关键技术难题，提升产品的质量和水平。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3.</span><span style=\"font-size:11px;color:#333333\">促进科技成果转化。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　加强政府引导与市场机制的有机结合，引导高校、科研院所依据企业的技术需求，开展科技攻关和新产品开发，加快创新成果向企业的技术转移。鼓励和引导省内企业承接境内外技术成果，实现商品化生产。重点转化</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">150</span><span style=\"font-size:11px;color:#333333\">项技术成熟、具有牵动产业发展作用的重大创新型成果，促进产品结构调整，提高企业竞争实力。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">4. </span><span style=\"font-size:11px;color:#333333\">引导高校科技资源向中小企业转移。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　通过组建、完善</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">50</span><span style=\"font-size:11px;color:#333333\">个高校中小企业服务中心，引导高校与我省中小企业建立广泛的合作关系，促进科技资源向企业转移，为中小企业发展提供全方位的科技服务。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">(</span><span style=\"font-size:11px;color:#333333\">三</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">)</span><span style=\"font-size:11px;color:#333333\">完善企业技术创新服务体系。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size:11px;color:#333333\">．建设全省技术交易、交流合作服务平台。</span><span style=\"font-size: 11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　加强沈阳、大连两大技术市场建设，推进企业与高校、科研院所之间的技术、产权的流动和交易；进一步办好</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">东博会</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size:11px;color:#333333\">、</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">软交会</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size:11px;color:#333333\">和</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">专交会</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size:11px;color:#333333\">等各种展会，形成以国家级展会为载体，各类交易主体良性互动，创新要素相互交流的多元化技术市场网络。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size:11px;color:#333333\">．加强公共信息服务平台建设。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　依托辽宁科技信息网，建设辽宁科技信息门户网站；加快建设和完善辽宁省科技信息资源共享服务平台、大型科学仪器设备协作共用网络以及包括专家库在内的各种数据库信息网络。强化辽宁专利检索分析公共服务平台建设。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3</span><span style=\"font-size:11px;color:#333333\">．建立、健全风险投资体系。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　充实省级风险投资专项资金，积极吸引民间资金和外资建立以政府资金为引导，社会资金为主体，符合市场经济体制，按照风险投资规则运行的各种专业型、区域型风险投资机构，完善科技企业投融资服务平台。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">4</span><span style=\"font-size:11px;color:#333333\">．建设技术、成果转移平台。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　依托著名科研院所、高等院校及重点企业，围绕节能降耗、减少环境污染、提高产品质量、增产增收等重大共性技术问题，组建</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">30</span><span style=\"font-size:11px;color:#333333\">个技术推广、转移中心或成果转化示范基地，加速技术推广、转移和转化，发挥技术源头辐射和基地的示范带动作用。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size:11px;color:#333333\">．加强科技创新基地和孵化基地建设。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　完善高新区技术创新体系，重点抓好中科院科技创新园、大学科技园、软件园、信息产业园、生物医药产业园等各类科技创新基地建设，营造良好的创新创业环境。加快高新技术创业服务中心及</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">IC</span><span style=\"font-size:11px;color:#333333\">装备、数控技术、生物工程与制药、新材料、精细化工等产业专业孵化器建设，加速建成一批科技创新基地和高新技术产业化基地，为促进新兴企业的形成和发展提供基础条件。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">6</span><span style=\"font-size:11px;color:#333333\">．促进科技中介服务业的快速发展。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　大力促进生产力促进中心、科技咨询评估机构、行业协会等各类科技中介机构的快速发展。重点扶持</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">80</span><span style=\"font-size:11px;color:#333333\">个有发展前景、有实力的骨干科技中介机构，使其拓宽领域，增强功能，提高质量和水平，带动科技中介服务业的发展。实施</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">科技中介人才培训工程</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size: 11px;color:#333333\">，每年培训科技中介人才</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1000</span><span style=\"font-size:11px;color:#333333\">人，提高其执业水平和职业素质。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">　　五、保障措施</span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（一）精心组织，加强领导。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　在省委、省政府的领导下，由省科技创新工作领导小组具体负责实施方案的组织协调工作。在方案的实施过程中要加强省部、省市和省直各部门的联动，充分发挥各地区、各部门的资源优势，形成合力，共同推进以企业为主体的技术创新体系建设。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（二）营造有利的政策环境。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　落实《辽宁省人民政府关于提高科技创新能力，加速老工业基地振兴的若干规定》中的相关政策，按照省政府对《若干规定》的任务分解要求，抓好各项政策的协调、跟踪和落实，研究制定民营科技企业认定办法、科技成果转化实施细则等配套办法及创新型企业创建工作方案，发挥各部门的职能作用，保障本方案的实施工作取得实效，同时要加大宣传力度，营造良好的创新环境。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（三）拓宽投入渠道，引导企业成为创新投入的主体。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　发挥财政投入对增强企业技术创新能力的引导作用，综合运用财政补助、贴息和税收优惠等多种扶持方式，推动企业增加科技创新投入；制定配套政策，激发社会各界投资者的积极性，实现全社会科技资源的优化配置，鼓励金融机构、社会资金和外资等不断加大对企业技术创新的投入，逐步引导企业成为创新的投入主体。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（四）加大对技术创新的奖励。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　逐步完善企业科技创新人才奖励制度，重点奖励在实施科技成果转化和专利产业化中做出突出贡献的创造者、实施者和中介服务者，以及在依靠科技创新创建国家、省名牌产品和国家驰名商标、省著名商标过程中做出突出贡献的企业技术创新团队和个人。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">(</span><span style=\"font-size:11px;color:#333333\">五</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">)</span><span style=\"font-size:11px;color:#333333\">完善对国有企业及国有控股企业负责人的考核机制，加强对创新工作的考核。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　要完善对国有企业及国有控股企业负责人的考核机制，将技术创新、研发投入、技术创新发展规划制定实施情况等纳入考核指标体系，通过加强考核，推动国有及国有控股企业负责人确立自主创新的理念，研究建立推进企业技术创新的长效机制，保证我省以企业为主体的技术创新体系建设工作的顺利实施。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">省</span> <span style=\"font-size:11px;color:#333333\">科</span> <span style=\"font-size:11px;color:#333333\">技</span> <span style=\"font-size:11px;color:#333333\">厅</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">二〇〇七年二月十二日</span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　关于社会主义新农村建设科技支撑行动方案</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　为贯彻《中共中央、国务院关于推进社会主义新农村建设的若干意见》（中发〔</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2006</span><span style=\"font-size:11px;color:#333333\">〕</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size:11px;color:#333333\">号），充分发挥科技在社会主义新农村建设中的支撑作用，结合我省实际，特制定社会主义新农村建设科技支撑行动方案。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">　　一、指导思想和基本原则</span><span style=\"font-size: 11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（一）指导思想。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　全面落实《中共辽宁省委、辽宁省人民政府关于推进社会主义新农村建设的实施意见》（辽委发〔</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2006</span><span style=\"font-size:11px;color:#333333\">〕</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">8</span><span style=\"font-size:11px;color:#333333\">号）和省第十次党代会精神，以发展壮大县域经济为重要载体，以工业化为主导，以发展现代农业为基础，以体制机制创新为动力，充分发挥科技的支撑和引领作用，面向新农村建设的科技需求，积极推动产学研结合和科技成果转化，实现科技与农村经济社会发展的紧密结合，促进我省社会主义新农村建设的快速发展。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（二）基本原则。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size:11px;color:#333333\">．坚持求真务实。使科技在社会主义新农村建设中切实起到推动县域经济发展，惠及广大农民的作用。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size:11px;color:#333333\">．坚持体制机制创新。充分调动广大科技人员的积极性和创造性。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3</span><span style=\"font-size:11px;color:#333333\">．坚持以人为本。让农民掌握技术，使科技走进千家万户。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">4</span><span style=\"font-size:11px;color:#333333\">．坚持典型引路。充分发挥科技的示范带动作用。</span><span style=\"font-size: 11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">　　二、行动目标</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　到</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2010</span><span style=\"font-size:11px;color:#333333\">年，建成一批现代农业科技示范基地，培育一批科技龙头企业和知名品牌，形成一批特色产业集群和特色产业技术联盟，促进县域经济快速发展。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　引导科技资源向农村集聚，造就一批植根农村的科技创新、成果推广团队和农村用得上、留得住的乡土科技人才，农业产业结构进一步优化，农民科技素质和收入水平显著提高。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　开发和推广农村新能源、医疗卫生、饮水安全等方面的适宜技术，建立一批适应我省农村的成熟模式，推进农村人居环境和卫生健康水平的提升。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">　　三、重点任务</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（一）开展现代农业科技行动，为农业增效农民增收提供科技支撑。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　按照集约化、标准化、专业化、产业化的要求，积极推进现代农业建设，加速我省农业结构调整，提高农业生产效益，促进农民增收。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size:11px;color:#333333\">．实施良种工程。每年引进</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">100</span><span style=\"font-size: 11px;color:#333333\">个名特优蔬菜、果树、花卉、经济作物及畜牧、水产新品种，选育推广</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">50</span><span style=\"font-size:11px;color:#333333\">个优质专用、高产高效主要农作物、畜禽、水产及林木新品种。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size:11px;color:#333333\">．强化现代农业技术研发。针对现代农业产业体系发展需求，对粮、畜、渔、果、菜五大优势产业和油、花、菌、药、林五大特色农产品发展中的技术问题，组织联合攻关，为现代农业发展提供强有力的技术支撑。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3</span><span style=\"font-size:11px;color:#333333\">．开展现代农业技术示范。建立设施农业高效安全生产示范基地</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size:11px;color:#333333\">个，畜禽安全无疫病标准化养殖技术示范区</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size:11px;color:#333333\">个，海珍品、浅海滩涂贝类增养殖和稻田渔业生态养殖技术示范基地</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size:11px;color:#333333\">个，无公害优质出口水果示范基地</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size: 11px;color:#333333\">个，</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">GAP</span><span style=\"font-size:11px;color:#333333\">标准道地药材示范基地</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size: 11px;color:#333333\">个。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">4</span><span style=\"font-size:11px;color:#333333\">．培育农业科技龙头企业。培育</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">100</span><span style=\"font-size: 11px;color:#333333\">个农业科技龙头企业，支持企业通过与大专院校、科研院所联合攻关、技术合作等产学研结合方式，研制开发新技术、新产品；打造</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">100</span><span style=\"font-size:11px;color:#333333\">个具有一定知名度和市场占有率的知名品牌，促进县乡农业特色产业集群的形成和发展，增加农民收入。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（二）开展特色产业基地培育行动，为县域支柱产业发展提供科技支撑。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　大力培育</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">一县一业</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size:11px;color:#333333\">的特色经济，在未来</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">4</span><span style=\"font-size:11px;color:#333333\">年内，重点支持大石桥镁制品、法库陶瓷、桓仁中药、凌源花卉等</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">10</span><span style=\"font-size:11px;color:#333333\">个特色产业基地建设，促进</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size: 11px;color:#333333\">一县一业</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size:11px;color:#333333\">特色支柱产业的形成。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size:11px;color:#333333\">．建立特色产业公共研发和技术服务平台。积极引导特色产业基地内企业联合科研单位和大专院校，建立特色产业研发和公共技术服务机构，解决企业发展中存在的技术问题和为农户提供技术服务。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size:11px;color:#333333\">．支持企业开发新产品。省、市、县三级科技主管部门联动，支持企业围绕资源优势开发新产品，拉长产业链，将资源优势变为经济优势，尽快形成特色产业集群。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3</span><span style=\"font-size:11px;color:#333333\">．鼓励企业转化科技成果。广泛采用新技术、新工艺，在不断提高产品质量的同时，降低企业能源消耗。吸纳来自国内外、省内外先进、成熟、适用的科技成果，加速转化成现实生产力，把县域特色产业基地打造成科技成果转化基地。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">4</span><span style=\"font-size:11px;color:#333333\">．加大技术引进和招商引资力度。积极引进国外先进技术，并在消化吸收的基础上实现再创新，实现对资源的深度开发；努力打造招商引资的软硬环境，吸引国外公司在基地内投资建厂；大力开拓国内外市场，打造优势品牌，提高特色产业基地的国内外知名度和产品市场占有率。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（三）开展科技惠民行动，为提高农民生活质量提供科技支撑。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　以改善农村环境、提高农民健康水平为目标，依靠科技提高农民生活质量。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size:11px;color:#333333\">．推进农村新能源建设。开发推广秸秆造气、秸秆造粒、沼气、太阳能、风能等技术，建设生物质能源科技示范基地，建成</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">10</span><span style=\"font-size:11px;color:#333333\">个示范村，解决农村柴垛问题，为农村新能源建设积累经验。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size:11px;color:#333333\">．实施农村卫生适宜技术推广示范工程。在庄河市、本溪县、西丰县、阜新县、建平县、大洼县，依托大学、科研机构的人才和技术资源，为每县筛选出</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">15</span><span style=\"font-size:11px;color:#333333\">至</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">20</span><span style=\"font-size:11px;color:#333333\">项安全、有效、经济、适用的卫生技术，开展卫生技术培训，推广农村多发病和常见病的适宜诊疗技术，显著提高示范县医务人员的技术水平和服务质量，建成一批深受农民欢迎的适宜技术推广重点医院（科室），培养出一批适宜技术推广医生，降低农村患者的疾病负担。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3</span><span style=\"font-size:11px;color:#333333\">．开展农村饮水安全示范。针对我省中、西部地区农村饮水存在的铁锰含量严重超标、氟含量超标和地下水污染问题，解决低成本、高质量的新型除铁除锰、新型除氟过滤和高效苦咸水淡化等关键技术，在黑山县和台安县示范应用。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">4. </span><span style=\"font-size:11px;color:#333333\">开展村镇建设实用技术研究和示范。针对村镇建设中普遍存在的建筑设计水平不高、技术含量低、用地不集约和住宅耗能高等问题，研究开发节能省地、实用安全、经济环保、设施配套、特色鲜明的村镇住宅，建设一批科技示范村镇。开发粪污无害化处理低成本、高效益技术，在国家辽东半岛无规定动物疫病示范区和抚顺市示范应用。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size:11px;color:#333333\">．深入开展科技扶贫工作。组织百队千人科技扶贫活动，组织辽宁高级专家振兴服务团农业团成员，深入全省</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">77</span><span style=\"font-size:11px;color:#333333\">个涉农县</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">(</span><span style=\"font-size:11px;color:#333333\">市、区</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">)</span><span style=\"font-size:11px;color:#333333\">开展科技扶贫工作，指导贫困农民合理开发资源，增强自我积累和自我发展的能力，使贫困农民依靠科技脱贫致富，每队每年培养</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">50</span><span style=\"font-size:11px;color:#333333\">个科技示范户，培训</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1000</span><span style=\"font-size: 11px;color:#333333\">名贫困农民。各市要组织当地科技人员组成科技扶贫服务队开展科技扶贫工作。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（四）开展农村科技服务行动，为促进科技成果转化和提高农民素质提供科技支撑。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size:11px;color:#333333\">．广泛开展科技特派员活动。按照双向选择原则，组织省内大专院校、科研院所与各县（市、区）建立长期合作共建关系，并派出科技特派团。各县（市、区）广泛开展科技特派员活动，与企业、农村种养殖大户结成各种形式的利益共同体，允许科技人员兼职并取得报酬，充分调动科技人员的积极性，深入农村为企业和农业生产解决关键技术问题，实现企业增效、农民增收。深入实施</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">一村一名大学生</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size: 11px;color:#333333\">计划，采取灵活多样的方式选派大学毕业生到农村工作，让他们用先进的科技知识带领农民走科技致富之路。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size:11px;color:#333333\">．实施农民技术员培养工程。在农村种养殖科技示范户中选拔有一定实践经验的青年农民进入省内农业高等院校，接受以需求为导向、半年制、非学历的技术培训，结业通过考试和技能鉴定，颁发国家农业部、劳动保障部的职业资格证书。做好农村实用人才职称评定工作。到</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2010</span><span style=\"font-size:11px;color:#333333\">年为全省每村培养一名农民技术员，实现村村都有懂科技、善经营、留得住、用得上的乡土科技人才。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3</span><span style=\"font-size:11px;color:#333333\">．深入开展新型农民培训。建立以国家星火学校和星火培训基地为龙头，以各地农民科技培训中心为骨干，以科研院所、大专院校为依托，以企业与民间服务组织为补充的多部门相互配合的新型农民培训网络。根据农村产业发展的需要和各地实际，采取现场培训和远程教育相结合，组织开展各种形式的农村实用技术培训。建立农村劳动力转移就业培训基地，开展农村劳动力转移的职业技能培训，保证农村富余劳动力具有较高的科技文化素质，提高农民转岗致富能力。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">4</span><span style=\"font-size:11px;color:#333333\">．加强科学技术普及。选择</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">30</span><span style=\"font-size: 11px;color:#333333\">个村建设村级科普基地，以电视教学、信息网络、科普画廊、科普实用展具、科普图书等形式，通过科技下乡、科技活动周、科普之冬、科普大篷车等活动，开展实用技术培训和大规模群众性科普宣传，普及现代农业科技知识、实用法律政策常识、健康卫生等科技知识，全面提高农民的科学文化素质。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5. </span><span style=\"font-size:11px;color:#333333\">实施农村科技信息化工程。进一步完善全省农业新品种、农业科技成果、农业科技专家数据库和星火科技信息网，建设农村科技信息平台，开展信息技术应用培训，让科技特派员、农民科技经纪人、农民技术员在信息平台上建立自己个性化的互动信息网站，为广大农民提供信息服务。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（五）开展科技进步示范行动，为县域经济发展提供科技支撑。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　充分发挥科技的引领作用，探索适合我省不同地区的农村经济社会发展模式，为我省社会主义新农村建设积累经验，提供示范。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1. </span><span style=\"font-size:11px;color:#333333\">开展科技进步示范县乡活动。在全省范围内选择</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size:11px;color:#333333\">个县、</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">50</span><span style=\"font-size:11px;color:#333333\">个乡，通过与大专院校和科研院所建立长期共建关系，广泛开展县乡企业与科研单位、大专院校互惠互利的技术合作，打造核心企业，努力提升技术水平和产品档次。做大做强农业产业化龙头企业，带动农业集约化、标准化生产。大力发展</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">一县一业</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size:11px;color:#333333\">、</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">一乡一品</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size:11px;color:#333333\">的特色经济，并通过聘请国外专家技术指导、引进国外智力成果等方式，推进特色经济发展。结合小城镇建设，大力推广农村新能源、节能住房等先进适用技术，使县乡经济和农民生活水平显著提高。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2. </span><span style=\"font-size:11px;color:#333333\">实施科技进山增效示范工程。在我省东部山区的清原县、桓仁县和宽甸县开展科技进山活动的基础上，增加新宾县和本溪县，开展社会主义</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">新山村</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size:11px;color:#333333\">建设。通过技术引进、推广和开发，推动经济林、山药材、山野菜、林蛙、食用菌、马鹿等特色山区资源的综合有效利用，将资源优势转变为商品优势和经济优势，加快山区农业产业结构的调整和农民脱贫致富的步伐，促进山区经济、社会和环境全面协调发展，实现山村环境生态化，山村经济产业化。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\">　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3. </span><span style=\"font-size:11px;color:#333333\">加快现代农业科技园区建设。加快沈阳辉山农业高新技术园区和阜新、大连金州国家农业科技园区建设，引导资金、技术、人才等生产要素向农业科技园区聚集。以农产品精深加工为重点，提高农产品加工企业科技含量和产品竞争力，培育壮大农业产业化科技龙头企业。通过农业科技园区示范作用，辐射带动周边地区农业发展，促进农业产业化经营，带动农民致富。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">　　四、保障措施</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（一）加强组织领导。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　社会主义新农村建设科技支撑行动是辽宁省科技创新体系建设的一个重要组成部分，省直有关部门要各负其责，要实现省、市、县联动。各市、县都要把新农村建设科技支撑行动放在重要的位置，从组织领导上提供有力保障，整合资源、创新机制、统筹协调，形成建设新农村的合力。各县（市、区）要把新农村建设科技支撑行动的任务作为领导责任目标考核的重要内容，保证新农村建设科技支撑行动各项任务的顺利完成。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（二）制定和完善相关政策。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　制定鼓励科技人员深入农村一线参加新农村建设的政策，鼓励科技人员深入农村一线创新创业，搭建有利于解除科技人员后顾之忧的政策平台。制定开展科技特派员活动的相关政策，积极营造有利于科技特派员选派和科技特派员工作的环境。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（三）多渠道增加对新农村建设科技支撑行动的投入。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　省、市、县各级财政要加大对新农村建设科技支撑行动的投入力度。鼓励社会资本参与新农村建设科技支撑行动，逐步形成以政府投入为引导，企业、农民等社会资本多方投入的多元化投融资机制。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　（四）加强宣传，营造舆论环境。</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">　　及时总结各地、各单位在新农村建设科技支撑行动中的新成果、新经验、新模式，对取得突出成绩的单位和个人给予表彰奖励。利用报纸、广播电视、杂志和互联网等媒体，加大宣传力度，努力营造广大科技人员积极参与社会主义新农村建设的舆论环境。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">省　科　技　厅</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"></span></p><p style=\"text-align:right;line-height:27px\"><span style=\"font-size:11px;color:#333333\">二〇〇七年二月十二日</span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">发布部门：辽宁省政府　发布日期：</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2007</span><span style=\"font-size:11px;color:#333333\">年</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">02</span><span style=\"font-size:11px;color:#333333\">月</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">15</span><span style=\"font-size:11px;color:#333333\">日　实施日期：</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2007</span><span style=\"font-size: 11px;color:#333333\">年</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">02</span><span style=\"font-size:11px;color:#333333\">月</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">15</span><span style=\"font-size:11px;color:#333333\">日　</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">(</span><span style=\"font-size:11px;color:#333333\">地方法规</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">)</span></p><p><br/></p>', '0', '0', '1', '2007-02-25 00:00:00', '1', '2017-11-13 13:27:21', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:23:01', '0', '');
INSERT INTO `site_policy_rule` VALUES ('6b6381b869a9450e8dd8de9b43c1066b', 'ZCFG_6b6381b869a9450e8dd8de9b43c1066b', 'test', '0', 'test', '<p>‘</p>', '0', '1', '', '2020-08-28 00:00:00', '1', '2020-08-28 13:25:33', '1', '2020-08-28 13:25:53', '1', '');
INSERT INTO `site_policy_rule` VALUES ('6c42c3cdc8204e8e911c1c26da99895a', 'ZCFG_6c42c3cdc8204e8e911c1c26da99895a', '沈阳市科技条件平台管理办法（修订）', '2', '沈阳市科学技术局', '<p><img src=\"http://www.sykjtjpt.cn/image/show?id=20D8QW1yhvvpx2PP8e4qJKAvGoTfILHAmG88HOgWaq2_20UBcv_X2uyWkkf3Vmw0SkaYwrHwBqA.jpg\" title=\"2020040900019464.jpg\" alt=\"040910542777.jpg\"/></p>', '0', '0', '', '2019-10-29 00:00:00', '1', '2020-04-09 10:57:30', '1', '2020-04-09 10:57:30', '0', '');
INSERT INTO `site_policy_rule` VALUES ('6d6d9edbb2d640f68bd08c97d1750cc4', 'ZCFG_6d6d9edbb2d640f68bd08c97d1750cc4', '关于印发《高新技术企业认定管理工作指引》的通知', '0', '科技部 财政部 国家税务总局', '<p style=\"line-height: 37px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\"></span></p><p><span style=\"color: rgb(51, 51, 51); font-family: 仿宋_GB2312; font-size: 24px; text-align: center;\">&nbsp;&nbsp;&nbsp;&nbsp;各省、自治区、直辖市及计划单列市科技厅（局）、财政厅（局）、国家税务局、地方税务局:</span></p><p style=\"text-indent: 42px;line-height: 43px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">《高新技术企业认定管理办法》（国科发火</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">[2008]172</span><span style=\"font-size: 24px;font-family:仿宋_GB2312;color:#333333\">号，以<span style=\"letter-spacing:-1px\">下称《认定办法》）及《国家重点支持的高新技术领域》已经印</span>发给你们。为确保认定管理工作高效、规范，根据《认定办法》第<span style=\"letter-spacing:-1px\">十九条的规定，现将《高新技术企业认定管理工作指引》（以下</span>称《工作指引》）印发给你们，并就有关事项通知如下：</span></p><p style=\"text-indent: 42px;line-height: 43px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">一、各省、自治区、直辖市及计划单列市的科技、财政、税务部门应充分认识高新技术企业认定管理工作的重要性，密切配合，及时成立认定管理机构，共同做好本地区高新技术企业认定和税收优惠政策的落实工作。</span></p><p style=\"text-indent: 42px;line-height: 43px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">二、</span><span style=\"font-size: 24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">2007</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">年底前国家高新技术产业开发区（包括北京市新技术产业开发试验区）内、外已按原认定办法认定的仍在有效期内的高新技术企业资格依然有效，但在按《认定办法》和《工作指引》重新认定合格后方可依照《企业所得税法》及其实施条例等有关规定享受企业所得税优惠政策。企业可提前按《认定办法》和《工作指引》申请重新认定，亦可在资格到期后申请重新认定。</span></p><p style=\"text-indent: 42px;line-height: 43px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">三、对原依法享受企业所得税定期减免税优惠未期满的高新技术企业，可依照《国务院关于实施企业所得税过渡优惠政策的通知》（国发</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">[2007]39</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">号</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">)</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">的有关规定执行。</span></p><p style=\"text-indent: 42px;line-height: 43px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">四、对经济特区和上海浦东新区内新设立并按《认定办法》和《工作指引》认定的高新技术企业，按《国务院关于经济特区和上海浦东新区新设立高新技术企业实行过渡性税收优惠的通知》（国发</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">[2007]40</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">号）的有关规定执行。</span></p><p style=\"text-indent: 42px;line-height: 43px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">五、高新技术企业认定管理工作政策性强、专业要求高，各地应配备骨干人员，保障认定工作所需经费，及时对本地区在认定工作中出现的新情况、新问题提出切实可行的政策建议。</span></p><p style=\"text-indent: 42px;line-height: 42px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">附件：高新技术企业认定管理工作指引</span></p><p style=\"line-height: 37px\"><span style=\"font-size: 20px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;</span></p><p style=\"line-height: 37px\"><span style=\"font-size: 20px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;</span></p><p style=\"line-height: 37px\"><span style=\"font-size: 20px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;</span></p><p style=\"text-indent: 31px;line-height: 37px\"><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">科</span><span style=\"font-size: 24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">技</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">部</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">财</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">政</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">部</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">国家税务总局</span><span style=\"font-size:11px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:24px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:24px;font-family:仿宋_GB2312;color:#333333\">二ＯＯ八年七月八日</span></p><p><br/></p>', '0', '0', '1', '2008-07-08 00:00:00', 'ce35431464824abcbd886b68737e87d5', '2017-11-13 16:13:56', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:28:50', '0', '');
INSERT INTO `site_policy_rule` VALUES ('707300eeba3846a185f4457a714a1222', 'ZCFG_707300eeba3846a185f4457a714a1222', '国务院关于印发实施《中华人民共和国促进科技成果转化法》若干规定的通知', '0', '国务院', '<p style=\";margin-bottom:0;text-align:center;background:white\"><strong><span style=\"font-size:24px;color:#333333\"><br/> </span></strong><span style=\"font-family:楷体_GB2312;color:#333333\"></span></p><p style=\"margin-bottom: 0px; background: white;\"><span style=\"font-family:楷体_GB2312;color:#333333\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 国发〔2016〕16号</span></p><p style=\";margin-bottom:0;background:white\"><span style=\"color:#333333\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;各省、自治区、直辖市人民政府，国务院各部委、各直属机构：　　</span></p><p style=\";margin-bottom:0;background:white\"><span style=\"color:#333333\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;现将《实施〈中华人民共和国促进科技成果转化法〉若干规定》印发给你们，请认真贯彻执行。</span></p><p style=\";margin-bottom:0;background:white\"><span style=\"color:#333333\">　　　　　　　　　　　　　　　　　　　　　　　　　　　　　&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;国务院<br/> 　　　　　　　　　　　　　　　　　　　　　　　　　　　&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2016年2月26日<br/> 　　&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;（此件公开发布）</span></p><p style=\";margin-bottom:0;background:white\"><span style=\"color:#333333\"><br/></span></p><p style=\"margin-top:0;margin-right:0;margin-bottom:12px;margin-left:0;background:white\"><span style=\"color:#333333\">&nbsp;</span></p><p style=\";margin-bottom:0;text-align:center;background:white\"><strong><span style=\"font-size:24px;font-family:宋体;color:#333333\">实施《中华人民共和国</span></strong><strong><span style=\"font-size:24px;color:#333333\"><br/> </span></strong><strong><span style=\"font-size:24px;font-family:宋体;color:#333333\">促进科技成果转化法》若干规定</span></strong></p><p style=\";margin-bottom:0;background:white\"><span style=\"color:#333333\">　　为加快实施创新驱动发展战略，落实《中华人民共和国促进科技成果转化法》，打通科技与经济结合的通道，促进大众创业、万众创新，鼓励研究开发机构、高等院校、企业等创新主体及科技人员转移转化科技成果，推进经济提质增效升级，作出如下规定。<br/> 　　<strong><span style=\"font-family:宋体\">一、促进研究开发机构、高等院校技术转移</span></strong><strong><br/> </strong>　　（一）国家鼓励研究开发机构、高等院校通过转让、许可或者作价投资等方式，向企业或者其他组织转移科技成果。国家设立的研究开发机构和高等院校应当采取措施，优先向中小微企业转移科技成果，为大众创业、万众创新提供技术供给。<br/> 　　国家设立的研究开发机构、高等院校对其持有的科技成果，可以自主决定转让、许可或者作价投资，除涉及国家秘密、国家安全外，不需审批或者备案。<br/> 　　国家设立的研究开发机构、高等院校有权依法以持有的科技成果作价入股确认股权和出资比例，并通过发起人协议、投资协议或者公司章程等形式对科技成果的权属、作价、折股数量或者出资比例等事项明确约定，明晰产权。<br/> 　　（二）国家设立的研究开发机构、高等院校应当建立健全技术转移工作体系和机制，完善科技成果转移转化的管理制度，明确科技成果转化各项工作的责任主体，建立健全科技成果转化重大事项领导班子集体决策制度，加强专业化科技成果转化队伍建设，优化科技成果转化流程，通过本单位负责技术转移工作的机构或者委托独立的科技成果转化服务机构开展技术转移。鼓励研究开发机构、高等院校在不增加编制的前提下建设专业化技术转移机构。<br/> 　　国家设立的研究开发机构、高等院校转化科技成果所获得的收入全部留归单位，纳入单位预算，不上缴国库，扣除对完成和转化职务科技成果作出重要贡献人员的奖励和报酬后，应当主要用于科学技术研发与成果转化等相关工作，并对技术转移机构的运行和发展给予保障。<br/> 　　（三）国家设立的研究开发机构、高等院校对其持有的科技成果，应当通过协议定价、在技术交易市场挂牌交易、拍卖等市场化方式确定价格。协议定价的，科技成果持有单位应当在本单位公示科技成果名称和拟交易价格，公示时间不少于15日。单位应当明确并公开异议处理程序和办法。<br/> 　　（四）国家鼓励以科技成果作价入股方式投资的中小企业充分利用资本市场做大做强，国务院财政、科技行政主管部门要研究制定国家设立的研究开发机构、高等院校以技术入股形成的国有股在企业上市时豁免向全国社会保障基金转持的有关政策。<br/> 　　（五）国家设立的研究开发机构、高等院校应当按照规定格式，于每年3月30日前向其主管部门报送本单位上一年度科技成果转化情况的年度报告，主管部门审核后于每年4月30日前将各单位科技成果转化年度报告报送至科技、财政行政主管部门指定的信息管理系统。年度报告内容主要包括：<br/> 　　1.科技成果转化取得的总体成效和面临的问题；<br/> 　　2.依法取得科技成果的数量及有关情况；<br/> 　　3.科技成果转让、许可和作价投资情况；<br/> 　　4.推进产学研合作情况，包括自建、共建研究开发机构、技术转移机构、科技成果转化服务平台情况，签订技术开发合同、技术咨询合同、技术服务合同情况，人才培养和人员流动情况等；<br/> 　　5.科技成果转化绩效和奖惩情况，包括科技成果转化取得收入及分配情况，对科技成果转化人员的奖励和报酬等。<br/> 　　<strong><span style=\"font-family:宋体\">二、激励科技人员创新创业</span></strong><strong><br/> </strong>　　（六）国家设立的研究开发机构、高等院校制定转化科技成果收益分配制度时，要按照规定充分听取本单位科技人员的意见，并在本单位公开相关制度。依法对职务科技成果完成人和为成果转化作出重要贡献的其他人员给予奖励时，按照以下规定执行：<br/> 　　1.以技术转让或者许可方式转化职务科技成果的，应当从技术转让或者许可所取得的净收入中提取不低于50%的比例用于奖励。<br/> 　　2.以科技成果作价投资实施转化的，应当从作价投资取得的股份或者出资比例中提取不低于50%的比例用于奖励。<br/> 　　3.在研究开发和科技成果转化中作出主要贡献的人员，获得奖励的份额不低于奖励总额的50%。<br/> 　　4.对科技人员在科技成果转化工作中开展技术开发、技术咨询、技术服务等活动给予的奖励，可按照促进科技成果转化法和本规定执行。<br/> 　　（七）国家设立的研究开发机构、高等院校科技人员在履行岗位职责、完成本职工作的前提下，经征得单位同意，可以兼职到企业等从事科技成果转化活动，或者离岗创业，在原则上不超过3年时间内保留人事关系，从事科技成果转化活动。研究开发机构、高等院校应当建立制度规定或者与科技人员约定兼职、离岗从事科技成果转化活动期间和期满后的权利和义务。离岗创业期间，科技人员所承担的国家科技计划和基金项目原则上不得中止，确需中止的应当按照有关管理办法办理手续。<br/> 　　积极推动逐步取消国家设立的研究开发机构、高等院校及其内设院系所等业务管理岗位的行政级别，建立符合科技创新规律的人事管理制度，促进科技成果转移转化。<br/> 　　（八）对于担任领导职务的科技人员获得科技成果转化奖励，按照分类管理的原则执行：<br/> 　　1.国务院部门、单位和各地方所属研究开发机构、高等院校等事业单位（不含内设机构）正职领导，以及上述事业单位所属具有独立法人资格单位的正职领导，是科技成果的主要完成人或者对科技成果转化作出重要贡献的，可以按照促进科技成果转化法的规定获得现金奖励，原则上不得获取股权激励。其他担任领导职务的科技人员，是科技成果的主要完成人或者对科技成果转化作出重要贡献的，可以按照促进科技成果转化法的规定获得现金、股份或者出资比例等奖励和报酬。<br/> 　　2.对担任领导职务的科技人员的科技成果转化收益分配实行公开公示制度，不得利用职权侵占他人科技成果转化收益。<br/> 　　（九）国家鼓励企业建立健全科技成果转化的激励分配机制，充分利用股权出售、股权奖励、股票期权、项目收益分红、岗位分红等方式激励科技人员开展科技成果转化。国务院财政、科技等行政主管部门要研究制定国有科技型企业股权和分红激励政策，结合深化国有企业改革，对科技人员实施激励。<br/> 　　（十）科技成果转化过程中，通过技术交易市场挂牌交易、拍卖等方式确定价格的，或者通过协议定价并在本单位及技术交易市场公示拟交易价格的，单位领导在履行勤勉尽责义务、没有牟取非法利益的前提下，免除其在科技成果定价中因科技成果转化后续价值变化产生的决策责任。<br/> 　　<strong><span style=\"font-family:宋体\">三、营造科技成果转移转化良好环境</span></strong><strong><br/> </strong>　　（十一）研究开发机构、高等院校的主管部门以及财政、科技等相关部门，在对单位进行绩效考评时应当将科技成果转化的情况作为评价指标之一。<br/> 　　（十二）加大对科技成果转化绩效突出的研究开发机构、高等院校及人员的支持力度。研究开发机构、高等院校的主管部门以及财政、科技等相关部门根据单位科技成果转化年度报告情况等，对单位科技成果转化绩效予以评价，并将评价结果作为对单位予以支持的参考依据之一。<br/> 　　国家设立的研究开发机构、高等院校应当制定激励制度，对业绩突出的专业化技术转移机构给予奖励。<br/> 　　（十三）做好国家自主创新示范区税收试点政策向全国推广工作，落实好现有促进科技成果转化的税收政策。积极研究探索支持单位和个人科技成果转化的税收政策。<br/> 　　（十四）国务院相关部门要按照法律规定和事业单位分类改革的相关规定，研究制定符合所管理行业、领域特点的科技成果转化政策。涉及国家安全、国家秘密的科技成果转化，行业主管部门要完善管理制度，激励与规范相关科技成果转化活动。对涉密科技成果，相关单位应当根据情况及时做好解密、降密工作。<br/> 　　（十五）各地方、各部门要切实加强对科技成果转化工作的组织领导，及时研究新情况、新问题，加强政策协同配合，优化政策环境，开展监测评估，及时总结推广经验做法，加大宣传力度，提升科技成果转化的质量和效率，推动我国经济转型升级、提质增效。<br/> 　　（十六）《国务院办公厅转发科技部等部门关于促进科技成果转化若干规定的通知》（国办发〔1999〕29号）同时废止。此前有关规定与本规定不一致的，按本规定执行。</span></p><p><br/></p>', '0', '0', '1', '2016-02-02 00:00:00', '1', '2017-11-13 11:44:16', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:54:52', '0', '');
INSERT INTO `site_policy_rule` VALUES ('74051950dfe44e1fb8a1ccc3b64a8479', 'ZCFG_74051950dfe44e1fb8a1ccc3b64a8479', '沈阳市人民政府关于支持机器人产业发展和科技创新的实施意见', '2', '沈阳市人民政府', '<p style=\"text-align: left;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 18px;\">各区、县(市)人民政府，市政府各部门，各直属单位：</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">为推动我市机器人产业大发展、快发展，将沈阳建设成为具有国际竞争力的世界级机器人产业基地，加快我市产业结构调整和转型升级进程，根据《国务院关于近期支持东北振兴若干重大政策举措的意见》(国发〔2014〕28号)精神，结合我市实际，制订本实施意见。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">一、总体思路</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">按照“创新驱动、应用先行、龙头引领、全链布局、集群发展”原则，将机器人和智能装备产业作为我市当前的先导产业、支柱产业及未来重要的增长极优先发展。一是围绕机器人整机和关键零部件，提升自主研发能力和成果转化水平。二是培育拓展机器人及智能装备应用市场，加快提升我市传统行业机器人及成套装备应用水平。三是支持新松公司等龙头企业迅速扩张，成为世界级领军企业。四是建立机器人整机及零部件、系统集成、产业服务协同发展的产业格局，突破上游、强壮中游、做大下游，以工业机器人为主，在特种机器人、服务机器人等发展领域全面布局。五是将各类生产要素向机器人产业倾斜，同时加大招商引资力度，以浑南区为核心构建机器人产业集群。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">二、发展目标</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">到2017年，全市工业机器人智能装备产业发展水平取得跨越式提升，重点产品工业机器人整机力争形成15000台的产能规模，工业机器人及成套装备销量超过10000台(套)，工业机器人整机、零部件及成套装备销售收入突破200亿元；培养打造销售规模排</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">名全球前五的机器人龙头企业，培育3家以上在细分市场优势突出企业；实现30项以上重大技术和产品突破；在浑南区初步建成集研发创新、生产制造、配套加工、设计服务、检验检测、体验营销为一体的沈阳机器人产业园；建成机器人高端研究院和机器人产业培训基地，引进机器人产业相关的高级专家和专业人士30人以上，培养机器人学科方向的博士、硕士技术人才300人以上，产业高级技工3000名以上；工业机器人及成套装备在我市重点工业领域应用覆盖率显著提升，在重点企业主要生产环节实现普遍应用，企业生产自动化水平、劳动生产率和质量效益等显著增强。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">三、主要任务</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">一)坚持创新驱动，强化机器人产业自主创新能力。一是整合创新力量。以骨干企业为主体、利益为纽带，组建我市机器人产业技术创新联盟；加强校(院)企协同创新，提升创新效能。二是搭建高端平台。打造机器人创新研究院、创新创业产业园、高端技</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">术研究院等一批产业创新平台。三是突出科技成果转移转化。着力引导科研院所为我市传统装备产业提升提供技术解决方案，鼓励我市企业应用机器人产业研发成果。四是突破关键技术。打破体制限制，采取激励机制，在高精度伺服控制技术、高速实时总线技术、人机安全协作等关键技术和伺服驱动器、伺服电机等核心零部件方面实现突破。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">二)培育世界级机器人龙头企业，扶持骨干企业。扶持现有机器人整机、零部件和系统集成企业做大做强，鼓励新松公司通过裂变式发展成为机器人行业世界级领军企业，到2017年建成年产能10000台(套)机器人系列产品的机器人数字化制造生产线。以沈阳自动化所为核心，孵化、培育一批在细分领域具有竞争优势的“专、精、特、新”企业，逐步形成大中小企业梯次发展的良性格局。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">三)促进我市传统装备与机器人技术的融合，形成智能装备产业新格局。以机床、工程机械、电子装备等为重点，以智能化、精准化、集成化为方向，实现传统装备和机器人技术的有效对接，在单机智能化、单元自动化的基础上，提升成线能力，形成智能化成套装备和数字化工厂解决方案，促进我市装备制造业向价值链高端迈进。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">四)着力推进重大项目建设，壮大产业集群规模。重点推进国家级机器人检测中心、机器人产品体验中心、数字装备制造中心、成套装备制造中心、柔性智能制造中心、大型装备制造中心、沈机数字化工厂及智能制造创新研究院等一批重大项目建设。促进大数据、新材料和物联网等新兴产业与机器人产业融合发展。鼓励我市装备制造企业进入机器人领域，利用自身优势参与机器人本体制造和系统集成，通过整合延伸、配套分工，提升我市机器人产业集群规模水平，实现规模经济效应。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">五)加快机器人及智能装备推广应用，强化市场培育。围绕装备制造业整体升级和工业转型发展的现实需求，在我市汽车及零部件、工程机械、民用航空、电子电力、食品饮料、出版印刷、包装材料等行业领域，引导企业应用工业机器人及智能装备对传统生产线或生产系统进行技术改造，抓好一批效果突出、带动性强、关联度高的典型应用工程。同时积极运用融资租赁等机器人商业推广和营运模式，培育产品应用市场。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">六)强化企业和技术引进，完善产业链条。围绕我市机器人产业链伺服电机与驱动、减速器等关键环节，重点面向国内实力较强和已在国内设立办事机构但尚未完成在我国布局的国际知名企业，采取投资合作、技术引进和资本收购等模式，加大引进力度，借助外力实现产业快速扩张。鼓励本土企业与国内外工业机器人巨头和科研机构结为战略伙伴，积极参与标准制定、技术开发的国际协作，完善延伸上下游产业链条。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">七)推进我市机器人产业基地建设，构建产业支撑体系。整合优化空间资源，提升基地、园区对产业发展的综合承载能力；围绕龙头企业，加快聚集发展要素。以新松智慧产业园建设为核心，抓紧布局建设机器人与智能制造创新研究院、沈阳材料国家实验室、东北区域超算(云计算)中心、机器人博览中心、机器人产业孵化和人才培养基地等，形成我市机器人产业发展支撑体系。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">四、支持政策</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">一)设立沈阳市机器人产业科技创新重大项目。支持新松公司等核心企业，依托机器人产业技术创新战略联盟开展产学研合作，以招标的方式，开展产业关键技术攻关、重大新产品研制等研发活动，加快机器人产业发展。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">二)支持沈阳市机器人与智能制造创新平台建设。根据我市机器人产业成果转化的实际需求，依托中国科学院沈阳自动化所等单位，重点支持机器人产业为我市企业提供技术解决方案及科技成果转化应用的项目。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">三)加强机器人技术和产品的知识产权保护工作。我市企业、大学、科研院所围绕机器人产业申请的发明专利，享受市专利择优支持费支持；对于获得授权的发明专利，补齐申请费其余部分。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">四)支持机器人产业人才引进。支持以项目为载体引进或创建机器人新型研发机构，鼓励高端技术人才来沈创新创业。市科技计划对其研发项目予以优先立项支持；市风险基金和科技贷款担保基金对新创办的机器人产业公司给予重点支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">五)支持机器人产业技术转移活动。对我市企业应用大学、科研院所关于机器人技术成果的项目，市科技计划予以立项支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">六)支持机器人产业孵化器建设。大力培育机器人企业，每年评选一定数量的孵化培育企业，孵化期为3年，并给予一定的项目启动资金。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">七)支持机器人产业科技交流活动。举办沈阳市机器人产品大赛，鼓励在我市召开机器人领域国际会议及行业论坛，并给予一定补贴。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">八)支持机器人项目建设。自2015年起将全市机器人整机和关键零部件生产制造、研发检测等公共服务平台建设、集成应用等项目列入沈阳市年度重点建设项目计划，在市工业发展专项中安排资金，采用固定资产投资补助、贷款贴息等方式予以支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">九)多元化支持机器人企业发展。充分发挥我市现有各产业发展基金作用，同时努力引导其他社会资本参与，共同对机器人整机及零部件、研发检测、集成应用等企业采取资本金注入、股权投资等方式予以支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">十)支持企业应用工业机器人。鼓励本地企业购买或租赁本市制造的工业机器人及成套设备。在市工业发展专项中安排资金，通过固定资产投资补助的方式予以支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">十一)支持机器人首台(套)重大技术装备及关键零部件的研制和推广应用。对经认定的本市机器人研发生产企业生产的机器人首台(套)设备，参照《辽宁省首台(套)重大技术装备专项补助资金管理暂行办法》在市工业发展专项中安排资金给予等额配套支持。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">十二)支持企业参加各类展会。支持我市机器人联盟组团参加国内外博览会、展销会，鼓励筹办我市机器人产业省级以上专业展会，根据相关政策规定享受展会补贴。</span></p><p style=\"text-indent: 36px;line-height: 30px\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">(</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">十三)强化机器人产业要素保障。建立机器人产业项目审批绿色通道，对产业发展重大项目，在金融、用地等要素保障方面予以重点倾斜。 上述政策执行期限为2015年1月1日至2017年12月31日，由市经济和信息化委负责解释产业化政策、市科技局负责解释科技创新政策。</span></p><p><br/></p>', '0', '0', '1', '2015-07-07 00:00:00', '1', '2017-11-13 14:23:03', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:30:48', '0', '');
INSERT INTO `site_policy_rule` VALUES ('798e61dae86349ada8d8d2dff18aada4', 'ZCFG_798e61dae86349ada8d8d2dff18aada4', '中华人民共和国技术进出口管理条例', '0', '国务院', '<p style=\"background: white; text-align: center;\"><br/></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一章 总 则</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一条 为了规范技术进出口管理，维护技术进出口秩序，促进国民经济和社会发展，根据《中华人民共和国对外贸易法》（以下简称对外贸易法）及其他有关法律的有关规定，制定本条例。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二条 本条例所称技术进出口，是指从中华人民共和国境外向中华人民共和国境内，或者从中华人民共和国境内向中华人民共和国境外，通过贸易、投资或者经济技术合作的方式转移技术的行为。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">前款规定的行为包括专利权转让、专利申请权转让、专利实施许可、技术秘密转让、技术服务和其他方式的技术转移。&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三条 国家对技术进出口实行统一的管理制度，依法维护公平、自由的技术进出口秩序。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四条 技术进出口应当符合国家的产业政策、科技政策和社会发展政策，有利于促进我国科技进步和对外经济技术合作的发展，有利于维护我国经济技术权益。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五条 国家准许技术的自由进出口；但是，法律、行政法规另有规定的除外。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六条 国务院对外经济贸易主管部门（以下简称国务院外经贸主管部门）依照对外贸易法和本条例的规定，负责全国的技术进出口管理工作。省、自治区、直辖市人民政府外经贸主管部门根据国务院外经贸主管部门的授权，负责本行政区域内的技术进出口管理工作。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国务院有关部门按照国务院的规定，履行技术进出口项目的有关管理职责。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二章 技术进口管理</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七条 国家鼓励先进、适用的技术进口。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第八条 有对外贸易法第十六条、第十七条规定情形之一的技术，禁止或者限制进口。&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国务院外经贸主管部门会同国务院有关部门，制定、调整并公布禁止或者限制进口的技术目录。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第九条 属于禁止进口的技术，不得进口。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十条 属于限制进口的技术，实行许可证管理；未经许可，不得进口。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十一条 进口属于限制进口的技术，应当向国务院外经贸主管部门提出技术进口申请并附有关文件。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">技术进口项目需经有关部门批准的，还应当提交有关部门的批准文件。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十二条 国务院外经贸主管部门收到技术进口申请后，应当会同国务院有关部门对申请进行审查，并自收到申请之日起３０个工作日内作出批准或者不批准的决定。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十三条 技术进口申请经批准的，由国务院外经贸主管部门发给技术进口许可意向书。&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">进口经营者取得技术进口许可意向书后，可以对外签订技术进口合同。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十四条 进口经营者签订技术进口合同后，应当向国务院外经贸主管部门提交技术进口合同副本及有关文件，申请技术进口许可证。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国务院外经贸主管部门对技术进口合同的真实性进行审查，并自收到前款规定的文件之日起１０个工作日内，对技术进口作出许可或者不许可的决定。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十五条 申请人依照本条例第十一条的规定向国务院外经贸主管部门提出技术进口申请时，可以一并提交已经签订的技术进口合同副本。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国务院外经贸主管部门应当依照本条例第十二条和第十四条的规定对申请及其技术进口合同的真实性一并进行审查，并自收到前款规定的文件之日起４０个工作日内，对技术进口作出许可或者不许可的决定。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十六条 技术进口经许可的，由国务院外经贸主管部门颁发技术进口许可证。技术进口合同自技术进口许可证颁发之日起生效。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十七条 对属于自由进口的技术，实行合同登记管理。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">进口属于自由进口的技术，合同自依法成立时生效，不以登记为合同生效的条件。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十八条 进口属于自由进口的技术，应当向国务院外经贸主管部门办理登记，并提交下列文件：</span><span style=\"font-size: 18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）技术进口合同登记申请书；</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）技术进口合同副本；</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）签约双方法律地位的证明文件。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十九条 国务院外经贸主管部门应当自收到本条例第十八条规定的文件之日起３个工作日内，对技术进口合同进行登记，颁发技术进口合同登记证。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十条 申请人凭技术进口许可证或者技术进口合同登记证，办理外汇、银行、税务、海关等相关手续。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十一条 依照本条例的规定，经许可或者登记的技术进口合同，合同的主要内容发生变更的，应当重新办理许可或者登记手续。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">经许可或者登记的技术进口合同终止的，应当及时向国务院外经贸主管部门备案。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十二条 设立外商投资企业，外方以技术作为投资的，该技术的进口，应当按照外商投资企业设立审批的程序进行审查或者办理登记。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十三条 国务院外经贸主管部门和有关部门及其工作人员在履行技术进口管理职责中，对所知悉的商业秘密负有保密义务。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十四条 技术进口合同的让与人应当保证自己是所提供技术的合法拥有者或者有权转让、许可者。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">技术进口合同的受让人按照合同约定使用让与人提供的技术，被第三方指控侵权的，受让人应当立即通知让与人；让与人接到通知后，应当协助受让人排除妨碍。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">技术进口合同的受让人按照合同约定使用让与人提供的技术，侵害他人合法权益的，由让与人承担责任。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十五条 技术进口合同的让与人应当保证所提供的技术完整、无误、有效，能够达到约定的技术目标。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十六条 技术进口合同的受让人、让与人应当在合同约定的保密范围和保密期限内，对让与人提供的技术中尚未公开的秘密部分承担保密义务。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">在保密期限内，承担保密义务的一方在保密技术非因自己的原因被公开后，其承担的保密义务即予终止。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十七条 在技术进口合同有效期内，改进技术的成果属于改进方。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十八条 技术进口合同期满后，技术让与人和受让人可以依照公平合理的原则，就技术的继续使用进行协商。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十九条 技术进口合同中，不得含有下列限制性条款：</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）要求受让人接受并非技术进口必不可少的附带条件，包括购买非必需的技术、原材料、产品、设备或者服务；</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）要求受让人为专利权有效期限届满或者专利权被宣布无效的技术支付使用费或者承担相关义务；</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）限制受让人改进让与人提供的技术或者限制受让人使用所改进的技术；</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）限制受让人从其他来源获得与让与人提供的技术类似的技术或者与其竞争的技术；</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（五）不合理地限制受让人购买原材料、零部件、产品或者设备的渠道或者来源；</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（六）不合理地限制受让人产品的生产数量、品种或者销售价格。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（七）不合理地限制受让人利用进口的技术生产产品的出口渠道。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三章 技术出口管理</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十条 国家鼓励成熟的产业化技术出口。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十一条 有对外贸易法第十六条、第十七条规定情形之一的技术，禁止或者限制出口。</span><span style=\"font-size: 18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国务院外经贸主管部门会同国务院有关部门，制定、调整并公布禁止或者限制出口的技术目录。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十二条 属于禁止出口的技术，不得出口。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十三条 属于限制出口的技术，实行许可证管理；未经许可，不得出口。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十四条 出口属于限制出口的技术，应当向国务院外经贸主管部门提出申请。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十五条 国务院外经贸主管部门收到技术出口申请后，应当会同国务院科技管理部门对申请出口的技术进行审查，并自收到申请之日起３０个工作日内作出批准或者不批准的决定。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">限制出口的技术需经有关部门进行保密审查的，按照国家有关规定执行。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十六条 技术出口申请经批准的，由国务院外经贸主管部门发给技术出口许可意向书。</span><span style=\"font-size: 18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">申请人取得技术出口许可意向书后，方可对外进行实质性谈判，签订技术出口合同。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十七条 申请人签订技术出口合同后，应当向国务院外经贸主管部门提交下列文件，申请技术出口许可证：</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）技术出口许可意向书；</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）技术出口合同副本；</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）技术资料出口清单；</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）签约双方法律地位的证明文件。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国务院外经贸主管部门对技术出口合同的真实性进行审查，并自收到前款规定的文件之日起１５个工作日内，对技术出口作出许可或者不许可的决定。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十八条 技术出口经许可的，由国务院外经贸主管部门颁发技术出口许可证。技术出口合同自技术出口许可证颁发之日起生效。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十九条 对属于自由出口的技术，实行合同登记管理。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">出口属于自由出口的技术，合同自依法成立时生效，不以登记为合同生效的条件。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十条 出口属于自由出口的技术，应当向国务院外经贸主管部门办理登记，并提交下列文件：</span><span style=\"font-size: 18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）技术出口合同登记申请书；</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）技术出口合同副本；</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）签约双方法律地位的证明文件。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十一条 国务院外经贸主管部门应当自收到本条例第四十条规定的文件之日起３个工作日内，对技术出口合同进行登记，颁发技术出口合同登记证。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十二条 申请人凭技术出口许可证或者技术出口合同登记证办理外汇、银行、税务、海关等相关手续。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十三条 依照本条例的规定，经许可或者登记的技术出口合同，合同的主要内容发生变更的，应当重新办理许可或者登记手续。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">经许可或者登记的技术出口合同终止的，应当及时向国务院外经贸主管部门备案。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十四条 国务院外经贸主管部门和有关部门及其工作人员在履行技术出口管理职责中，对国家秘密和所知悉的商业秘密负有保密义务。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十五条 出口核技术、核两用品相关技术、监控化学品生产技术、军事技术等出口管制技术的，依照有关行政法规的规定办理。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四章 法律责任</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十六条 进口或者出口属于禁止进出口的技术的，或者未经许可擅自进口或者出口属于限制进出口的技术的，依照刑法关于走私罪、非法经营罪、泄露国家秘密罪或者其他罪的规定，依法追究刑事责任；尚不够刑事处罚的，区别不同情况，依照海关法的有关规定处罚，或者由国务院外经贸主管部门给予警告，没收违法所得，处违法所得１倍以上５倍以下的罚款；国务院外经贸主管部门并可以撤销其对外贸易经营许可。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十七条 擅自超出许可的范围进口或者出口属于限制进出口的技术的，依照刑法关于非法经营罪或者其他罪的规定，依法追究刑事责任；尚不够刑事处罚的，区别不同情况，依照海关法的有关规定处罚，或者由国务院外经贸主管部门给予警告，没收违法所得，处违法所得１倍以上３倍以下的罚款；国务院外经贸主管部门并可以暂停直至撤销其对外贸易经营许可。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十八条 伪造、变造或者买卖技术进出口许可证或者技术进出口合同登记证的，依照刑法关于非法经营罪或者伪造、变造、买卖国家机关公文、证件、印章罪的规定，依法追究刑事责任；尚不够刑事处罚的，依照海关法的有关规定处罚；国务院外经贸主管部门并可以撤销其对外贸易经营许可。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四十九条 以欺骗或者其他不正当手段获取技术进出口许可的，由国务院外经贸主管部门吊销其技术进出口许可证，暂停直至撤销其对外贸易经营许可。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五十条 以欺骗或者其他不正当手段获取技术进出口合同登记的，由国务院外经贸主管部门吊销其技术进出口合同登记证，暂停直至撤销其对外贸易经营许可。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五十一条 技术进出口管理工作人员违反本条例的规定，泄露国家秘密或者所知悉的商业秘密的，依照刑法关于泄露国家秘密罪或者侵犯商业秘密罪的规定，依法追究刑事责任；尚不够刑事处罚的，依法给予行政处分。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五十二条 技术进出口管理工作人员滥用职权、玩忽职守或者利用职务上的便利收受、索取他人财物的，依照刑法关于滥用职权罪、玩忽职守罪、受贿罪或者其他罪的规定，依法追究刑事责任；尚不够刑事处罚的，依法给予行政处分。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五章 附 则</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五十三条 对国务院外经贸主管部门作出的有关技术进出口的批准、许可、登记或者行政处罚决定不服的，可以依法申请行政复议，也可以依法向人民法院提起诉讼。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五十四条 本条例公布前国务院制定的有关技术进出口管理的规定与本条例的规定不一致的，以本条例为准。</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span></p><p><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五十五条 本条例自２００２年１月１日起施行。１９８５年５月２４日国务院发布的《中华人民共和国技术引进合同管理条例》和１９８７年１２月３０日国务院批准、１９８８年１月２０日对外经济贸易部发布的《中华人民共和国技术引进合同管理条例施行细则》同时废止。</span></p><p><br/></p>', '0', '0', '1', '2002-01-01 00:00:00', '1', '2017-11-13 13:23:48', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:33:34', '0', '');
INSERT INTO `site_policy_rule` VALUES ('7f56ceffa05343629426774476260d31', 'ZCFG_7f56ceffa05343629426774476260d31', '沈阳市技术市场条例', '2', '沈阳市人民代表大会常务委员会', '<p style=\"background: white; text-align: center;\"><br/></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一章 总则</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一条 为了促进技术市场的发展，加速科学技术成果的转化，维护技术市场秩序，保护技术贸易当事人的合法权益，根据国家有关法律、法规，结合本市实际，制定本条例。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二条 公民、法人和其他组织在本市行政区域内从事技术贸易、技术贸易服务等活动，适用本条例。法律另有规定的除外。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三条 本条例所称技术贸易包括技术开发、技术转让、技术咨询、技术服务等交易活动；技术贸易服务包括技术信息服务、技术贸易经纪服务、技术交易场所服务、技术贸易咨询服务、技术评估服务等。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四条 市人民政府科学技术行政管理部门是本市技术市场的主管部门。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">市技术市场管理办公室负责全市技术市场的日常管理工作；县（市）、区技术市场管理部门负责本地区技术市场的日常管理工作。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">工商、税务、财政、物价等有关部门，按照职责分工做好技术市场的监督管理工作。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五条 从事技术贸易和技术贸易服务，必须遵循自愿平等、有偿互利、诚实信用和协商一致的原则。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六条 凡有益于经济建设、社会发展和科技进步的技术，均可进入技术市场，国家另有规定的除外。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">技术贸易和技术贸易服务活动不受地区、行业、隶属关系、经济性质和专业范围的限制。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二章 技术贸易和技术贸易服务</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七条 设立技术贸易机构或技术贸易服务机构应当具备下列条件：</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）有明确的经营、服务范围和与其相对应的名称；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）有与经营和服务范围、规模相适应的专业技术人员和管理人员；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）有固定的场所、设施和与经营规模相适应的资金；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）有组织章程和服务规范。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第八条 设立技术贸易机构或技术贸易服务机构应向市或县（市）、区科学技术行政部门提出申请，经审查批准后凭核定的《技术贸易证书》到所在地工商行政管理部门登记注册，凭营业执照到税务部门办理税务登记。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第九条 技术贸易机构或技术贸易服务机构合并、分立及变更主要登记事项的，应按原审批程序办理登记；撤销的，应到税务部门、工商行政管理部门办理注销登记，并报技术市场管理部门备案。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十条 申请从事技术经纪活动的个人，应当向市工商行政管理部门申请经纪资格证书，凭经纪资格证书到市技术市场管理部门申请专业技术资格考核认定，由工商行政管理部门发给专业经纪资格证书。未取得专业经纪资格证书的，不得从事技术经纪活动。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十一条 技术贸易活动可采取多种形式进行。鼓励科技计划项目采取公开招标，科技项目实行招标的应通过技术市场进行。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">招标应当坚持平等、择优、公正的原则。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十二条 举办技术交易会，须经技术市场主管部门和工商行政管理部门批准，接受有关部门的监督管理。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十三条 在技术贸易和技术贸易服务活动中，禁止下列行为：</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）窃取或者侵占他人拥有的技术秘密；</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）以欺骗、胁迫等手段从事技术贸易和技术贸易服务活动。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十四条 为技术贸易提供服务的，按约定收取服务费。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十五条 从事技术贸易和技术贸易服务活动，应使用由市税务部门监制的“技术贸易专用发票”。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三章 技术合同管理</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十六条 技术合同实行认定登记制度。技术合同订立后，由技术贸易的卖方向技术市场管理部门申请认定登记，经审查和认定，对符合条件的，在5日内予以登记，对不予登记有异议的，可向技术市场主管部门申请复核。同一项技术合同不得重复登记。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">技术合同登记可以收取技术合同登记工本费，收费标准按国家和省的有关规定执行。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十七条 技术合同的价款、报酬和使用费，应当根据技术成果的经济效益和社会效益、研究开发技术的成本、技术成果的工业化开发程度、当事人享有的权益和承担的责任，由当事人协商议定。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">技术合同的价款、报酬或使用费结算支付办法，按国家有关规定执行。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十八条 技术合同的变更、解除，由申请认定登记的卖方在变更、解除之日起30日内，到技术市场管理部门备案。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十九条 对非法垄断技术、妨碍技术进步、侵害他人权益的技术合同，由市工商行政管理部门委托市技术市场管理部门作出结论后，再行处理。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十条 技术合同发生争议时，当事人可以根据有关法律、法规，采取协商、调解或仲裁、诉讼方式解决。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四章 扶持和奖励</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十一条 经认定登记的技术合同，企事业单位可以从技术贸易的纯收入中提取25%至40%，在企事业单位统一组织下，业余时间从事技术贸易活动的，可从纯收入中提取60%至80%，作为科技人员的酬金。科技人员个人在完成本职工作和不侵犯本单位经济、技术权益的前提下，从事业余技术贸易活动，依法纳税后，收入全部归己，其中使用单位资料、设备、材料的，由所在单位核收相应费用。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">酬金费用的列支，按照《中华人民共和国技术合同法》及有关规定执行，不计入单位工资总额。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十二条 经认定登记的技术合同，买方在三年内可从投产项目的新增利润中每年一次性提取3%至5%的费用，奖励实施该项目有贡献的科技人员。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十三条 技术贸易一次性提取的酬金，作为劳务报酬所得，应当按年计算、分月计征个人所得税。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十四条 经认定登记的技术合同，当事人可以享受国家和省、市有关税收方面的优惠政策，有关部门依据市技术市场管理部门的登记证明办理手续。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十五条 市人民政府设立技术市场基金，促进技术市场的发展。基金的具体管理办法，由市人民政府制定。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十六条 市人民政府设立技术市场金桥奖，表彰和奖励在技术贸易和技术贸易服务活动中做出突出贡献的单位和个人。奖励的具体办法，由市人民政府制定。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五章 法律责任</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十七条 违反本条例第八条、第十条规定，未经批准设立的技术贸易机构和技术贸易服务机构，以及未取得经纪资格证书和专业经纪资格证书从事技术贸易和技术贸易服务活动的，由有关部门按照有关规定没收违法所得，并可处以违法所得一倍以下的罚款。未办理工商登记注册的，由工商行政管理部门依法处罚。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十八条 违反本条例第十二条规定，未经批准或者未按批准规定举办技术交易会的，由技术市场管理部门责令改正或停办，情节严重的，处以500元以上5000元以下的罚款；利用技术交易会从事非法经营活动的，由工商行政管理部门依法处罚。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十九条 违反本条例第十三条规定，侵犯他人技术权益的，由技术市场管理部门责令其停止侵害，赔偿损失。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十条 违反本条例第十六条规定，以非技术冒充技术骗取登记证明的，除没收非法所得外，并由市技术市场管理部门处以200元以上2000元以下罚款。构成犯罪的，依法追究刑事责任。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十一条 技术市场管理人员玩忽职守、徇私舞弊、收受贿赂、滥用职权的，由市技术市场管理部门或者有关主管部门给予行政处分；构成犯罪的，依法追究刑事责任。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十二条 当事人对行政处罚决定不服的，可按《行政复议条例》、《行政诉讼法》的规定申请复议，提起诉讼，逾期不申请 复议、不起诉又不履行处罚决定的，由作出行政处罚决定的机关申请人民法院强制执行。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六章 附则</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十三条 本条例应用中的具体问题由市人民政府负责解释。</span></p><p style=\"text-indent: 36px;line-height: 30px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三十四条 本条例自1996年11月15日起施行</span></p><p><br/></p>', '0', '0', '1', '1996-11-11 00:00:00', '1', '2017-11-13 14:07:15', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:34:56', '0', '');
INSERT INTO `site_policy_rule` VALUES ('7f8e2e38b045422bafbacfce680e9d58', 'ZCFG_7f8e2e38b045422bafbacfce680e9d58', '国务院关于国家重大科研基础设施和大型科研仪器向社会开放的意见', '0', '国务院', '<p style=\"text-align: center;\"><span style=\"font-family: 宋体; font-size: 24px;\">国务院关于国家重大科研基础设施和</span></p><p style=\"text-align: center;\"><span style=\"font-family: 宋体; font-size: 24px;\">大型科研仪器向社会开放的意见</span></p><p style=\"text-align: center;\"><span style=\"font-family:宋体\">国发〔</span>2014<span style=\"font-family:宋体\">〕</span>70<span style=\"font-family:宋体\">号</span></p><p>&nbsp;</p><p><span style=\"font-family:宋体\">&nbsp;&nbsp;&nbsp;&nbsp;各省、自治区、直辖市人民政府，国务院各部委、各直属机构：</span></p><p><span style=\"font-family:宋体\">　　国家重大科研基础设施和大型科研仪器（以下称科研设施与仪器）是用于探索未知世界、发现自然规律、实现技术变革的复杂科学研究系统，是突破科学前沿、解决经济社会发展和国家安全重大科技问题的技术基础和重要手段。近年来，科研设施与仪器规模持续增长，覆盖领域不断拓展，技术水平明显提升，综合效益日益显现。同时，科研设施与仪器利用率和共享水平不高的问题也逐渐凸显出来，部分科研设施与仪器重复建设和购置，存在部门化、单位化、个人化的倾向，闲置浪费现象比较严重，专业化服务能力有待提高，科研设施与仪器对科技创新的服务和支撑作用没有得到充分发挥。为加快推进科研设施与仪器向社会开放，进一步提高科技资源利用效率，现提出以下意见。</span></p><p><span style=\"font-family:宋体\">　　一、总体要求</span></p><p><span style=\"font-family:宋体\">　　（一）指导思想。以邓小平理论、“三个代表”重要思想、科学发展观为指导，深入贯彻党的十八大和十八届二中、三中、四中全会精神，认真落实党中央和国务院的决策部署，围绕健全国家创新体系和提高全社会创新能力，通过深化改革和制度创新，加快推进科研设施与仪器向高校、科研院所、企业、社会研发组织等社会用户开放，实现资源共享，避免部门分割、单位独占，充分释放服务潜能，为科技创新和社会需求服务，为实施创新驱动发展战略提供有效支撑。</span></p><p><span style=\"font-family:宋体\">　　（二）主要目标。力争用三年时间，基本建成覆盖各类科研设施与仪器、统一规范、功能强大的专业化、网络化管理服务体系，科研设施与仪器开放共享制度、标准和机制更加健全，建设布局更加合理，开放水平显著提升，分散、重复、封闭、低效的问题基本解决，资源利用率进一步提高。</span></p><p><span style=\"font-family:宋体\">　　（三）基本原则。</span></p><p><span style=\"font-family:宋体\">　　制度推动。制定促进科研设施与仪器开放的管理制度和办法，明确管理部门和单位的责任，理顺开放运行的管理机制，逐步纳入法制化轨道，推动非涉密和无特殊规定限制的科研设施与仪器一律向社会开放。</span></p><p><span style=\"font-family:宋体\">　　信息共享。搭建统一的网络管理平台，实现科研设施与仪器配置、管理、服务、监督、评价的全链条有机衔接。</span></p><p><span style=\"font-family:宋体\">　　资源统筹。既要盘活存量，统筹管理，挖掘现有科研设施与仪器的潜力，促进利用效率最大化；又要调控增量，合理布局新增科研设施与仪器，以开放共享推动解决重复购置和闲置浪费的问题。</span></p><p><span style=\"font-family:宋体\">　　奖惩结合。建立以用为主、用户参与的评估监督体系，形成科研设施与仪器向社会服务的数量质量与利益补偿、后续支持紧密挂钩的奖惩机制。</span></p><p><span style=\"font-family:宋体\">　　分类管理。对于不同类型的科研设施与仪器，采取不同的开放方式，制定相应的管理制度、支撑措施及评价办法。</span></p><p><span style=\"font-family:宋体\">　　（四）适用范围。科研设施与仪器包括大型科学装置、科学仪器中心、科学仪器服务单元和单台套价值在</span>50<span style=\"font-family:宋体\">万元及以上的科学仪器设备等，主要分布在高校、科研院所和部分企业的各类重点实验室、工程（技术）研究中心、分析测试中心、野外科学观测研究站及大型科学设施中心等研究实验基地。其中，科学仪器设备可以分为分析仪器、物理性能测试仪器、计量仪器、电子测量仪器、海洋仪器、地球探测仪器、大气探测仪器、特种检测仪器、激光器、工艺试验仪器、计算机及其配套设备、天文仪器、医学科研仪器、核仪器、其他仪器等</span>15<span style=\"font-family:宋体\">类。</span></p><p><span style=\"font-family:宋体\">　　二、重点措施</span></p><p><span style=\"font-family:宋体\">　　（一）所有符合条件的科研设施与仪器都纳入统一网络平台管理。</span></p><p><span style=\"font-family:宋体\">　　科技部会同有关部门和地方建立统一开放的国家网络管理平台，并将所有符合条件的科研设施与仪器纳入平台管理。科研设施与仪器管理单位（以下简称管理单位）按照统一的标准和规范，建立在线服务平台，公开科研设施与仪器使用办法和使用情况，实时提供在线服务。管理单位的服务平台统一纳入国家网络管理平台，逐步形成跨部门、跨领域、多层次的网络服务体系。</span></p><p><span style=\"font-family:宋体\">　　管理单位建立完善科研设施与仪器运行和开放情况的记录，并通过国家网络管理平台，向社会发布科研设施与仪器开放制度及实施情况，公布科研设施与仪器分布、利用和开放共享情况等信息。</span></p><p><span style=\"font-family:宋体\">　　（二）按照科研设施与仪器功能实行分类开放共享。</span></p><p><span style=\"font-family:宋体\">　　对于大型科学装置、科学仪器中心，有关部门和管理单位要将向社会开放纳入日常运行管理工作。对于科学仪器服务单元和单台套价值在</span>50<span style=\"font-family:宋体\">万元及以上的科学仪器设备，科技行政主管部门要加强统筹协调，按不同专业领域或仪器功能，打破管理单位的界限，推动形成专业化、网络化的科学仪器服务机构群。对于单台套价值在</span>50<span style=\"font-family:宋体\">万元以下的科学仪器设备，可采取管理单位自愿申报、行政主管部门择优加入的方式，纳入国家网络管理平台管理。对于通用科学仪器设备，通过建设仪器中心、分析测试中心等方式，集中集约管理，促进开放共享和高效利用。对于拟新建设施和新购置仪器，应强化查重评议工作，并将开放方案纳入建设或购置计划。管理单位应当自科研设施与仪器完成安装使用验收之日起</span>30<span style=\"font-family:宋体\">个工作日内，将科研设施与仪器名称、规格、功能等情况和开放制度提交国家网络管理平台。</span></p><p><span style=\"font-family:宋体\">　　鼓励国防科研单位在不涉密条件下探索开展科研设施与仪器向社会开放服务。</span></p><p><span style=\"font-family:宋体\">　　对于利用科研设施与仪器形成的科学数据、科技文献（论文）、科技报告等科技资源，要根据各自特点采取相应的方式对外开放共享。开放共享情况要作为科技资源建设和科技计划项目管理考核的重要内容。</span></p><p><span style=\"font-family:宋体\">　　（三）建立促进开放的激励引导机制。</span></p><p><span style=\"font-family:宋体\">　　管理单位对外提供开放共享服务，可以按照成本补偿和非盈利性原则收取材料消耗费和水、电等运行费，还可以根据人力成本收取服务费，服务收入纳入单位预算，由单位统一管理。管理单位对各类科研设施与仪器向社会开放服务建立公开透明的成本核算和服务收费标准，行政主管部门要加强管理和监督。对于纳入国家网络管理平台统一管理、享受科教用品和科技开发用品进口免税政策的科学仪器设备，在符合监管条件的前提下，准予用于其他单位的科技开发、科学研究和教学活动。探索建立用户引导机制，鼓励共享共用。</span></p><p><span style=\"font-family:宋体\">　　统筹考虑和严格控制在新上科研项目中购置科学仪器设备。将优先利用现有科研设施与仪器开展科研活动作为各科研单位获得国家科技计划（专项、基金等）支持的重要条件。</span></p><p><span style=\"font-family:宋体\">　　鼓励企业和社会力量以多种方式参与共建国家重大科研基础设施，组建专业的科学仪器设备服务机构，促进科学仪器设备使用的社会化服务。</span></p><p><span style=\"font-family:宋体\">　　（四）建立科研设施与仪器开放评价体系和奖惩办法。</span></p><p><span style=\"font-family:宋体\">　　科技部会同有关部门建立评价制度，制定评价标准和办法，引入第三方专业评估机制，定期对科研设施与仪器的运行情况、管理单位开放制度的合理性、开放程度、服务质量、服务收费和开放效果进行评价考核。评价考核结果向社会公布，并作为科研设施与仪器更新的重要依据。对于通用科研设施与仪器，重点评价用户使用率、用户的反馈意见、有效服务机时、服务质量以及相关研究成果的产出、水平与贡献；对于专用科研设施与仪器，重点评价是否有效组织了高水平的设施应用专业团队以及相关研究成果的产出、水平与贡献。</span></p><p><span style=\"font-family:宋体\">　　管理单位应在满足单位科研教学需求的基础上，最大限度推进科研设施与仪器对外开放，不断提高资源利用率。对于科研设施与仪器开放效果好、用户评价高的管理单位，同级财政部门会同有关部门根据评价考核结果和财政预算管理的要求，建立开放共享后补助机制，调动管理单位开放共享积极性。对于不按规定如实上报科研设施与仪器数据、不按规定公开开放与利用信息、开放效果差、使用效率低的管理单位，科技行政主管部门会同有关部门在网上予以通报，限期整改，并采取停止管理单位新购仪器设备、在申报科技计划（专项、基金等）项目时不准购置仪器设备等方式予以约束。对于通用性强但开放共享差的科研设施与仪器，结合科技行政主管部门的评价考核结果，相关行政主管部门和财政部门可以按规定在部门内或跨部门无偿划拨，管理单位也可以在单位内部调配。科技行政主管部门、相关行政主管部门要建立投诉渠道，接受社会对科研设施与仪器调配的监督。</span></p><p><span style=\"font-family:宋体\">　　（五）加强开放使用中形成的知识产权管理。</span></p><p><span style=\"font-family:宋体\">　　用户独立开展科学实验形成的知识产权由用户自主拥有，所完成的著作、论文等发表时，应明确标注利用科研设施与仪器情况。加强网络防护和网络环境下数据安全管理，管理单位应当保护用户身份信息以及在使用过程中形成的知识产权、科学数据和技术秘密。</span></p><p><span style=\"font-family:宋体\">　　（六）强化管理单位的主体责任。</span></p><p><span style=\"font-family:宋体\">　　管理单位是科研设施与仪器向社会开放的责任主体，要强化法人责任，切实履行开放职责，自觉接受相关部门的考核评估和社会监督。要根据科研设施与仪器的类型和用户需求，建立相应的开放、运行、维护、使用管理制度，保障科研设施与仪器的良好运行与开放共享。要落实实验技术人员岗位、培训、薪酬、评价等政策。科学仪器设备集中使用的单位，要建立专业化的技术服务团队，不断提高实验技术水平和开放水平。</span></p><p><span style=\"font-family:宋体\">　　各行政主管部门要切实履行对管理单位开放情况的管理和监督职责，实施年度考核，把开放水平和结果作为年度考核的重要内容。</span></p><p><span style=\"font-family:宋体\">　　三、组织实施和进度安排</span></p><p><span style=\"font-family:宋体\">　　改革分阶段实施，在</span>2014<span style=\"font-family:宋体\">年科技部会同有关部门和地方启动现有科研设施与仪器的资源调查，摸清家底，建立科研设施与仪器资源数据库的基础上，逐步实现科研设施与仪器向社会开放的全覆盖。</span></p><p>　　2015<span style=\"font-family:宋体\">年，科技部会同有关部门充分利用现有全国大型科学仪器设备协作共用平台，启动统一开放的科研设施与仪器国家网络管理平台建设，年底前基本建立。遴选状态良好、管理制度健全、开放绩效突出并具有代表性的科研设施与仪器，先行开展向社会开放试点。制定管理单位服务平台的标准规范，制定并发布统一的评价办法，开展评价考核工作，财政部门会同有关部门建立开放共享后补助机制。完善科技部、财政部、教育部、中科院等相关部门对新购科学仪器设备的查重和联合评议机制。所有管理单位制定完善的开放制度，并在国家网络管理平台上发布。</span></p><p>　　2016<span style=\"font-family:宋体\">年，科技部会同有关部门和地方建成覆盖各类科研设施与仪器、统一规范、功能强大的专业化、网络化国家网络管理平台，将所有符合条件的科研设施与仪器纳入平台管理。所有管理单位按照统一的标准规范建成各自的服务平台，明确服务方式、服务内容、服务流程，纳入国家网络管理平台，形成跨部门、跨领域、多层次的网络服务体系。所有管理单位在国家网络管理平台上发布符合开放条件的科研设施与仪器开放清单和开放信息。</span></p><p>　　2017<span style=\"font-family:宋体\">年，科技行政主管部门对管理单位的科研设施与仪器向社会开放情况进行评价考核，并向社会公布评价考核结果。</span></p><p>&nbsp;</p><p><span style=\"font-family:宋体\">　　　　　　　　　　　　　　　　　　　　　　　　　　　　　　国务院</span></p><p>　　　　　　　　　　　　　　　　　　　　　　　　　　　　2014<span style=\"font-family:宋体\">年</span>12<span style=\"font-family:宋体\">月</span>31<span style=\"font-family:宋体\">日</span></p><p><br/></p>', '0', '0', '1', '2014-12-31 00:00:00', '1', '2017-11-13 13:22:28', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:37:11', '0', '');
INSERT INTO `site_policy_rule` VALUES ('8ecb358f2ed44f9f9e349745e36cb4b7', 'ZCFG_8ecb358f2ed44f9f9e349745e36cb4b7', '2019年创新券办法补充规定', '1', '沈阳市科学技术局', '<p>2019年创新券办法补充规定，详情参考附件！</p>', '0', '0', '', '2019-05-16 00:00:00', '1', '2019-05-16 15:21:32', '1', '2019-05-16 15:21:32', '0', '');
INSERT INTO `site_policy_rule` VALUES ('95097e2d8471452b901b57bfc1ae4db3', 'ZCFG_95097e2d8471452b901b57bfc1ae4db3', '沈阳市科技创新券实施办法（暂行）', '3', '沈阳市科学技术局', '<p><span style=\"font-size:21px;font-family:仿宋\"> </span><span style=\"font-size:21px;font-family:仿宋\">为深入实施创新驱动发展战略，营造“大众创业，万众创新”的良好环境，创新财政科技资金使用方式，激发科技型中小微企业（简称企业）和创业团队的科技创新活力，降低科技创新成本，根据辽宁省科技厅和辽宁省财政厅《关于实施科技创新券制度的若干意见（试行）》（辽科发〔2015〕28号）相关规定，结合我市实际情况，制定本办法。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family: 黑体\">第一章&nbsp;&nbsp;总则</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第一条&nbsp; 沈阳市科技创新券（简称创新券）是免费向我市企业和创业团队发放的，用于向沈阳市科技条件平台服务机构（简称服务机构）购买科技服务的政府无偿补助经费。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第二条&nbsp; 沈阳市科学技术局（简称市科技局）在市科技创新专项资金中安排创新券补助经费。创新券实行总量控制，采用电子券形式事前申请事后补助的支持方式。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第三条&nbsp; 创新券的使用和管理应遵循突出重点、公开透明、专款专用、开放共赢的原则。</span></p><p style=\"text-align:center;text-indent:43px\"><span style=\"font-size:21px;font-family:黑体\">第二章&nbsp; 工作机构及职责</span><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family:仿宋\">第四条&nbsp; 市科技局是创新券的管理机构，负责创新券的政策制定、审核管理、监督指导、绩效评价及协调解决创新券实施过程中的重大事项。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第五条&nbsp; 市科技局以政府购买公共服务的形式委托沈阳科技资源共享服务中心（简称共享中心）负责创新券的年度预算编制和受理创新券的申请、发放、兑付和统计等工作。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第六条&nbsp; 市科技局指导各区、县（市）科技局做好辖区创新券政策的宣传发动和组织实施工作。鼓励各区、县（市）科技局结合实际制定辖区创新券的优惠政策。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family:仿宋\">第七条&nbsp; 经市科技局认定的服务机构是创新券的服务机构。创新券服务机构要接受沈阳市科技条件平台（简称条件平台）的管理，积极为用户提供创新服务。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">创新券服务机构的基本条件：</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（一）提供大型科学仪器设备共享服务的单位或机构。拥有单台（套）原值在20万元以上的仪器设备数量5台（套）以上，或拥有特殊用途的精密、稀缺科学仪器设备，性能指标达到我市现有装备的先进水平，运行正常、数据准确、加工精度高，配有熟练操作人员，能向社会提供共享服务。具备专业分析检测相关的技术咨询、培训的能力。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（二）提供专业技术服务的单位或机构。具备稳定的服务机构管理人员，有明确的专业服务方向，具有一定的专业技术管理优势和较强的组织协调能力，配备高级职称的专业技术带头人和技术服务人员，其中，专职技术服务人员不少于10人，硕士学位或中级职称的人员不低于30%。有较好的前期工作基础，有对外提供专业技术服务的案例，有相对稳定的用户群体。具备承担国家、省、市级科研项目的能力。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（三）提供科技文献、科学数据等科技信息服务的单位或机构。拥有专职服务人员5名（含）以上，拥有一定规模的数字化文献、情报、信息资源，可为用户提供文献检索、全文下载、电子图书在线阅读、科技查新与情报信息等服务，年累计服务企业10家（含）以上。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（四）提供知识产权服务的单位或机构。拥有执业资格人员10名以上，具备信息数据分析、挖掘、预警、知识产权战略研究能力，有专业服务案例，年累计服务企业5家（含）以上。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（五）其他可对外提供各类公共科技服务的单位或机构。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family: 黑体\">第三章&nbsp; 使用范围与标准</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第八条&nbsp; 创新券可用于服务机构提供的研发、检测、知识产权等科技服务，服务范围以条件平台每年发布的科技服务目录为准。创新券不支持按照法律法规或者强制性标准要求必须开展的强制检测和法定检测等商业活动。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第九条&nbsp; 市科技局定期发布开展创新券申领和兑付工作的通知，共享中心每季度对前一季度创新券使用情况开展预审核和统计。创新券有效期为12个月，逾期作废。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十条&nbsp; 企业是创新券申领和兑付的主体单位。创业团队由所在的科技企业孵化器（简称孵化器）或众创空间代为申领和兑付。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">符合条件的创新券兑付额度：技术服务金额在 10万元及以下的部分按照不超过60%的比例核定支持；超过10万元的部分按照最高不超过30%的比例核定支持。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family: 黑体\">第四章&nbsp; 审核申领</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十一条&nbsp; 申领创新券的企业应符合以下条件：</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">（一）在沈阳市登记注册，并在本市纳税的企业。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">（二）符合《工业和信息化部关于印发中小企业划型标准规定的通知》（工信部联企业〔2011〕300号）规定标准。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">（三）企业与开展合作的服务机构无隶属、共建、产权纽带等关联关系。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十二条&nbsp; 申领创新券的创业团队应同时符合以下条件：</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">（一）不具备独立法人资格，尚未注册企业。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">（二）入住市级以上孵化器或众创空间。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">（三）创业项目须具有产品研发转化所需的测试或研发工作（不包括仅限创业文本策划的项目）。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十三条&nbsp; 企业申领创新券需登录沈阳市科技条件平台信息系统（简称信息系统）<a href=\"http://www.syskjj.gov.cn/webManage_yhdl.do%EF%BC%89%EF%BC%88%E4%BB%A5%E4%B8%8B%E7%AE%80%E7%A7%B0%E7%AE%A1%E7%90%86%E5%B9%B3%E5%8F%B0%EF%BC%89%EF%BC%8C%E5%A1%AB%E5%86%99%E3%80%8A%E6%B2%88%E9%98%B3%E5%B8%82%E7%A7%91%E6%8A%80%E5%88%9B%E6%96%B0%E5%88%B8%E7%94%B3%E8%AF%B7%E4%B8%8E%E5%AE%A1%E6%89%B9%E8%A1%A8%E3%80%8B%EF%BC%8C%E5%B9%B6%E4%B8%8A%E4%BC%A0%E4%BC%81%E4%B8%9A%E8%90%A5%E4%B8%9A%E6%89%A7%E7%85%A7%E3%80%81%E7%A8%8E%E5%8A%A1%E7%99%BB%E8%AE%B0%E8%AF%81%E5%92%8C%E6%8A%80%E6%9C%AF%E5%90%88%E5%90%8C%E3%80%82\"><span style=\"color: windowtext\">，填报《沈阳市科技创新券申领表（企业）》，上传加盖公章的企业营业执照副本扫描件。</span></a></span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">创业团队申请创新券由所在孵化器或众创空间登录信息系统，统一填报《沈阳市科技创新券申领推荐表（创业团队）》，上传孵化器或众创空间加盖公章的营业执照副本和创业团队负责人的身份证扫描件。</span></p><p style=\"margin-left:0;text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\">第十四条 </span><span style=\"font-size:21px;font-family: 仿宋\">&nbsp;</span><span style=\"font-size:21px;font-family:仿宋\">共享中心对申报材料及证明材料进行核准，通过条件平台发放创新券。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">在一个创新券有效期内，企业申领创新券的最高额度不超过20万元，分两次申领，第一次申领额度10万元使用完毕后，可再次申领10万元。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family:仿宋\">在一个创新券有效期内，创业团队申领创新券的最高额度不超过5万元，分两次申领，第一次申领额度2.5万元使用完毕后，可再次申领2.5万元。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family:仿宋\">企业和创业团队在递交申领材料15个工作日后，通过信息系统查询申领结果。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family: 黑体\">第五章&nbsp; 资金拨付</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十五条&nbsp; 企业和创业团队开展科技服务需先行支付服务费用，完成合同约定服务内容后，须及时在信息系统输入创新券券号并上传相关证明材料，作为办理兑付的凭据。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十六条&nbsp;\r\n \r\n企业申请兑付创新券应登录信息系统，填报《沈阳市科技创新券兑付申请表（企业）》，上传技术合同、银行转账支付凭证、发票等。以下材料若存在需一并上传：服务结果证明(包括检测报告、技术解决方案、知识产权服务报告等)、创新成果证明（包括专利、著作权、新产品、新工艺、样机等证明材料）。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">创业团队申请兑付创新券由所在孵化器或众创空间登录信息系统，统一填报《沈阳市科技创新券兑付申请表（创业团队）》，提供材料内容与企业相同。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十七条&nbsp; 共享中心每年集中受理创新券兑付工作，组织专家对申请材料进行评审，评审结果公示后，履行政府审批程序，拨付下达资金。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family: 黑体\">第六章&nbsp;&nbsp;评价和监督</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十八条&nbsp; 市科技局定期对共享中心、区、县（市）科技局和服务机构创开展新券工作情况进行评价，评价结果作为绩效考核的重要依据。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十九条&nbsp;\r\n \r\n创新券不得转让、赠送、买卖。对虚报、冒领、截留等行为，一经查实，依据辽宁省科技厅和辽宁省财政厅《关于实施科技创新券制度的若干意见（试行）》（辽科发〔2015〕28号）和《沈阳市财政科技创新专项资金管理办法》（沈财企〔2017〕439号）相关规定执行，涉嫌违法的，交由司法机关依法处理。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:黑体\">&nbsp;</span><span style=\"font-size:21px;font-family:黑体\">第七章&nbsp; 附则</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第二十条&nbsp; 本办法由沈阳市科技局负责解释，执行期自2018年1月1日至2019年12月31日止。</span></p><p><br/></p>', '0', '0', '1', '2017-11-07 00:00:00', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-27 15:12:09', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-27 15:12:09', '0', '');
INSERT INTO `site_policy_rule` VALUES ('a07e2a41d257455db193b74d85382ab5', 'ZCFG_a07e2a41d257455db193b74d85382ab5', '沈阳市高层次人才创业扶持资金管理细则', '2', '沈阳市科学技术局', '<p style=\"background: white; text-align: center;\"><br/></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第一章&nbsp;总则</span></strong></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第一条&nbsp;根据《中共沈阳市委 沈阳市人民政府关于实施“盛京人才”战略打造具有国际竞争力人才高地的意见》（沈委发〔2015〕9号）文件精神，为支持各类高层次人才创业发展，设立“沈阳市高层次人才创业扶持资金”（以下简称“扶持资金”）。为科学合理使用扶持资金，制定本细则。</span></p><p style=\"text-indent: 47px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第二条&nbsp;&nbsp;扶持资金是市本级财政预算安排的，</span><span style=\"font-size: 19px;font-family: 宋体\">专项用于<span style=\"letter-spacing:1px\">沈阳市高层次人才创业扶持的资金，原则上不与我市其他专项资金重复支持。</span></span></p><p style=\"text-indent: 47px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第三条&nbsp;扶持资金遵循“公平公开、择优扶持、科学管理、加强监督”的原则，由市科技局负责管理和组织实施。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第二章&nbsp;扶持对象、方式及条件</span></strong></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第四条&nbsp;重点扶持领办、创办、合资兴办经济实体或以技术入股等方式创业的高层次人才和团队（三人以上，下同）。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第五条&nbsp;扶持方式为无偿资助。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第六条&nbsp;扶持期限为初创期（成立之日起3年内），最高连续扶持时限为3年。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第七条&nbsp;人才或团队应具备以下条件：</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（一）符合《沈阳市高层次人才认定办法》（沈人社发〔2016〕54号）规定条件或经市人社局认定。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（二）领办或独立创办企业的。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（三）与他人合伙创办企业且占股比例不少于20%的。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（四）以专利技术所有者身份入股企业且占股比例不少于10%的。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（五）团队成员要符合上述第一款条件和第二、三、四款条件之一。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第八条&nbsp;人才和团队以领办、创办、合资兴办或技术入股的企业为依托进行申报。申报的企业应具备以下基本条件：</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（一）沈阳行政区域内注册、具有独立法人资格的企业，具有健全的企业管理和财务管理制度，无不良信用记录。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（二）企业成立年限在3年以内。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第九条&nbsp;人才或团队应依托所创办企业正在进行的科技研发、技术应用、技术转移、成果转化、技术服务等项目进行申报。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第三章&nbsp;扶持范围及标准</span></strong></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十条&nbsp;重点扶持在高端装备与先进制造、新一代信息技术、新能源、新材料、生物医药、资源与环境、人口与健康、城市建设与城镇化、现代服务业、现代农业等领域创办企业的人才和团队。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十一条&nbsp;扶持额度为10—20万，团队形式申报的扶持额度上浮50%，最高不超过20万元。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（一）领办、独立创办企业的扶持额度为20万元。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（二）参与创办、合资兴办或技术入股企业的扶持额度为10万元。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第四章&nbsp;组织实施及要求</span></strong></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十二条&nbsp;申报由人才或团队创办的企业负责组织。</span></p><p style=\"text-indent: 42px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十三条&nbsp;通过沈阳市科技创新管理平台申报，经初审后将将纸质申报材料及相关附件报送市科技局。市科技局组织专家进行评审，对评审通过的项目予以考察，依据评审、考察结果择优推荐，经局长办公会议审议通过向社会公示且无异议，会同市人才办、市财政局报市政府审批后拨付扶持资金。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第五章&nbsp;管理监督</span></strong></p><p style=\"text-indent: 42px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十四条&nbsp;扶持资金依照《沈阳市盛京人才计划专项资金管理办法》（沈人才〔2016〕4号）有关规定支出。</span></p><p style=\"text-indent: 42px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十五条&nbsp;获得补助资金单位每半年向市科技局提交补助资金执行情况报告。补助资金必须设立专帐进行财务核算，专款专用。</span></p><p style=\"text-indent: 42px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十六条&nbsp;&nbsp;对伪造相关证明文件、虚报、冒领、截留、挪用、挤占扶持资金等违纪行为，除追缴资金外，三年内取消该单位盛京人才计划资金申报资格。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><span style=\"font-size: 19px;font-family: 宋体\">第六章&nbsp;附则</span></p><p style=\"text-indent: 47px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十七条&nbsp;本细则由市科技局负责解释。</span></p><p style=\"text-indent: 47px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十八条&nbsp;本细则自发布之日起实施。</span></p><p><br/></p>', '0', '0', '1', '2017-03-23 00:00:00', '1', '2017-11-13 13:52:18', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:45:34', '0', '');
INSERT INTO `site_policy_rule` VALUES ('a99b7ac762604597a2c40b18f6166553', 'ZCFG_a99b7ac762604597a2c40b18f6166553', '沈阳市中青年科技创新人才支持计划管理细则', '2', '沈阳市科学技术局', '<p style=\"text-align: center;\"><br/></p><p style=\"text-align:center;line-height:20px;background:white\"><span style=\"font-size: 19px;font-family: 宋体\">第一章&nbsp;总则</span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第一条</span><span style=\"font-size: 19px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">根据《中共沈阳市委沈阳市人民政府关于实施“盛京人才”战略打造具有国际竞争力人才高地的意见》（沈委发〔2015〕9号）精神，为支持具有优秀科技创新潜能中青年科技创新人才，设立“沈阳市中青年科技创新人才支持计划”。为科学合理使用支持资金，制定本细则。</span></p><p style=\"text-indent: 47px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第二条&nbsp;&nbsp;沈阳市中青年科技创新人才支持资金（以下简称“支持资金”）是市本级财政预算安排的，</span><span style=\"font-size: 19px;font-family: 宋体\">专项用于<span style=\"letter-spacing:1px\">沈阳市中青年科技创新人才支持计划的资金，原则上不与我市其他专项资金重复支持。</span></span></p><p style=\"text-indent: 43px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第三条&nbsp;&nbsp;支持资金遵循“公平公开、择优支持、科学管理、加强监督”的原则，由市科技局负责管理和组织实施。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第二章&nbsp;支持对象、方式及条件</span></strong></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第四条&nbsp;重点支持具有特殊优秀的科技创新潜能、课题研究方向和技术路线具有重要创新和应用前景、年龄在45周岁以下的中青年科技创新人才和团队。其中35周岁以下青年科技创新人才和团队占一定数量。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第五条&nbsp;支持方式为一次性无偿资助。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第六条&nbsp;人才或团队应具备以下条件：</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（一）符合《沈阳市高层次人才认定办法》（沈人社发〔2016〕54号）规定条件或经市人社局认定。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（二）本单位在岗、在聘人员。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（三）主持或以主要研发者身份参与创新项目研发工作。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（四）以团队形式申报的，团队成员应符合上述（一）、（二）、（三）款之条件。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（五）人才和团队成员年龄应在45周岁以下。年龄按申报起始时间计算，精确到月份。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第七条&nbsp;申报单位应是在沈阳行政区域内注册、具有独立法人资格的企事业单位，具有健全的管理和财务制度，无不良信用记录。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第八条&nbsp;人才或团队应依托正在进行的科技研发、推广应用等项目进行申报。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第三章&nbsp;支持范围及标准</span></strong></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第九条&nbsp;重点支持在高端装备与先进制造、新一代信息技术、新能源、新材料、生物医药、资源与环境、人口与健康、城市建设与城镇化、现代服务业、现代农业等领域创新的人才和团队。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体\">第十条&nbsp;<span style=\"letter-spacing: 1px\">每个项目年度支持额度为10—100万元，团队形式申报的补助额度上浮50%，最高不超过100万元。</span></span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十一条&nbsp;支持资金额度依据创新项目层次、创新水平、应用前景高低分为10万元、20万元、40万元、70万元和100万元五档，根据专家评审结果和支持建议加以确定。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第四章&nbsp;组织实施及要求</span></strong></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十二条&nbsp;申报由人才或团队所在单位负责组织。</span></p><p style=\"text-indent: 42px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十三条&nbsp;通过沈阳市科技创新管理平台申报，经初审后将将纸质申报材料及相关附件报送市科技局。市科技局组织专家进行评审，对评审通过的项目予以考察，依据评审、考察结果择优推荐，经局长办公会议审议通过向社会公示且无异议，会同市人才办、市财政局报市政府审批后拨付支持资金。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体\">第五章&nbsp;管理监督</span></strong></p><p style=\"text-indent: 47px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十四条&nbsp;支持资金依照《沈阳市盛京人才计划专项资金管理办法》（沈人才〔2016〕4号）有关规定支出。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十五条</span><span style=\"font-size: 19px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">获得支持资金单位每半年向市科技局提交支持资金执行情况报告。支持资金必须设立专帐进行财务核算，专款专用。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十六条</span><span style=\"font-size: 19px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">对伪造相关证明文件、虚报、冒领、截留、挪用、挤占支持资金等违纪行为，除追缴资金外，三年内取消该单位盛京人才计划资金申报资格。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体\">第六章&nbsp;附则</span></strong></p><p style=\"text-indent: 47px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十七条&nbsp;本细则由市科技局负责解释。</span></p><p><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">&nbsp;&nbsp;&nbsp; 第十八条&nbsp;&nbsp;本细则自发布之日起实施。</span></p><p><br/></p>', '0', '0', '1', '2017-03-23 00:00:00', '1', '2017-11-13 14:25:50', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:48:05', '0', '');
INSERT INTO `site_policy_rule` VALUES ('b27d2fcc10b344cbb92a9f47a5ef4b66', 'ZCFG_b27d2fcc10b344cbb92a9f47a5ef4b66', '辽宁省人民政府关于加快构建大众创业 万众创新支撑平台的实施意见', '1', '辽宁省人民政府', '<p style=\"margin-bottom: 16px; text-align: center;\"><br/></p><p style=\"margin-bottom: 16px\"><span style=\"font-size: 16px;font-family: 宋体\">各市人民政府，省政府各厅委、各直属机构：</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　为贯彻落实《国务院关于加快构建大众创业万众创新支撑平台的指导意见》（国发〔2015〕53号）精神，加快构建众创、众包、众扶、众筹（以下统称四众）等大众创业万众创新支撑平台，推进平台持续健康发展，培育“互联网+”新业态新模式，促进创业创新，现提出如下实施意见。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;一、总体要求</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　立足我省实际，按照“坚持市场主导、包容创业创新、公平有序发展、优化治理方式、深化开放合作”的原则，推动线上与线下相结合、传统与新兴相结合、引导与规范相结合。大力发展专业空间众创，鼓励推进网络平台众创，培育壮大企业内部众创，释放创业创新能量；广泛应用研发创意众包，大力实施制造运维众包，加快推广知识内容众包，鼓励发展生活服务众包，激发创业创新活力；积极推广社会公共众扶，鼓励倡导企业分享众扶，大力支持公众互助众扶，集聚创业创新活力；积极开展实物众筹，稳步推进股权众筹，规范发展网络借贷，拓展创业创新融资。营造四众发展良好环境，提高资源配置效率，在更大范围、更高层次、更深程度上推进大众创业、万众创新，加快构建大众创业万众创新支撑平台。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;二、主要任务</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（一）完善市场准入制度。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;1．加大金融政策支持力度。落实创业担保贷款政策，扶持政策对象创业就业。推动设立第三方支付法人机构，对申请支付业务许可证的机构进行材料审核和业务验收。（人民银行沈阳分行牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;2．优化经营环境。按“先照后证”的原则，将“道路运输站（场）经营”、“机动车维修经营”、“水路运输服务经营”、“港口经营”等前置审批事项改为后置审批。简化行政审批流程，将水运工程（港口、航道）开工备案下放至市级交通、港口行政主管部门。提高行政审批效率，将公路水运监理企业监理资质等级评定，办理时限由60天压缩至30天；将企业监理人员、检测人员的网上注册、注销、信息变更审核办理时限由20个工作日压缩至15个工作日。在审批需求集中的产业集群建立代办点，积极开展上门对接服务；允许实行集约化和规模化经营、运用卫星定位系统管理、安全生产达标的道路运输企业，按照国家和行业有关标准，结合车辆类别、行驶里程、道路条件和使用年限等因素，自行确定车辆维护周期。研究解决城市配送车辆“最后一公里”通行难问题。（省交通厅牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;3．提高运输服务水平。在保证安全的前提下依托互联网技术，逐步开放交通运输信息资源，鼓励和支持社会力量参与交通物流和信息服务市场开发。积极探索政企合作新模式，建立物流和公众出行服务平台。（省交通厅牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;4．简化快递业务经营许可程序。优化快递业务经营许可工作，压缩工作时限、提高工作效率。（省邮政管理局牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;（二）建立健全监管制度。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;5．推动四众标准制定。放开众创各业态企业标准备案管理，提供四众平台相关国家标准信息服务，进一步提高服务水平。（省质监局牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;6．大力推广网上办税服务，积极推广电子发票应用。（省国税局、省地税局牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;7．完善社会保险制度，进一步加强监管，切实保障职工合法权益。（省人力资源社会保障厅牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;（三）提升公共服务水平。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;8．深入推进“三证合一”“一照一码”登记制度改革。贯彻落实《辽宁省人民政府关于全面实施“一照一码”登记制度改革的意见》（辽政发〔2015〕39号），加快清理与‘三证合一’登记制度相冲突的地方性法规、规章及规范性文件，组织实施“一照一码”登记制度改革。（省工商局、省政府法制办牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;9．简化住所（经营场所）登记手续。贯彻落实《辽宁省简化市场主体住所登记条件暂行规定》（省政府令290号），执行“一址多照”“一照多址”和简化企业提交住所（经营场所）证明材料等政策。支持企业依托众创空间等新型孵化机构集群注册，实行住所（经营场所）报备制度，免予提交场地使用证明等政策措施。试行企业简易注销登记。制定《企业简易注销登记办法（试行）》和《企业简易注销改革试点实施方案》，对未开业企业、无债权债务企业试行简易注销程序，方便其办理退出市场登记。（省工商局牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;10．推进全程电子化登记和电子营业执照应用。依托全省网上审批平台，大力推进以电子营业执照为支撑的网上申请、网上受理、网上审核、网上公示、网上发照等全程电子化登记管理方式，提高市场主体登记管理的信息化、便利化、规范化水平。（省工商局牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;11．加快建立小微企业名录。制定并发布《关于建设全省小微企业名录促进小微企业加快发展的意见》，依托全省企业信用信息公示系统，支持小微企业发展，进一步提高小微企业“成活率”和生存质量，实现对小微企业的精准扶持。力争2016年底前，完成全省小微企业名录系统建设。（省工商局、各有关单位负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;12．推动电子商务产业集聚发展。鼓励结合当地优势产业，建设特色电子商务基地（园区），在中小企业孵化、服务模式创新、公共平台建设、产业链条搭建等方面发挥带动作用，吸引国内外电子商务企业和相关配套企业入驻。鼓励各类电子商务基地（园区）设立电子商务创业园、孵化器，支持大专院校与电商企业共建孵化基地、创业园，鼓励大学毕业生在电子商务领域实现就业创业。开展省级电子商务示范企业创建，支持示范企业建设一批骨干性电商平台，在创新经营模式、整合市场资源、带动流通企业发展等方面发挥引领示范作用。（省商务厅牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;13．加强电子商务培训。结合全省电子商务发展水平和人才需求实际，建设一批省级电子商务培训基地，制定电子商务人才培训工作规划。加强与专业培训机构合作，组织开展针对各级商务主管部门和相关政府部门工作人员、企业人员的电子商务培训。加大对电子商务紧缺人才、高端人才和专业技能人才引进和培养的支持力度。充分发挥高等院校、社会专业培训机构、电子商务企业和行业协会的作用，开展岗位对接，缓解人才供需矛盾。（省商务厅牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;14．营造良好发展环境。充分发挥省内外主流媒体舆论导向作用，加大宣传电子商务宣传力度，普及电子商务应用知识，推广电子商务优秀网站和典型案例，提高社会各界对电子商务普及应用的认知度。（省商务厅牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;15．大力发展创业咖啡、创业梦工厂等众创空间孵化机构，鼓励和支持民营资本、社会力量参与创业孵化基地建设。通过盘活商业用房、闲置厂房等资源提供低成本的创业创新场所，推进政府主导型创业孵化基地（园区）建设。充分利用已有各类园区，建设创业孵化示范基地。积极创建农民工、大学生和退役士兵返乡创业园，推进电子商务平台向基层倾斜，带动城乡网络创业。（省人力资源社会保障厅牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;（四）促进开放合作发展。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;16．鼓励四众平台企业利用全球创新资源，面向国际市场拓展服务。加强国际合作，鼓励小微企业和创业者承接国际业务。（省经合局、省工商局、省发展改革委牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;（五）构建以信用为核心的新型市场监管机制。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;17．加快信用体系建设。做好企业信用信息发布和管理工作。完善辽宁省失信企业黑名单数据库。抓紧出台《辽宁省政府部门公示企业信息管理办法》、《辽宁省失信企业联合惩戒实施办法》。建设全省企业信用信息公示系统数据共享交换平台，为实现政府部门统一公示企业信息和实施失信主体联合惩戒提供技术保障。（省发展改革委、省工商局牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;18．鼓励信用咨询、信用评估、信用担保和信用保险等信用服务业发展。建立健全守信激励机制和失信联合惩戒机制，加大对守信行为的表彰和宣传力度。（省发展改革委、省工商局牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;（六）完善知识产权环境。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;19．开展知识产权执法宣传，组织专项执法行动，打击知识产权侵权假冒行为，营造“双创”良好氛围。（省知识产权局牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;20．大力发展知识产权维权援助体系，积极开展知识产权维权援助活动，主动为企业提供知识产权保护的知识，提供维护知识产权权益的咨询意见，保护知识产权成果，为创业创新提供法律支持。（省知识产权局牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;21．大力宣传著作权法，明晰四众平台在著作权方面的责任、权利和义务。推动版权成果有效运用，切实维护创业创新者权益。建立版权管理长效机制，深入开展打击侵权盗版行动。加强对专利侵权的调处力度，严格保护专利权人合法权益，为创新创业消除后顾之忧。（省新闻出版广电局、省知识产权局牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;（七）加强行业自律规范。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;22．强化行业自律，规范四众从业机构市场行为，保护行业合法权益。完善行业纠纷协调和解决机制，鼓励第三方以及用户参与平台治理。研究建立在线争议解决、现场接待受理、监管部门受理投诉、第三方调解以及仲裁、诉讼等多元化纠纷解决机制。（省交通厅、省邮政管理局、人民银行沈阳分行、省卫生计生委、省教育厅等负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;23．充分发挥行业协会、学会的桥梁纽带作用，开展诚信体系建设工作，营造诚信和谐的行业氛围。整顿市场秩序，加大对违法经营和不规范竞争的治理力度，促进企业之间的业务交流和信息共享，为四众健康持续发展营造良好的市场环境。（省新闻出版广电局牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;（八）保障网络信息安全。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;24．提升四众平台企业技术安全水平，提高发现和有效应对各类网络安全事件能力，确保网络平台安全稳定运行。加强用户资料和交易信息保管，保障信息安全。强化守法、诚信、自律意识，营造诚信规范发展的良好氛围。（省交通厅、省邮政管理局、人民银行沈阳分行、省卫生计生委、省教育厅、省发展改革委、省工商局等负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;&nbsp;（九）落实财政支持政策。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;&nbsp;25．主动适应采用四众模式的小微企业发展要求，不断扩大政府购买服务范围，支持企业发展。（省财政厅牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;&nbsp;26．鼓励有条件的市、县（市、区）支持众创空间内创业企业及团队，对众创空间的房租、宽带接入费用、用于创业服务购置的公共软件、开发工具，以及举办各类创业活动等支出费用给予适当的财政补贴。建立科技创新券制度，为创业企业向高等院校购买科技研发、科研成果等多元创新资源及服务“买单”。（省科技厅牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;&nbsp;（十）实行适用税收政策。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;&nbsp;27．对符合规定条件的年应纳税所得额低于30万元（含30万元）的小型微利企业（包括采取查账征收和核定征收方式的企业），其所得减按50%计入应纳税所得额，按20%的税率缴纳企业所得税，同时延长部分小微企业、个体工商业户等免征增值税、营业税优惠期限至2017年底。对月销售额2万元至3万元的小规模增值税和营业税纳税人，免征增值税和营业税。（省财政厅、省地税局、省国税局等负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;28．全面落实取消非行政许可审批事项，调整更新税务行政审批目录。简化办税流程、扩大即时办结范围、缩短办税时限、精简办理手续。升级纳税申报系统，进行税收优惠政策提示，实现申报自动计税。借力“互联网+”，加强数据分析应用。（省国税局牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;（十一）创新金融服务模式</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;29．运用再贷款、再贴现等货币政策工具。促进信贷资金向小微企业倾斜；广泛开展非金融企业债务融资工具的宣传培训，进一步拓宽小微企业直接融资渠道、扩大融资规模。推进实施小微企业信贷政策导向效果评估工作，鼓励金融机构加大小微企业信贷投放，增强小微企业信贷政策导向力。（人民银行沈阳分行牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;30．积极发展知识产权质押融资，打造线上服务平台，为专利权人提供更优质更便捷的金融服务。设立风险补偿基金，引导并支持各类担保机构为知识产权质押融资提供第三方担保服务，缓解企业短期还贷压力，有效降低银行贷款风险。（省知识产权局牵头负责）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（十二）深化科技体制改革。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;31．推进高等院校、科研院所科技成果使用、处置和收益权管理改革，完善科技人员创业股权激励和分红激励机制。建立健全大型科研仪器设备、科学数据、科技文献等科技基础条件平台向众创空间和创业企业开放机制。依托省级以上工程技术（研究）中心、重点实验室等创新载体，实现创业科技资源开放共享。推动大学科技园建设发展，促进科技成果在大学科技园内转移转化，支持大学科技园内孵化企业创业创新。（省教育厅、省科技厅牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　&nbsp;&nbsp;32．推动国家和具备条件的省级质检中心实施实验室对外开放，与四众企业和中小企业共享大型检测设备、实验场地等，提供技术专家咨询，分享检验检测技术和研究成果，推动企业技术升级和产品结构优化。（省质监局牵头负责）</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　（十三）繁荣创业创新文化。</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;33．办好中国（大连）海外学子创业周、辽宁创新创业大赛、科技活动周等活动。支持众创空间等创业创新服务机构举办创业沙龙、创业文化周、创业训练营等活动，打造一批具有辽宁特色的创业活动品牌。（省科技厅牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;34．设立“辽宁省大众创业万众创新活动周”，加强政策宣传，展示创业创新成果，促进创业投资对接和交流互动，为创业创新提供展示平台。（省发展改革委牵头负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　&nbsp;35．利用传统媒体、新媒体以及各类科普设施，积极宣传成功创业者、青年创业者、天使投资人、创业导师、创业服务机构及四众知识，推动形成大众创业万众创新的良好局面。（省委宣传部、省新闻出版广电局、省科协等负责）</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　&nbsp;&nbsp;三、保障措施</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　各地区、各部门要高度重视，把推进大众创业、万众创新放在重要位置，加强沟通协调，密切配合协作，形成工作合力。以沈阳市开展全面创新改革试验为契机，学习借鉴其他省份的新举措新思路，积极争取国家政策支持。各地区要结合实际制定具体工作方案，细化政策措施，确保落到实处。各有关部门要根据本通知要求，及时制定或完善配套措施。省政府将对贯彻落实工作适时开展督导检查，确保工作顺利推进。</span></p><p style=\"margin: 16px 0\"><span style=\"font-size: 16px;font-family: 宋体\">　　辽宁省人民政府</span></p><p><span style=\"font-size: 16px;font-family: 宋体\">　　2016年1月20日</span></p><p><br/></p>', '0', '0', '1', '2016-01-20 00:00:00', '1', '2017-11-13 13:40:51', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:48:54', '0', '');
INSERT INTO `site_policy_rule` VALUES ('b6e5ff49e1334f0cb3ce3025ee24b4b7', 'ZCFG_b6e5ff49e1334f0cb3ce3025ee24b4b7', '沈阳市科技创新券实施办法', '2', '沈阳市科学技术局', '<p><img src=\"http://www.sykjtjpt.cn/image/show?id=20D8QW1yhvvpx2PP8e4qJKAvGoTfILHAnT5oguKkG598_3EW2sCDY55vRJndFOUzn03yXPHx0ko.jpg\" title=\"2020030655000666.jpg\" alt=\"1.jpg\"/><img src=\"http://www.sykjtjpt.cn/image/show?id=20D8QW1yhvvpx2PP8e4qJKAvGoTfILHAnT5oguKkG598_3EW2sCDYxZmkzHsC0m2W3PfF2woJvQ.jpg\" title=\"2020030602012433.jpg\" alt=\"2.jpg\"/><img src=\"http://www.sykjtjpt.cn/image/show?id=20D8QW1yhvvpx2PP8e4qJKAvGoTfILHAnT5oguKkG598_3EW2sCDY6iUY6djjTSkw96e4hg2mVM.jpg\" title=\"2020030608095590.jpg\" alt=\"3.jpg\"/><img src=\"http://www.sykjtjpt.cn/image/show?id=20D8QW1yhvvpx2PP8e4qJKAvGoTfILHAnT5oguKkG598_3EW2sCDYw9QwolemjU3EC9Tf_RtEBM.jpg\" title=\"2020030611025839.jpg\" alt=\"4.jpg\"/><img src=\"http://www.sykjtjpt.cn/image/show?id=20D8QW1yhvvpx2PP8e4qJKAvGoTfILHAnT5oguKkG598_3EW2sCDY5oZ6Z0IO4I-w96e4hg2mVM.jpg\" title=\"2020030615063520.jpg\" alt=\"5.jpg\"/><img src=\"http://www.sykjtjpt.cn/image/show?id=20D8QW1yhvvpx2PP8e4qJKAvGoTfILHAnT5oguKkG598_3EW2sCDY9pTliDLTQenW3PfF2woJvQ.jpg\" title=\"2020030618027053.jpg\" alt=\"6.jpg\"/></p>', '0', '0', '', '2020-03-06 00:00:00', '1', '2020-03-06 14:13:33', '1', '2020-03-06 14:13:33', '0', '');
INSERT INTO `site_policy_rule` VALUES ('bd6e39903c2f44c393f3d4e2be93e87c', 'ZCFG_bd6e39903c2f44c393f3d4e2be93e87c', '沈阳市高层次人才创新成果转化补助资金实施细则', '2', '沈阳市科学技术局', '<p style=\"background: white; text-align: center;\"><span style=\"font-size: 24px;\"><strong><span style=\"font-family: 宋体; color: rgb(41, 147, 199);\">沈阳市高层次人才创新成果转化补助资金实施细则</span></strong></span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体\">第一章&nbsp;总则</span></strong></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第一条&nbsp;&nbsp;根据《中共沈阳市委沈阳市人民政府关于实施“盛京人才”战略打造具有国际竞争力人才高地的意见》（沈委发〔2015〕9号）文件精神，为鼓励各类高层次人才创新成果就地转化，设立“沈阳市高层次人才创新成果转化补助资金”（以下简称补助资金）。为科学合理使用补助资金，制定本细则。</span></p><p style=\"text-indent: 47px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第二条</span><span style=\"font-size: 19px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">补助资金是市本级财政预算安排的，</span><span style=\"font-size: 19px;font-family: 宋体\">专项用于<span style=\"letter-spacing:1px\">沈阳市高层次人才创新成果转化补助的资金，原则上不与我市其他专项资金重复支持。</span></span></p><p style=\"text-indent: 47px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第三条</span><span style=\"font-size: 19px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">补助资金的使用和管理遵循“公平公开、择优补助、科学管理、加强监督”的原则，由市科技局负责管理和组织实施。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体\">第二章&nbsp;补助对象、方式及条件</span></strong></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第四条&nbsp;对创造并就地转化且取得较好经济和社会效益科技成果的高层次创新人才和团队（三人以上，下同）予以补助。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第五条&nbsp;补助方式为一次性后补助。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第六条&nbsp;人才和团队应具备以下条件：</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（一）符合《沈阳市高层次人才认定办法》（沈人社发〔2016〕54号）规定条件或经市人社局认定。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（二）所转化创新成果研发任务的主持者或主要完成者（个人申报排名应在前五名，团队申报排名应在前七名）。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（三）创新成果转化过程的主要指导者或参与者。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（四）团队成员均要符合上述三款之条件。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第七条&nbsp;人才和团队创造的科技成果应符合下列条件之一。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（一）通过验收且形成验收意见的市级以上各类计划项目。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（二）核心技术获得自主知识产权（发明、实用新型、外观设计专利证书，植物新品种权证书，商标注册证书，软件登记证书等）或取得行业许可、准入证明的。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（三）通过市以上行业协会评定的。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第八条&nbsp;创新成果在沈阳本地转化，且转化时间在一年以上五年以下的。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第九条&nbsp;创新成果转化一年后的产值和净利润较上年（上年同期，下同）增幅达20%以上的；新产品转化一年后实现产值200万元以上、净利润10万元以上的。农业创新成果转化效益指标不低于上述增幅或数额的50%（下同）。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体\">第三章&nbsp;补助范围及标准</span></strong></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十条&nbsp;重点补助在高端装备与先进制造、新一代信息技术、新能源、新材料、生物医药、资源与环境、人口与健康、城市建设与城镇化、现代服务业、现代农业等领域创造成果的人才和团队。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十一条</span><span style=\"font-size: 19px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">补助额度为10—100万。团队形式申报的补助额度上浮50%，最高不超过100万元。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（一）创新成果转化一年后产值和净利润较上年增幅达20%以上、50%以下（含50%）或新产品转化一年后实现产值200万元以上、1000万元（含1000万元）以下且净利润10万元以上、50万元（含50万元）以下的，补助额度为10万元。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（二）创新成果转化一年后产值和净利润较上年增幅达50%以上、100%（含100%）以下或新产品转化一年后实现产值1000万元以上、5000万元（含5000万元）以下且净利润50万元以上、300万元（含300万元）以下的，补助额度为20万元。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（三）创新成果转化一年后产值和净利润较上年增幅达100%以上、200%（含200%）以下或新产品转化一年后实现产值5000万元以上、1亿元（含1亿元）以下且净利润300万元以上、500万元（含500万元）以下的，补助额度为40万元。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">（四）创新成果转化一年后产值和净利润较上年增幅达200%以上或新产品转化一年后实现产值1亿元以上且净利润500万元以上补助额度为70万元。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体\">第四章&nbsp;组织实施及要求</span></strong></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十二条&nbsp;申报由人才或团队所在的科技成果研发单位负责组织。所在单位应是创造科技成果的、沈阳行政区域内具有独立法人资格的企事业单位，具有健全的企业管理和财务管理制度，无不良信用记录。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十三条&nbsp;创新成果转化效益情况由转化单位负责提供，其真实性由转化单位负责。转化单位应是沈阳行政区域内注册的、具有独立法人资格的企事业单位，具有健全的企业管理和财务管理制度，无不良信用记录。</span></p><p style=\"text-indent: 42px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十四条&nbsp;通过沈阳市科技创新管理平台申报，经初审后将将纸质申报材料及相关附件报送市科技局。市科技局组织专家进行评审，对评审通过的项目予以考察，依据评审、考察结果择优推荐，经局长办公会议审议通过向社会公示且无异议，会同市人才办、市财政局报市政府审批后拨付补助资金。</span></p><p style=\"text-indent: 42px;line-height: 20px;background: white\"><strong><span style=\"font-size: 19px;font-family: 宋体\">第五章&nbsp;管理监督</span></strong></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十五条</span><span style=\"font-size: 19px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">补助资金依照《沈阳市盛京人才计划专项资金管理办法》（沈人才〔2016〕4号）有关规定支出。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十六条&nbsp;</span><span style=\"font-size: 19px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">获得补助资金单位每半年向市科技局提交补助资金执行情况报告。补助资金必须设立专帐进行财务核算，专款专用。</span></p><p style=\"text-indent: 45px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十七条</span><span style=\"font-size: 19px;font-family: 宋体\">&nbsp;</span><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">对虚报成果转化经济效益情况、伪造相关证明文件或冒领、截留、挪用、挤占补助资金等违纪行为，除追缴资金外，三年内取消该单位盛京人才计划资金申报资格。</span></p><p style=\"text-align:center;line-height:20px;background:white\"><strong><span style=\"font-size: 19px;font-family: 宋体\">第六章&nbsp;附则</span></strong></p><p style=\"text-indent: 47px;line-height: 20px;background: white\"><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">第十八条&nbsp;本细则由市科技局负责解释。</span></p><p><span style=\"font-size: 19px;font-family: 宋体;letter-spacing: 1px\">&nbsp;&nbsp;&nbsp;&nbsp;第十九条&nbsp;本细则自发布之日起实施。</span></p><p><br/></p>', '0', '0', '1', '2017-03-23 00:00:00', '1', '2017-11-13 13:50:42', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:50:28', '0', '');
INSERT INTO `site_policy_rule` VALUES ('bf5c24b2563949a4a0cb72e53a2eeaf0', 'ZCFG_bf5c24b2563949a4a0cb72e53a2eeaf0', '辽宁省自主创新促进条例', '1', '辽宁省人民代表大会常务委员会', '<p style=\"text-align: center;\"><span style=\"font-size: 24px;\"><strong><span style=\"font-family: 宋体; color: rgb(51, 51, 51);\">辽宁省自主创新促进条例</span></strong></span></p><p><strong><span style=\"font-size:18px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></strong></p><p style=\"text-indent: 37px;line-height: 27px\"><span style=\"font-size:19px;font-family:楷体_GB2312;color:#333333\">2014</span><span style=\"font-size:19px;font-family:楷体_GB2312;color:#333333\">年1月9日辽宁省第十二届人民代表大会常务委员会第六次会议通过</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第一章</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:黑体;color:#333333\">总</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:黑体;color:#333333\">则</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第一条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">为了提高自主创新能力，推动科学研究和技术创新成果向现实生产力转化，促进经济社会发展，根据《中华人民共和国科学技术进步法》等有关法律、法规，结合本省实际，制定本条例。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第二条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">本条例适用于本省行政区域内研究开发与创造成果、成果转化与产业化、创新型人才队伍建设以及创新环境优化等自主创新促进活动。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">本条例所称自主创新，是指公民、法人或者其他组织，通过开展科学研究和技术创新，形成自主知识产权或者专有技术，运用多种方式，向市场提供新产品、新工艺、新服务的活动。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第三条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">省、市、县（含县级市、区，下同）人民政府领导本行政区域内自主创新促进工作。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">省、市、县人民政府科学技术行政部门负责本行政区域内自主创新促进工作的宏观管理和组织协调。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">发展改革、经济与信息化、人力资源和社会保障、财政、教育、农业、税务、知识产权等有关部门在各自职责范围内，负责相关的自主创新促进工作。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第四条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">自主创新促进工作应当坚持以企业为主体、以市场为导向、产学研相结合、政府引导和扶持、全社会共同参与的原则。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第五条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">促进自主创新是全社会的共同责任。政府有关部门、新闻媒体以及其他社会组织，应当推动形成崇尚科学、尊重人才、尊重创造、敢于创新、宽容失败的舆论氛围和社会风尚。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第二章</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:黑体;color:#333333\">创新平台</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第六条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">省、市、县人民政府应当制定措施引导创新要素向企业聚集，提高原始创新、集成创新和引进消化吸收再创新能力，健全技术创新市场导向机制，完善产业技术创新链，提高企业核心技术开发能力，促进企业成为创新决策、研发投入、科研组织和成果转化的主体。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第七条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">鼓励企业围绕提高核心竞争力和长远发展，增加技术研究开发费用投入。高新技术企业和创新型企业的研究开发费用支出，应当不低于国家高新技术企业认定标准规定的比例；大中型企业和创新型中小企业每年用于研究开发的投入，应当不低于国家和省规定的标准。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">国有企业应当根据盈利情况逐步增加科技研发投入经费。其技术创新投入、能力建设、成效以及知识产权产出与应用等，应当纳入对国有企业负责人的业绩考核范围。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第八条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">省人民政府应当制定促进海洋科技发展政策，发展海洋产业核心技术，支持海洋产业核心技术创新，提高海洋科技成果转化率，引导和鼓励科技型涉海企业发展。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第九条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">企业可以自主设立或者与其他企业、科研机构、高等学校联合创建各类研究开发机构和技术创新组织，自主或者联合开展核心关键技术攻关、基础研究和人才培养等自主创新活动，实施国家、省、市重大项目或者研发重大装备和新产品。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第十条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">支持企业与科研机构、高等学校共同推进国家重大技术创新产品、服务标准的研究开发，参与行业标准、国家标准和国际标准的制定，推动自主创新成果形成相关技术标准。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">对在标准制定中起主导作用，或者取得国家著名、知名品牌称号以及获得国家专利金奖、优秀奖的企业，按照相关规定给予奖励。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第十一条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">企业、事业单位可以通过技术合作、技术外包、专利许可等方式进行集成创新，开展产业关键共性技术研发，完善系统集成和工程化条件，形成具有市场竞争力的产品或者新兴产业。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第十二条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">鼓励企业引进有利于提升企业核心竞争力的先进适用技术，并购其他科技型企业，或者在省外、国外建立研发机构开展技术合作。消化吸收拥有自主知识产权或者独特核心技术的，可以作为对企业引进重大技术、装备进行评估和验收的重要依据。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">省科学技术、发展改革、经济和信息化等有关部门应当根据国家和省相关政策，引导企业、事业单位引进先进技术和装备并进行消化吸收再创新，限制引进国内已具备研发能力的技术和装备，禁止引进高能耗、高污染及落后的技术和装备。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第十三条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">引导企业采用合同能源管理、重大技术设备融资租赁、电子商务等商业模式，提升商业运营能力；支持企业利用互联网、物联网等新技术，优化内部流程和整合外部资源，开发使用信息管理技术，开展产业链融合重组，推进运营模式创新。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第十四条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">鼓励企业开展资源与环境、人口与健康、文化创意、节能减排、公共安全、防震减灾、城市建设等领域的自主创新。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第十五条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">省人民政府根据需要批准建立高新技术产业开发区、科技园、软件园等创新园区，健全创新园区的研发、检测、孵化器、科技金融、基础数据库等创新平台。推动创新园区利用自身特色和优势，发展高新技术产业、先进制造业、现代服务业和战略性新兴产业。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">建立特色产业集群并依托产业集群优势发展公共研发、检测、中试等创新平台，利用主导产业集聚优势，增强企业的国际竞争力。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">加快建设科技企业孵化器、大学科技园、留学生创业园等载体，推动大学科技园成为自主创新基地，为高新技术企业孵化、高等学校科技成果转化和创新创业人才培养提供服务。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第十六条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">省人民政府统筹规划全省科学技术研究开发机构的布局，鼓励国内外企业、公民、法人或者其他组织在本省依法独立或者联合设立集科技创新与产业化于一体的新型源头创新机构，或者投资兴建科研基础设施，为创新成果加速实现产业化创造条件。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">支持国家驻辽科研机构、高等学校、国防科研机构参与本省自主创新活动。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">科研机构、高等学校可以创办科技型企业或者技术转移中心等平台，开展高技术服务。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第十七条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">省、市科学技术行政部门应当会同有关部门推动科研机构、高等学校和企业开放科研设备、设施，完善大型科学仪器设备、科技文献、科学数据等科技基础条件服务平台，建立开放共享的运行服务管理模式，制定相应的评价标准和监督、奖惩办法。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">省科学技术行政部门应当对大型科学仪器设备共享进行统筹协调，建立全省统一的数据库，向社会提供大型科学仪器设备共享的信息查询、服务推介等服务。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">运用财政性资金或者国有资本购置、建设的大型科学仪器设备和科研基础设施，应当进行事前评估，避免重复购置，并对社会公布相关信息，履行开放共享义务。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第三章</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:黑体;color:#333333\">成果转化</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第十八条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">支持高等学校利用自身设备、人才等优势开展基础研究和自主创新工作，促进自主创新成果转移和转化。教育、科学技术、财政等有关部门应当对高等学校的基础研究、学科建设和成果转化给予支持。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第十九条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">鼓励开展农业基础研究和应用研究，加强新品种引进、选育，提高农产品产量和质量，支持农业共性关键技术攻关，支持企业开展以规模化、标准化为重点的农业产业化技术创新和成果转化。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">科学技术、农业等部门应当选派科技人员，促进农业科技成果推广应用，对地域特征明显且条件成熟的特色、优势农产品，实行地理标志保护。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第二十条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><a><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">高等学校、科学技术研究开发机构和企业按照国家有关规定，可以采取知识产权入股、科技成果折股或者收益分成、股权奖励、股权出售、股票期权等方式对科学技术人员和经营管理人员进行股权和分红激励，促进自主创新成果转化与产业化</span></a><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">以专利权或者其他可以用货币估价并可以依法转让的科技成果作价出资入股有限责任公司和股份有限公司的，可以占公司注册资本中非货币财产作价出资金额的法定最高限额。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第二十一条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">省科学技术、发展改革、经济和信息化等有关部门应当按照国家发布的高新技术产业化重点领域指南，优先支持高新技术产业、先进制造业、现代服务业和战略性新兴产业自主创新成果的转化与产业化活动，支持企业、科研机构、高等学校利用留学人员科技交流会、高新技术成果交易会等平台，吸引国内外高层次人才在本省实施创新成果转化与产业化。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第二十二条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:red\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:red\">&nbsp;</span><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">科技成果完成单位将其主要利用财政性资金资助的科技项目所取得的职务创新成果，以技术转让、股权投入等方式实施转化的，应当将不低于百分之三十的技术转让净收入或者职务创新成果作价所得的股权，一次性奖励给职务创新成果完成人以及为成果转化做出重要贡献的人员。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">科技成果完成单位自行实施转化或者以合作方式实施转化的，应当自项目盈利之日起五年内，每年从税后利润中提取不低于百分之五的比例，用于奖励成果完成人。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">科研机构、高等学校对其主要利用财政性资金取得的具有实用价值的技术成果，在完成后两年内没有以转让、许可或者入股等方式运用的，技术成果完成人有权要求有偿受让该技术成果，科研机构、高等学校应当予以转让。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第二十三条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">科学技术等有关部门应当制定激励措施，引导技术交易、知识产权服务、科技评估与咨询、科技创业孵化器、生产力促进中心、科技风险投资和担保等各类科技中介机构，向专业化、社会化、网络化、规范化方向发展，为企业、科研机构、高等学校的创新活动和成果转化与产业化提供服务。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第四章</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:黑体;color:#333333\">创新人才</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第二十四条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">省、市人民政府应当制定科技人才发展规划和高层次人才特殊支持计划，建立科技创新创业人才扶持机制，支持区域优先发展的重点学科、重点产业、重大项目和具有竞争优势的领域组建优势创新团队。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">鼓励科研机构、高等学校和企业、事业单位建立创新实践基地和高层次科技人才培养基地，结合实施重大科研、工程项目和重点学科及科研基地建设，加强科技领军人才、卓越工程师、青年拔尖人才和高水平创新团队培养。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">支持企业、科研机构和高等学校根据高新技术产业发展和传统产业转型升级需求，共建实习、实验基地，联合培养科研生产岗位急需的专业技术人才和技能型人才。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第二十五条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">省级引进高层次创新人才专项资金和博士、博士后科研资助专项资金，应当重点资助高层次科技创新人才和引进研发团队。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">引进的高层次科技创新人才经相关程序认定的，在晋升专业技术职务和评定职称时，不受单位指标限制，有关部门应当在项目申报、科研条件保障、出入境、户籍迁移、社会保障、子女入学等方面给予优先。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第二十六条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">科研机构、高等学校与企业之间可以进行高层次科技创新创业人才双向兼职、任职，联合开展科研攻关、成果转化和技术创新服务活动。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">允许科技人员在完成本职工作并且不损害本单位利益的前提下，经本单位同意，兼职创办科技型企业或者从事技术开发、新产品研制、技术咨询、技术服务等相关工作，并获得报酬。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">科研机构、高等学校应当对符合条件的科技人员离岗从事科技成果、专利技术转化或者创办科技型企业的，三年内保留其原有身份和职称，档案工资正常晋升。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第二十七条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">科研机构、高等学校应当健全有利于创新型人才发挥作用的多种分配方式，完善专业技术职务聘任和岗位聘用制度，对在自主创新方面有突出贡献的科技人员给予优厚待遇。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第二十八条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">建立以科研能力和创新成果为导向的科技人员考核评价机制和专业技术职称、职务评聘条件。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">技术创新、知识产权创造、成果转化和应用、创新管理的业绩及其经济、社会、生态效益，应当作为科技人员专业技术考核评价和职称职务评聘、项目申报、成果奖励的重要依据。对在自主创新方面做出突出或者重大贡献的，允许破格晋升专业技术职称。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">科研机构、高等学校和企业科技人员参加科学技术普及、技术开发、技术服务、技术咨询和科技派遣活动的情况，应当作为项目申报、成果奖励、职称评定、职务聘任、职级晋升的依据，并可以计入专业工作经历。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第二十九条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">科研机构、高等学校选派科技人员到企业和各类园区开展科技创新、技术服务和成果转化活动期间，其原职级、工资福利和岗位保留不变，工资、专业技术职务、职称的晋升和岗位变动，与选派单位在职人员同等对待。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第三十条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">对以财政性资金或者国有资本为主资助的探索性强、风险性高的自主创新项目，原始记录证明承担项目的单位和科技人员已经履行了勤勉尽责义务仍不能完成的，经立项主管部门、财政等有关部门组织专家论证后，可以允许该项目结题，并允许相关单位和个人继续申请利用财政性资金或者国有资本设立的自主创新项目。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第三十一条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><a><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">科学技术人员应当遵守学术规范，不得在科学技术活动中有抄袭、剽窃等弄虚作假的行为，不得侵害他人的知识产权。</span></a></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第五章</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:黑体;color:#333333\">创新保障</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第三十二条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">建立以企业投入、市场融资、外资引进等社会投入为主体，以政府投入为引导的多渠道、多元化创新投入体系。省、市、县人民政府财政性科技资金，应当按照《中华人民共和国科学技术进步法》的相关规定，保障投入事项，提高增长幅度，增强使用效益。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">全省科技研究开发经费应当逐年增长，占全省生产总值的比例应当高于全国平均水平。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">省人民政府设立省自然科学基金，主要用于资助基础研究、科学技术前沿探索，促进提高原始创新能力；设立省首台（套）重大技术装备专项资金，重点用于补助企业研制和示范使用省内首台（套）产品。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">省、市、县人民政府设立科技型中小企业技术创新资金，重点扶持科技型中小企业发展。研发投入额度较大并能够持续开展研发活动的企业，其研究项目可以优先获得中小企业创新资金、技术改造资金等政府各类计划资金的支持。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第三十三条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">省、市、县人民政府应当整合财政性科技资金，设立引导性专项资金，通过风险补偿、参股创业投资企业等多种投入方式，引导社会资金投向企业技术创新、高新技术产业和关键技术攻关的中试项目。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第三十四条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">省、市、县人民政府应当制定相关扶持政策，通过贷款贴息、补助资金、保费补贴和创业风险投资资助等方式，支持自主创新成果转化与产业化，引导企业加大自主创新成果转化与产业化投入。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">对具有自主知识产权、可以形成较大经济规模或者较强竞争力的重大科技成果转化项目，以及自主知识产权首次在本省转化应用的，应当给予重点支持。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><a><span style=\"font-size:21px;font-family:黑体;color:#333333\">第三十五条</span></a><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family: 仿宋_GB2312;color:#333333\">审计机关、财政部门应当依法对财政性科技资金的管理和使用情况进行监督检查。<br/> 　　任何组织或者个人不得虚报、冒领、贪污、挪用、截留财政性科技资金。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第三十六条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">利用财政性援助资金设立的自主创新项目，承担项目的人力资源成本费可以从项目经费中支出，最高比例可以占该项目经费的百分之三十；其中软科学研究项目和软件开发类项目，人力资源成本费最高比例可以占该项目经费的百分之五十。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第三十七条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">企业并购境外科技型企业、引进重大技术或者装备，编制引进消化吸收再创新方案并按照方案实施的，经市以上发展改革、经济与信息化、科技、财政等有关部门组织专家论证评估通过，政府予以资金补助。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第三十八条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">各级、各类科技专项资金应当按照国库集中收付制度由财政直接拨付给项目单位，任何单位和部门不得以任何理由截留。财政、科学技术行政部门应当会同有关部门建立和完善有关自主创新财政性资金的绩效评价制度，提高有关自主创新财政性资金的使用效益。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">利用财政性资金设立的自主创新项目，主管部门应当建立评审专家库，实行项目公平竞争、专家评审和评审结果公示以及评审专家的遴选、回避、问责等制度，但涉及国家秘密的项目除外。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">利用财政性援助资金设立的自主创新项目及其承担者的情况，应当由项目主管部门向社会公开，但涉及国家秘密的项目除外。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第三十九条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">对科研机构、高等学校和企业自筹资金研究开发并具有自主知识产权的自主创新项目，以及企业与科研机构、高等学校联合开展核心关键技术攻关并取得成果的，县级以上人民政府可以采取后补助方式予以财政性资金资助。资助资金应当用于该项目在本省的后续研究开发、成果转化及产业化活动和科技基础条件服务平台建设。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第四十条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">重点科研基础设施、重大科技创新工程等建设项目用地，应当纳入土地利用总体规划、城乡规划和政府投资计划。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">省级以上产业园区的战略性新兴产业、高新技术产业的研究开发项目用地，符合法定条件的，可以依法采取协议出让等方式取得。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">对国家高新技术企业和自主创新示范企业的生产性建设用房、科研机构科研用房，以及省级以上工程技术（研究）中心、企业技术中心、重点实验室、中试基地等建设工程，依照国家规定减免城市基础设施配套费。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第四十一条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">支持国内外各类创业（风险）投资机构开展创业（风险）投资业务。对注册地在本省境内并且投资行为符合条件的创业（风险）投资机构，按照省有关规定享受政府创业（风险）投资专项补助资金。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">鼓励金融机构加大科技信贷投放，扩大知识产权抵押、质押等科技贷款规模；支持科技型企业通过境内外上市、发行集合债券和信托产品、开展融资租赁等方式进行多渠道融资。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">鼓励建立科技小额贷款公司，推动保险机构开发科技保险品种，为新产品研发、试生产和高新技术企业提供融资和保险保障。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第四十二条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">科学技术等有关部门应当制定支持自主创新的服务指南，为企业、事业单位及科技人员享受有关优惠政策提供高效便捷服务。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">知识产权等有关部门应当加强与金融机构、相关中介服务机构的合作，推动建立知识产权质押融资服务平台，为企业知识产权质押融资提供知识产权展示、登记、评估、咨询和融资推荐等服务。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">金融管理部门应当协调银行、保险、创业投资、担保等机构和企业联动合作，推动发展科技金融专营服务。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">税务机关应当依法落实高新技术企业税收优惠政策。企业开发新技术、新产品、新工艺发生的研究开发费用，按照法律规定税前列支并加计扣除；企业科技研究开发仪器、设备，可以按照国家有关规定加速折旧。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第四十三条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">省科学技术行政部门应当会同有关部门建立健全自主创新统计、监测和考核评价制度，对全省自主创新发展状况进行监测、分析和评价，编制、发布全省自主创新年度报告，并定期向社会公布。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第四十四条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">鼓励机关、企业、事业单位、社会团体和公民参与、支持科学技术普及、群众性技能竞赛和发明创造活动。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">有条件的单位应当向公众开放展示其自主创新成果的场馆和设施。不同权属的科技场馆和设施均应当实行社会化有效利用。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">鼓励各类学校举办各种面向青少年的科技创新活动，培育青少年的创新意识和兴趣。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第四十五条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">省人民政府应当完善科学技术奖励制度，创新奖励模式，对在科学技术进步活动和自主创新工作中做出重要贡献的单位和个人给予奖励。市、县人民政府可以结合实际情况给予表彰或者奖励。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">鼓励国内外公民、法人或者其他组织在本省设立科学技术和自主创新奖项，并可以自主决定奖项命名和奖励办法。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第六章</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:黑体;color:#333333\">法律责任</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第四十六条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><a><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">违反本条例规定，虚报、冒领、贪污、挪用、截留财政性科技资金的，依照有关财政违法行为处罚处分的规定责令改正，追回有关财政性资金和违法所得，依法给予行政处罚；对直接负责的主管人员和其他直接责任人员依法给予处分</span></a><span style=\"font-size:21px;font-family: 黑体;color:#333333\">；</span><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">构成犯罪的，依法追究刑事责任。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第四十七条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><a><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">违反本条例规定，抄袭、剽窃他人科学技术成果，由科学技术人员所在单位或者单位主管机关责令改正，对直接负责的主管人员和其他直接责任人员依法给予处分；获得财政性资金或者有违法所得的，由有关部门追回财政性资金和违法所得；情节严重的，由所在单位或者单位主管机关向社会公布其违法行为，禁止其在一定期限内申请科学技术研究项目。</span></a></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第四十八条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">企业、科研单位和其他组织利用财政性资金或者国有资本引进重大技术、装备，未按引进计划进行消化吸收再创新，以及利用财政性资金购置大型科学仪器设施不履行开放共享义务的，由科学技术等有关部门责令其限期改正，对直接负责的主管人员和其他直接责任人员依法给予处分。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第四十九条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">有关组织或者个人在科研项目评审、科技成果鉴定或者评奖等活动中，有下列情形之一的，由主管部门记入诚信档案；自该行为被记入诚信档案之日起五年内，任何部门不得委托其从事评审、鉴定或者评奖工作；构成犯罪的，依法追究刑事责任：</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">（一）提出虚假意见，严重影响公正评审、鉴定或者评奖工作的；</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">（二）违反评审、鉴定或者评奖规定，泄露评审、鉴定或者评奖信息，造成不良影响的；</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">（三）泄露国家秘密和商业秘密的。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第五十条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">有关行政部门及其工作人员有下列情形之一的，由上级主管部门或者监察机关对直接负责的主管人员和其他直接责任人员依法给予处分；构成犯罪的，依法追究刑事责任：</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">（一）未按照规定拨付财政性科技资金的；</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">（二）未依法对财政性科技资金的管理和使用情况进行监督的；</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">（三）侵害研究开发机构和科技人员合法权益的；</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:仿宋_GB2312;color:#333333\">（四）有其他玩忽职守、滥用职权、徇私舞弊行为的。</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第七章</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:黑体;color:#333333\">附</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:黑体;color:#333333\">则</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></p><p style=\"text-indent: 43px;line-height: 27px\"><span style=\"font-size:21px;font-family:黑体;color:#333333\">第五十一条</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:21px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size: 21px;font-family:仿宋_GB2312;color:#333333\">本条例自2014年3月1日起施行。</span></p><p><br/></p>', '0', '0', '1', '2014-02-27 00:00:00', '1', '2017-11-13 13:46:25', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-20 14:31:42', '0', '');
INSERT INTO `site_policy_rule` VALUES ('c01ddd2a000346a49d701ab17b5b3e73', 'ZCFG_c01ddd2a000346a49d701ab17b5b3e73', '《辽宁省科学技术厅 辽宁省财政厅关于实施科技创新券制度的若干意见（试行）》', '3', '辽宁省科学技术厅 辽宁省财政厅', '<p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:宋体\">关于印发《辽宁省科学技术厅 辽宁省</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:宋体\">财政厅关于实施科技创新券制度的</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:宋体\">若干意见（试行）》的通知</span></strong></p><p style=\"text-align:center;line-height:28px\"><span style=\"font-size:21px;font-family:宋体\">&nbsp;</span></p><p><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;&nbsp;&nbsp;&nbsp;各市、绥中县、昌图县科技局、财政局，各省级以上高新区管委会，各有关单位：</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">现将《辽宁省科学技术厅 辽宁省财政厅关于实施科技创新券制度的若干意见（试行）》印发给你们，请结合实际贯彻落实。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;</span></p><p style=\"text-indent:192px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;辽宁省科学技术厅&nbsp;&nbsp;&nbsp;&nbsp; 辽宁省财政厅</span></p><p style=\"text-indent:395px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;</span></p><p style=\"text-indent:288px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2015</span><span style=\"font-size:21px;font-family:仿宋_GB2312\">年6月8日</span></p><p style=\"text-align:center\"><strong><span style=\"font-size:29px;font-family:宋体\">辽宁省科学技术厅 辽宁省财政厅关于实施科技创新券制度的若干意见（试行）</span></strong></p><p style=\"text-align:center\"><span style=\";font-family:黑体\">&nbsp;</span></p><p style=\"text-align:center\"><span style=\";font-family:黑体\">&nbsp;</span></p><p style=\"text-align:center;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第一章&nbsp; 总&nbsp; 则</span></p><p style=\"text-indent:42px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第一条</span><span style=\"font-size:21px;font-family: 仿宋_GB2312\"> 为深入实施创新驱动发展战略，创新财政科技投入方式，鼓励我省中小微企业高效利用省内高校、科研院所等社会科技资源，推动大众创业、万众创新，省科技厅、省财政厅决定实施科技创新券（以下简称“创新券”）制度。</span></p><p style=\"text-indent:42px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第二条</span><span style=\"font-size:21px;font-family: 仿宋_GB2312\"> 创新券是政府免费向中小微企业发放，专门用于向高校、科研机构购买科技创新服务的权益凭证。实施创新券制度是政府推动企业加大科技投入，加强产学研合作，提升科技创新能力的一种激励措施。</span></p><p style=\"text-indent:42px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第三条</span><span style=\"font-size:21px;font-family: 仿宋_GB2312\"> 省科技厅、省财政厅共同负责创新券的顶层设计、决策指导、统筹协调、监督检查。各创新券实施区（包括各市、省级以上高新区等）负责创新券的具体工作。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第四条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 我省开展创新券工作采取先行先试的方式。即支持条件成熟的地区先行开展创新券试点工作，取得经验后在全省适时逐步推广。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第五条 </span><span style=\"font-size:21px;font-family:仿宋_GB2312\">创新券资金来源于各级财政科技资金，其中以创新券实施区所出资金为主，省本级资金为引导资金。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:黑体\">第六条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 创新券的使用和管理须遵守国家有关法律、行政法规和财务规章制度，遵循公开透明、专款专用的原则。</span></p><p style=\"text-align:center;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第二章&nbsp; 工作组织与要求</span></p><p style=\"text-indent:42px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第七条 </span><span style=\"font-size:21px;font-family: 仿宋_GB2312\">各创新券实施区应依据本意见，结合当地实际情况制定相应管理办法、工作方案或实施细则，成立专门管理机构（以下简称“专门机构”），并具体负责创新券制作、发放、兑现等日常管理工作。专门机构应由创新券实施区的科技管理部门会同同级财政管理部门组成。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第八条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\">\r\n 创新券拟实施区自愿提出申请，申请材料包括：本地开展创新券工作的正式申请文件，内容要包括本地基本情况、科技型小微企业发展情况、开展创新券的工作基础、保障条件及地方财政的创新券资金额度；高新区须所在市政府出具以支持该高新区开展创新券工作为主要内容的正式文件；本地创新券管理办法、工作方案或实施细则。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第九条 </span><span style=\"font-size:21px;font-family:仿宋_GB2312\">省科技厅会同省财政厅根据各创新券拟实施区的申请材料、省本级财政经费预算等情况，本着鼓励先进的原则，确定创新券实施区及经费分配额度。</span></p><p style=\"text-indent: 43px;line-height: 28px\"><span style=\"font-size:21px;font-family:黑体\">第十条 </span><span style=\"font-size:21px;font-family:仿宋_GB2312\">省直有关部门根据部门工作任务和工作重点可直接向省科技厅、省财政厅提出开展创新券工作申请，并根据本意见制定相应管理办法、工作方案或实施细则。</span></p><p style=\"text-indent: 43px;line-height: 28px\"><span style=\"font-size:21px;font-family:黑体\">第十一条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 省内各高校、科研院所等单位应积极支持创新券工作开展，主动向社会开放共享科技资源，提供科技创新服务。</span></p><p style=\"text-indent: 43px;line-height: 28px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">&nbsp;</span></p><p style=\"text-align:center;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第三章&nbsp; 支持范围与方式</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第十二条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 创新券支持对象为我省具备企业法人资格、注册期限不满5年、上年度销售收入不超过2000万元、且原则上未获得过各级财政科技资金支持的科技型小微企业（以下简称“企业”）。优先支持在各类创新创业大赛上取得名次的企业和创业者。</span></p><p style=\"line-height:28px\">　　<span style=\"font-size: 21px;font-family:黑体\">第十三条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 创新券用于企业向我省高校、科研院所等单位或机构购买科技服务、科技成果以及实施科技成果转化项目的相关科技创新活动支出。</span></p><p style=\"line-height:28px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">　　购买科技服务、科技成果以及实施科技成果转化项目是指：企业在研究开发等科技创新活动中开展的科技研发及合作、试验研究、产品研制、工艺验证、检验检测、技术转让、专利购买等；创新券不支持非科技创新活动，如法定检测、强制检测等。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:黑体\">第十四条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 创新券采取无偿资助方式，实行实名制及登记备案制，不得转让、买卖，有效期原则上为1年，最多不超过2年。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:黑体\">&nbsp;</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:黑体\">第四章&nbsp; 发放与兑现</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第十五条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 创新券的申领由企业携带相关材料向专门机构提出，经审核后发放。企业申领的创新券总金额最多为创新券有效期内企业科技创新活动预期支出额，原则上不高于20万元。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">申领所需相关材料包括：企业营业执照副本；税务登记证副本；组织机构代码证副本；法定代表人身份证；近两年财务报表；计划使用创新券的科技创新活动简介；其它有利于本企业取得创新券的证明材料。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第十六条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 企业持创新券及相关材料，在创新券有效期内，向专门机构一次性提出兑现申请，经审核后一次性兑现。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:仿宋_GB2312\">兑现所需相关材料包括：发票或收据；技术合同；创新成果证明材料；资金支出证明材料以及项目经费支出情况；其它证明材料等。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第十七条 </span><span style=\"font-size:21px;font-family:仿宋_GB2312\">兑现金额原则上要大于企业之前领取创新券总金额的二分之一，小于或等于企业科技创新活动实际支出额的三分之一。</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第十八条 </span><span style=\"font-size:21px;font-family:仿宋_GB2312\">兑现后的企业可重复申领创新券，但同一个企业累计兑现金额不能超过20万元。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:黑体\">&nbsp;</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:黑体\">第五章&nbsp; 监督与管理</span></p><p style=\"line-height:28px\">　　<span style=\"font-size: 21px;font-family:黑体\">第十九条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\">\r\n 省科技厅、省财政厅会同专门机构授权或委托有关机构对申请材料的真实性进行审查和监督。对创新券申请兑现材料弄虚作假，恶意骗取财政资金的，按《财政违法行为处罚处分条例》的相关规定进行处理，除追回创新券及已兑现资金外，相关企业将被列入信用“黑名单”，相关高校、科研院所等单位视情况也将被追究相应责任。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:黑体\">第二十条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 省科技厅、省财政厅对各创新券实施区的实际工作情况进行跟踪、监督和评价，根据评价结果，对创新券实施管理适时提出调整意见。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:黑体\">&nbsp;</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:黑体\">第六章&nbsp; 附&nbsp; 则</span></p><p style=\"text-indent:43px;line-height:28px\"><span style=\"font-size:21px;font-family:黑体\">第二十一条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 本意见由省科技厅、省财政厅负责解释。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:黑体\">第二十二条</span><span style=\"font-size:21px;font-family:仿宋_GB2312\"> 本意见自2015年7月1日起施行。</span></p><p><br/></p>', '0', '0', '1', '2015-06-08 00:00:00', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-23 09:32:31', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-23 09:33:05', '0', '');
INSERT INTO `site_policy_rule` VALUES ('ccb6d50f01fc4b68b9a53c270c8508d3', 'ZCFG_ccb6d50f01fc4b68b9a53c270c8508d3', '对《省科技厅 省教育厅 省财政厅 省人力资源和社会保障厅 省国资委 省知识产权局关于加快促进科技成果', '1', '省科技厅', '<p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\"></span></p><p style=\"text-align:center\"><strong><span style=\"font-size:27px;font-family:宋体\">对《省科技厅 省教育厅 省财政厅 省人力资源</span></strong></p><p style=\"text-align: center;\"><strong><span style=\"font-size:27px;font-family:宋体\">和社会保障厅</span></strong><strong><span style=\"font-size:27px;font-family:宋体\">省国资委 省知识产权局关于加</span></strong></p><p style=\"text-align: center;\"><strong><span style=\"font-size:27px;font-family:宋体\">快促进科技成果转化的若干意见》的解读</span></strong></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:楷体\">2015</span><span style=\"font-size:21px;font-family:楷体\">年1月30日</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:仿宋\">省科技厅</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\"><br/></span><br/></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\">为深入贯彻落实党的十八大和十八届三中、四中全会关于实施创新驱动发展战略的部署和省委十一届八次、九次全会要求，积极推进《国务院关于近期支持东北振兴若干重大政策举措的意见》（国发〔2014〕28号）（以下简称国发〔2014〕28号文件）任务落实，进一步转变政府职能，深化科技体制改革，加快推进科技成果转化和产业化，激发全社会尤其是广大科技人员创新创造活力，形成大众创业、万众创新的良好氛围，经省政府批准，省科技厅、省教育厅、省财政厅、省人力资源和社会保障厅、省国资委、省知识产权局共同制定了《关于加快促进科技成果转化的若干意见》（以下简称《意见》）。现就《意见》起草背景和相关内容解读如下。</span></p><p><strong><span style=\"font-size:21px;font-family:仿宋\">　　一、出台《意见》的背景和意义</span></strong></p><p style=\"text-indent: 43px\"><span style=\"font-size:21px;font-family:仿宋\">党的十八大提出实施创新驱动发展战略，明确要求提高科研水平和成果转化能力，抢占科技发展战略制高点。十八届三中全会提出推进包括深化科技体制改革在内的全面改革，让一切劳动、知识、技术、管理、资本的活力竞相迸发，并对促进科技成果资本化和产业化提出了明确要求。为加快科技成果转化和产业化，充分调动科技人员的积极性和创造性，2011年，经国务院批准，财政部在中关村国家自主创新示范区开展了中央级事业单位科技成果处置和收益权管理改革试点。2013年9月，改革试点实施范围从北京中关村扩大到武汉东湖、上海张江国家自主创新示范区和安徽合芜蚌自主创新综合试验区。2014年8月国发〔2014〕28号文件提出“研究将中关村国家自主创新示范区有关试点政策向东北地区推广，鼓励在科技成果处置权、收益权、股权激励等方面探索试验。”2014年10月17日经国务院批准，财政部、科技部、国家知识产权局印发《关于开展深化中央级事业单位科技成果使用、处置和收益管理改革试点的通知》（财教〔2014〕233号），决定将在中关村开展的中央级事业单位科技成果处置权和收益管理改革试点政策扩大到全国所有国家自主创新示范区及合芜蚌自主创新综合试验区。2014年12月3日国务院常务会议部署在更大范围推广中关村自主创新试点政策。</span></p><p style=\"text-indent: 43px\"><span style=\"font-size: 21px;font-family: 仿宋\">我省作为科技大省，科教资源丰富，高校和科研院所相对比较集中，集聚了一大批高层次科技人才，经过多年的努力，打下了良好的科研基础，</span><span style=\"font-size:21px;font-family:仿宋\">积累了大量科技成果，是我省科技成果创造和科技成果转化的主要力量。特别是近年来，随着科技的快速发展，我省高校和科研机构科技成果转化活动日趋活跃，为全省经济社会发展提供了有力支撑。但在实践中还存在着科技成果相关管理制度不适应成果转化需要的情况，成果使用、处置事项的审批环节多、周期长，影响了转化的时效性；成果处置收益统一上缴财政，用于人员奖励的支出挤占了工资总额基数，削弱了单位和科技人员科技成果转移转化的积极性。对此，省委、省政府高度重视，要求政府相关部门研究解决好这些问题，在用足用好国家创新政策的同时，不断完善我省的相关政策措施。尤其是国发〔2014〕28号文件发布后，省领导对在我省推广中关村国家自主创新示范区有关试点政策多次作出批示，并组织省政府相关部门加强对中关村自主创新政策的研究，在反复听取各方面意见建议并征求国家相关部委意见的基础上，形成了我省的《意见》。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\">出台《意见》的目的，就是要通过深化省属高校、科研院所等企事业单位科技成果使用、处置和收益管理改革，破除制约科技成果转化的制度性障碍，打通科技成果向现实生产力转化的通道，构建符合科技成果特点和科技成果转化规律的管理新模式；就是要按照权责一致、利益共享、激励与约束并重的原则，为单位和科研人员创新创造“松绑”加力，让创造性劳动的价值得到更好的实现，进一步调动高校、科研机构及广大科技工作者创新创业的积极性和创造性；就是要加快政府职能转变，把一切有利于落实创新驱动发展战略、一切有利于调动科技人员积极性和全社会创新活力、一切有利于推进科技成果转化和产业竞争力提升的因素调动起来，作为政府支持科技创新的着力点和深化科技体制改革的重要举措。</span></p><p style=\"margin-left:0;text-indent:43px\"><strong><span style=\"font-size:21px;font-family:仿宋\">二、</span></strong><strong><span style=\"font-size:21px;font-family:仿宋\">新政策的主要内容</span></strong><strong><span style=\"font-size:21px;font-family: 仿宋\">有哪些？</span></strong></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\">《意见》包括七个部分内容，分别就改革科技成果使用、处置管理制度、建立对科技成果的市场定价机制、改革科技成果收益权管理制度、建立健全科技成果收益分配激励制度、建立科技成果转移转化年度报告制度、建立健全有利于科技成果转移转化的管理制度、实施分红激励等方面做出了一系列制度性安排。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\">在科技成果使用、处置权方面，取消了财政部门和单位主管部门对科技成果转化的审批和备案要求，赋予了科技成果所有单位自由处置权。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\">在科技成果收益权管理方面，成果转化收益不再上缴，留归处置单位自行安排。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family: 仿宋\">在科技成果收益分配激励方面，规定了享受收益分配激励政策的人员范围、比例和相关程序。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family: 仿宋\">在</span><span style=\"font-size:21px;font-family:仿宋\">实施股权和分红激励方面，规定国有及国有控股的相关单位，对做出突出贡献的科技人员等人员，经批准，可按国家相关规定，实施股权奖励、股权出售、股票期权和分红激励。</span></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\">此外，还从一切有利于促进科技成果转移转化的角度出发，积极探索建立符合科技成果特点和科技成果转化规律的管理新模式，对建立科技成果市场定价机制，建立规范、合理、有效的单位科技成果转移转化收入分配和激励制度，落实科技成果向境外转让、许可的管理制度，落实相关职能部门的职责，单位建立健全科技成果转移转化管理制度等做出了较为详细的规定。</span></p><p style=\"text-indent:38px\"><strong><span style=\"font-size:21px;font-family: 仿宋\">三、对科技成果使用、处置管理有何具体规定？</span></strong></p><p style=\"text-indent:38px\"><span style=\"font-size:21px;font-family:仿宋\">首先，将财政性资金资助科研项目形成的科技成果，授权项目承担单位依法取得。也就是项目承担单位对财政性资金资助科研项目形成的科技成果享有所有权、处置权和收益权。</span></p><p style=\"text-indent:38px\"><span style=\"font-size:21px;font-family:仿宋\">其次，取消了科技成果处置的审批程序。科技成果是事业单位国有资产的重要组成部分，其使用、处置一直按照现行事业单位国有资产管理制度执行，即经过单位主管部门或财政部门审批方可进行。新政策取消了财政部门和单位主管部门对科技成果转化的审批和备案要求，将科技成果转化的权利完全授予了成果处置单位，并可自主采取转让、许可、作价入股等方式转移转化科技成果。</span></p><p style=\"text-indent:54px\"><strong><span style=\"font-size:21px;font-family: 仿宋\">四、对建立科技成果市场化定价机制有何具体规定？</span></strong></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\">科技成果的定价是科技成果转化的重要环节，也是保护科技成果完成人和科技成果持有单位合法权益、维护交易公平的一个重要问题。建立由市场决定科技成果交易价格的市场化定价机制，既能够真实反映科技成果的价值，降低交易成本，又能在一定程度上打消单位负责人国有资产流失的顾虑。因此，《意见》规定单位可通过协议定价、技术市场挂牌交易、拍卖等方式确定科技成果价格。同时，为防止交易双方暗箱操作、利益输送等问题，对要求实行协议定价的，应当在本单位公示科技成果的名称和拟交易价格，在此基础上，确定科技成果最终成交价格。此外，涉及科技成果评估的，按相关法律制度规定执行。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><strong><span style=\"font-size:21px;font-family: 仿宋\">五、对科技成果转移转化收益管理有哪些新举措？</span></strong></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\">按照现行事业单位国有资产管理规定，单位利用科技成果对外投资、许可所取得的使用收入留给单位，但单位科技成果的处置收入如转让收入，作为非税收入，必须上缴国家财政，实行“收支两条线”管理。为进一步调动单位的积极性，让单位从科技成果转化活动中受益，并着眼科技成果转化对经济的提质增效升级、改善民生的长远考虑，《意见》规定单位科技成果处置收入全部留归单位自主支配，纳入单位预算，实行统一管理，不再上缴国家财政。</span></p><p style=\"text-indent:43px\"><strong><span style=\"font-size:21px;font-family: 仿宋\">六、对科技成果转移转化收益分配有哪些具体规定？</span></strong></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">现行政策法规对科技人员在科技成果转化中的奖励制度作了明确规定。如《促进科技成果转化法》规定，单位将科技成果转让给他人的，应当从转让该科技成果所取得的净收入中，提取不低于20%的比例，对完成该项科技成果及其转化做出重要贡献的人员给予奖励。《辽宁省自主创新促进条例》也对成果转化奖励作出了具体规定。但由于各种原因，有些奖励制度在部分单位没有得到完全落实，影响了科技人员科技成果转移转化的积极性。为改变这种状况，进一步建立健全知识、技术、管理等由要素市场决定的报酬机制，保障现行的对科技人员的奖励制度的落实，《意见》规定：一是单位的收入分配和股权激励方案要明确对科技成果完成人（团队）、院系（所）以及为科技成果转移转化作出重要贡献的人员、技术转移机构等相关方的收入或股权奖励比例，以保障各方的利益，建立利益共享机制；二是对发明人、共同发明人等在科技成果完成和转移转化中作出重要贡献人员的奖励比例，不得低于有关法律法规规定的最低比例；三是以科技成果作价入股，用于奖励人员的股权超过入股时作价金额50%的，按国家促进科技成果转移转化的有关规定，由单位职工代表大会讨论决定。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><strong><span style=\"font-size:21px;font-family: 仿宋\">七、为落实科技成果转化中对科技人员的奖励制度，在工资管理制度上做了哪些调整？</span></strong></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp; &nbsp;</span><span style=\"font-size:21px;font-family:仿宋\">按照现行有关政策，对事业单位的工资总额实行计划管理，单位用于科技人员奖励的支出部分纳入单位工资总额，并作为单位工资总额基数来核定。为避免单位用于人员奖励的支出部分对单位工资总额基数的挤占，影响单位落实奖励制度的积极性，《意见》规定：单位用于人员奖励的支出部分计入当年单位工资总额，但不纳入工资总额基数。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp; <strong>&nbsp;</strong></span><strong><span style=\"font-size:21px;font-family: 仿宋\">八、在取消科技成果使用、处置的行政审批后，相关政府部门如何加强对科技成果转化活动的监管？</span></strong></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\">在取消对科技成果使用、处置的行政审批后，如何加强对科技成果转移转化活动的监管显得尤为重要。《意见》建立了单位和单位主管部门、财政部门、项目管理机构各个层次相互衔接的科技成果转移转化年度报告制度。《意见》规定：省属各高校、科研院所等事业单位应当按规定将上一年度科技成果转移转化情况，主要包括获得的科技成果情况、科技成果转移转化情况、收入及分配情况等，向单位主管部门报告。单位主管部门应当按规定将年度科技成果转移转化报告报送省科技行政主管部门，同时抄送省人力资源和社会保障部门等相关部门。科技计划（专项、基金）管理机构也应建立相应的科技成果转移转化报告制度。此外，《意见》鼓励科技成果首先在省（国）内使用，科技成果向境外转让、独占许可的，应当依法履行审批程序。涉及国家安全、国家利益和重大社会公共利益的科技成果转移转化，依照相关法律规定管理和实施。对列入《中国禁止出口限制出口技术目录》中禁止出口以及其他影响、损害国家竞争力和国家安全的科技成果，禁止向境外许可或转让。</span></p><p><strong><span style=\"font-size:21px;font-family:仿宋\">　　九、《意见》对加强科技成果转移转化管理上有哪些规定？</span></strong></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\">高校、科研机构作为依法取得的科技成果的权利人，在科技成果转移转化中发挥着重要作用。按照权责一致、激励与约束并重的原则，单位应当建立健全有利于科技成果转移转化的管理制度。《意见》对省属高校、科研院所规定了三点：一是要建立科技成果转移转化重大事项领导班子集体决策制度；二是要规范和优化科技成果转移转化流程，明确转移转化、科技成果报告、知识产权管理、资产管理、评价奖励等工作的责任主体，建立符合科技成果转移转化特点的岗位管理、考核评价和奖励制度；三是要建立科技成果转移转化内部控制制度，以防止私自转移转化、低价处置科技成果、违法从事关联交易等行为。</span></p><p style=\"margin-left:0;text-indent:43px\"><strong><span style=\"font-size:21px;font-family:仿宋\">十、</span></strong><strong><span style=\"font-size:21px;font-family:仿宋\">《意见》对实施股权和分红激励有哪些规定？</span></strong></p><p style=\"text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\">实施股权和分红激励是激发科技人员创新原动力、加快促进科技成果转移转化的重要措施，对建立有利于自主创新和科技成果转化的中长期激励分配机制，充分发挥技术、管理等要素的作用，推动高新技术产业化，具有重要的意义。《意见》规定了实施股权和分红激励政策的单位范围、人员类别、相关程序等，下一步省政府相关部门将制定具体实施办法。在本省实施办法出台前，可参照《财政部、科技部关于中关村国家自主创新示范区企业股权和分红激励实施办法》（财企〔2010〕8号）的相关规定执行。</span></p><p><br/></p>', '0', '0', '1', '2017-11-15 00:00:00', '1', '2017-11-13 13:29:43', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 10:24:31', '1', '');
INSERT INTO `site_policy_rule` VALUES ('d32a8b38060541cebdda18ef88441439', 'ZCFG_d32a8b38060541cebdda18ef88441439', '辽宁省人民政府办公厅转发省科技厅《关于促进辽宁省科技中介发展意见的通知》', '1', '辽宁省人民政府', '<p style=\"text-align: center;\"><span style=\"font-size: 24px;\"><strong><span style=\"font-family: 宋体;\">辽宁省人民政府办公厅转发省科技厅</span><span style=\"font-family: ˎ̥, serif;\"><br/> </span><span style=\"font-family: 宋体;\">关于促进辽宁省科技中介发展意见的通知</span></strong></span></p><p style=\"line-height: 27px\"><span style=\"font-size: 15px;font-family: 宋体\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;各市人民政府，省政府各厅委、各直属机构：</span><span style=\"font-size: 15px;font-family: &#39;Times New Roman&#39;, serif\"></span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span><span style=\"font-size: 15px;font-family: 宋体\">　　&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;经省政府同意，现将省科技厅《关于促进辽宁省科技中介发展的意见》转发给你们，请认真贯彻执行。</span></p><p style=\"text-align:right;line-height:27px\"><span style=\"font-size: 15px;font-family: ˎ̥, serif\">&nbsp;</span></p><p style=\"text-align:right;line-height:27px\"><span style=\"font-size: 15px;font-family: 宋体\">辽宁省人民政府办公厅</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"text-align:right;line-height:27px\"><span style=\"font-size: 15px;font-family: 宋体\">二</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\">○○</span><span style=\"font-size: 15px;font-family: 宋体\">七年十二月二十三日</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size: 15px;font-family: ˎ̥, serif\">&nbsp;</span></p><p style=\"text-align:center;line-height:27px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 宋体;\">关于促进辽宁省科技中介发展的意见</span></strong></span></p><p style=\"line-height: 27px\"><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span><span style=\"font-size: 15px;font-family: 宋体\">　　为深入贯彻落实《中共辽宁省委</span> <span style=\"font-size: 15px;font-family: 宋体\">辽宁省人民政府关于提高科技创新能力加快老工业基地振兴的决定》（辽委发〔</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\">2006</span><span style=\"font-size: 15px;font-family: 宋体\">〕</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\">6</span><span style=\"font-size: 15px;font-family: 宋体\">号）精神，促进我省科技中介的发展，不断提升科技创新能力，推动科技与经济紧密结合，支撑辽宁老工业基地实现全面振兴，现提出如下意见。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span><span style=\"font-size: 15px;font-family: 宋体\">　　<strong>一、促进科技中介发展的重要意义</strong></span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">1.</span><span style=\"font-size: 15px;font-family: 宋体\">促进科技中介发展是提高区域创新能力的必然选择。科技中介是指从事技术扩散、成果转化、科技评估、创新资源配置、创新决策与管理咨询等专业化服务的中介机构和活动，是促进科技与经济紧密结合的桥梁和纽带，是建立科技中介服务体系和完善区域创新体系的重要实现途径，也是全面提升区域创新能力的重要举措。大力促进科技中介发展，有助于科技创新资源的优化配置，降低创新成本，化解创新风险，能够活跃和促进企业技术创新，加速科技成果产业化进程，从而促进区域产业结构的优化升级和创新能力的提高。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">2.</span><span style=\"font-size: 15px;font-family: 宋体\">促进科技中介发展是实现老工业基地全面振兴的现实要求。近年来，我省科技中介机构建设取得长足发展，以创业服务中心、生产力促进中心、技术交易市场等为标志的一大批科技中介机构蓬勃发展，为促进科技成果转化，推动高新技术产业发展和传统产业改造做出了较大贡献，积累了一定经验。但从总体上看，我省的科技中介服务仍存在着发展不平衡，门类不齐全，服务水平、服务质量和人员素质偏低，公共信息基础设施薄弱，人才队伍建设滞后，人才培养机制不完善等问题，远远不能满足建设国家新型产业基地和实现老工业基地全面振兴的需要，成为区域创新体系建设中亟待加强的薄弱环节。因此，大力发展科技中介，加强科技创新服务体系建设，已成为各级政府面临的一项紧迫的重要任务。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span><span style=\"font-size: 15px;font-family: 宋体\">　　<strong>二、促进科技中介发展的指导思想、目标和原则</strong></span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">3.</span><span style=\"font-size: 15px;font-family: 宋体\">指导思想：深入贯彻落实科学发展观，以提升企业技术创新能力，加快建立以企业为主体、市场为导向、产学研相结合的技术创新体系为重点，结合辽宁实际，引导社会力量大力兴办各类科技中介机构，鼓励社会各类中介机构和研究与服务机构开展多种形式的科技服务，引导和支持创新要素向企业集聚，提高科技创新服务能力，为建设国家新型产业基地和实现老工业基地全面振兴提供有力支撑。</span><span style=\"font-size: 15px;font-family: &#39;Times New Roman&#39;, serif\"></span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">4.</span><span style=\"font-size: 15px;font-family: 宋体\">主要目标：利用</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\">5</span><span style=\"font-size: 15px;font-family: 宋体\">年左右的时间，培育一批服务专业化、发展规模化、运行规范化的骨干科技中介机构；造就一支具有较高专业素质的科技中介服务队伍；建立起有利于科技中介健康发展的组织制度、运行机制和政策法规环境；推动科技中介全面发展，基本形成科技创新服务体系，满足全省科技创新的服务需求。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">5.</span><span style=\"font-size: 15px;font-family: 宋体\">基本原则：坚持政府推动与市场调节相结合，坚持发展与规范相结合，坚持全面推进与分类指导相结合，坚持专业化分工与网络化协作相结合。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span><span style=\"font-size: 15px;font-family: 宋体\">　　<strong>三、培育科技中介服务主体和鼓励科技中介服务创新</strong></span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">6.</span><span style=\"font-size: 15px;font-family: 宋体\">组织和引导专业技术力量创办和发展中介机构。鼓励科研单位、高等院校立足于科研设备和人才优势，兴办各类科技中介机构；支持已转制为科技中介机构的科研单位不断提高和完善为社会服务的水平和功能；鼓励国有企业、民营企业与科研单位联合兴办科技企业孵化器或生产力促进中心，盘活存量资产；鼓励大学毕业生创办、领办各类科技中介机构。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">7.</span><span style=\"font-size: 15px;font-family: 宋体\">培育一批骨干科技中介机构。按照我省产业发展重点及产业技术需求，有重点地选择一批行业内具有服务优势的生产力促进中心、科技企业孵化器、技术交易机构等中介服务机构，在共用技术开发平台建设、共用科研服务设备配备、技术转移平台构建、从业人员培训等方面加大支持力度，逐步推进科技中介从业人员素质和能力的提高以及机构服务品质的提升。推动科技中介机构打造精品服务项目和品牌，提升服务质量和水平，发挥示范带头作用。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">8.</span><span style=\"font-size: 15px;font-family: 宋体\">推动科技中介机构向国际化发展。支持资质好、信誉高、运作规范的科技中介机构开展国际合作与交流，通过请进来、走出去的方式使国内机构的服务水平逐步与国际接轨。积极吸引跨国公司和境内外研究机构、高等院校等在我省建立科技中介机构或与我省高等院校、科研机构等联合建立科技中介机构。结合我省产业技术需求，有重点地吸引一批国际知名中介机构来我省开展业务。鼓励海外留学人员回国创办科技中介机构。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">9.</span><span style=\"font-size: 15px;font-family: 宋体\">鼓励有条件的社会各类中介机构和研究与服务机构开展多种形式的科技中介服务。鼓励政府部门所属政策调研、软科学研究等事业单位转变运作机制，在为政府决策服务的同时，面向社会开展科技咨询、评估活动；提高科技情报信息机构的信息采集、分析和综合加工能力，与技术交易机构共同发挥区域技术转移中心的作用；采取有效措施，继续强化农业技术服务机构建设，大力扶持农业</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\">“</span><span style=\"font-size: 15px;font-family: 宋体\">龙头</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\">”</span><span style=\"font-size: 15px;font-family: 宋体\">企业和农村技术经济协作组织的发展，健全、完善农业技术服务体系。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">10.</span><span style=\"font-size: 15px;font-family: 宋体\">大力培育和发展各类科技中介行业协会。进一步加大对行业协会的支持力度，发挥协会的桥梁和纽带作用，鼓励建立行业联盟，提升行业协会在促进行业发展中的代表性和影响力。推动行业协会建立行业自律制度。支持行业协会建立行业行为规范、服务标准、职业操守，提倡科技中介机构和从业人员自觉遵守、共同维护，形成重合同、守信用、诚信经营的行业风尚，使行业发展走上法制化、规范化的轨道。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span><span style=\"font-size: 15px;font-family: 宋体\">　　<strong>四、进一步营造良好的科技中介发展环境</strong></span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">11.</span><span style=\"font-size: 15px;font-family: 宋体\">加大对科技中介机构的资金支持力度。省科技资金应加大对科技中介机构的支持力度，主要用于支持骨干科技中介机构做强做大、科技中介服务人才培训等。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">12.</span><span style=\"font-size: 15px;font-family: 宋体\">加大对科技中介机构的税收优惠政策扶持力度。对符合条件的科技企业孵化器、创业投资服务中心、技术市场和国家大学科技园，可按税法及其有关规定享受企业所得税优惠政策</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\">,</span><span style=\"font-size: 15px;font-family: 宋体\">并在一定期限内免征营业税、房产税和城镇土地使用税。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span><span style=\"font-size: 15px;font-family: 宋体\">　　科技中介机构从事技术转让、技术开发和与之相关的技术咨询、技术服务业务取得的收入，技术交易合同经登记后，可免征营业税、城市维护建设税和教育费附加。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span><span style=\"font-size: 15px;font-family: 宋体\">　　科技中介机构为完成特定服务项目，凡聘请属于海外留学人员和国内享受政府特殊津贴的专家，所支付的咨询费、劳务费用可直接进入成本。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">13.</span><span style=\"font-size: 15px;font-family: 宋体\">鼓励投融资类中介机构的发展。对主要投资于中小高新技术企业的创业投资企业，可按税法及其有关规定享受企业所得税优惠政策。进一步完善贷款担保机制。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">14.</span><span style=\"font-size: 15px;font-family: 宋体\">鼓励中介服务者积极开展科技中介服务。对在我省经济建设和社会发展中，通过科技成果的有效转化，为取得重大效益的科技成果转化项目做出突出贡献的成果创造者、转化实施者和中介服务者，省政府通过科技成果转化奖给予奖励。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">15.</span><span style=\"font-size: 15px;font-family: 宋体\">积极推进政府采购科技中介服务工作。各级政府和相关部门要积极培育科技中介服务市场，通过招标、委托等方式，在政策咨询、科技成果评估与转化、重大项目分析与论证和重要工作部署等方面，要进一步依靠科技中介机构。在推进决策科学化与民主化的同时，提高科技中介机构的服务能力。</span><span style=\"font-size: 15px;font-family: &#39;Times New Roman&#39;, serif\"></span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">16.</span><span style=\"font-size: 15px;font-family: 宋体\">加强科技中介服务网络化建设。鼓励各种类型中介机构通过联合建立网络化服务体系，为科技创新提供一体化服务。加强科技中介机构与科研单位、高等院校的联合与协作，通过广泛建立协作网络，为开展科技中介业务提供重要支撑。建立统一的技术信息平台，整合政府部门、企业、科研院所、高校和信息分析机构的信息资源，实现服务与需求信息的对接，使其成为辽宁省科技成果转化的信息桥梁和枢纽。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">17.</span><span style=\"font-size: 15px;font-family: 宋体\">加强科技中介人才队伍建设。推进科技中介服务人才的培养和引进，建立与完善科技中介行业的分配与激励机制，增强行业吸引力，吸引更多的高素质人才。开展科技中介人才培训，鼓励优秀的从业人员出国培训，提高科技中介从业人员的专业素质和服务水平。鼓励国内科技中介机构间的人才交流与合作，同时通过政府及民间多种途径的合作，加强与国外知名科技中介机构的人才交流与合作，拓展业务范围，提高国际竞争力。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">18.</span><span style=\"font-size: 15px;font-family: 宋体\">鼓励相关科技中介行业协会推行科技咨询（中介）职业水平评价制度，建立科技咨询（中介）职业水平评价体系。在条件成熟的科技中介领域，开展科技咨询师职业水平评价工作。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span><span style=\"font-size: 15px;font-family: 宋体\">　　<strong>五、加强对科技中介发展工作的领导</strong></span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">19.</span><span style=\"font-size: 15px;font-family: 宋体\">科技中介事业的发展壮大事关提高辽宁区域科技创新能力的全局。全省各地区、各部门要进一步统一思想，从实现老工业基地全面振兴、建设创新型辽宁的战略高度认识科技中介发展的重要性，结合本地区、本部门特点做好科技中介发展规划，制定和完善各项配套政策。明确科技中介的法律地位，保障科技中介服务的合法权利和收益。要不断研究和把握科技中介发展的规律和特点，及时解决实践中出现的新情况、新问题。</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\"><br/> </span>　　<span style=\"font-size: 15px;font-family: ˎ̥, serif\">20.</span><span style=\"font-size: 15px;font-family: 宋体\">各级科技行政管理部门作为科技中介的归口管理部门，要积极协调有关部门加强对科技中介的管理和监督，在促进科技中介机构发展中相互协作，形成合力，促进科技中介机构健康有序地发展。各地区、各部门要大力宣传发展科技中介对科技创新的重大意义和贡献，促进社会了解、重视和支持科技中介机构，充分调动各方面的积极性，共同推动科技中介的全面发展，为辽宁老工业基地全面振兴做出重要贡献。</span></p><p style=\"line-height: 27px\"><span style=\"font-size: 15px;font-family: ˎ̥, serif\">&nbsp;</span></p><p style=\"text-align:right;line-height:27px\"><span style=\"font-size: 15px;font-family: 宋体\">省科技厅</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p><span style=\"font-size: 15px;font-family: 宋体\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;二</span><span style=\"font-size: 15px;font-family: ˎ̥, serif\">○○</span><span style=\"font-size: 15px;font-family: 宋体\">七年十二月十一日</span></p><p><br/></p>', '0', '0', '1', '2010-08-27 00:00:00', '1', '2017-11-13 13:39:12', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 10:26:45', '0', '');
INSERT INTO `site_policy_rule` VALUES ('d36ede21bbfe4c1d949a45c9a0290f0d', 'ZCFG_d36ede21bbfe4c1d949a45c9a0290f0d', '沈阳市民营科技企业促进条例', '2', '沈阳市民营科技企业促进', '<p style=\"background: white; text-align: center;\"><br/></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">2002</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">年10月29日沈阳市第十二届人民代表大会常务委员会第四十一次会议通过;2002年11月29日辽宁省第九届人民代表</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">大会常务委员会第二十四次会议批准。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一条 为了促进民营科技企业的发展,保障民营科技企业的合法权益,根据《中华人民共和国科学技术进步法》及有关法律、法规的规定,结合本市实际,制定本条例。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二条 本条例所称民营科技企业是指由公民、法人或其他组织依法设立,实行自筹资金、自愿组合、自主经营、自负盈亏的经营机制,主要从事科技成果产业化以及技术开发、技术转让、技术咨询、技术服务等活动的经济实体。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三条 本市行政区域内民营科技企业的创办与发展适用于本条例。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四条 市科技行政部门负责民营科技企业的政策指导、协调和服务,组织本条例的实施。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">区、县(市)科技行政部门负责本行政区域内民营科技企业的组织协调和业务指导。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">市政府有关部门应当按照职责分工,加强对民营科技企业的扶持、引导和服务,依法进行管理和监督。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五条 市、区、县(市)政府应当将民营科技企业纳入国民经济和社会发展规划,鼓励、扶持、引导和促进民营科技企业健康发展。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六条 民营科技企业应当具备下列条件:</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">一)符合国家和地方产业政策、技术政策;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">二)拥有合法的专利、专有技术、科技成果、高新技术产品或有从事技术开发、技术转让、技术咨询、科技服务的能力;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">三)应当有与技术业务相适应的科技人员;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">四)科学研究和技术开发投入占企业总收入的2%以上；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">五)具有技术性收入或者科技产品销售收入;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">六)有健全的科技管理制度;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">七)法律、法规规定的其他条件。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七条 民营科技企业享有以下权利:</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">一)申请承担各级、各类科技计划项目、技术改造项目和参与公开项目竞标;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">二)申请科技成果、新产品鉴定和高新技术企业、高新技术产品认定;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">三)依照国家有关规定参与国际科学技术合作和交流,申请进出口经营权和在境外设立分支机构、销售网点;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">四)依照国家规定从境外引进资金、技术、人才、设备或与外商合资经营、合作经营;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">五)在资金支持、创业扶持、技术创新、市场开拓、社会服务等方面享有国家和省的有关待遇;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">六)法律、法规规定的其他权利。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第八条 民营科技企业应当履行下列义务:</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">一)依法经营、照章纳税;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">二)以合法的专利、专有技术、技术成果申请计划立项、成果鉴定、评奖、参加高新技术产品认定;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">三)确保必要的研究开发投入;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">四)开展经常性技术培训,组织专业技术人员接受继续教育;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;(</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">五)法律、法规规定的其他义务。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第九条 民营科技企业及科技人员的知识产权受法律保护,任何单位和个人不得侵犯。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">民营科技企业可以在劳动合同或技术保密合同中,约定竞业限制条款,也可以订立专门的竞业限制合同,竞业限制合同最长期限不得超过三年,双方另有约定的除外。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十条 市、区、县(市)科技行政部门对民营科技企业的专利实施项目应当择优扶持,对重大专利实施项目应当优先立项,对重大发明专利的维护费可以给予一定补贴。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十一条 鼓励公民、法人和其他组织创办各种类型的民营科技企业。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">支持科研院所、高等院校及科技人员利用自有科技成果创办民营科技企业。专利、非专利技术和科技成果经评估作价,可以作为企业的注册资本。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">科技人员在与科技成果所有权单位达成协议后,可以带职务科技成果创办民营科技企业。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十二条 国有企业、科研院所、高等院校的在职科技人员经单位批准,可以到民营科技企业工作,或在保证完成本职工作和不侵犯本单位技术权益、经济利益的前提下,到民营科技企业兼职,取得报酬。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十三条 民营科技企业可以采取多种形式参与国有、集体企业产权制度改革,对自身进行股份制改造的,其内部职工可以投资人股,也可以设立技术股、管理股,企业内部职工持股比例不受限制。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十四条 市政府应当培育技术产权交易市场,促进技术产权的合理流动,为民营科技企业提供规范、公开、便捷的技术产权交易服务。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十五条 市、区、县(市)政府应当制定政策,鼓励民营科技企业创办风险投资公司和相关咨询服务机构,引导国内外资本所有者在本市设立风险投资机构;应当开辟多种融资渠道,扩大民营科技贷款担保资本金规模。鼓励担保金实行公司化运作,提高担保金的使用效益。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十六条 市政府应当为民间组织开展民营科技企业信用评价创造条件。由资信评估机构对民营科技企业经营信用、资本信用、质量信用、完税信用等进行评价或评级,信用等级可以作为科技立项和信贷支持的依据。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十七条 市、区、县(市)政府可以建立专项资金,鼓励民营科技企业开发高新技术产品,对其择优重点扶持。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十八条 市、区、县(市)政府应当建立用于孵化中小科技企业的创业机构,为民营科技企业创业和成长创造条件。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十九条 鼓励科研院所、大专院校、中介组织及其他专业服务机构为民营科技企业提供技术支持、人才培训、信息咨询等服务,形成社会化的服务体系。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十条 民营科技企业人员可以申请专业技术职称(资格)。专业技术资格考试、评审由人事行政部门按规定办理。其专业技术职务的聘任和待遇由所在单位自主决定。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十一条 金融机构应当支持民营科技企业的发展,根据国家信贷政策和民营科技企业的需要,予以信贷扶持。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十二条 市、区、县(市)政府应当对做出突出贡献的民营科技企业和个人,予以表彰和奖励。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十三条 行政机关工作人员办理民营科技企业有关事项时,滥用职权、玩忽职守、徇私舞弊的,由其所在单位或者上级主管部门给予行政处分。构成犯罪的,由司法机关依法追究刑事责任。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十四条 本条例自2003年1月1日起施行</span></p><p><br/></p>', '0', '0', '1', '2015-07-07 00:00:00', '1', '2017-11-13 14:17:27', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 10:28:30', '0', '');
INSERT INTO `site_policy_rule` VALUES ('d56a62a36f234a43985cd36c4cdf01ba', 'ZCFG_d56a62a36f234a43985cd36c4cdf01ba', '辽宁省科技企业孵化器（高新技术创业服务中心）认定和管理办法', '1', '辽宁省科技厅', '<p style=\"text-align: center;\"><span style=\"font-size: 24px;\"><strong><span style=\"font-family: 宋体; color: rgb(51, 51, 51);\">辽宁省科技企业孵化器（高新技术创业服务中心）认定和管理办法</span></strong></span></p><p><strong><span style=\"font-size:18px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span></strong></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">第一章</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">总</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">则</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第一条　为提高科技创新能力，加快科技成果转化，培育科技型中小企业，发展高新技术产业，规范我省科技企业孵化器（高新技术创业服务中心）的认定和管理工作，促进高新技术创业服务中心的健康发展，根据《中共辽宁省委辽宁省人民政府关于提高科技创新能力加快老工业基地振兴的决定》的精神，参照科技部《科技企业孵化器（高新技术创业服务中心）认定和管理办法》，制定本办法。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第二条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">科技企业孵化器（也称高新技术创业服务中心，以下简称创业中心）是以促进科技成果转化、培育高新技术企业和企业家为宗旨的科技创业服务机构。创业中心是我省创新体系的重要组成部分，是区域创新体系的重要核心内容。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第三条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">省科技厅负责对全省创业中心进行宏观管理和业务指导工作。</span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">第二章</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">创业中心主要功能与目标</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第四条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">创业中心的主要功能是以科技型中小企业为服务对象，为入孵企业提供研发、中试生产、经营的场地和办公方面的共享设施，提供政策、管理、法律、财务、融资、市场推广和培训等方面的服务，以降低企业的创业风险和创业成本，提高企业的成活率和成功率，为社会培养成功的科技企业和企业家。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第五条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">创业中心要建立适应社会主义市场经济的运行机制，通过各种途径和手段完善服务功能。在提供高品质服务的同时逐步实现自收自支、自主经营、自我约束、自我发展的良性循环，同时要充分利用当地科研院所、高等院校、企业和企业服务机构的研究、试验、测试、生产等条件，扩大自身的服务功能，提高孵化服务水平。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第六条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">为提高创业中心的服务水平和质量，鼓励建立专业技术型创业中心。专业技术型创业中心是指围绕某一特定技术领域，在孵化对象、孵化条件、服务内容和管理团队上实现专业化，培育和发展某一特定技术领域的高新技术企业的一种创业中心形式。</span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">第三章</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">创业中心的认定和管理</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第七条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">省科技厅负责辽宁省创业中心的认定和管理工作。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第八条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">申请认定辽宁省高新技术创业服务中心，应当具备下列条件：</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 1</span><span style=\"font-size:11px;color:#333333\">．发展方向明确，符合本办法第二条和第四条所规定的条件；</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 2</span><span style=\"font-size:11px;color:#333333\">．领导班子得力，机构设置合理，管理人员中具有大专以上学历的占</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">70%</span><span style=\"font-size:11px;color:#333333\">以上；</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 3</span><span style=\"font-size:11px;color:#333333\">．可自主支配孵化场地面积在</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">8000</span><span style=\"font-size:11px;color:#333333\">平方米以上（如是专业技术型创业中心，则可自主支配孵化场地面积</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">4000</span><span style=\"font-size:11px;color:#333333\">平方米以上），其中孵化企业使用的场地占</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2/3</span><span style=\"font-size:11px;color:#333333\">以上；</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 4</span><span style=\"font-size:11px;color:#333333\">．服务设施齐备，服务功能强，可为企业提供商务、资金、信息、咨询、市场、培训、技术开发与交流、国际合作等多方面的服务；</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 5</span><span style=\"font-size:11px;color:#333333\">．管理规范，具有严格的财务管理制度，自身及在孵企业的统计数据齐全，并能按要求及时上报相关统计数据；</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 6</span><span style=\"font-size:11px;color:#333333\">．在孵企业达</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">50</span><span style=\"font-size: 11px;color:#333333\">家以上（如是专业技术型创业中心，则在孵化企业应达</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">30</span><span style=\"font-size:11px;color:#333333\">家以上）；</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 7</span><span style=\"font-size:11px;color:#333333\">．累计毕业企业</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">15</span><span style=\"font-size: 11px;color:#333333\">家以上，毕业企业及在孵企业为社会提供</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">800</span><span style=\"font-size:11px;color:#333333\">个以上的就业机会（如是专业技术型创业中心，则毕业企业在</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">10</span><span style=\"font-size:11px;color:#333333\">家以上，毕业企业及在孵企业为社会提供</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">400</span><span style=\"font-size: 11px;color:#333333\">个以上的就业机会）；</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 8</span><span style=\"font-size:11px;color:#333333\">．创业中心自身拥有</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">200</span><span style=\"font-size: 11px;color:#333333\">万元以上的种子资金或孵化资金，并与创业投资、担保机构等建立了正常的业务联系；</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 9</span><span style=\"font-size:11px;color:#333333\">．实际运营时间在</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size: 11px;color:#333333\">年以上，经营状况良好；</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 10</span><span style=\"font-size:11px;color:#333333\">．专业技术型创业中心自身应具备专业技术平台或专业化的中试基地，并具备专业化的技术咨询和专业化的管理培训能力。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第九条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">辽宁省高新技术创业服务中心的孵化企业应当具备以下条件：</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 1</span><span style=\"font-size:11px;color:#333333\">．企业注册及办公场所必须在创业中心孵化场地内；</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 2</span><span style=\"font-size:11px;color:#333333\">．属新注册企业或申请进入创业中心前企业成立时间不超过</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size:11px;color:#333333\">年；</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 3</span><span style=\"font-size:11px;color:#333333\">．企业在创业中心孵化的时间一般不超过</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3</span><span style=\"font-size:11px;color:#333333\">年；</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 4</span><span style=\"font-size:11px;color:#333333\">．企业注册资金一般不得超过</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">200</span><span style=\"font-size:11px;color:#333333\">万元；</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 5</span><span style=\"font-size:11px;color:#333333\">．属迁入企业的，上年营业收入一般不得超过</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">200</span><span style=\"font-size:11px;color:#333333\">万元；</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 6</span><span style=\"font-size:11px;color:#333333\">．企业租用创业中心孵化场地面积低于</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1000</span><span style=\"font-size:11px;color:#333333\">平方米；</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 7</span><span style=\"font-size:11px;color:#333333\">．企业从事研究、开发、生产的项目或产品应属于科学技术部等部门颁布的《中国高新技术产品目录》范围；</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp;</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 8</span><span style=\"font-size:11px;color:#333333\">．企业的负责人是熟悉本企业产品研究、开发的科技人员。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第十条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">辽宁省高新技术创业服务中心的毕业企业应当具备以下条件之中至少两条：</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 1</span><span style=\"font-size:11px;color:#333333\">．经省级科技行政主管部门认定为高新技术企业；</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 2</span><span style=\"font-size:11px;color:#333333\">．有</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size: 11px;color:#333333\">年以上的运营期，经营状况良好，主导产品有一定的生产规模，年营业总收入达</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">500</span><span style=\"font-size:11px;color:#333333\">万元以上，且有</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">100</span><span style=\"font-size: 11px;color:#333333\">万元以上的固定资产和自有资金。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> 3</span><span style=\"font-size:11px;color:#333333\">．建立了现代企业制度和健全的财务制度。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第十一条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">申报辽宁省高新技术创业服务中心，应首先向所在地市科技行政主管部门或省级以上高新区管委会提出申请，经审核后，由所在地市科技行政主管部门或省级以上高新区管委会上报省科技厅。省科技厅组织专家评审，并依据评审意见进行认定。对符合本办法第八条、第九条和第十条的高新技术创业服务中心颁发</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">“</span><span style=\"font-size:11px;color:#333333\">辽宁省高新技术创业服务中心</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">”</span><span style=\"font-size: 11px;color:#333333\">标牌，予以公布。被认定为辽宁省高新技术创业服务中心的单位与其主管部门的隶属关系不变。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第十二条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">省科技厅每年将对辽宁省高新技术创业服务中心进行考核，对连续</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size:11px;color:#333333\">年达不到条件的高新技术创业服务中心，将取消辽宁省高新技术创业服务中心的资格。</span></p><p style=\"line-height:27px\"><span style=\"font-size:11px;color:#333333\">第四章</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">管理与扶持</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第十三条　省科技厅是辽宁省高新技术创业服务中心的业务归口管理部门，负责创业中心的规划布局和认定，并对创业中心的发展进行指导和服务。地级以上市科技行政主管部门或省级以上高新区管委会是推进创业中心建设与发展的主体力量，要切实加强对创业中心的协调与领导。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第十四条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">根据国家和省有关政策法规，各地政府及其相关部门应在规划、用地、财政、税收等方面为创业中心提供政策支持。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第十五条　经认定的创业中心，省、市科技行政主管部门或高新区管委会将在科技计划项目的立项、信息交流、人才培训等方面优先支持。对运行高效、发展良好的创业中心，优先推荐申报国家级创业中心。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第十六条　省科技厅将全省创业中心工作纳入辽宁省科技发展计划。各级地方科技行政主管部门和省级以上高新区管委会要将创业中心工作纳入当地的科技发展计划，为创业中心的建设和发展提供必要的支持。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第十七条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">省科技厅支持和鼓励各级地方政府建立社会公益性的创业中心，引导带动地方创新体系建设；支持和鼓励企业、个人及其它机构创办多种形式的创业中心。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;color:#333333\">第十八条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;color:#333333\">省和地方各级科技行政主管部门将不定期对创业中心的工作进行考评，并对在创业中心工作中做出突出贡献的单位和个人给予表彰。</span></p><p><span style=\"font-size:11px;font-family:宋体;color:#333333\">第五章</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">附</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">则</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第十九条　各级地方科技行政主管部门可参照本办法制定市级高新技术创业服务中心认定和管理办法。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第二十条　本办法自发布之日起施行。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第二十一条　本办法由省科技厅负责解释。</span></p><p><br/></p>', '0', '0', '1', '2009-01-15 00:00:00', '1', '2017-11-13 13:34:09', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 10:31:09', '0', '');
INSERT INTO `site_policy_rule` VALUES ('d81b6b320e9c4a10b879d3c60c888328', 'ZCFG_d81b6b320e9c4a10b879d3c60c888328', '国家科学技术奖励条例', '0', '全国人民代表大会', '<h1 style=\"background: white; text-align: center;\"><span style=\"font-size:17px;color:#2993C7\">国家科学技术奖励条例</span></h1><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国务院总理温家宝2003年11月20日签署第396号国务院令，发布施行《国务院关于修改〈国家科学技术奖励条例〉的决定》。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一章</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">总</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">则</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第一条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">为了奖励在科学技术进步活动中做出突出贡献的公民、组织，调动科学技术工作者的积极性和创造性，加速科学技术事业的发展，提高综合国力，制定本条例。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国务院设立下列国家科学技术奖：</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）国家最高科学技术奖；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）国家自然科学奖；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）国家技术发明奖；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）国家科学技术进步奖；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（五）中华人民共和国国际科学技术合作奖。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家科学技术奖励贯彻尊重知识、尊重人才的方针。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家维护国家科学技术奖的严肃性。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家科学技术奖的评审、授予，不受任何组织或者个人的非法干涉。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国务院科学技术行政部门负责国家科学技术奖评审的组织工作。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第六条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家设立国家科学技术奖励委员会，国家科学技术奖励委员会聘请有关方面的专家、学者组成评审委员会，依照本条例的规定，负责国家科学技术奖的评审工作。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家科学技术奖励委员会的组成人员人选由国务院科学技术行政部门提出，报国务院批准。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第七条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">社会力量设立面向社会的科学技术奖，应当在科学技术行政部门办理登记手续。具体办法由国务院科学技术行政部门规定。&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">社会力量经登记设立的面向社会的科学技术奖，在奖励活动中不得收取任何费用。&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二章</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家科学技术奖的设置</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第八条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家最高科学技术奖授予下列科学技术工作者：</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）在当代科学技术前沿取得重大突破或者在科学技术发展中有卓越建树的；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）在科学技术创新、科学技术成果转化和高技术产业化中，创造巨大经济效益或者社会效益的。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家最高科学技术奖每年授予人数不超过2名。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第九条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家自然科学奖授予在基础研究和应用基础研究中阐明自然现象、特征和规律，做出重大科学发现的公民。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">前款所称重大科学发现，应当具备下列条件：</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）前人尚未发现或者尚未阐明；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）具有重大科学价值；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）得到国内外自然科学界公认。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家技术发明奖授予运用科学技术知识做出产品、工艺、材料及其系统等重大技术发明的公民。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">前款所称重大技术发明，应当具备下列条件：</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）前人尚未发明或者尚未公开；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）具有先进性和创造性；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）经实施，创造显著经济效益或者社会效益。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十一条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家科学技术进步奖授予在应用推广先进科学技术成果，完成重大科学技术工程、计划、项目等方面，做出突出贡献的下列公民、组织：</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）在实施技术开发项目中，完成重大科学技术创新、科学技术成果转化，创造显著经济效益的；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）在实施社会公益项目中，长期从事科学技术基础性工作和社会公益性科学技术事业，经过实践检验，创造显著社会效益的；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）在实施国家安全项目中，为推进国防现代化建设、保障国家安全做出重大科学技术贡献的；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）在实施重大工程项目中，保障工程达到国际先进水平的。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">前款第（四）项重大工程类项目的国家科学技术进步奖仅授予组织。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十二条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">中华人民共和国国际科学技术合作奖授予对中国科学技术事业做出重要贡献的下列外国人或者外国组织：</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）同中国的公民或者组织合作研究、开发，取得重大科学技术成果的；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）向中国的公民或者组织传授先进科学技术、培养人才，成效特别显著的；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）为促进中国与外国的国际科学技术交流与合作，做出重要贡献的。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十三条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家最高科学技术奖、中华人民共和国国际科学技术合作奖不分等级。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家自然科学奖、国家技术发明奖、国家科学技术进步奖分为一等奖、二等奖2个等级；对做出特别重大科学发现或者技术发明的公民，对完成具有特别重大意义的科学技术工程、计划、项目等做出突出贡献的公民、组织，可以授予特等奖。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家自然科学奖、国家技术发明奖、国家科学技术进步奖每年奖励项目总数不超过400项。&nbsp;</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第三章</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家科学技术奖的评审和授予</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十四条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家科学技术奖每年评审一次。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十五条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家科学技术奖候选人由下列单位和个人推荐：</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（一）省、自治区、直辖市人民政府；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（二）国务院有关组成部门、直属机构；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（三）中国人民解放军各总部；</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">（四）经国务院科学技术行政部门认定的符合国务院科学技术行政部门规定的资格条件的其他单位和科学技术专家。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">前款所列推荐单位推荐的国家科学技术奖候选人，应当根据有关方面的科学技术专家对其科学技术成果的评审结论和奖励种类、等级的建议确定。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">香港、澳门、台湾地区的国家科学技术奖候选人的推荐办法，由国务院科学技术行政部门规定。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">中华人民共和国驻外使馆、领馆可以推荐中华人民共和国国际科学技术合作奖的候选人。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十六条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">推荐的单位和个人限额推荐国家科学技术奖候选人；推荐时，应当填写统一格式的推荐书，提供真实、可靠的评价材料。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十七条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">评审委员会作出认定科学技术成果的结论，并向国家科学技术奖励委员会提出获奖人选和奖励种类及等级的建议。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家科学技术奖励委员会根据评审委员会的建议，作出获奖人选和奖励种类及等级的决议。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家科学技术奖的评审规则由国务院科学技术行政部门规定。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十八条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国务院科学技术行政部门对国家科学技术奖励委员会作出的国家科学技术奖的获奖人选和奖励种类及等级的决议进行审核，报国务院批准。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第十九条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家最高科学技术奖报请国家主席签署并颁发证书和奖金。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家自然科学奖、国家技术发明奖、国家科学技术进步奖由国务院颁发证书和奖金。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">中华人民共和国国际科学技术合作奖由国务院颁发证书。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家最高科学技术奖的奖金数额由国务院规定。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家自然科学奖、国家技术发明奖、国家科学技术进步奖的奖金数额由国务院科学技术行政部门会同财政部门规定。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">国家科学技术奖的奖励经费由中央财政列支。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第四章</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">罚</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">则</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十一条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">剽窃、侵夺他人的发现、发明或者其他科学技术成果的，或者以其他不正当手段骗取国家科学技术奖的，由国务院科学技术行政部门报国务院批准后撤销奖励，追回奖金。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十二条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">推荐的单位和个人提供虚假数据、材料，协助他人骗取国家科学技术奖的，由国务院科学技术行政部门通报批评；情节严重的，暂停或者取消其推荐资格；对负有直接责任的主管人员和其他直接责任人员，依法给予行政处分。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十三条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">社会力量未经登记，擅自设立面向社会的科学技术奖的，由科学技术行政部门予以取缔。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">社会力量经登记设立面向社会的科学技术奖，在科学技术奖励活动中收取费用的，由科学技术行政部门没收所收取的费用，可以并处所收取的费用1倍以上3倍以下的罚款；情节严重的，撤销登记。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十四条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">参与国家科学技术奖评审活动和有关工作的人员在评审活动中弄虚作假、徇私舞弊的，依法给予行政处分。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第五章</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">附</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family: 宋体;color:#333333\">则</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十五条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">国务院有关部门根据国防、国家安全的特殊情况，可以设立部级科学技术奖。具体办法由国务院有关部门规定，报国务院科学技术行政部门备案。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">省、自治区、直辖市人民政府可以设立一项省级科学技术奖。具体办法由省、自治区、直辖市人民政府规定，报国务院科学技术行政部门备案。</span></p><p style=\"text-indent: 36px;background: white\"><span style=\"font-size:18px;font-family:宋体;color:#333333\">第二十六条</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">&nbsp;</span><span style=\"font-size:18px;font-family:宋体;color:#333333\">本条例自公布之日起施行。1993年6月28日国务院修订发布的《中华人民共和国自然科学奖励条例》、《中华人民共和国发明奖励条例》和《中华人民共和国科学技术进步奖励条例》同时废止。</span></p><p><br/></p>', '0', '0', '1', '2003-11-20 00:00:00', '1', '2017-11-13 13:13:45', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:53:21', '0', '');
INSERT INTO `site_policy_rule` VALUES ('dc90cd3bc3214b9a9653e22e969ecd37', 'ZCFG_dc90cd3bc3214b9a9653e22e969ecd37', '沈阳市农村科技推广奖实施细则', '2', '沈阳市科学技术局', '<p style=\"text-align: center;\"><br/></p><p style=\"text-align:center;line-height:40px\"><span style=\"font-size: 21px;font-family: 宋体\">第一章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">总</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">则</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">为促进农业和农村科技进步，表彰在推动农村科技发展中做出突出贡献的单位和个人，根据《沈阳市科学技术奖励办法》的规定，制定本细则。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">本细则适用于市农村科技推广奖的申报、推荐、评审、授奖等有关活动。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">市农村科技推广奖是市人民政府设立的科学技术奖的奖项之一，每年评审和奖励一次。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 18px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">市农村科技推广奖评审工作实行公开、公平、公正的原则，并接受社会各界的监督。</span></p><p style=\"text-align:center;line-height:40px\"><span style=\"font-size: 21px;font-family: 宋体\">第二章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">奖励范围</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">在我市行政区域内实施应用一年以上，有一定应用规模，经过技术评价，取得明显技术效果、经济效益、社会效益或生态效益，为农村经济发展做出突出贡献的科技成果均属授奖范围。</span></p><p style=\";line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（一）</span><span style=\"font-size: 9px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 9px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">促进现代农业发展的新品种、新产品、新技术；</span></p><p style=\"text-indent: 1px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（二）</span><span style=\"font-size: 9px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 9px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">引进、消化、吸收以及开发、推广已有的先进科技成果并取得技术突破或重大效益的成果；</span></p><p style=\";line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（三）</span><span style=\"font-size: 9px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 9px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">有助于改善农村民生的先进科技成果；</span></p><p style=\";line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（四）</span><span style=\"font-size: 9px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 9px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">综合开发利用县域资源的成果；</span></p><p style=\";line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（五）</span><span style=\"font-size: 9px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 9px;font-family: &#39;Times New Roman&#39;, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">先进的农业科技管理研究成果。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><strong><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span></strong></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">第三章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">获奖条件</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">根据项目的技术水平、创新程度、推广应用规模、经济（社会、生态）效益以及对农村科技发展的作用，设置一、二、三等奖，其中：一等奖项目数不超过项目总数的</span><span style=\"font-size: 21px;font-family: Simsun, serif\">10%</span><span style=\"font-size: 21px;font-family: 宋体\">，二等奖项目数不超过项目总数的</span><span style=\"font-size: 21px;font-family: Simsun, serif\">30%</span><span style=\"font-size: 21px;font-family: 宋体\">，三等奖项目数不得超过项目总数的</span><span style=\"font-size: 21px;font-family: Simsun, serif\">60%</span><span style=\"font-size: 21px;font-family: 宋体\">。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">市农村科技推广奖奖励标准。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">一等奖，项目总体技术水平和主要技术指标达到国际先进水平或国内领先水平，有大规模的推广应用，创造了巨大的经济、社会或生态效益的；有关战略、政策、规划、法规草案及其他软科学成果，经实施对振兴农村经济起到重要促进作用的或创造显著的经济、社会或生态效益的。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">二等奖，项目总体技术水平和主要技术指标达到国内领先水平或国内先进水平，有较大规模的推广应用，创造了重大的经济、社会或生态效益的；有关战略、政策、规划、法规草案及其他软科学成果，经实施对振兴农村经济起到促进作用或取得较好的经济、社会或生态效益的。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">模的推广应用，创造了显著的经济、社会或生态效益的；有关战略、政策、规划、法规草案及其他软科学成果，经实施对振兴农村经济起到一定促进作用或取得一定的经济、社会或生态效益的。</span></p><p style=\"text-align:center;line-height:40px\"><span style=\"font-size: 21px;font-family: 宋体\">第四章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">评审机构</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">设立市农村科技推广奖评审委员会（以下简称评审委员会），由市科学技术局（以下简称市科技局）依照本细则的规定聘请专家、学者，组织开展市农村科技推广奖的评审工作。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">评审委员会设主任委员</span><span style=\"font-size: 21px;font-family: Simsun, serif\">1</span><span style=\"font-size: 21px;font-family: 宋体\">人，由市科技局分管农村科技工作的领导担任；副主任委员</span><span style=\"font-size: 21px;font-family: Simsun, serif\">1-2</span><span style=\"font-size: 21px;font-family: 宋体\">人，由评审委员会成员共同推选的知名专家担任；委员不超过</span><span style=\"font-size: 21px;font-family: Simsun, serif\">30</span><span style=\"font-size: 21px;font-family: 宋体\">名，由专业领域专家、学者和管理专家担任，由市科技局提出人选，每年按一定比例进行轮换。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">评审委员会主要职责：</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（一）拟定市农村科技推广奖评审的具体方案；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（二）负责市农村科技推广奖的评审，提出拟授奖项目、人员以及奖励等级建议；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（三）对有争议的获奖项目进行终审裁决。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">评审委员会下设办公室，具体职责是：</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（一）组织、指导奖励申报工作；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（二）负责项目申报材料的形式审查；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（三）筹备召开评审会议；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（四）承办评审委员会交办的其他事项。</span></p><p style=\"text-align:center;line-height:40px\"><span style=\"font-size: 21px;font-family: 宋体\">第五章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">申报条件</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">我市行政区域内的单位和个人（包括中央、省驻沈单位及个人）研究、开发、推广、应用的科技成果，符合本细则第五条规定，均可申报市农村科技推广奖。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">凡存在知识产权纠纷及其他争议的项目，在未解决争议前，不得申报奖励。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">两个以上单位共同完成的项目，由项目第一完成单位牵头组织申报，不得重复申报。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">特殊成果应当符合以下规定：</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（一）农作物新品种应当通过省品种审定委员会审定或认定。家畜、家禽、牧草、水产新品种应当经全国相关新品种审定委员会审定；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（二）肥料、土壤调理剂、动植物生长调节剂、农药新产品、复混肥、配方肥、饲料等新产品应当有相关部门批准颁发的产品登记证；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（三）新兽药和兽药新制剂以及兽用新生物制品应当符合国务院《兽药管理条例》和《新兽药及兽药新制剂管理办法》、《兽用新生物制品管理办法》的规定；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（四）以工业品为主的项目应当有企业标准和符合有关主管部门批准的产品标准；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（五）食品、药品、化妆品类项目应当符合国家主管部门批准的产品标准，并获生产许可证；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（六）对于通过转基因技术获得的生物品种及产品，应当符合科技部《基因工程安全管理办法》以及《农业生物基因工程安全管理实施细则》的规定。</span></p><p style=\"text-align:center;line-height:40px\"><span style=\"font-size: 21px;font-family: 宋体\">第六章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">申报材料</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">申报市农村科技推广奖的单位和个人应当提供以下材料：</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（一）市农村科技推广奖项目申报书；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（二）区、县（市）、开发区以上有关部门出具的技术评价材料；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（三）应用证明材料，一般不少于</span><span style=\"font-size: 21px;font-family: Simsun, serif\">3</span><span style=\"font-size: 21px;font-family: 宋体\">份；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（四）效益证明材料；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（五）主要完成人近期一寸免冠照片；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（六）市农村科技推广奖项目评审表；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（七）其他有关证明材料。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">申报书及相关证明材料按</span><span style=\"font-size: 21px;font-family: Simsun, serif\">A4</span><span style=\"font-size: 21px;font-family: 宋体\">纸大小统一装订成册，一式</span><span style=\"font-size: 21px;font-family: Simsun, serif\">2</span><span style=\"font-size: 21px;font-family: 宋体\">份；项目评审表单独打印，一式</span><span style=\"font-size: 21px;font-family: Simsun, serif\">30</span><span style=\"font-size: 21px;font-family: 宋体\">份；除按要求申报书中粘贴主要完成人照片外，另附制作证书照片</span><span style=\"font-size: 21px;font-family: Simsun, serif\">1</span><span style=\"font-size: 21px;font-family: 宋体\">张。</span></p><p style=\"text-align:center;line-height:40px\"><span style=\"font-size: 21px;font-family: 宋体\">第七章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">项目推荐</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">申报市农村科技推广奖由下列单位和个人推荐：</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（一）各区、县（市）政府、开发区管理委员会的科技行政部门；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（二）市政府相关行业主管部门；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（三）中央、省驻沈院校、科研单位；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（四）市科学技术行政部门认定的其他单位及科学技术专家。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">各推荐单位和个人应当积极组织申报市农村科技推广奖，审核申报材料的真实性，完整地填写推荐意见。</span></p><p style=\"text-align:center;line-height:40px\"><span style=\"font-size: 21px;font-family: 宋体\">第八章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">申报程序</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">市农村科技推广奖的申报采取网上提交申报材料和报送书面材料并行的方式。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（一）向推荐单位申请获得用户编号和密码（密码可自行修改）；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（二）凭用户编号和密码登录市农村科技推广奖网上申报系统（</span><span style=\"font-size: 21px;font-family: Simsun, serif\">http://www.countryside.com.cn</span><span style=\"font-size: 21px;font-family: 宋体\">），下载申报书和评审表样表，离线填写完成后，按要求上传；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（三）各推荐单位上传推荐意见；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（四）在评审委员会办公室审查通过后，报送纸质申报材料到推荐单位，由推荐单位审核并统一报送市评审委员会办公室。</span></p><p style=\"text-align:center;line-height:40px\"><span style=\"font-size: 21px;font-family: 宋体\">第九章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">评审程序</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">评审委员会办公室负责对申报材料进行形式审查，对不符合规定的，一次性告之申报者，申报者自告之日起</span><span style=\"font-size: 21px;font-family: Simsun, serif\">7</span><span style=\"font-size: 21px;font-family: 宋体\">个工作日内补正，逾期不补正或经补正仍不符合要求的，不得提交评审。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">评审委员会组织项目评审：</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（一）组织专家对形式审查合格的申报项目进行复核，根据项目内容和单位性质，分成不同的评审组，并确定出各评审组一、二、三等奖候选项目比例；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（二）各评审组分别召开初审会议，项目单位进行现场答辩，评审专家按照评审标准进行实名制打分，按各组的一、二、三等奖候选项目比例评选出一、二、三等奖候选项目；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（三）组织评审专家召开终审会议，除三等奖候选项目外，评审专家对一、二等奖候选项目投票表决，评选出一、二等奖项目，其中，一等奖项目要求全体评审专家半数以上通过方能生效；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（四）一、二等奖侯选项目中，农村基层单位申报项目比例不得低于</span><span style=\"font-size: 21px;font-family: Simsun, serif\">20%</span><span style=\"font-size: 21px;font-family: 宋体\">；</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">（五）由评审委员会根据本年度入围奖项的数量，以不超过一、二等奖之和的</span><span style=\"font-size: 21px;font-family: Simsun, serif\">2</span><span style=\"font-size: 21px;font-family: 宋体\">倍，确定三等奖数量。</span></p><p style=\"text-align:center;line-height:40px\"><span style=\"font-size: 21px;font-family: 宋体\">第十章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">主要完成人和主要完成单位</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">主要完成单位是指在项目的研究、开发、推广、应用的全过程中提供技术、设备、经费等条件，对项目的完成起到重要作用的单位。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">一等奖单项授奖人数不超过</span><span style=\"font-size: 21px;font-family: Simsun, serif\">11</span><span style=\"font-size: 21px;font-family: 宋体\">人，二等奖单项授奖人数不超过</span><span style=\"font-size: 21px;font-family: Simsun, serif\">9</span><span style=\"font-size: 21px;font-family: 宋体\">人，三等奖单项授奖人数不超过</span><span style=\"font-size: 21px;font-family: Simsun, serif\">7</span><span style=\"font-size: 21px;font-family: 宋体\">人，主要完成单位授奖数量不超过</span><span style=\"font-size: 21px;font-family: Simsun, serif\">3</span><span style=\"font-size: 21px;font-family: 宋体\">个。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">大专院校、科研院所在基层开展的推广项目，申报市农村科技推广奖时，要求主要完成人应包含基层的推广人员。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">市农村科技推广奖获奖者的待遇与市科技进步奖获奖者等同。获奖者的事迹，应记入本人档案，并作为考核、评定、晋升技术职称的依据之一。</span></p><p style=\"text-align:center;line-height:40px\"><span style=\"font-size: 21px;font-family: 宋体\">第十一章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">奖励批准与授奖</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">评审委员会将市农村科技推广奖拟定获奖人员、项目以及奖励等级向社会进行公示，征求各方面意见。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">任何单位和个人对市农村科技推广奖拟定获奖人员、项目持有异议的，可自公示之日起</span><span style=\"font-size: 21px;font-family: Simsun, serif\">15</span><span style=\"font-size: 21px;font-family: 宋体\">日内向评审委员会提出，并提供必要的证明材料。超出异议期的不予受理。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">评审委员会应当在</span><span style=\"font-size: 21px;font-family: Simsun, serif\">15</span><span style=\"font-size: 21px;font-family: 宋体\">个工作日内对异议项目进行调查核实，做出处理决定，并将处理决定通知异议方及推荐单位。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">市科技局根据评审委员会的奖励建议及异议处理情况，将拟定获奖人员、获奖项目、奖励等级报市人民政府批准。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">市农村科技推广奖由市人民政府按一、二、三等奖励级别颁发荣誉证书和奖金。各级别奖金数额同市科技进步奖奖金相同。奖励资金由市财政部门列入预算。</span></p><p style=\"text-align:center;line-height:40px\"><span style=\"font-size: 21px;font-family: 宋体\">第十二章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">法律责任</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">剽窃、侵夺他人科技成果的，或者以其他不正当手段骗取市农村科技推广奖的，一经查实由市科技局报市人民政府批准后撤销奖励，收回证书和奖金，两年内不得申报奖励，涉嫌犯罪的移送司法机关处理。</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">协助他人骗取市农村科技推广奖的，一经查实由市科技局通报批评；对负有直接责任的相关人员，由主管部门依据相关规定给予行政处分。</span></p><p style=\"text-align:center;line-height:40px\"><span style=\"font-size: 21px;font-family: 宋体\">第十三章</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">附</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;</span><span style=\"font-size: 21px;font-family: 宋体\">则</span></p><p style=\"text-indent: 43px;line-height: 40px\"><span style=\"font-size: 21px;font-family: 宋体\">本细则自</span><span style=\"font-size: 21px;font-family: Simsun, serif\">2016</span><span style=\"font-size: 21px;font-family: 宋体\">年</span><span style=\"font-size: 21px;font-family: Simsun, serif\">5</span><span style=\"font-size: 21px;font-family: 宋体\">月</span><span style=\"font-size: 21px;font-family: Simsun, serif\">1</span><span style=\"font-size: 21px;font-family: 宋体\">日起施行，</span><span style=\"font-size: 21px;font-family: Simsun, serif\">2001</span><span style=\"font-size: 21px;font-family: 宋体\">年印发的《沈阳市农村科技推广奖实施细则》（沈科发</span><span style=\"font-size: 21px;font-family: Simsun, serif\">[2001]42</span><span style=\"font-size: 21px;font-family: 宋体\">号）同时废止。</span></p><p style=\"line-height: 40px\"><span style=\"font-size: 18px;font-family: Simsun, serif\">&nbsp;</span></p><p style=\"line-height: 40px\"><span style=\"font-size: 18px;font-family: Simsun, serif\">&nbsp;</span></p><p style=\"text-indent: 32px\"><span style=\"font-size: 21px;font-family: 宋体\">沈阳市科学技术局办公室</span><span style=\"font-size: 21px;font-family: Simsun, serif\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;2016</span><span style=\"font-size: 21px;font-family: 宋体\">年</span><span style=\"font-size: 21px;font-family: Simsun, serif\">4</span><span style=\"font-size: 21px;font-family: 宋体\">月</span><span style=\"font-size: 21px;font-family: Simsun, serif\">15</span><span style=\"font-size: 21px;font-family: 宋体\">日印发</span></p><p><br/></p>', '0', '0', '1', '2016-04-15 00:00:00', '1', '2017-11-13 14:18:54', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:54:11', '0', '');
INSERT INTO `site_policy_rule` VALUES ('deb111c341004365941dcdae57fb2b8a', 'ZCFG_deb111c341004365941dcdae57fb2b8a', '沈阳市知识产权局&ldquo;双随机 一公开&rdquo;工作实施细则（试行）', '2', '沈阳市知识产权局', '<p style=\"text-align: center;\"><br/></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">第一条 为规范专利行政执法，营造公平公正的市场秩序,优化营商环境，按照《辽宁省人民政府办公厅关于加快推进全省“双随机、一公开”监管改革工作的通知》等文件要求，制定本细则。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">第二条 “双随机一公开”的基本原则。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">依法监管。推进“双随机”抽查制度化、规范化，法律法规规章没有授权规定的，不得擅自开展检查。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">公开透明。“阳光执法”，随机抽查事项公开、程序公开、结果公开，保障市场主体权利平等、机会平等、规则平等。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">第三条 建立“两库一单”。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">建立本级市场主体名录库。市场主体名录库的重点为从事食品、药品、家电的批发零售企业。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">建立执法检查人员名录库。以市、县(区)两级专利行政执法人员数据库为基础，建立检查人员名录库。名录库中的行政执法人员必须持有国家知识产权局或省政府法制办颁发的《执法证》。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">建立“双随机”抽查事项清单。“双随机”清单由专利法律法规规定。抽查事项名称、抽查依据、抽查内容、抽查比例等报相关部门备案。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">第四条 随机抽查事项为：对是否存在假冒专利行为情况的检查。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">第五条 “两库一单”全程实行动态管理。根据法律法规和工作实际，对市场主体名录库、执法检查人员名录库及“双随机”抽查事项清单实行动态管理，及时更新信息，确保检查对象齐全、检查人员合格、抽查事项合法。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">第六条 原则上每年对市场主体库所有抽查对象进行一遍巡查。抽查方式为现场抽样检查，抽查内容为：涉及专利的各类商品、商品说明书、外包装上的专利标注标识。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">第七条 抽查对象的确定。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">由具有执法权的知识产权局，从本级市场主体名录库中随机摇号确定。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">第八条 执法人员的确定。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">由本单位执法处负责人从执法检查人员名录库随机摇号确定，每次抽取执法人员不得少于2人，市局执法人员可以指导区县执法人员开展具体工作。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">第九条 及时公布抽查结果。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">通过本单位或本级政府门户网站等途径，及时向社会公布，接受社会监督。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">对作出行政处罚决定的应当按规定进行信息公开。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">第十条 严格抽查纪律。执法人员不得妨碍市场主体正常的生产经营活动，不得索取或收受市场主体的财物，不得谋取其他利益。对工作中失职渎职和违纪的，要依法依纪处理。</span></p><p style=\"text-indent: 36px\"><span style=\"font-size: 24px;font-family: 宋体\">第十一条 本细则由市知识产权局解释。</span></p><p><span style=\"font-size: 24px;font-family: 宋体\">本细则从2017年1月1日实施，有效期至2018年12月31日。</span></p><p><br/></p>', '0', '0', '1', '2017-01-01 00:00:00', '1', '2017-11-13 14:24:13', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:54:42', '0', '');
INSERT INTO `site_policy_rule` VALUES ('e3f18ca5eefa447690ecdf9c4dd61f62', 'ZCFG_e3f18ca5eefa447690ecdf9c4dd61f62', '沈阳市重点实验室管理办法', '2', '沈阳市科学技术局', '<p style=\"background: white; text-align: center;\"><br/></p><p><span style=\"font-size: 18px;font-family: 宋体;background: white\">第一章</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">总则</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第一条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">为规范市级重点实验室（以下简称</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">“</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">”</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">）的建设与管理，发挥重点实验室在开展应用基础研究和科研成果转化，聚集和培养科技创新人才，为我市经济社会发展提供知识支撑和技术储备等方面的作用，根据国家、省、市有关规定，结合我市实际，制定本办法。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第二条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室是指依托在我市依法设立的高等院校、科研院所、高新技术企业或其它科研机构（以下简称</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">“</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">依托单位</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">”</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">）建设，从事科学前沿和高新技术领域研究，具有明确的科研方向、学科优势和原始创新能力的科研实体。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第三条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室的任务是：</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（一）围绕我市科技发展的战略目标，针对优势产业和重点发展领域开展应用基础研究，获取原始创新成果和自主知识产权，解决经济社会发展中基础性、关键性和前瞻性的科技问题，提高我市自主创新能力；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（二）建立学术思想活跃、学科知识面广、科技攻关能力强、专业知识结构和年龄结构合理的高层次学术队伍，引进、培育高素质的学术带头人；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（三）创造开放的科研环境和实验条件，面向社会开展示范建设、科学研究和学术交流和科技服务等工作。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第二章</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">&nbsp; </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">职责</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第四条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">市科技局是重点实验室的行政主管部门，主要职责是：</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（一）根据全市经济社会发展需要和科技发展规划，编制和组织实施重点实验室总体规划和发展计划；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（二）负责重点实验室的认定、调整和撤销，组织重点实验室的评估、检查和考核验收。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第五条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">依托单位是重点实验室建设和运行管理的具体负责单位，主要职责是：</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（一）为重点实验室建设和运行提供场地、资金及其他配套条件保障，协调解决重点实验室建设和运行中的重大问题；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（二）组织聘任重点实验室主任、学术委员会主任以及学术委员会委员；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（三）对重点实验室进行年度考核，配合行政主管部门开展评估、检查和验收工作。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第三章</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">&nbsp; </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">组建和审核</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第六条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室须经过依托单位组织申报、专家论证、市科技局依法审核三个步骤。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第七条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">申报组建重点实验室的依托单位应具备以下条件：</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（一）学科特色突出，在本领域达到国内先进水平，具有承担省、市重大科研任务的能力；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（二）拥有学术水平高、组织能力强的学术带头人和科研业绩显著、年龄与知识结构合理的科研人才队伍，并具有培养高层次科技人才的能力；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（三）具备一定规模的科研实验条件和工作基础，其中，重点实验室面积不少于</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">200</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">平方米；拥有的科研仪器设备基本能满足科研实验的要求，其总价值（原值）不低于</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">100</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">万元；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（四）能够承担建设和管理重点实验室的责任，能为重点实验室提供必要的技术支撑、后勤保障及相应经费等配套条件。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第八条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">申报组建重点实验室的依托单位需提供以下材料：</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> <span style=\"background:white\">1</span></span><span style=\"font-size: 18px;font-family: 宋体;background: white\">．《沈阳市重点实验室建设申请书》（见附件</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">1</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">）；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> <span style=\"background:white\">2</span></span><span style=\"font-size: 18px;font-family: 宋体;background: white\">．《沈阳市重点实验室建设可行性研究报告》（提纲见附件</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">2</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">）；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> <span style=\"background:white\">3</span></span><span style=\"font-size: 18px;font-family: 宋体;background: white\">．其他附件及证明材料。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第九条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">市科技局对申请材料进行初审后，组织专家进行评估论证，在综合专家评审意见的基础上，提出审核意见。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第十条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室可采取</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">“</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">边组建、边运行</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">”</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">的工作方式，组建期限一般不超过</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">2</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">年。正在组建的国家级和省级重点实验室，可直接列入市级重点实验室组建计划。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第十一条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">市科技局从以下方面鼓励和引导重点实验室为经济和社会发展提供科技服务：</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（一）依托单位可申报年度市科技计划的重点实验室专项，经立项审批程序，择优给予经费支持；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（二）优先推荐申报省级重点实验室建设计划；</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">（三）市科技局推行的鼓励创新创业的新政策和新举措，优先在重点实验室先行先试。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第十二条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">鼓励企业与科研院所、高等院校共同出资，联合组建重点实验室。本市辖区内原则上不重复组建研究领域相同的重点实验室。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第四章</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">&nbsp; </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">运行和管理</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第十三条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室实行依托单位领导下的主任负责制。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第十四条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室主任由依托单位聘任，报市科技局备案。重点实验室主任应是本领域内的高水平学术、学科带头人，具有较强的组织管理和协调能力，一般不超过</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">60</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">岁，每年在重点实验室工作时间一般不少于</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">6</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">个月。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> <span style=\"background:white\">&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第十五条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室设立独立的学术委员会。学术委员会是重点实验室的学术指导机构，负责审议重点实验室的发展目标、研究方向、重大学术活动、年度工作计划和总结。学术委员会由国内外优秀专家组成，人数一般不超过</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">13</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">人，其中依托单位的专家不超过总人数的</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">1/3</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">，学术委员会成员任期</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">5</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">年，由依托单位聘任，报市科技局备案。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> <span style=\"background:white\">&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室每年至少召开一次学术委员会会议，每次实到人数不得少于学术委员会总人数的</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">2/3</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第十六条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室的工作人员由固定人员和流动人员组成，固定人员包括研究人员、技术人员和管理人员，由重点实验室主任聘任；流动人员包括访问学者、博士后研究人员。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第十七条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室须具有健全的知识产权管理制度，切实加强知识产权保护。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第十八条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室运行坚持对外开放的原则，积极开展国内外合作与学术交流，根据研究方向设置开放课题和经费，制定人才培养和科研团队建设的具体措施和办法，吸引优秀科技人才从事科学研究工作，吸收和培养优秀青年科技人员，引进有成就的出国留学、进修人员回国参加工作。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第十九条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室应当重视并加强运行管理，建立健全内部规章制度，运行期间，每年度要向市科技局报告运行情况。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第五章</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">&nbsp; </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">考核和验收</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第二十条</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">&nbsp; </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">重点实验室建设期满后，市科技局组织有关专家进行验收，对验收合格的，正式审核确定为</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">“</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">沈阳市重点实验室</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">”</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第二十一条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">按照重点实验室组建任务要求提前完成任务的，可申请提前验收；逾期未完成组建任务的，市科技局将视具体情况，作出调整。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第二十二条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">市科技局对的重点实验室实行动态管理，定期对重点实验室的运行情况进行考核，对运行情况不良的，责成限期改进或取消资格。</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第六章</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">&nbsp; </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">附</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">&nbsp; </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">则</span><span style=\"font-size: 18px;font-family: Simsun, serif\"><br/> </span><span style=\"font-size: 18px;font-family: 宋体;background: white\">第二十三条</span> <span style=\"font-size: 18px;font-family: 宋体;background: white\">本办法自</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">2015</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">年</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">9</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">月</span><span style=\"font-size: 18px;font-family: Simsun, serif;background: white\">1</span><span style=\"font-size: 18px;font-family: 宋体;background: white\">日起施行。</span></p><p><br/></p>', '0', '0', '1', '2015-09-01 00:00:00', '1', '2017-11-13 14:26:48', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:55:25', '0', '');
INSERT INTO `site_policy_rule` VALUES ('edfca8b098f7425e916be23d34bd1c4b', 'ZCFG_edfca8b098f7425e916be23d34bd1c4b', '考评实施细则', '2', '沈阳市科学技术局', '<p>服务机构考评实施细则，详情查看附件！</p>', '0', '0', '', '2020-03-17 00:00:00', '1', '2020-03-19 11:15:57', '1', '2020-03-19 11:15:57', '0', '');
INSERT INTO `site_policy_rule` VALUES ('f2176195db9f49c7991a9f06e5b2eb2b', 'ZCFG_f2176195db9f49c7991a9f06e5b2eb2b', '国家知识产权战略纲要', '0', '国家知识产权局', '<p><span style=\"font-size: 11px;font-family:宋体;color:#333333\"></span></p><p style=\"text-align:center\"><br/></p><p><span style=\"font-size: 11px;font-family:宋体;color:#333333\">为提升我国知识产权创造、运用、保护和管理能力，建设创新型国家，实现全面建设小康社会目标，制定本纲要。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　一、序言</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）改革开放以来，我国经济社会持续快速发展，科学技术和文化创作取得长足进步，创新能力不断提升，知识在经济社会发展中的作用越来越突出。我国正站在新的历史起点上，大力开发和利用知识资源，对于转变经济发展方式，缓解资源环境约束，提升国家核心竞争力，满足人民群众日益增长的物质文化生活需要，具有重大战略意义。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）知识产权制度是开发和利用知识资源的基本制度。知识产权制度通过合理确定人们对于知识及其他信息的权利，调整人们在创造、运用知识和信息过程中产生的利益关系，激励创新，推动经济发展和社会进步。当今世界，随着知识经济和经济全球化深入发展，知识产权日益成为国家发展的战略性资源和国际竞争力的核心要素，成为建设创新型国家的重要支撑和掌握发展主动权的关键。国际社会更加重视知识产权，更加重视鼓励创新。发达国家以创新为主要动力推动经济发展，充分利用知识产权制度维护其竞争优势；发展中国家积极采取适应国情的知识产权政策措施，促进自身发展。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）经过多年发展，我国知识产权法律法规体系逐步健全，执法水平不断提高；知识产权拥有量快速增长，效益日益显现；市场主体运用知识产权能力逐步提高；知识产权领域的国际交往日益增多，国际影响力逐渐增强。知识产权制度的建立和实施，规范了市场秩序，激励了发明创造和文化创作，促进了对外开放和知识资源的引进，对经济社会发展发挥了重要作用。但是，从总体上看，我国知识产权制度仍不完善，自主知识产权水平和拥有量尚不能满足经济社会发展需要，社会公众知识产权意识仍较薄弱，市场主体运用知识产权能力不强，侵犯知识产权现象还比较突出，知识产权滥用行为时有发生，知识产权服务支撑体系和人才队伍建设滞后，知识产权制度对经济社会发展的促进作用尚未得到充分发挥。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">4</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）实施国家知识产权战略，大力提升知识产权创造、运用、保护和管理能力，有利于增强我国自主创新能力，建设创新型国家；有利于完善社会主义市场经济体制，规范市场秩序和建立诚信社会；有利于增强我国企业市场竞争力和提高国家核心竞争力；有利于扩大对外开放，实现互利共赢。必须把知识产权战略作为国家重要战略，切实加强知识产权工作。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　二、指导思想和战略目标</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（一）指导思想。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）实施国家知识产权战略，要坚持以邓小平理论和</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&quot;</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">三个代表</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&quot;</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">重要思想为指导，深入贯彻落实科学发展观，按照激励创造、有效运用、依法保护、科学管理的方针，着力完善知识产权制度，积极营造良好的知识产权法治环境、市场环境、文化环境，大幅度提升我国知识产权创造、运用、保护和管理能力，为建设创新型国家和全面建设小康社会提供强有力支撑。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（二）战略目标。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">6</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）到</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">2020</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">年，把我国建设成为知识产权创造、运用、保护和管理水平较高的国家。知识产权法治环境进一步完善，市场主体创造、运用、保护和管理知识产权的能力显著增强，知识产权意识深入人心，自主知识产权的水平和拥有量能够有效支撑创新型国家建设，知识产权制度对经济发展、文化繁荣和社会建设的促进作用充分显现。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">7</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）近五年的目标是：</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span>　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">——</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">自主知识产权水平大幅度提高，拥有量进一步增加。本国申请人发明专利年度授权量进入世界前列，对外专利申请大幅度增加。培育一批国际知名品牌。核心版权产业产值占国内生产总值的比重明显提高。拥有一批优良植物新品种和高水平集成电路布图设计。商业秘密、地理标志、遗传资源、传统知识和民间文艺等得到有效保护与合理利用。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span>　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">——</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">运用知识产权的效果明显增强，知识产权密集型商品比重显著提高。企业知识产权管理制度进一步健全，对知识产权领域的投入大幅度增加，运用知识产权参与市场竞争的能力明显提升。形成一批拥有知名品牌和核心知识产权，熟练运用知识产权制度的优势企业。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span>　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">——</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">知识产权保护状况明显改善。盗版、假冒等侵权行为显著减少，维权成本明显下降，滥用知识产权现象得到有效遏制。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span>　　<span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">——</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">全社会特别是市场主体的知识产权意识普遍提高，知识产权文化氛围初步形成。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　三、战略重点</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（一）完善知识产权制度。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">8</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）进一步完善知识产权法律法规。及时修订专利法、商标法、著作权法等知识产权专门法律及有关法规。适时做好遗传资源、传统知识、民间文艺和地理标志等方面的立法工作。加强知识产权立法的衔接配套，增强法律法规可操作性。完善反不正当竞争、对外贸易、科技、国防等方面法律法规中有关知识产权的规定。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">9</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）健全知识产权执法和管理体制。加强司法保护体系和行政执法体系建设，发挥司法保护知识产权的主导作用，提高执法效率和水平</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">,</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">强化公共服务。深化知识产权行政管理体制改革，形成权责一致、分工合理、决策科学、执行顺畅、监督有力的知识产权行政管理体制。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">10</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）强化知识产权在经济、文化和社会政策中的导向作用。加强产业政策、区域政策、科技政策、贸易政策与知识产权政策的衔接。制定适合相关产业发展的知识产权政策，促进产业结构的调整与优化；针对不同地区发展特点，完善知识产权扶持政策，培育地区特色经济，促进区域经济协调发展；建立重大科技项目的知识产权工作机制，以知识产权的获取和保护为重点开展全程跟踪服务；健全与对外贸易有关的知识产权政策，建立和完善对外贸易领域知识产权管理体制、预警应急机制、海外维权机制和争端解决机制。加强文化、教育、科研、卫生等政策与知识产权政策的协调衔接，保障公众在文化、教育、科研、卫生等活动中依法合理使用创新成果和信息的权利，促进创新成果合理分享；保障国家应对公共危机的能力。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（二）促进知识产权创造和运用。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">11</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）运用财政、金融、投资、政府采购政策和产业、能源、环境保护政策，引导和支持市场主体创造和运用知识产权。强化科技创新活动中的知识产权政策导向作用，坚持技术创新以能够合法产业化为基本前提，以获得知识产权为追求目标，以形成技术标准为努力方向。完善国家资助开发的科研成果权利归属和利益分享机制。将知识产权指标纳入科技计划实施评价体系和国有企业绩效考核体系。逐步提高知识产权密集型商品出口比例，促进贸易增长方式的根本转变和贸易结构的优化升级。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">12</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）推动企业成为知识产权创造和运用的主体。促进自主创新成果的知识产权化、商品化、产业化，引导企业采取知识产权转让、许可、质押等方式实现知识产权的市场价值。充分发挥高等学校、科研院所在知识产权创造中的重要作用。选择若干重点技术领域，形成一批核心自主知识产权和技术标准。鼓励群众性发明创造和文化创新。促进优秀文化产品的创作。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（三）加强知识产权保护。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">13</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）修订惩处侵犯知识产权行为的法律法规，加大司法惩处力度。提高权利人自我维权的意识和能力。降低维权成本，提高侵权代价，有效遏制侵权行为。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（四）防止知识产权滥用。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">14</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）制定相关法律法规，合理界定知识产权的界限，防止知识产权滥用，维护公平竞争的市场秩序和公众合法权益。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（五）培育知识产权文化。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">15</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）加强知识产权宣传，提高全社会知识产权意识。广泛开展知识产权普及型教育。在精神文明创建活动和国家普法教育中增加有关知识产权的内容。在全社会弘扬以创新为荣、剽窃为耻，以诚实守信为荣、假冒欺骗为耻的道德观念，形成尊重知识、崇尚创新、诚信守法的知识产权文化。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　四、专项任务</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（一）专利。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">16</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）以国家战略需求为导向，在生物和医药、信息、新材料、先进制造、先进能源、海洋、资源环境、现代农业、现代交通、航空航天等技术领域超前部署，掌握一批核心技术的专利，支撑我国高技术产业与新兴产业发展。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">17</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）制定和完善与标准有关的政策，规范将专利纳入标准的行为。支持企业、行业组织积极参与国际标准的制定。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">18</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）完善职务发明制度，建立既有利于激发职务发明人创新积极性，又有利于促进专利技术实施的利益分配机制。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">19</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）按照授予专利权的条件，完善专利审查程序，提高审查质量。防止非正常专利申请。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">20</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）正确处理专利保护和公共利益的关系。在依法保护专利权的同时，完善强制许可制度，发挥例外制度作用，研究制定合理的相关政策，保证在发生公共危机时，公众能够及时、充分获得必需的产品和服务。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（二）商标。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">21</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）切实保护商标权人和消费者的合法权益。加强执法能力建设，严厉打击假冒等侵权行为，维护公平竞争的市场秩序。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">22</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）支持企业实施商标战略，在经济活动中使用自主商标。引导企业丰富商标内涵，增加商标附加值，提高商标知名度，形成驰名商标。鼓励企业进行国际商标注册，维护商标权益，参与国际竞争。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">23</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）充分发挥商标在农业产业化中的作用。积极推动市场主体注册和使用商标，促进农产品质量提高，保证食品安全，提高农产品附加值，增强市场竞争力。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">24</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）加强商标管理。提高商标审查效率，缩短审查周期，保证审查质量。尊重市场规律，切实解决驰名商标、著名商标、知名商品、名牌产品、优秀品牌的认定等问题。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（三）版权。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">25</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）扶持新闻出版、广播影视、文学艺术、文化娱乐、广告设计、工艺美术、计算机软件、信息网络等版权相关产业发展，支持具有鲜明民族特色、时代特点作品的创作，扶持难以参与市场竞争的优秀文化作品的创作。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">26</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）完善制度，促进版权市场化。进一步完善版权质押、作品登记和转让合同备案等制度，拓展版权利用方式，降低版权交易成本和风险。充分发挥版权集体管理组织、行业协会、代理机构等中介组织在版权市场化中的作用。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">27</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）依法处置盗版行为，加大盗版行为处罚力度。重点打击大规模制售、传播盗版产品的行为，遏制盗版现象。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">28</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）有效应对互联网等新技术发展对版权保护的挑战。妥善处理保护版权与保障信息传播的关系，既要依法保护版权，又要促进信息传播。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（四）商业秘密。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">29</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）引导市场主体依法建立商业秘密管理制度。依法打击窃取他人商业秘密的行为。妥善处理保护商业秘密与自由择业、涉密者竞业限制与人才合理流动的关系，维护职工合法权益。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（五）植物新品种。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">30</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）建立激励机制，扶持新品种培育，推动育种创新成果转化为植物新品种权。支持形成一批拥有植物新品种权的种苗单位。建立健全植物新品种保护的技术支撑体系，加快制订植物新品种测试指南，提高审查测试水平。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">31</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）合理调节资源提供者、育种者、生产者和经营者之间的利益关系，注重对农民合法权益的保护。提高种苗单位及农民的植物新品种权保护意识，使品种权人、品种生产经销单位和使用新品种的农民共同受益。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（六）特定领域知识产权。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">32</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）完善地理标志保护制度。建立健全地理标志的技术标准体系、质量保证体系与检测体系。普查地理标志资源，扶持地理标志产品，促进具有地方特色的自然、人文资源优势转化为现实生产力。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">33</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）完善遗传资源保护、开发和利用制度，防止遗传资源流失和无序利用。协调遗传资源保护、开发和利用的利益关系，构建合理的遗传资源获取与利益分享机制。保障遗传资源提供者知情同意权。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">34</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）建立健全传统知识保护制度。扶持传统知识的整理和传承，促进传统知识发展。完善传统医药知识产权管理、保护和利用协调机制，加强对传统工艺的保护、开发和利用。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">35</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）加强民间文艺保护，促进民间文艺发展。深入发掘民间文艺作品，建立民间文艺保存人与后续创作人之间合理分享利益的机制，维护相关个人、群体的合法权益。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">36</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）加强集成电路布图设计专有权的有效利用，促进集成电路产业发展。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（七）国防知识产权。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">37</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）建立国防知识产权的统一协调管理机制，着力解决权利归属与利益分配、有偿使用、激励机制以及紧急状态下技术有效实施等重大问题。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">38</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）加强国防知识产权管理。将知识产权管理纳入国防科研、生产、经营及装备采购、保障和项目管理各环节，增强对重大国防知识产权的掌控能力。发布关键技术指南，在武器装备关键技术和军民结合高新技术领域形成一批自主知识产权。建立国防知识产权安全预警机制，对军事技术合作和军品贸易中的国防知识产权进行特别审查。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">39</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）促进国防知识产权有效运用。完善国防知识产权保密解密制度，在确保国家安全和国防利益基础上，促进国防知识产权向民用领域转移。鼓励民用领域知识产权在国防领域运用。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　五、战略措施</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（一）提升知识产权创造能力。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">40</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）建立以企业为主体、市场为导向、产学研相结合的自主知识产权创造体系。引导企业在研究开发立项及开展经营活动前进行知识产权信息检索。支持企业通过原始创新、集成创新和引进消化吸收再创新，形成自主知识产权，提高把创新成果转变为知识产权的能力。支持企业等市场主体在境外取得知识产权。引导企业改进竞争模式，加强技术创新，提高产品质量和服务质量，支持企业打造知名品牌。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（二）鼓励知识产权转化运用。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">41</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）引导支持创新要素向企业集聚，促进高等学校、科研院所的创新成果向企业转移，推动企业知识产权的应用和产业化，缩短产业化周期。深入开展各类知识产权试点、示范工作，全面提升知识产权运用能力和应对知识产权竞争的能力。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">42</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）鼓励和支持市场主体健全技术资料与商业秘密管理制度，建立知识产权价值评估、统计和财务核算制度，制订知识产权信息检索和重大事项预警等制度，完善对外合作知识产权管理制度。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">43</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）鼓励市场主体依法应对涉及知识产权的侵权行为和法律诉讼，提高应对知识产权纠纷的能力。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（三）加快知识产权法制建设。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">44</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）建立适应知识产权特点的立法机制，提高立法质量，加快立法进程。加强知识产权立法前瞻性研究，做好立法后评估工作。增强立法透明度，拓宽企业、行业协会和社会公众参与立法的渠道。加强知识产权法律修改和立法解释，及时有效回应知识产权新问题。研究制定知识产权基础性法律的必要性和可行性。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（四）提高知识产权执法水平。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">45</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）完善知识产权审判体制，优化审判资源配置，简化救济程序。研究设置统一受理知识产权民事、行政和刑事案件的专门知识产权法庭。研究适当集中专利等技术性较强案件的审理管辖权问题，探索建立知识产权上诉法院。进一步健全知识产权审判机构，充实知识产权司法队伍，提高审判和执行能力。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">46</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）加强知识产权司法解释工作。针对知识产权案件专业性强等特点，建立和完善司法鉴定、专家证人、技术调查等诉讼制度，完善知识产权诉前临时措施制度。改革专利和商标确权、授权程序，研究专利无效审理和商标评审机构向准司法机构转变的问题。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">47</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）提高知识产权执法队伍素质，合理配置执法资源，提高执法效率。针对反复侵权、群体性侵权以及大规模假冒、盗版等行为，有计划、有重点地开展知识产权保护专项行动。加大行政执法机关向刑事司法机关移送知识产权刑事案件和刑事司法机关受理知识产权刑事案件的力度。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">48</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）加大海关执法力度，加强知识产权边境保护，维护良好的进出口秩序，提高我国出口商品的声誉。充分利用海关执法国际合作机制，打击跨境知识产权违法犯罪行为，发挥海关在国际知识产权保护事务中的影响力。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（五）加强知识产权行政管理。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">49</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）制定并实施地区和行业知识产权战略。建立健全重大经济活动知识产权审议制度。扶持符合经济社会发展需要的自主知识产权创造与产业化项目。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">50</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）充实知识产权管理队伍，加强业务培训，提高人员素质。根据经济社会发展需要，县级以上人民政府可设立相应的知识产权管理机构。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">51</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）完善知识产权审查及登记制度，加强能力建设，优化程序，提高效率，降低行政成本，提高知识产权公共服务水平。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">52</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）构建国家基础知识产权信息公共服务平台。建设高质量的专利、商标、版权、集成电路布图设计、植物新品种、地理标志等知识产权基础信息库，加快开发适合我国检索方式与习惯的通用检索系统。健全植物新品种保护测试机构和保藏机构。建立国防知识产权信息平台。指导和鼓励各地区、各有关行业建设符合自身需要的知识产权信息库。促进知识产权系统集成、资源整合和信息共享。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">53</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）建立知识产权预警应急机制。发布重点领域的知识产权发展态势报告，对可能发生的涉及面广、影响大的知识产权纠纷、争端和突发事件，制订预案，妥善应对，控制和减轻损害。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（六）发展知识产权中介服务。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">54</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）完善知识产权中介服务管理，加强行业自律，建立诚信信息管理、信用评价和失信惩戒等诚信管理制度。规范知识产权评估工作，提高评估公信度。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">55</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）建立知识产权中介服务执业培训制度，加强中介服务职业培训，规范执业资质管理。明确知识产权代理人等中介服务人员执业范围，研究建立相关律师代理制度。完善国防知识产权中介服务体系。大力提升中介组织涉外知识产权申请和纠纷处置服务能力及国际知识产权事务参与能力。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">56</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）充分发挥行业协会的作用，支持行业协会开展知识产权工作，促进知识产权信息交流，组织共同维权。加强政府对行业协会知识产权工作的监督指导。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">57</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）充分发挥技术市场的作用，构建信息充分、交易活跃、秩序良好的知识产权交易体系。简化交易程序，降低交易成本，提供优质服务。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">58</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）培育和发展市场化知识产权信息服务，满足不同层次知识产权信息需求。鼓励社会资金投资知识产权信息化建设，鼓励企业参与增值性知识产权信息开发利用。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（七）加强知识产权人才队伍建设。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">59</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）建立部门协调机制，统筹规划知识产权人才队伍建设。加快建设国家和省级知识产权人才库和专业人才信息网络平台。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">60</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）建设若干国家知识产权人才培养基地。加快建设高水平的知识产权师资队伍。设立知识产权二级学科，支持有条件的高等学校设立知识产权硕士、博士学位授予点。大规模培养各级各类知识产权专业人才，重点培养企业急需的知识产权管理和中介服务人才。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">61</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）制定培训规划，广泛开展对党政领导干部、公务员、企事业单位管理人员、专业技术人员、文学艺术创作人员、教师等的知识产权培训。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">62</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）完善吸引、使用和管理知识产权专业人才相关制度，优化人才结构，促进人才合理流动。结合公务员法的实施，完善知识产权管理部门公务员管理制度。按照国家职称制度改革总体要求，建立和完善知识产权人才的专业技术评价体系。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（八）推进知识产权文化建设。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">63</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）建立政府主导、新闻媒体支撑、社会公众广泛参与的知识产权宣传工作体系。完善协调机制，制定相关政策和工作计划，推动知识产权的宣传普及和知识产权文化建设。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">64</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）在高等学校开设知识产权相关课程，将知识产权教育纳入高校学生素质教育体系。制定并实施全国中小学知识产权普及教育计划，将知识产权内容纳入中小学教育课程体系。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（九）扩大知识产权对外交流合作。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">　　（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">65</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">）加强知识产权领域的对外交流合作。建立和完善知识产权对外信息沟通交流机制。加强国际和区域知识产权信息资源及基础设施建设与利用的交流合作。鼓励开展知识产权人才培养的对外合作。引导公派留学生、鼓励自费留学生选修知识产权专业。支持引进或聘用海外知识产权高层次人才。积极参与国际知识产权秩序的构建，有效参与国际组织有关议程。</span></p><p><br/></p>', '0', '0', '1', '2010-08-03 00:00:00', '1', '2017-11-13 13:18:34', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 15:36:51', '0', '');
INSERT INTO `site_policy_rule` VALUES ('f2d85dcb5ad24a3faf84e79b746cac8f', 'ZCFG_f2d85dcb5ad24a3faf84e79b746cac8f', '沈阳市科技创新券实施办法（暂行）', '2', '沈阳市科学技术局', '<p style=\"text-align:center;line-height:37px\"><span style=\"font-size:29px;font-family:方正小标宋简体;background:white\">沈阳市科技创新券实施办法（暂行）</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">为深入实施创新驱动发展战略，营造“大众创业，万众创新”的良好环境，创新财政科技资金使用方式，激发科技型中小微企业（简称企业）和创业团队的科技创新活力，降低科技创新成本，根据辽宁省科技厅和辽宁省财政厅《关于实施科技创新券制度的若干意见（试行）》（辽科发〔2015〕28号）相关规定，结合我市实际情况，制定本办法。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family: 黑体\">第一章&nbsp;&nbsp;总则</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第一条&nbsp; 沈阳市科技创新券（简称创新券）是免费向我市企业和创业团队发放的，用于向沈阳市科技条件平台服务机构（简称服务机构）购买科技服务的政府无偿补助经费。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第二条&nbsp; 沈阳市科学技术局（简称市科技局）在市科技创新专项资金中安排创新券补助经费。创新券实行总量控制，采用电子券形式事前申请事后补助的支持方式。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第三条&nbsp; 创新券的使用和管理应遵循突出重点、公开透明、专款专用、开放共赢的原则。</span></p><p style=\"text-align:center;text-indent:43px\"><span style=\"font-size:21px;font-family:黑体\">第二章&nbsp; 工作机构及职责</span><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family:仿宋\">第四条&nbsp; 市科技局是创新券的管理机构，负责创新券的政策制定、审核管理、监督指导、绩效评价及协调解决创新券实施过程中的重大事项。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第五条&nbsp; 市科技局以政府购买公共服务的形式委托沈阳科技资源共享服务中心（简称共享中心）负责创新券的年度预算编制和受理创新券的申请、发放、兑付和统计等工作。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第六条&nbsp; 市科技局指导各区、县（市）科技局做好辖区创新券政策的宣传发动和组织实施工作。鼓励各区、县（市）科技局结合实际制定辖区创新券的优惠政策。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family:仿宋\">第七条&nbsp; 经市科技局认定的服务机构是创新券的服务机构。创新券服务机构要接受沈阳市科技条件平台（简称条件平台）的管理，积极为用户提供创新服务。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">创新券服务机构的基本条件：</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（一）提供大型科学仪器设备共享服务的单位或机构。拥有单台（套）原值在20万元以上的仪器设备数量5台（套）以上，或拥有特殊用途的精密、稀缺科学仪器设备，性能指标达到我市现有装备的先进水平，运行正常、数据准确、加工精度高，配有熟练操作人员，能向社会提供共享服务。具备专业分析检测相关的技术咨询、培训的能力。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（二）提供专业技术服务的单位或机构。具备稳定的服务机构管理人员，有明确的专业服务方向，具有一定的专业技术管理优势和较强的组织协调能力，配备高级职称的专业技术带头人和技术服务人员，其中，专职技术服务人员不少于10人，硕士学位或中级职称的人员不低于30%。有较好的前期工作基础，有对外提供专业技术服务的案例，有相对稳定的用户群体。具备承担国家、省、市级科研项目的能力。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（三）提供科技文献、科学数据等科技信息服务的单位或机构。拥有专职服务人员5名（含）以上，拥有一定规模的数字化文献、情报、信息资源，可为用户提供文献检索、全文下载、电子图书在线阅读、科技查新与情报信息等服务，年累计服务企业10家（含）以上。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（四）提供知识产权服务的单位或机构。拥有执业资格人员10名以上，具备信息数据分析、挖掘、预警、知识产权战略研究能力，有专业服务案例，年累计服务企业5家（含）以上。</span></p><p><span style=\"font-size: 21px;font-family: 仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size: 21px;font-family: 仿宋\">（五）其他可对外提供各类公共科技服务的单位或机构。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family: 黑体\">第三章&nbsp; 使用范围与标准</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第八条&nbsp; 创新券可用于服务机构提供的研发、检测、知识产权等科技服务，服务范围以条件平台每年发布的科技服务目录为准。创新券不支持按照法律法规或者强制性标准要求必须开展的强制检测和法定检测等商业活动。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第九条&nbsp; 市科技局定期发布开展创新券申领和兑付工作的通知，共享中心每季度对前一季度创新券使用情况开展预审核和统计。创新券有效期为12个月，逾期作废。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十条&nbsp; 企业是创新券申领和兑付的主体单位。创业团队由所在的科技企业孵化器（简称孵化器）或众创空间代为申领和兑付。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">符合条件的创新券兑付额度：技术服务金额在 10万元及以下的部分按照不超过60%的比例核定支持；超过10万元的部分按照最高不超过30%的比例核定支持。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family: 黑体\">第四章&nbsp; 审核申领</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十一条&nbsp; 申领创新券的企业应符合以下条件：</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">（一）在沈阳市登记注册，并在本市纳税的企业。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">（二）符合《工业和信息化部关于印发中小企业划型标准规定的通知》（工信部联企业〔2011〕300号）规定标准。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">（三）企业与开展合作的服务机构无隶属、共建、产权纽带等关联关系。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十二条&nbsp; 申领创新券的创业团队应同时符合以下条件：</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">（一）不具备独立法人资格，尚未注册企业。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">（二）入住市级以上孵化器或众创空间。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">（三）创业项目须具有产品研发转化所需的测试或研发工作（不包括仅限创业文本策划的项目）。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十三条&nbsp; 企业申领创新券需登录沈阳市科技条件平台信息系统（简称信息系统）<a href=\"http://www.syskjj.gov.cn/webManage_yhdl.do%EF%BC%89%EF%BC%88%E4%BB%A5%E4%B8%8B%E7%AE%80%E7%A7%B0%E7%AE%A1%E7%90%86%E5%B9%B3%E5%8F%B0%EF%BC%89%EF%BC%8C%E5%A1%AB%E5%86%99%E3%80%8A%E6%B2%88%E9%98%B3%E5%B8%82%E7%A7%91%E6%8A%80%E5%88%9B%E6%96%B0%E5%88%B8%E7%94%B3%E8%AF%B7%E4%B8%8E%E5%AE%A1%E6%89%B9%E8%A1%A8%E3%80%8B%EF%BC%8C%E5%B9%B6%E4%B8%8A%E4%BC%A0%E4%BC%81%E4%B8%9A%E8%90%A5%E4%B8%9A%E6%89%A7%E7%85%A7%E3%80%81%E7%A8%8E%E5%8A%A1%E7%99%BB%E8%AE%B0%E8%AF%81%E5%92%8C%E6%8A%80%E6%9C%AF%E5%90%88%E5%90%8C%E3%80%82\"><span style=\"color: windowtext\">，填报《沈阳市科技创新券申领表（企业）》，上传加盖公章的企业营业执照副本扫描件。</span></a></span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">创业团队申请创新券由所在孵化器或众创空间登录信息系统，统一填报《沈阳市科技创新券申领推荐表（创业团队）》，上传孵化器或众创空间加盖公章的营业执照副本和创业团队负责人的身份证扫描件。</span></p><p style=\"margin-left:0;text-indent:43px\"><span style=\"font-size:21px;font-family:仿宋\">第十四条 </span><span style=\"font-size:21px;font-family: 仿宋\">&nbsp;</span><span style=\"font-size:21px;font-family:仿宋\">共享中心对申报材料及证明材料进行核准，通过条件平台发放创新券。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">在一个创新券有效期内，企业申领创新券的最高额度不超过20万元，分两次申领，第一次申领额度10万元使用完毕后，可再次申领10万元。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family:仿宋\">在一个创新券有效期内，创业团队申领创新券的最高额度不超过5万元，分两次申领，第一次申领额度2.5万元使用完毕后，可再次申领2.5万元。</span></p><p style=\"text-indent:43px\"><span style=\"font-size: 21px;font-family:仿宋\">企业和创业团队在递交申领材料15个工作日后，通过信息系统查询申领结果。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family: 黑体\">第五章&nbsp; 资金拨付</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十五条&nbsp; 企业和创业团队开展科技服务需先行支付服务费用，完成合同约定服务内容后，须及时在信息系统输入创新券券号并上传相关证明材料，作为办理兑付的凭据。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十六条&nbsp; 企业申请兑付创新券应登录信息系统，填报《沈阳市科技创新券兑付申请表（企业）》，上传技术合同、银行转账支付凭证、发票等。以下材料若存在需一并上传：服务结果证明(包括检测报告、技术解决方案、知识产权服务报告等)、创新成果证明（包括专利、著作权、新产品、新工艺、样机等证明材料）。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">创业团队申请兑付创新券由所在孵化器或众创空间登录信息系统，统一填报《沈阳市科技创新券兑付申请表（创业团队）》，提供材料内容与企业相同。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十七条&nbsp; 共享中心每年集中受理创新券兑付工作，组织专家对申请材料进行评审，评审结果公示后，履行政府审批程序，拨付下达资金。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family: 黑体\">第六章&nbsp;&nbsp;评价和监督</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十八条&nbsp; 市科技局定期对共享中心、区、县（市）科技局和服务机构创开展新券工作情况进行评价，评价结果作为绩效考核的重要依据。</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第十九条&nbsp; 创新券不得转让、赠送、买卖。对虚报、冒领、截留等行为，一经查实，依据辽宁省科技厅和辽宁省财政厅《关于实施科技创新券制度的若干意见（试行）》（辽科发〔2015〕28号）和《沈阳市财政科技创新专项资金管理办法》（沈财企〔2017〕439号）相关规定执行，涉嫌违法的，交由司法机关依法处理。</span></p><p style=\"text-align:center\"><span style=\"font-size:21px;font-family:黑体\">&nbsp;</span><span style=\"font-size:21px;font-family:黑体\">第七章&nbsp; 附则</span></p><p><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">第二十条&nbsp; 本办法由沈阳市科技局负责解释，执行期自2018年1月1日至2019年12月31日止。</span></p><p><br/></p>', '0', '0', '1', '2017-11-07 00:00:00', '1', '2017-11-15 15:01:40', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 10:19:58', '0', '');
INSERT INTO `site_policy_rule` VALUES ('f7f728600b6547ae932eb8db9c3e4e6a', 'ZCFG_f7f728600b6547ae932eb8db9c3e4e6a', '辽宁省高新技术特色产业基地认定和管理办法（试行）', '1', '辽宁省科学技术厅', '<p style=\"text-align: center;\"><br/></p><p><span style=\"font-size:11px;font-family:宋体;color:#333333\">第一条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">为引导和推动我省高新技术产业集群和创新集群的健康有序发展，规范辽宁省高新技术特色产业基地（以下简称特色产业基地）的认定和管理工作，根据《国家火炬计划特色产业基地管理办法》，特制定本办法。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第二条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">特色产业基地的认定和管理，适用本办法。</span><span style=\"font-size: 11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第三条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">特色产业基地是指，在一定地域范围内，在实施辽宁省科技计划和开展自主创新活动的基础上，充分发挥对人才、技术、资本等资源集聚的优势，依托一批产业特色鲜明、产业关联度较大、产业链条较完整、技术水平较高、创新能力较强的骨干企业和与之密切相关的科技型中小企业而建立起来的高新技术产业集群或创新集群。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第四条</span> <span style=\"font-size:11px;font-family:宋体;color:#333333\">发展特色产业基地的目的是，组织社会各界力量，集成各方优势资源，积极推动以企业为主体、市场为导向、产学研合作的创新体系建设，进一步发挥辽宁省科技计划对培育、发展高新技术产业集群和创新集群的引导示范作用，着力提高自主创新能力，不断增强区域内特色产业的竞争优势。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第五条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">发展特色产业基地的指导方针是：引导、示范、集聚、创新。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第六条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">辽宁省科学技术厅负责特色产业基地认定和管理工作。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第七条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">特色产业基地应当具备下列各项条件：</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（一）具有切实可行的发展规划。县级以上地方政府或省级以上高新区管委会已经制定了产业基地发展规划，并有相应的促进创新行为发生和创新能力提高的具体政策和配套措施。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（二）具有职责明确的管理、服务和产业发展促进机构。应成立由县级以上政府有关部门主要领导组成的产业基地建设发展领导小组，应明确科技行政部门对基地的归口管理和促进职能。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（三）具有较完备的创新服务平台。有一批支撑发展的创新创业服务和产业发展的促进机构，并且具有为产业基地内企业提供共性技术研发、产品检测、技术交易、人员培训、信息服务、投融资等服务功能。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（四）具有较强的技术创新能力。已初步建立以企业为主体的技术创新体系，开展了一定规模的产学研活动，获得了一批有产业化前景的专利和有广泛影响力的品牌；产业基地内有较好的科技、教育等环境，较丰富的研究成果和科技人才资源。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（五）基本具有产业集群特征。已初步形成在市场机制作用下的核心主导企业与配套的中小企业联系密切、分工协作的合理格局，以及较为完整且上、下游互动频繁的产业链条，已成立一定数量的促进产业集群发展的产业技术联盟等产业组织。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（六）具有较好的特色产业基础。产业和产品符合国家产业发展方向和政策，在国内市场具有较强竞争力并占有较大份额。主导产业集聚度较高，其年销售收入不低于</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">10</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">亿元。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（七）具有创新能力较强的骨干企业。有</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">家以上骨干企业；或有</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">家年销售收入超过</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">亿元的骨干企业和</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">10</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">家以上产业关联性强的中小企业。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第八条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">上述骨干企业应当具备下列各项条件：</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（一）经省科技行政管理部门认定的高新技术企业，其年销售收入超过</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">5000</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">万元；或为当地政府重点扶持的大型骨干企业，其年销售收入超过</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">1</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">亿元。</span><span style=\"font-size:11px;font-family: &#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（二）承担过辽宁省各类科技计划项目。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（三）研究开发（</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">R&amp;D</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">）的经费投入占年销售收入的比例在</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">3%</span><span style=\"font-size: 11px;font-family:宋体;color:#333333\">以上，新技术、新产品研发人员比例在</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">10%</span><span style=\"font-size:11px;font-family:宋体;color:#333333\">以上，建有企业研发机构，具备较强的技术创新能力和产业化能力。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（四）与基地内的企业有一定的配套关系，对周围相关的中小企业具有较强的辐射和带动作用。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（五）企业产权明晰，管理规范，运营良好。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第九条</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\">&nbsp; </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">特色产业基地的申报、认定程序：</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（一）基地所在地人民政府或高新区管委会根据特色产业基地认定条件，填报《辽宁省高新技术特色产业基地申报书》（附件一），《辽宁省高新技术特色产业基地发展规划方案》（附件二），《辽宁省高新技术特色产业基地骨干企业情况表》（附件三），《与基地主导产业相关联的科技型中小企业情况表》（附件四），《产业基地中介服务机构情况表》（附件五），向省科学技术厅提出申请。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">（二）省科学技术厅受理上报材料后，将召开专家评审会，组织有关专家对基地内产业集群发展现状、特色和优势，骨干企业和配套中小企业的经营情况，以及今后产业基地的工作思路和发展方向进行评审。必要时也可采取组织有关专家进行实地考察评审的方式。对于符合条件的，予以认定并授牌。</span><span style=\"font-size:11px;font-family:&#39;Simsun&#39;,&#39;serif&#39;;color:#333333\"><br/> </span><span style=\"font-size:11px;font-family:宋体;color:#333333\">第十条　本办法由省科学技术厅负责解释，自发布之日起实施。</span></p><p><br/></p>', '0', '0', '1', '2009-01-15 00:00:00', '1', '2017-11-13 13:32:27', 'cec3c5b1065c4e0e84a900512d97522e', '2017-11-16 16:57:45', '0', '');
