DROP TABLE IF EXISTS `site_policy_rule`;
CREATE TABLE `site_policy_rule`  (
  `id` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '主键标识',
  `code` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '编号',
  `policy_title` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标题',
  `policy_type` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '法规类型：0-科技动态；1-平台咨询；2-创新资源；3-融资政策；4-科技活动；5-其他；',
  `policy_group` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '发布机构',
  `policy_content` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '政策摘要',
  `is_show` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '是否显示：0-显示；1-隐藏',
  `home_recommend` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '是否首页推荐(0-是;1-否)',
  `policy_recommend` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '政策推荐：0-推荐；1-不推荐；',
  `public_date` datetime NULL DEFAULT NULL COMMENT '发布时间',
  `create_by` varchar(64) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '创建人',
  `create_date` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_by` varchar(64) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '修改人',
  `update_date` datetime NULL DEFAULT NULL COMMENT '修改时间',
  `del_flag` char(1) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '删除标识：0-未删除；1-已删除',
  `remarks` varchar(256) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '政策法规信息管理表' ROW_FORMAT = Dynamic;

